// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

/// @title IOrderTrigger
/// @notice Interface for pluggable order triggers in the Kapan conditional order system
/// @dev Triggers determine when an order should execute and how much to trade
interface IOrderTrigger {
    /// @notice Check if the order should execute based on current conditions
    /// @param staticData ABI-encoded trigger-specific parameters
    /// @param owner The order owner (user whose position is being monitored)
    /// @return shouldExecute True if the trigger condition is met
    /// @return reason Human-readable reason for the result (for debugging/logging)
    function shouldExecute(
        bytes calldata staticData,
        address owner
    ) external view returns (bool shouldExecute, string memory reason);

    /// @notice Calculate the execution amounts when the trigger fires
    /// @dev Only called when shouldExecute returns true
    /// @param staticData ABI-encoded trigger-specific parameters
    /// @param owner The order owner
    /// @param iterationCount Number of executions completed so far (for chunked orders)
    /// @return sellAmount Amount of sell token to trade in this execution
    /// @return minBuyAmount Minimum amount to receive (includes slippage protection)
    function calculateExecution(
        bytes calldata staticData,
        address owner,
        uint256 iterationCount
    ) external view returns (uint256 sellAmount, uint256 minBuyAmount);

    /// @notice Check if the order is complete and should stop executing
    /// @dev Called after each execution to determine if order should be marked complete
    /// @param staticData ABI-encoded trigger-specific parameters
    /// @param owner The order owner
    /// @param iterationCount Number of executions completed so far
    /// @return complete True if the order goal has been reached
    function isComplete(
        bytes calldata staticData,
        address owner,
        uint256 iterationCount
    ) external view returns (bool complete);

    /// @notice Get human-readable name of the trigger type
    /// @return name Trigger name (e.g., "LTV", "Price", "HealthFactor")
    function triggerName() external pure returns (string memory name);
}

contracts/v2/triggers/LimitPriceTrigger..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { IOrderTrigger } from "../interfaces/IOrderTrigger.sol";

/// @title KapanViewRouter Interface (for price and position queries)
interface IKapanViewRouter {
    function getCollateralPrice(
        bytes4 protocolId,
        address collateralToken,
        bytes calldata context
    ) external view returns (uint256 price);

    function getDebtPrice(
        bytes4 protocolId,
        address debtToken,
        bytes calldata context
    ) external view returns (uint256 price);

    function getPositionValue(
        bytes4 protocolId,
        address user,
        bytes calldata context
    ) external view returns (uint256 collateralValueUsd, uint256 debtValueUsd);
}

/// @title LimitPriceTrigger
/// @notice Triggers order execution when price crosses a limit threshold
/// @dev Designed for chunked limit orders - executes fixed chunks when price condition is met
///
/// Use cases:
/// - Take profit: Sell when price rises above limit
/// - Stop loss: Sell when price drops below limit
/// - DCA buy: Buy when price drops below limit
///
/// Chunking:
/// - totalSellAmount is the full amount to sell across all chunks
/// - numChunks determines how many iterations
/// - Each iteration sells totalSellAmount / numChunks
/// - Last iteration sells remaining to handle rounding
contract LimitPriceTrigger is IOrderTrigger {
    // ============ Errors ============
    error InvalidTriggerParams();

    // ============ Constants ============
    bytes4 public constant AAVE_V3 = bytes4(keccak256("aave-v3"));
    bytes4 public constant COMPOUND_V3 = bytes4(keccak256("compound-v3"));
    bytes4 public constant MORPHO_BLUE = bytes4(keccak256("morpho-blue"));

    // ============ Immutables ============
    IKapanViewRouter public immutable viewRouter;

    // ============ Structs ============

    /// @notice Trigger parameters for price-based limit orders
    struct TriggerParams {
        bytes4 protocolId;           // Protocol for price oracle
        bytes protocolContext;       // Protocol-specific context
        address sellToken;           // Token to sell
        address buyToken;            // Token to buy
        uint8 sellDecimals;          // Decimals of sell token
        uint8 buyDecimals;           // Decimals of buy token
        uint256 limitPrice;          // Limit price (8 decimals, like Chainlink)
        bool triggerAbovePrice;      // true = trigger when price >= limit (take profit)
                                     // false = trigger when price <= limit (stop loss)
        uint256 totalSellAmount;     // Total amount to sell across all chunks
        uint8 numChunks;             // Number of chunks (1 = single execution)
        uint256 maxSlippageBps;      // Maximum slippage tolerance
    }

    // ============ Constructor ============

    constructor(address _viewRouter) {
        if (_viewRouter == address(0)) revert InvalidTriggerParams();
        viewRouter = IKapanViewRouter(_viewRouter);
    }

    // ============ IOrderTrigger Implementation ============

    /// @inheritdoc IOrderTrigger
    function shouldExecute(
        bytes calldata staticData,
        address owner
    ) external view override returns (bool, string memory) {
        TriggerParams memory params = abi.decode(staticData, (TriggerParams));

        // Get current price (sellToken price in USD, 8 decimals)
        uint256 currentPrice = viewRouter.getCollateralPrice(
            params.protocolId,
            params.sellToken,
            params.protocolContext
        );

        if (currentPrice == 0) {
            return (false, "Price unavailable");
        }

        // Check price condition
        if (params.triggerAbovePrice) {
            if (currentPrice >= params.limitPrice) {
                return (true, "Price above limit");
            }
            return (false, "Price below limit");
        } else {
            if (currentPrice <= params.limitPrice) {
                return (true, "Price below limit");
            }
            return (false, "Price above limit");
        }
    }

    /// @inheritdoc IOrderTrigger
    function calculateExecution(
        bytes calldata staticData,
        address owner,
        uint256 iterationCount
    ) external view override returns (uint256 sellAmount, uint256 minBuyAmount) {
        TriggerParams memory params = abi.decode(staticData, (TriggerParams));

        uint8 chunks = params.numChunks > 0 ? params.numChunks : 1;

        // Calculate chunk size
        uint256 chunkSize = params.totalSellAmount / chunks;

        // For last chunk, use remaining to handle rounding
        if (iterationCount >= chunks - 1) {
            // Last chunk: sell everything remaining
            uint256 alreadySold = iterationCount * chunkSize;
            sellAmount = params.totalSellAmount > alreadySold
                ? params.totalSellAmount - alreadySold
                : 0;
        } else {
            sellAmount = chunkSize;
        }

        // For flash loan based swaps, we don't cap to wallet balance
        // The totalSellAmount and chunks define execution, post-hook handles withdrawal

        if (sellAmount == 0) return (0, 0);

        // Truncate precision to prevent order spam
        sellAmount = _truncatePrecision(sellAmount, params.sellDecimals);

        if (sellAmount == 0) return (0, 0);

        // Calculate minBuyAmount using LIMIT price (guarantees limit or better)
        // expectedBuy = sellAmount * sellPrice / buyPrice
        // Using limit price ensures we get at least the limit price
        uint256 buyPrice = viewRouter.getDebtPrice(
            params.protocolId,
            params.buyToken,
            params.protocolContext
        );

        if (buyPrice == 0) return (0, 0);

        // expectedBuy = sellAmount * limitPrice / buyPrice
        // Adjust for decimals: result in buyToken decimals
        uint256 expectedBuy = (sellAmount * params.limitPrice) / buyPrice;
        expectedBuy = (expectedBuy * (10 ** params.buyDecimals)) / (10 ** params.sellDecimals);

        // Truncate before slippage
        expectedBuy = _truncatePrecision(expectedBuy, params.buyDecimals);

        // Apply slippage after truncation
        minBuyAmount = (expectedBuy * (10000 - params.maxSlippageBps)) / 10000;
    }

    /// @inheritdoc IOrderTrigger
    function isComplete(
        bytes calldata staticData,
        address /* owner */,
        uint256 iterationCount
    ) external pure override returns (bool) {
        TriggerParams memory params = abi.decode(staticData, (TriggerParams));

        uint8 chunks = params.numChunks > 0 ? params.numChunks : 1;

        // Complete when all chunks executed
        return iterationCount >= chunks;
    }

    /// @inheritdoc IOrderTrigger
    function triggerName() external pure override returns (string memory) {
        return "LimitPrice";
    }

    // ============ View Helpers ============

    /// @notice Get current price for a token
    function getCurrentPrice(
        bytes4 protocolId,
        address token,
        bytes calldata context
    ) external view returns (uint256) {
        return viewRouter.getCollateralPrice(protocolId, token, context);
    }

    /// @notice Encode trigger params for order creation
    function encodeTriggerParams(TriggerParams memory params) external pure returns (bytes memory) {
        return abi.encode(params);
    }

    /// @notice Decode trigger params from static data
    function decodeTriggerParams(bytes calldata staticData) external pure returns (TriggerParams memory) {
        return abi.decode(staticData, (TriggerParams));
    }

    // ============ Internal Functions ============

    /// @dev Truncate precision to prevent order spam
    /// Same logic as LtvTrigger/AutoLeverageTrigger for consistency
    function _truncatePrecision(uint256 amount, uint8 decimals) internal pure returns (uint256) {
        if (decimals <= 4) return amount;
        uint256 keep;
        if (decimals > 12) keep = 5;
        else if (decimals > 6) keep = 6;
        else keep = 4;
        uint256 precision = 10 ** (decimals - keep);
        return (amount / precision) * precision;
    }
}
