// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { IEulerVault, IEulerPriceOracle } from "../../interfaces/euler/IEulerVault.sol";
import { IEVC } from "../../interfaces/euler/IEVC.sol";
import { IERC20Metadata } from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

/**
 * @title EulerGatewayView
 * @notice Read-only gateway for Euler V2 lending protocol
 * @dev Provides LTV queries and position data for ADL and frontend
 *
 * Sub-Account Model:
 *   - Each user has 256 possible sub-accounts (index 0-255)
 *   - Sub-account address = (userAddress & 0xFF...FF00) | subAccountIndex
 *   - Each sub-account can have at most 1 controller (debt) + N collaterals
 */
contract EulerGatewayView {
    /// @notice The Ethereum Vault Connector (EVC) singleton
    IEVC public immutable evc;

    error ZeroAddress();

    constructor(address evc_) {
        if (evc_ == address(0)) revert ZeroAddress();
        evc = IEVC(evc_);
    }

    // ============ Sub-Account Helpers ============

    /// @notice Get sub-account address from user and index
    /// @param user The user's main address
    /// @param subAccountIndex The sub-account index (0-255)
    /// @return The sub-account address
    function getSubAccount(address user, uint8 subAccountIndex) public pure returns (address) {
        return address(uint160(uint160(user) ^ subAccountIndex));
    }

    /// @notice Get the user's "main" sub-account (index matches last byte of address)
    /// @param user The user's main address
    /// @return The main sub-account address and its index
    function getMainSubAccount(address user) public pure returns (address, uint8) {
        uint8 mainIndex = uint8(uint160(user) & 0xFF);
        return (getSubAccount(user, mainIndex), mainIndex);
    }

    // ============ LTV Queries (for ADL) ============

    /// @notice Returns the current LTV (debt/collateral) in basis points
    /// @dev Uses Euler's native accountLiquidity function
    /// @param borrowVault The vault where the user has debt
    /// @param user The user address
    /// @param subAccountIndex The sub-account index
    /// @return Current LTV in basis points (e.g., 6500 = 65%)
    function getCurrentLtvBps(
        address borrowVault,
        address user,
        uint8 subAccountIndex
    ) external view returns (uint256) {
        address subAccount = getSubAccount(user, subAccountIndex);

        // Get account liquidity using liquidation=false (borrow LTV)
        // collateralValue is already LTV-adjusted, so we need raw values
        // Use liquidation=true to get values closer to raw (higher LTV means less adjustment)
        (uint256 collateralValueLiq, uint256 liabilityValue) = IEulerVault(borrowVault).accountLiquidity(subAccount, true);

        if (collateralValueLiq == 0 || liabilityValue == 0) return 0;

        // accountLiquidity returns collateral adjusted by LLTV, so:
        // rawCollateral ≈ collateralValueLiq / LLTV
        // currentLTV = liability / rawCollateral = liability * LLTV / collateralValueLiq

        // Get the effective LLTV (minimum across all collaterals)
        address[] memory collaterals = IEulerVault(borrowVault).LTVList();
        uint256 minLltv = 10000; // Default to 100% if no collaterals
        for (uint256 i = 0; i < collaterals.length; i++) {
            uint16 lltv = IEulerVault(borrowVault).LTVLiquidation(collaterals[i]);
            if (lltv > 0 && lltv < minLltv) {
                minLltv = lltv;
            }
        }

        // currentLTV (bps) = (liability * LLTV) / collateralValueLiq
        // Since LLTV is already in 1e4 scale (bps), and we want result in bps:
        // currentLTV = (liability * 10000) / collateralValueLiq * (10000 / LLTV)
        // Simplified: currentLTV = (liability * 10000 * 10000) / (collateralValueLiq * LLTV)

        if (minLltv == 0) return 0;
        return (liabilityValue * 10000 * 10000) / (collateralValueLiq * minLltv);
    }

    /// @notice Returns the current LTV using raw collateral/liability values
    /// @dev Alternative calculation that doesn't rely on LLTV adjustment
    /// @param borrowVault The vault where the user has debt
    /// @param user The user address
    /// @param subAccountIndex The sub-account index
    /// @return Current LTV in basis points
    function getCurrentLtvBpsSimple(
        address borrowVault,
        address user,
        uint8 subAccountIndex
    ) external view returns (uint256) {
        address subAccount = getSubAccount(user, subAccountIndex);

        // Get detailed liquidity to sum raw collateral values
        (
            address[] memory collaterals,
            uint256[] memory collateralValues,
            uint256 liabilityValue
        ) = IEulerVault(borrowVault).accountLiquidityFull(subAccount, true);

        if (liabilityValue == 0) return 0;

        // Sum raw collateral values (un-adjust by dividing by LLTV)
        uint256 rawCollateralValue = 0;
        for (uint256 i = 0; i < collaterals.length; i++) {
            if (collateralValues[i] > 0) {
                uint16 lltv = IEulerVault(borrowVault).LTVLiquidation(collaterals[i]);
                if (lltv > 0) {
                    // rawValue = adjustedValue * 10000 / LLTV
                    rawCollateralValue += (collateralValues[i] * 10000) / lltv;
                }
            }
        }

        if (rawCollateralValue == 0) return 0;

        // currentLTV = liability / rawCollateral * 10000
        return (liabilityValue * 10000) / rawCollateralValue;
    }

    /// @notice Returns the liquidation LTV threshold in basis points
    /// @dev Returns the minimum LLTV across all enabled collaterals
    /// @param borrowVault The vault where the user has debt
    /// @return Minimum liquidation threshold in basis points
    function getLiquidationLtvBps(address borrowVault) external view returns (uint256) {
        address[] memory collaterals = IEulerVault(borrowVault).LTVList();
        if (collaterals.length == 0) return 0;

        uint256 minLltv = type(uint256).max;
        for (uint256 i = 0; i < collaterals.length; i++) {
            uint16 lltv = IEulerVault(borrowVault).LTVLiquidation(collaterals[i]);
            if (lltv > 0 && lltv < minLltv) {
                minLltv = lltv;
            }
        }

        return minLltv == type(uint256).max ? 0 : minLltv;
    }

    /// @notice Returns the borrow LTV limit in basis points
    /// @dev Returns the minimum borrow LTV across all enabled collaterals
    /// @param borrowVault The vault where the user has debt
    /// @return Minimum borrow LTV in basis points
    function getBorrowLtvBps(address borrowVault) external view returns (uint256) {
        address[] memory collaterals = IEulerVault(borrowVault).LTVList();
        if (collaterals.length == 0) return 0;

        uint256 minLtv = type(uint256).max;
        for (uint256 i = 0; i < collaterals.length; i++) {
            uint16 ltv = IEulerVault(borrowVault).LTVBorrow(collaterals[i]);
            if (ltv > 0 && ltv < minLtv) {
                minLtv = ltv;
            }
        }

        return minLtv == type(uint256).max ? 0 : minLtv;
    }

    // ============ Position Queries ============

    /// @notice Check if a position is healthy (not liquidatable)
    /// @param borrowVault The vault where the user has debt
    /// @param user The user address
    /// @param subAccountIndex The sub-account index
    /// @return True if position is healthy
    function isHealthy(
        address borrowVault,
        address user,
        uint8 subAccountIndex
    ) external view returns (bool) {
        address subAccount = getSubAccount(user, subAccountIndex);
        (uint256 collateralValue, uint256 liabilityValue) = IEulerVault(borrowVault).accountLiquidity(subAccount, true);

        // Position is healthy if collateralValue >= liabilityValue (using liquidation LTV)
        return collateralValue >= liabilityValue;
    }

    /// @notice Get health factor (collateral / liability ratio)
    /// @param borrowVault The vault where the user has debt
    /// @param user The user address
    /// @param subAccountIndex The sub-account index
    /// @return Health factor in 1e18 scale (1e18 = 1.0)
    function getHealthFactor(
        address borrowVault,
        address user,
        uint8 subAccountIndex
    ) external view returns (uint256) {
        address subAccount = getSubAccount(user, subAccountIndex);
        (uint256 collateralValue, uint256 liabilityValue) = IEulerVault(borrowVault).accountLiquidity(subAccount, true);

        if (liabilityValue == 0) return type(uint256).max;
        return (collateralValue * 1e18) / liabilityValue;
    }

    /// @notice Get debt balance for a sub-account
    /// @param borrowVault The vault where the user has debt
    /// @param user The user address
    /// @param subAccountIndex The sub-account index
    /// @return Debt in underlying asset units
    function getDebtBalance(
        address borrowVault,
        address user,
        uint8 subAccountIndex
    ) external view returns (uint256) {
        address subAccount = getSubAccount(user, subAccountIndex);
        return IEulerVault(borrowVault).debtOf(subAccount);
    }

    /// @notice Get collateral balance for a sub-account in a specific vault
    /// @param collateralVault The vault where collateral is deposited
    /// @param user The user address
    /// @param subAccountIndex The sub-account index
    /// @return Collateral in underlying asset units
    function getCollateralBalance(
        address collateralVault,
        address user,
        uint8 subAccountIndex
    ) external view returns (uint256) {
        address subAccount = getSubAccount(user, subAccountIndex);
        return IEulerVault(collateralVault).maxWithdraw(subAccount);
    }

    // ============ Vault Info ============

    /// @notice Get vault's underlying asset
    /// @param vault The vault address
    /// @return The underlying asset address
    function getVaultAsset(address vault) external view returns (address) {
        return IEulerVault(vault).asset();
    }

    /// @notice Get vault's name
    /// @param vault The vault address
    /// @return The vault name
    function getVaultName(address vault) external view returns (string memory) {
        return IEulerVault(vault).name();
    }

    /// @notice Get vault's symbol
    /// @param vault The vault address
    /// @return The vault symbol
    function getVaultSymbol(address vault) external view returns (string memory) {
        return IEulerVault(vault).symbol();
    }

    /// @notice Get list of accepted collaterals for a borrow vault
    /// @param borrowVault The borrow vault address
    /// @return Array of collateral vault addresses with configured LTVs
    function getAcceptedCollaterals(address borrowVault) external view returns (address[] memory) {
        return IEulerVault(borrowVault).LTVList();
    }

    /// @notice Get LTV configuration for a specific collateral
    /// @param borrowVault The borrow vault address
    /// @param collateralVault The collateral vault address
    /// @return borrowLTV The borrow LTV in basis points
    /// @return liquidationLTV The liquidation LTV in basis points
    function getCollateralLtv(
        address borrowVault,
        address collateralVault
    ) external view returns (uint256 borrowLTV, uint256 liquidationLTV) {
        borrowLTV = IEulerVault(borrowVault).LTVBorrow(collateralVault);
        liquidationLTV = IEulerVault(borrowVault).LTVLiquidation(collateralVault);
    }

    // ============ Price Queries (for ADL) ============

    /// @notice Get exchange rate between two tokens via vault's oracle
    /// @dev Returns how many quote tokens you get for 1 whole unit of base token
    /// @param vault Any vault that has the oracle configured
    /// @param baseToken The token to price (e.g., WETH)
    /// @param quoteToken The unit of account (e.g., USDC)
    /// @return rate Exchange rate scaled by quoteToken decimals
    function getExchangeRate(
        address vault,
        address baseToken,
        address quoteToken
    ) external view returns (uint256 rate) {
        address oracle = IEulerVault(vault).oracle();
        if (oracle == address(0)) return 0;

        // Get 1 whole unit of base token
        uint8 baseDecimals = IERC20Metadata(baseToken).decimals();
        uint256 oneUnit = 10 ** baseDecimals;

        try IEulerPriceOracle(oracle).getQuote(oneUnit, baseToken, quoteToken) returns (uint256 quote) {
            return quote;
        } catch {
            return 0;
        }
    }

    /// @notice Get price of a token in the vault's unit of account
    /// @dev Returns the value of 1 whole unit of token
    /// @param vault The vault to query (determines oracle and unit of account)
    /// @param token The token to price
    /// @return price Price in unit of account decimals
    function getAssetPrice(
        address vault,
        address token
    ) external view returns (uint256 price) {
        address oracle = IEulerVault(vault).oracle();
        address unitOfAccount = IEulerVault(vault).unitOfAccount();
        if (oracle == address(0) || unitOfAccount == address(0)) return 0;

        uint8 tokenDecimals = IERC20Metadata(token).decimals();
        uint256 oneUnit = 10 ** tokenDecimals;

        try IEulerPriceOracle(oracle).getQuote(oneUnit, token, unitOfAccount) returns (uint256 quote) {
            return quote;
        } catch {
            return 0;
        }
    }

    /// @notice Get exchange rate between collateral and debt token for a position
    /// @dev Useful for ADL: how many debt tokens per collateral token
    /// @param borrowVault The borrow vault (debt side)
    /// @param collateralVault The collateral vault
    /// @return rate18 Exchange rate with 18 decimals (collateral → debt)
    function getCollateralToDebtRate(
        address borrowVault,
        address collateralVault
    ) external view returns (uint256 rate18) {
        address oracle = IEulerVault(borrowVault).oracle();
        address unitOfAccount = IEulerVault(borrowVault).unitOfAccount();
        if (oracle == address(0) || unitOfAccount == address(0)) return 0;

        address collateralAsset = IEulerVault(collateralVault).asset();
        address debtAsset = IEulerVault(borrowVault).asset();

        uint8 collateralDecimals = IERC20Metadata(collateralAsset).decimals();
        uint8 debtDecimals = IERC20Metadata(debtAsset).decimals();

        // Get value of 1 collateral in unit of account
        uint256 oneCollateral = 10 ** collateralDecimals;
        uint256 collateralValue;
        try IEulerPriceOracle(oracle).getQuote(oneCollateral, collateralAsset, unitOfAccount) returns (uint256 v) {
            collateralValue = v;
        } catch {
            return 0;
        }

        // Get value of 1 debt token in unit of account
        uint256 oneDebt = 10 ** debtDecimals;
        uint256 debtValue;
        try IEulerPriceOracle(oracle).getQuote(oneDebt, debtAsset, unitOfAccount) returns (uint256 v) {
            debtValue = v;
        } catch {
            return 0;
        }

        if (debtValue == 0) return 0;

        // exchangeRate = collateralValue / debtValue (how many debt tokens per collateral)
        // Scale to 18 decimals
        return (collateralValue * 1e18) / debtValue;
    }
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { ProtocolGateway } from "../../../gateways/ProtocolGateway.sol";
import { IGateway } from "../../interfaces/IGateway.sol";
import { ProtocolTypes } from "../../interfaces/ProtocolTypes.sol";
import { IEVC } from "../../interfaces/euler/IEVC.sol";
import { IEulerVault } from "../../interfaces/euler/IEulerVault.sol";

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title EulerGatewayWrite
 * @notice Gateway for Euler V2 lending protocol
 * @dev Euler V2 uses vault-based architecture with EVC (Ethereum Vault Connector)
 *
 * Context encoding for LendingInstruction:
 *   - bytes context = abi.encode(address borrowVault, address collateralVault, uint8 subAccountIndex)
 *   - borrowVault: The vault to borrow from (becomes controller)
 *   - collateralVault: The vault where collateral is deposited
 *   - subAccountIndex: Sub-account index (0-255)
 *
 * Sub-Account Model:
 *   - Each user has 256 possible sub-accounts (index 0-255)
 *   - Sub-account address = (userAddress & 0xFF...FF00) | subAccountIndex
 *   - Each sub-account can have at most 1 controller (debt) + N collaterals
 *   - User's "main" account has index = last byte of their wallet address
 *     (e.g., wallet 0x...a3 → main account is index 163)
 *
 * EVC Authorization Model:
 *   - Users must enable collateral vaults via EVC before they can back debt
 *   - Users must enable controller (borrow vault) before borrowing
 *   - Gateway uses operator authorization to act on behalf of users/sub-accounts
 */
contract EulerGatewayWrite is IGateway, ProtocolGateway, Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    /// @notice The Ethereum Vault Connector (EVC) singleton
    IEVC public immutable evc;

    error ZeroAddress();
    error InvalidContext();
    error UnsupportedOperation();

    constructor(
        address router,
        address owner_,
        address evc_
    ) ProtocolGateway(router) Ownable(owner_) {
        if (evc_ == address(0)) revert ZeroAddress();
        evc = IEVC(evc_);
    }

    // ============ IGateway Implementation ============

    function processLendingInstruction(
        ProtocolTypes.Output[] calldata inputs,
        bytes calldata data
    ) external onlyRouter returns (ProtocolTypes.Output[] memory) {
        ProtocolTypes.LendingInstruction memory ins = abi.decode(data, (ProtocolTypes.LendingInstruction));
        (address borrowVault, address[] memory collateralVaults, uint8 subAccountIndex) = _decodeContext(ins.context);

        // Derive sub-account address from user + index
        address subAccount = _getSubAccount(ins.user, subAccountIndex);

        // Resolve amount from input if referenced
        uint256 amount = ins.input.index < inputs.length ? inputs[ins.input.index].amount : ins.amount;

        // For single-vault operations, use the first collateral vault
        address collateralVault = collateralVaults.length > 0 ? collateralVaults[0] : address(0);
        return _dispatch(ins.op, borrowVault, collateralVault, amount, subAccount);
    }

    function _dispatch(
        ProtocolTypes.LendingOp op,
        address borrowVault,
        address collateralVault,
        uint256 amount,
        address subAccount
    ) internal returns (ProtocolTypes.Output[] memory) {
        // Deposit operations - no output
        if (op == ProtocolTypes.LendingOp.DepositCollateral) {
            _depositCollateral(collateralVault, amount, subAccount);
            return _noOutput();
        }

        // Collateral operations - output collateral token
        if (op == ProtocolTypes.LendingOp.WithdrawCollateral) {
            address token = IEulerVault(collateralVault).asset();
            return _output(token, _withdrawCollateral(collateralVault, amount, subAccount));
        }
        if (op == ProtocolTypes.LendingOp.GetSupplyBalance) {
            address token = IEulerVault(collateralVault).asset();
            return _output(token, _getSupplyBalance(collateralVault, subAccount));
        }

        // Borrow operations - output loan token
        if (op == ProtocolTypes.LendingOp.Borrow) {
            address token = IEulerVault(borrowVault).asset();
            return _output(token, _borrow(borrowVault, amount, subAccount));
        }
        if (op == ProtocolTypes.LendingOp.Repay) {
            address token = IEulerVault(borrowVault).asset();
            return _output(token, _repay(borrowVault, amount, subAccount));
        }
        if (op == ProtocolTypes.LendingOp.GetBorrowBalance) {
            address token = IEulerVault(borrowVault).asset();
            return _output(token, _getBorrowBalance(borrowVault, subAccount));
        }

        revert UnsupportedOperation();
    }

    // ============ Internal Write Functions ============

    function _depositCollateral(
        address vault,
        uint256 amount,
        address onBehalfOf
    ) internal nonReentrant {
        address token = IEulerVault(vault).asset();
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(token).forceApprove(vault, amount);

        // Deposit to vault, shares credited to onBehalfOf
        IEulerVault(vault).deposit(amount, onBehalfOf);
    }

    function _withdrawCollateral(
        address vault,
        uint256 amount,
        address user
    ) internal nonReentrant returns (uint256) {
        // Get user's actual balance (shares converted to assets)
        // Note: We don't use maxWithdraw() here because it considers health factor,
        // but EVC batch uses deferred liquidity checks - the health check happens
        // at the END of the batch, not during the withdrawal operation.
        uint256 shares = IEulerVault(vault).balanceOf(user);
        uint256 userBalance = IEulerVault(vault).convertToAssets(shares);

        if (shares == 0) return 0;

        // Determine if this is a full withdrawal
        // Use redeem(shares) for full withdrawal to avoid dust from rounding
        bool isFullWithdrawal = amount >= userBalance;

        address token = IEulerVault(vault).asset();
        uint256 preBal = IERC20(token).balanceOf(address(this));

        // Use EVC batch to withdraw on behalf of user
        // Gateway must be authorized as operator via EVC
        // The EVC batch uses deferred liquidity checks - health is validated
        // at the end of the batch, allowing withdrawals that would fail
        // if checked immediately (e.g., partial withdrawals with active debt)
        IEVC.BatchItem[] memory items = new IEVC.BatchItem[](1);

        if (isFullWithdrawal) {
            // Full withdrawal: use redeem(shares) to withdraw ALL shares without dust
            items[0] = IEVC.BatchItem({
                targetContract: vault,
                onBehalfOfAccount: user,
                value: 0,
                data: abi.encodeCall(IEulerVault.redeem, (shares, address(this), user))
            });
        } else {
            // Partial withdrawal: use withdraw(amount) for exact asset amount
            items[0] = IEVC.BatchItem({
                targetContract: vault,
                onBehalfOfAccount: user,
                value: 0,
                data: abi.encodeCall(IEulerVault.withdraw, (amount, address(this), user))
            });
        }

        evc.batch(items);

        uint256 postBal = IERC20(token).balanceOf(address(this));
        uint256 withdrawn = postBal - preBal;

        // Forward to router
        IERC20(token).safeTransfer(msg.sender, withdrawn);

        return withdrawn;
    }

    function _borrow(
        address vault,
        uint256 amount,
        address user
    ) internal nonReentrant returns (uint256) {
        // In Euler V2, debt is attributed to the caller (msg.sender) or onBehalfOfAccount via EVC
        // Since gateway is the caller, we must use EVC batch to borrow on behalf of user
        // The borrowed assets are sent to this contract, then forwarded to router

        address token = IEulerVault(vault).asset();
        uint256 preBal = IERC20(token).balanceOf(address(this));

        // Build EVC batch item to borrow on behalf of user
        IEVC.BatchItem[] memory items = new IEVC.BatchItem[](1);
        items[0] = IEVC.BatchItem({
            targetContract: vault,
            onBehalfOfAccount: user,
            value: 0,
            data: abi.encodeCall(IEulerVault.borrow, (amount, address(this)))
        });

        evc.batch(items);

        uint256 postBal = IERC20(token).balanceOf(address(this));
        uint256 borrowed = postBal - preBal;

        IERC20(token).safeTransfer(msg.sender, borrowed);

        return borrowed;
    }

    function _repay(
        address vault,
        uint256 amount,
        address user
    ) internal nonReentrant returns (uint256 refund) {
        address token = IEulerVault(vault).asset();
        uint256 pre = IERC20(token).balanceOf(address(this));

        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(token).forceApprove(vault, amount);

        // Get actual debt to determine repay amount
        uint256 debt = IEulerVault(vault).debtOf(user);

        if (debt > 0) {
            uint256 toRepay = amount > debt ? debt : amount;
            IEulerVault(vault).repay(toRepay, user);
        }

        uint256 post = IERC20(token).balanceOf(address(this));
        refund = post > pre ? post - pre : 0;

        if (refund > 0) {
            IERC20(token).safeTransfer(msg.sender, refund);
        }
    }

    // ============ View Functions ============

    function _getBorrowBalance(address vault, address user) internal view returns (uint256) {
        return IEulerVault(vault).debtOf(user);
    }

    function _getSupplyBalance(address vault, address user) internal view returns (uint256) {
        // Convert shares to assets for accurate balance
        uint256 shares = IEulerVault(vault).balanceOf(user);
        return IEulerVault(vault).convertToAssets(shares);
    }

    // ============ Authorization ============

    /// @dev Helper struct to reduce stack depth in authorize()
    struct AuthState {
        uint256 targetIdx;
        uint256 pIdx;
        address subAccount;
        bool isOperator;
        bool operatorEmitted;
    }

    function authorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address caller,
        ProtocolTypes.Output[] calldata inputs
    ) external view returns (address[] memory targets, bytes[] memory data, ProtocolTypes.Output[] memory produced) {
        // Count outputs
        uint256 outCount = _countOutputs(instrs);
        produced = new ProtocolTypes.Output[](outCount);

        // Maximum possible targets: operator + multiple collateral enables + controller disable + controller enable per instruction
        // Assuming max 10 collaterals per instruction to be safe
        targets = new address[](1 + instrs.length * 12);
        data = new bytes[](1 + instrs.length * 12);

        AuthState memory state;
        // Note: isOperator is checked per sub-account in _processAuthInstruction

        for (uint256 i = 0; i < instrs.length; i++) {
            _processAuthInstruction(instrs[i], caller, inputs, targets, data, produced, state);
        }

        // Compact arrays to actual size
        assembly {
            mstore(targets, mload(state))
            mstore(data, mload(state))
        }
    }

    function _countOutputs(ProtocolTypes.LendingInstruction[] calldata instrs) internal pure returns (uint256 count) {
        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingOp op = instrs[i].op;
            if (
                op == ProtocolTypes.LendingOp.WithdrawCollateral ||
                op == ProtocolTypes.LendingOp.Borrow ||
                op == ProtocolTypes.LendingOp.Repay ||
                op == ProtocolTypes.LendingOp.GetBorrowBalance ||
                op == ProtocolTypes.LendingOp.GetSupplyBalance
            ) {
                count++;
            }
        }
    }

    function _processAuthInstruction(
        ProtocolTypes.LendingInstruction calldata ins,
        address caller,
        ProtocolTypes.Output[] calldata inputs,
        address[] memory targets,
        bytes[] memory data,
        ProtocolTypes.Output[] memory produced,
        AuthState memory state
    ) internal view {
        (address borrowVault, address[] memory collateralVaults, uint8 subAccountIndex) = _decodeContext(ins.context);
        address subAccount = _getSubAccount(caller, subAccountIndex);
        uint256 amount = ins.input.index < inputs.length ? inputs[ins.input.index].amount : ins.amount;

        // For single-vault operations, use the first collateral vault
        address collateralVault = collateralVaults.length > 0 ? collateralVaults[0] : address(0);

        // Update state with current sub-account and check operator status
        if (state.subAccount != subAccount) {
            state.subAccount = subAccount;
            state.isOperator = evc.isAccountOperatorAuthorized(subAccount, address(this));
            state.operatorEmitted = false;
        }

        if (ins.op == ProtocolTypes.LendingOp.DepositCollateral) {
            _authDepositCollateral(subAccount, collateralVault, targets, data, state);
        } else if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
            _authWithdrawCollateral(subAccount, collateralVault, ins.token, amount, targets, data, produced, state);
        } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
            // For borrow, enable ALL collateral vaults for sufficient LTV
            _authBorrow(subAccount, borrowVault, collateralVaults, ins.token, amount, targets, data, produced, state);
        } else if (ins.op == ProtocolTypes.LendingOp.GetBorrowBalance) {
            // Query actual balance from vault and apply 0.1% buffer to handle interest accrual
            uint256 bal = IEulerVault(borrowVault).debtOf(subAccount);
            bal = (bal * 1001) / 1000; // 0.1% buffer
            produced[state.pIdx++] = ProtocolTypes.Output({ token: ins.token, amount: bal });
        } else if (ins.op == ProtocolTypes.LendingOp.GetSupplyBalance) {
            // Query actual balance from vault and apply 0.1% buffer
            uint256 shares = IEulerVault(collateralVault).balanceOf(subAccount);
            uint256 bal = IEulerVault(collateralVault).convertToAssets(shares);
            bal = (bal * 1001) / 1000; // 0.1% buffer
            produced[state.pIdx++] = ProtocolTypes.Output({ token: ins.token, amount: bal });
        } else if (ins.op == ProtocolTypes.LendingOp.Repay) {
            produced[state.pIdx++] = ProtocolTypes.Output({ token: ins.token, amount: 0 });
        }
    }

    function _authDepositCollateral(
        address caller,
        address collateralVault,
        address[] memory targets,
        bytes[] memory data,
        AuthState memory state
    ) internal view {
        if (!evc.isCollateralEnabled(caller, collateralVault)) {
            targets[state.targetIdx] = address(evc);
            data[state.targetIdx] = abi.encodeWithSelector(IEVC.enableCollateral.selector, caller, collateralVault);
            state.targetIdx++;
        }
    }

    function _authWithdrawCollateral(
        address caller,
        address /* collateralVault */,
        address token,
        uint256 amount,
        address[] memory targets,
        bytes[] memory data,
        ProtocolTypes.Output[] memory produced,
        AuthState memory state
    ) internal view {
        if (!state.isOperator && !state.operatorEmitted) {
            targets[state.targetIdx] = address(evc);
            data[state.targetIdx] = abi.encodeWithSelector(IEVC.setAccountOperator.selector, caller, address(this), true);
            state.targetIdx++;
            state.operatorEmitted = true;
        }
        produced[state.pIdx++] = ProtocolTypes.Output({ token: token, amount: amount });
    }

    function _authBorrow(
        address caller,
        address borrowVault,
        address[] memory collateralVaults,
        address token,
        uint256 amount,
        address[] memory targets,
        bytes[] memory data,
        ProtocolTypes.Output[] memory produced,
        AuthState memory state
    ) internal view {
        // Operator authorization
        if (!state.isOperator && !state.operatorEmitted) {
            targets[state.targetIdx] = address(evc);
            data[state.targetIdx] = abi.encodeWithSelector(IEVC.setAccountOperator.selector, caller, address(this), true);
            state.targetIdx++;
            state.operatorEmitted = true;
        }
        // Enable ALL collateral vaults for sufficient LTV
        for (uint256 i = 0; i < collateralVaults.length; i++) {
            address collateralVault = collateralVaults[i];
            if (collateralVault != address(0) && !evc.isCollateralEnabled(caller, collateralVault)) {
                targets[state.targetIdx] = address(evc);
                data[state.targetIdx] = abi.encodeWithSelector(IEVC.enableCollateral.selector, caller, collateralVault);
                state.targetIdx++;
            }
        }
        // Enable controller - but first check if we need to disable an old one
        // In Euler V2, a sub-account can only have ONE controller at a time
        if (!evc.isControllerEnabled(caller, borrowVault)) {
            // Check for existing controllers that need to be disabled
            address[] memory existingControllers = evc.getControllers(caller);
            for (uint256 i = 0; i < existingControllers.length; i++) {
                address existingController = existingControllers[i];
                if (existingController != borrowVault) {
                    // Check if debt is 0 - only then can we disable the old controller
                    uint256 debt = IEulerVault(existingController).debtOf(caller);
                    if (debt == 0) {
                        // Use evc.call() to invoke vault.disableController() in sub-account context
                        // This is the correct Euler V2 pattern per docs
                        targets[state.targetIdx] = address(evc);
                        data[state.targetIdx] = abi.encodeWithSelector(
                            IEVC.call.selector,
                            existingController,  // target = the old borrow vault
                            caller,              // onBehalfOfAccount = sub-account
                            uint256(0),          // value = 0
                            abi.encodeWithSelector(IEulerVault.disableController.selector)
                        );
                        state.targetIdx++;
                    }
                    // Note: if debt > 0, the enableController will fail, which is correct behavior
                    // User must repay existing debt before switching to a new borrow vault
                }
            }
            // Now enable the new controller
            targets[state.targetIdx] = address(evc);
            data[state.targetIdx] = abi.encodeWithSelector(IEVC.enableController.selector, caller, borrowVault);
            state.targetIdx++;
        }
        produced[state.pIdx++] = ProtocolTypes.Output({ token: token, amount: amount });
    }

    function deauthorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address caller,
        ProtocolTypes.Output[] calldata /*inputs*/
    ) external view override returns (address[] memory targets, bytes[] memory data) {
        // Collect unique sub-accounts that need deauthorization
        // Max is instrs.length different sub-accounts
        address[] memory subAccountsToDeauth = new address[](instrs.length);
        uint256 deauthCount = 0;

        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingOp op = instrs[i].op;
            if (op == ProtocolTypes.LendingOp.WithdrawCollateral || op == ProtocolTypes.LendingOp.Borrow) {
                (,, uint8 subAccountIndex) = _decodeContext(instrs[i].context);
                address subAccount = _getSubAccount(caller, subAccountIndex);

                // Check if already in list
                bool found = false;
                for (uint256 j = 0; j < deauthCount; j++) {
                    if (subAccountsToDeauth[j] == subAccount) {
                        found = true;
                        break;
                    }
                }
                if (!found) {
                    subAccountsToDeauth[deauthCount++] = subAccount;
                }
            }
        }

        if (deauthCount > 0) {
            // Revoke operator authorization for each sub-account
            targets = new address[](deauthCount);
            data = new bytes[](deauthCount);
            for (uint256 i = 0; i < deauthCount; i++) {
                targets[i] = address(evc);
                data[i] = abi.encodeWithSelector(
                    IEVC.setAccountOperator.selector,
                    subAccountsToDeauth[i],
                    address(this),
                    false
                );
            }
        } else {
            targets = new address[](0);
            data = new bytes[](0);
        }
    }

    // ============ Helpers ============

    /**
     * @notice Decode context bytes into vault addresses and sub-account index
     * @dev Context format: abi.encode(address borrowVault, address collateralVault, uint8 subAccountIndex)
     */
    function _decodeContext(bytes memory ctx) internal pure returns (address borrowVault, address[] memory collateralVaults, uint8 subAccountIndex) {
        (borrowVault, collateralVaults, subAccountIndex) = abi.decode(ctx, (address, address[], uint8));
    }

    /**
     * @notice Derive sub-account address from user address and index
     * @param user The main user address
     * @param subAccountIndex Index 0-255
     * @return The sub-account address
     * @dev Sub-account = (user & 0xFF...FF00) | subAccountIndex
     *      The user's "main" account has index = last byte of their address.
     *      For example, user 0x...a3 has main account at index 163 (0xa3).
     *      Index 0 is a sub-account at address 0x...00 (NOT the same as user address
     *      unless user address happens to end with 0x00).
     */
    function _getSubAccount(address user, uint8 subAccountIndex) internal pure returns (address) {
        // Always apply the formula - don't special-case index 0
        return address(uint160(uint160(user) & ~uint160(0xFF)) | uint160(subAccountIndex));
    }

    function _noOutput() internal pure returns (ProtocolTypes.Output[] memory) {
        return new ProtocolTypes.Output[](0);
    }

    function _output(address token, uint256 amount) internal pure returns (ProtocolTypes.Output[] memory outputs) {
        outputs = new ProtocolTypes.Output[](1);
        outputs[0] = ProtocolTypes.Output({ token: token, amount: amount });
    }

    // ============ Emergency Recovery ============

    /// @notice Recover stuck tokens (owner only)
    function recoverTokens(address token, address to, uint256 amount) external onlyOwner {
        uint256 balance = IERC20(token).balanceOf(address(this));
        uint256 toRecover = amount == type(uint256).max ? balance : amount;
        if (toRecover > balance) toRecover = balance;
        if (toRecover > 0) {
            IERC20(token).safeTransfer(to, toRecover);
        }
    }
}

contracts/v2/interfaces/euler/IEulerVaul.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

/// @title IEulerVault
/// @notice Interface for Euler V2 vaults (EVK - Euler Vault Kit)
/// @dev Extends ERC-4626 with borrowing functionality
interface IEulerVault {
    // ============ ERC-4626 Standard Functions ============

    /// @notice The underlying asset of the vault
    function asset() external view returns (address);

    /// @notice Total assets held by the vault
    function totalAssets() external view returns (uint256);

    /// @notice Convert assets to shares
    function convertToShares(uint256 assets) external view returns (uint256);

    /// @notice Convert shares to assets
    function convertToAssets(uint256 shares) external view returns (uint256);

    /// @notice Deposit assets and receive shares
    /// @param assets Amount of assets to deposit
    /// @param receiver Address to receive shares
    /// @return shares Amount of shares minted
    function deposit(uint256 assets, address receiver) external returns (uint256 shares);

    /// @notice Withdraw assets by burning shares
    /// @param assets Amount of assets to withdraw
    /// @param receiver Address to receive assets
    /// @param owner Address whose shares will be burned
    /// @return shares Amount of shares burned
    function withdraw(uint256 assets, address receiver, address owner) external returns (uint256 shares);

    /// @notice Redeem shares for assets
    /// @param shares Amount of shares to redeem
    /// @param receiver Address to receive assets
    /// @param owner Address whose shares will be burned
    /// @return assets Amount of assets received
    function redeem(uint256 shares, address receiver, address owner) external returns (uint256 assets);

    /// @notice Get share balance of an account
    function balanceOf(address account) external view returns (uint256);

    /// @notice Maximum withdrawable assets for an owner
    function maxWithdraw(address owner) external view returns (uint256);

    /// @notice Maximum redeemable shares for an owner
    function maxRedeem(address owner) external view returns (uint256);

    // ============ EVK Borrowing Extensions ============

    /// @notice Borrow assets from the vault
    /// @param assets Amount of assets to borrow
    /// @param receiver Address to receive borrowed assets
    /// @return Amount actually borrowed
    function borrow(uint256 assets, address receiver) external returns (uint256);

    /// @notice Repay borrowed assets
    /// @param assets Amount of assets to repay
    /// @param receiver Address of the borrower whose debt to repay
    /// @return Amount actually repaid
    function repay(uint256 assets, address receiver) external returns (uint256);

    /// @notice Get debt balance of an account
    /// @param account Address to query
    /// @return Amount of debt in asset units
    function debtOf(address account) external view returns (uint256);

    /// @notice Get debt balance in shares
    /// @param account Address to query
    /// @return Amount of debt shares
    function debtOfExact(address account) external view returns (uint256);

    /// @notice Total borrowed assets
    function totalBorrows() external view returns (uint256);

    // ============ Interest Rate ============

    /// @notice Current interest rate
    function interestRate() external view returns (uint256);

    /// @notice Interest accumulator for debt calculation
    function interestAccumulator() external view returns (uint256);

    // ============ LTV Configuration ============

    /// @notice Get the borrow LTV for a collateral (max LTV for new borrows)
    /// @param collateral The collateral vault address
    /// @return Borrow LTV in 1e4 scale (8000 = 80%)
    function LTVBorrow(address collateral) external view returns (uint16);

    /// @notice Get the liquidation LTV for a collateral (LLTV threshold)
    /// @param collateral The collateral vault address
    /// @return Liquidation LTV in 1e4 scale (9000 = 90%)
    function LTVLiquidation(address collateral) external view returns (uint16);

    /// @notice Get full LTV configuration including ramping parameters
    /// @param collateral The collateral vault address
    /// @return borrowLTV The borrow LTV
    /// @return liquidationLTV The current liquidation LTV
    /// @return initialLiquidationLTV The initial liquidation LTV before ramping
    /// @return targetTimestamp When ramping completes
    /// @return rampDuration Duration of the ramp
    function LTVFull(address collateral) external view returns (
        uint16 borrowLTV,
        uint16 liquidationLTV,
        uint16 initialLiquidationLTV,
        uint48 targetTimestamp,
        uint32 rampDuration
    );

    /// @notice Get list of all collaterals with configured LTVs
    /// @return Array of collateral vault addresses
    function LTVList() external view returns (address[] memory);

    // ============ Account Liquidity ============

    /// @notice Get account liquidity (health check)
    /// @param account The account to check
    /// @param liquidation If true, use liquidation LTV; if false, use borrow LTV
    /// @return collateralValue Total collateral value in unit of account
    /// @return liabilityValue Total liability value in unit of account
    function accountLiquidity(address account, bool liquidation)
        external view returns (uint256 collateralValue, uint256 liabilityValue);

    /// @notice Get detailed account liquidity per collateral
    /// @param account The account to check
    /// @param liquidation If true, use liquidation LTV; if false, use borrow LTV
    /// @return collaterals Array of collateral addresses
    /// @return collateralValues Array of collateral values
    /// @return liabilityValue Total liability value
    function accountLiquidityFull(address account, bool liquidation)
        external view returns (
            address[] memory collaterals,
            uint256[] memory collateralValues,
            uint256 liabilityValue
        );

    // ============ Controller Management ============

    /// @notice Disable this vault as controller for the authenticated account
    /// @dev Must be called through EVC.call() with proper onBehalfOfAccount context
    ///      Reverts if the account has outstanding debt to this vault
    function disableController() external;

    // ============ Vault Info ============

    /// @notice Name of the vault token
    function name() external view returns (string memory);

    /// @notice Symbol of the vault token
    function symbol() external view returns (string memory);

    /// @notice Decimals of the vault token
    function decimals() external view returns (uint8);

    // ============ Oracle & Unit of Account ============

    /// @notice Get the price oracle for this vault
    function oracle() external view returns (address);

    /// @notice Get the unit of account (e.g., USD, ETH)
    function unitOfAccount() external view returns (address);
}

/// @title IEulerPriceOracle
/// @notice Interface for Euler's price oracle system
interface IEulerPriceOracle {
    /// @notice Get a quote for swapping base to quote tokens
    /// @param inAmount Amount of base tokens
    /// @param base The base token address
    /// @param quote The quote token address (unit of account)
    /// @return outAmount Amount of quote tokens
    function getQuote(
        uint256 inAmount,
        address base,
        address quote
    ) external view returns (uint256 outAmount);

    /// @notice Get both bid and ask quotes
    /// @param inAmount Amount of base tokens
    /// @param base The base token address
    /// @param quote The quote token address
    /// @return bidOutAmount Amount at bid price
    /// @return askOutAmount Amount at ask price
    function getQuotes(
        uint256 inAmount,
        address base,
        address quote
    ) external view returns (uint256 bidOutAmount, uint256 askOutAmount);
}

contracts/v2/interfaces/euler/IEVC.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

/// @title IEVC
/// @notice Interface for the Ethereum Vault Connector (EVC)
/// @dev The EVC mediates between vaults in the Euler V2 protocol
interface IEVC {
    /// @notice Batch operation item
    struct BatchItem {
        address targetContract;
        address onBehalfOfAccount;
        uint256 value;
        bytes data;
    }

    /// @notice Execute a batch of operations
    /// @param items Array of batch items to execute
    function batch(BatchItem[] calldata items) external payable;

    /// @notice Execute a call on behalf of an account
    /// @param targetContract The contract to call
    /// @param onBehalfOfAccount The account context for the call
    /// @param value ETH value to send
    /// @param data The calldata to execute
    /// @return result The return data from the call
    function call(
        address targetContract,
        address onBehalfOfAccount,
        uint256 value,
        bytes calldata data
    ) external payable returns (bytes memory result);

    /// @notice Enable a vault as collateral for an account
    /// @param account The account to enable collateral for
    /// @param vault The vault to enable as collateral
    function enableCollateral(address account, address vault) external;

    /// @notice Disable a vault as collateral for an account
    /// @param account The account to disable collateral for
    /// @param vault The vault to disable as collateral
    function disableCollateral(address account, address vault) external;

    /// @notice Enable a vault as controller for an account (allows borrowing)
    /// @param account The account to enable controller for
    /// @param vault The vault to enable as controller
    function enableController(address account, address vault) external;

    /// @notice Disable controller for an account
    /// @dev Will revert if account has outstanding debt
    /// @param account The account to disable controller for
    function disableController(address account) external;

    /// @notice Check if a vault is enabled as collateral for an account
    /// @param account The account to check
    /// @param vault The vault to check
    /// @return True if the vault is enabled as collateral
    function isCollateralEnabled(address account, address vault) external view returns (bool);

    /// @notice Check if a vault is enabled as controller for an account
    /// @param account The account to check
    /// @param vault The vault to check
    /// @return True if the vault is enabled as controller
    function isControllerEnabled(address account, address vault) external view returns (bool);

    /// @notice Get all collateral vaults for an account
    /// @param account The account to query
    /// @return Array of vault addresses enabled as collateral
    function getCollaterals(address account) external view returns (address[] memory);

    /// @notice Get all controller vaults for an account
    /// @param account The account to query
    /// @return Array of vault addresses enabled as controller
    function getControllers(address account) external view returns (address[] memory);

    /// @notice Set or revoke an operator for an account
    /// @param account The account to set operator for
    /// @param operator The operator address
    /// @param authorized True to authorize, false to revoke
    function setAccountOperator(address account, address operator, bool authorized) external;

    /// @notice Check if an operator is authorized for an account
    /// @param account The account to check
    /// @param operator The operator to check
    /// @return True if the operator is authorized
    function isAccountOperatorAuthorized(address account, address operator) external view returns (bool);

    /// @notice Get the owner of an account (handles sub-accounts)
    /// @param account The account address
    /// @return The owner address
    function getAccountOwner(address account) external view returns (address);
}

contracts/v2/interfaces/IGateway.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { ProtocolTypes } from "./ProtocolTypes.sol";

interface IGateway {
    function processLendingInstruction(
        ProtocolTypes.Output[] calldata inputs,
        bytes calldata data
    ) external returns (ProtocolTypes.Output[] memory outputs);

    /// @notice Return user-side authorization calls for the given lending instructions.
    ///         One element per input (or (address(0), bytes("")) if none needed).
    ///         Also returns the produced outputs from the simulation of these instructions.
    function authorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address caller,
        ProtocolTypes.Output[] calldata inputs
    ) external view returns (address[] memory targets, bytes[] memory data, ProtocolTypes.Output[] memory produced);

    /// @notice Return user-side deauthorization calls for the given lending instructions.
    ///         Used to revoke permissions after execution.
    function deauthorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address caller,
        ProtocolTypes.Output[] calldata inputs
    ) external view returns (address[] memory targets, bytes[] memory data);
}

contracts/v2/interfaces/morpho/IMorphoBl.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

/// @title IMorphoBlue Interface
/// @notice Interface for Morpho Blue lending protocol
/// @dev See https://github.com/morpho-org/morpho-blue

struct MarketParams {
    address loanToken;
    address collateralToken;
    address oracle;
    address irm;
    uint256 lltv;
}

struct Market {
    uint128 totalSupplyAssets;
    uint128 totalSupplyShares;
    uint128 totalBorrowAssets;
    uint128 totalBorrowShares;
    uint128 lastUpdate;
    uint128 fee;
}

struct Position {
    uint256 supplyShares;
    uint128 borrowShares;
    uint128 collateral;
}

interface IMorphoBlue {
    /// @notice The state of a market (by id)
    function market(bytes32 id) external view returns (Market memory);

    /// @notice The position of a user in a market
    function position(bytes32 id, address user) external view returns (Position memory);

    /// @notice Whether an address is authorized to act on behalf of another
    function isAuthorized(address authorizer, address authorized) external view returns (bool);

    /// @notice The nonce of an authorizer for EIP-712 signatures
    function nonce(address authorizer) external view returns (uint256);

    /// @notice Compute market ID from market params
    function idToMarketParams(bytes32 id) external view returns (MarketParams memory);

    // ============ Write Functions ============

    /// @notice Supply assets to a market (lender side - earns yield)
    /// @param marketParams The market to supply to
    /// @param assets The amount of assets to supply (use 0 if using shares)
    /// @param shares The amount of shares to mint (use 0 if using assets)
    /// @param onBehalf The address to supply on behalf of
    /// @param data Callback data (empty for no callback)
    /// @return assetsSupplied The actual amount of assets supplied
    /// @return sharesSupplied The actual amount of shares minted
    function supply(
        MarketParams memory marketParams,
        uint256 assets,
        uint256 shares,
        address onBehalf,
        bytes memory data
    ) external returns (uint256 assetsSupplied, uint256 sharesSupplied);

    /// @notice Withdraw assets from a market (lender side)
    /// @param marketParams The market to withdraw from
    /// @param assets The amount of assets to withdraw (use 0 if using shares)
    /// @param shares The amount of shares to burn (use 0 if using assets)
    /// @param onBehalf The address to withdraw on behalf of
    /// @param receiver The address to receive the withdrawn assets
    /// @return assetsWithdrawn The actual amount of assets withdrawn
    /// @return sharesWithdrawn The actual amount of shares burned
    function withdraw(
        MarketParams memory marketParams,
        uint256 assets,
        uint256 shares,
        address onBehalf,
        address receiver
    ) external returns (uint256 assetsWithdrawn, uint256 sharesWithdrawn);

    /// @notice Borrow assets from a market
    /// @param marketParams The market to borrow from
    /// @param assets The amount of assets to borrow (use 0 if using shares)
    /// @param shares The amount of borrow shares to mint (use 0 if using assets)
    /// @param onBehalf The address to borrow on behalf of
    /// @param receiver The address to receive the borrowed assets
    /// @return assetsBorrowed The actual amount of assets borrowed
    /// @return sharesBorrowed The actual amount of borrow shares minted
    function borrow(
        MarketParams memory marketParams,
        uint256 assets,
        uint256 shares,
        address onBehalf,
        address receiver
    ) external returns (uint256 assetsBorrowed, uint256 sharesBorrowed);

    /// @notice Repay borrowed assets
    /// @param marketParams The market to repay to
    /// @param assets The amount of assets to repay (use 0 if using shares)
    /// @param shares The amount of borrow shares to burn (use 0 if using assets)
    /// @param onBehalf The address to repay on behalf of
    /// @param data Callback data (empty for no callback)
    /// @return assetsRepaid The actual amount of assets repaid
    /// @return sharesRepaid The actual amount of borrow shares burned
    function repay(
        MarketParams memory marketParams,
        uint256 assets,
        uint256 shares,
        address onBehalf,
        bytes memory data
    ) external returns (uint256 assetsRepaid, uint256 sharesRepaid);

    /// @notice Supply collateral to a market
    /// @param marketParams The market to supply collateral to
    /// @param assets The amount of collateral assets to supply
    /// @param onBehalf The address to supply on behalf of
    /// @param data Callback data (empty for no callback)
    function supplyCollateral(
        MarketParams memory marketParams,
        uint256 assets,
        address onBehalf,
        bytes memory data
    ) external;

    /// @notice Withdraw collateral from a market
    /// @param marketParams The market to withdraw collateral from
    /// @param assets The amount of collateral assets to withdraw
    /// @param onBehalf The address to withdraw on behalf of
    /// @param receiver The address to receive the withdrawn collateral
    function withdrawCollateral(
        MarketParams memory marketParams,
        uint256 assets,
        address onBehalf,
        address receiver
    ) external;

    /// @notice Set authorization for an address to act on behalf of the caller
    /// @param authorized The address to authorize/deauthorize
    /// @param newIsAuthorized Whether to authorize (true) or deauthorize (false)
    function setAuthorization(address authorized, bool newIsAuthorized) external;

    /// @notice Set authorization with signature (EIP-712)
    function setAuthorizationWithSig(
        address authorizer,
        address authorized,
        bool newIsAuthorized,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external;

    /// @notice Accrue interest for a market
    function accrueInterest(MarketParams memory marketParams) external;

    /// @notice Executes a flash loan.
    /// @dev Flash loans have access to the whole balance of the contract (all markets combined).
    /// @dev Zero fees - flashFee is always zero.
    /// @param token The token to flash loan.
    /// @param assets The amount of assets to flash loan.
    /// @param data Arbitrary data to pass to the `onMorphoFlashLoan` callback.
    function flashLoan(address token, uint256 assets, bytes calldata data) external;
}

/// @title IMorphoFlashLoanCallback
/// @notice Interface that users willing to use `flashLoan`'s callback must implement.
interface IMorphoFlashLoanCallback {
    /// @notice Callback called when a flash loan occurs.
    /// @param assets The amount of assets that was flash loaned.
    /// @param data Arbitrary data passed to the `flashLoan` function.
    function onMorphoFlashLoan(uint256 assets, bytes calldata data) external;
}

/// @notice Library for computing Morpho Blue market IDs
library MorphoLib {
    /// @notice Compute the market ID from market params
    function id(MarketParams memory params) internal pure returns (bytes32) {
        return keccak256(abi.encode(params));
    }
}

contracts/v2/interfaces/ProtocolTypes.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

library ProtocolTypes {
    struct ProtocolInstruction {
        string protocolName;
        bytes data;
    }

    struct Output {
        address token;
        uint256 amount;
    }

    // Common lending instruction schema for gateways (deposit/withdraw/borrow/repay)
    enum LendingOp {
        Deposit,
        DepositCollateral,
        WithdrawCollateral,
        Borrow,
        Repay,
        GetBorrowBalance,
        GetSupplyBalance,
        Swap,
        SwapExactOut,
        SetEMode  // Aave V3 E-Mode: amount encodes categoryId (0 = disable E-Mode)
    }
    struct InputPtr {
        uint256 index;
    }
    /**
     * Cross-protocol instruction.
     * - For Compound you can pass `context = abi.encode(address marketBaseToken)`
     *   which tells the gateway which Comet (market) to use. Empty means infer from token.
     */
    struct LendingInstruction {
        LendingOp op;
        address token; // underlying being acted on (base or collateral)
        address user; // end user on whose account we operate
        uint256 amount; // amount in underlying units
        bytes context; // (optional) protocol-specific blob (e.g., Compound marketBaseToken)
        InputPtr input; // (optional) pointer to prior output (UTXO); use if index < inputs.length
    }
}

contracts/v2/KapanViewRouter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { MarketParams } from "./interfaces/morpho/IMorphoBlue.sol";

/**
 * @title Gateway View Interfaces
 * @notice Minimal interfaces for LTV queries on each protocol's gateway view
 */

interface IAaveGatewayView {
    function getCurrentLtvBps(address market, address user) external view returns (uint256);
    function getLiquidationLtvBps(address market, address user) external view returns (uint256);
    function getAssetPrice(address token) external view returns (uint256);
    function getAssetPrices(address[] calldata tokens) external view returns (uint256[] memory);
}

interface ICompoundGatewayView {
    function getCurrentLtvBps(address token, address user) external view returns (uint256);
    function getLiquidationLtvBps(address token, address user) external view returns (uint256);
    function getPrice(address token) external view returns (uint256);
    function getCollateralPrice(address market, address asset) external view returns (uint256);
}

interface IMorphoBlueGatewayView {
    function getCurrentLtvBps(MarketParams calldata params, address user) external view returns (uint256);
    function getLiquidationLtvBps(MarketParams calldata params) external view returns (uint256);
    function getOraclePrice(MarketParams calldata params) external view returns (uint256);
}

interface IEulerGatewayView {
    function getCurrentLtvBps(address vault, address user, uint8 subAccountIndex) external view returns (uint256);
    function getLiquidationLtvBps(address vault) external view returns (uint256);
    function getAssetPrice(address vault, address token) external view returns (uint256);
    function getCollateralToDebtRate(address borrowVault, address collateralVault) external view returns (uint256);
}

interface IVenusGatewayView {
    function getCurrentLtvBps(address market, address user) external view returns (uint256);
    function getLiquidationLtvBps(address market, address user) external view returns (uint256);
    function getAssetPrice(address underlyingToken) external view returns (uint256);
    function getAssetPrice8(address underlyingToken) external view returns (uint256);
}

/**
 * @title KapanViewRouter
 * @notice Unified read-only router for querying position data across lending protocols
 * @dev Aggregates gateway view contracts to provide protocol-agnostic LTV queries for ADL
 *
 * Supported protocols:
 *   - "aave-v3": Aave V3 (context: empty)
 *   - "compound-v3": Compound V3 (context: abi.encode(baseToken address))
 *   - "morpho-blue": Morpho Blue (context: abi.encode(MarketParams))
 *   - "euler-v2": Euler V2 (context: abi.encode(vault, subAccountIndex))
 *   - "venus": Venus (context: empty)
 */
contract KapanViewRouter {
    // ============ Errors ============

    error UnsupportedProtocol(bytes32 protocolHash);
    error GatewayNotSet(bytes32 protocolHash);
    error ZeroAddress();

    // ============ Events ============

    event GatewayUpdated(bytes32 indexed protocolHash, address gateway);

    // ============ Constants ============

    bytes32 public constant AAVE_V3 = keccak256("aave-v3");
    bytes32 public constant COMPOUND_V3 = keccak256("compound-v3");
    bytes32 public constant MORPHO_BLUE = keccak256("morpho-blue");
    bytes32 public constant EULER_V2 = keccak256("euler-v2");
    bytes32 public constant VENUS = keccak256("venus");

    // ============ State ============

    /// @notice Owner who can update gateway addresses
    address public owner;

    /// @notice Protocol hash => gateway view address
    mapping(bytes32 => address) public gateways;

    // ============ Modifiers ============

    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }

    // ============ Constructor ============

    constructor() {
        owner = msg.sender;
    }

    // ============ Admin ============

    /// @notice Set or update a gateway view address
    /// @param protocolHash Protocol identifier hash (use AAVE_V3, COMPOUND_V3, etc.)
    /// @param gateway Gateway view contract address
    function setGateway(bytes32 protocolHash, address gateway) external onlyOwner {
        if (gateway == address(0)) revert ZeroAddress();
        gateways[protocolHash] = gateway;
        emit GatewayUpdated(protocolHash, gateway);
    }

    /// @notice Batch set multiple gateways
    /// @param protocolHashes Array of protocol hashes
    /// @param gatewayAddresses Array of gateway addresses
    function setGateways(
        bytes32[] calldata protocolHashes,
        address[] calldata gatewayAddresses
    ) external onlyOwner {
        require(protocolHashes.length == gatewayAddresses.length, "Length mismatch");
        for (uint256 i = 0; i < protocolHashes.length; i++) {
            if (gatewayAddresses[i] == address(0)) revert ZeroAddress();
            gateways[protocolHashes[i]] = gatewayAddresses[i];
            emit GatewayUpdated(protocolHashes[i], gatewayAddresses[i]);
        }
    }

    /// @notice Transfer ownership
    /// @param newOwner New owner address
    function transferOwnership(address newOwner) external onlyOwner {
        if (newOwner == address(0)) revert ZeroAddress();
        owner = newOwner;
    }

    // ============ LTV Queries ============

    /// @notice Get current LTV for Aave V3
    /// @param user The user's address
    /// @return ltvBps Current LTV in basis points
    function getAaveLtvBps(address user) external view returns (uint256 ltvBps) {
        address gateway = gateways[AAVE_V3];
        if (gateway == address(0)) revert GatewayNotSet(AAVE_V3);
        return IAaveGatewayView(gateway).getCurrentLtvBps(address(0), user);
    }

    /// @notice Get liquidation LTV for Aave V3
    function getAaveLiquidationLtvBps(address user) external view returns (uint256) {
        address gateway = gateways[AAVE_V3];
        if (gateway == address(0)) revert GatewayNotSet(AAVE_V3);
        return IAaveGatewayView(gateway).getLiquidationLtvBps(address(0), user);
    }

    /// @notice Get current LTV for Compound V3
    /// @param baseToken The base token of the Comet market (e.g., USDC)
    /// @param user The user's address
    function getCompoundLtvBps(address baseToken, address user) external view returns (uint256) {
        address gateway = gateways[COMPOUND_V3];
        if (gateway == address(0)) revert GatewayNotSet(COMPOUND_V3);
        return ICompoundGatewayView(gateway).getCurrentLtvBps(baseToken, user);
    }

    /// @notice Get liquidation LTV for Compound V3
    function getCompoundLiquidationLtvBps(address baseToken, address user) external view returns (uint256) {
        address gateway = gateways[COMPOUND_V3];
        if (gateway == address(0)) revert GatewayNotSet(COMPOUND_V3);
        return ICompoundGatewayView(gateway).getLiquidationLtvBps(baseToken, user);
    }

    /// @notice Get current LTV for Morpho Blue
    /// @param params The Morpho market parameters
    /// @param user The user's address
    function getMorphoLtvBps(MarketParams calldata params, address user) external view returns (uint256) {
        address gateway = gateways[MORPHO_BLUE];
        if (gateway == address(0)) revert GatewayNotSet(MORPHO_BLUE);
        return IMorphoBlueGatewayView(gateway).getCurrentLtvBps(params, user);
    }

    /// @notice Get liquidation LTV for Morpho Blue
    function getMorphoLiquidationLtvBps(MarketParams calldata params) external view returns (uint256) {
        address gateway = gateways[MORPHO_BLUE];
        if (gateway == address(0)) revert GatewayNotSet(MORPHO_BLUE);
        return IMorphoBlueGatewayView(gateway).getLiquidationLtvBps(params);
    }

    /// @notice Get current LTV for Euler V2
    /// @param vault The borrow vault address
    /// @param user The user's address
    /// @param subAccountIndex The sub-account index (0-255)
    function getEulerLtvBps(address vault, address user, uint8 subAccountIndex) external view returns (uint256) {
        address gateway = gateways[EULER_V2];
        if (gateway == address(0)) revert GatewayNotSet(EULER_V2);
        return IEulerGatewayView(gateway).getCurrentLtvBps(vault, user, subAccountIndex);
    }

    /// @notice Get liquidation LTV for Euler V2
    function getEulerLiquidationLtvBps(address vault) external view returns (uint256) {
        address gateway = gateways[EULER_V2];
        if (gateway == address(0)) revert GatewayNotSet(EULER_V2);
        return IEulerGatewayView(gateway).getLiquidationLtvBps(vault);
    }

    /// @notice Get current LTV for Venus
    /// @param user The user's address
    function getVenusLtvBps(address user) external view returns (uint256) {
        address gateway = gateways[VENUS];
        if (gateway == address(0)) revert GatewayNotSet(VENUS);
        return IVenusGatewayView(gateway).getCurrentLtvBps(address(0), user);
    }

    /// @notice Get liquidation LTV for Venus
    function getVenusLiquidationLtvBps(address user) external view returns (uint256) {
        address gateway = gateways[VENUS];
        if (gateway == address(0)) revert GatewayNotSet(VENUS);
        return IVenusGatewayView(gateway).getLiquidationLtvBps(address(0), user);
    }

    // ============ Risk Assessment ============

    /// @notice Check if Aave position is at liquidation risk
    /// @param user The user's address
    /// @param bufferBps Safety buffer in basis points (e.g., 500 = 5%)
    /// @return atRisk True if current LTV + buffer >= liquidation LTV
    function isAaveAtRisk(address user, uint256 bufferBps) external view returns (bool atRisk) {
        uint256 currentLtv = this.getAaveLtvBps(user);
        uint256 liquidationLtv = this.getAaveLiquidationLtvBps(user);
        if (currentLtv == 0 || liquidationLtv == 0) return false;
        return currentLtv + bufferBps >= liquidationLtv;
    }

    /// @notice Check if Compound position is at liquidation risk
    function isCompoundAtRisk(address baseToken, address user, uint256 bufferBps) external view returns (bool) {
        uint256 currentLtv = this.getCompoundLtvBps(baseToken, user);
        uint256 liquidationLtv = this.getCompoundLiquidationLtvBps(baseToken, user);
        if (currentLtv == 0 || liquidationLtv == 0) return false;
        return currentLtv + bufferBps >= liquidationLtv;
    }

    /// @notice Check if Morpho position is at liquidation risk
    function isMorphoAtRisk(MarketParams calldata params, address user, uint256 bufferBps) external view returns (bool) {
        uint256 currentLtv = this.getMorphoLtvBps(params, user);
        uint256 liquidationLtv = this.getMorphoLiquidationLtvBps(params);
        if (currentLtv == 0 || liquidationLtv == 0) return false;
        return currentLtv + bufferBps >= liquidationLtv;
    }

    // ============ Price Queries (for ADL floor pricing) ============
    // All prices normalized to 8 decimals USD

    /// @notice Get asset price via Aave oracle (8 decimals)
    /// @param token The asset address
    /// @return price Price in 8 decimals USD
    function getAavePrice(address token) external view returns (uint256) {
        address gateway = gateways[AAVE_V3];
        if (gateway == address(0)) revert GatewayNotSet(AAVE_V3);
        return IAaveGatewayView(gateway).getAssetPrice(token);
    }

    /// @notice Get multiple asset prices via Aave oracle
    function getAavePrices(address[] calldata tokens) external view returns (uint256[] memory) {
        address gateway = gateways[AAVE_V3];
        if (gateway == address(0)) revert GatewayNotSet(AAVE_V3);
        return IAaveGatewayView(gateway).getAssetPrices(tokens);
    }

    /// @notice Get asset price via Compound (8 decimals)
    /// @param baseToken The Comet base token (e.g., USDC)
    /// @param asset The asset to price (collateral or base token)
    /// @return price Price in 8 decimals USD
    function getCompoundPrice(address baseToken, address asset) external view returns (uint256) {
        address gateway = gateways[COMPOUND_V3];
        if (gateway == address(0)) revert GatewayNotSet(COMPOUND_V3);
        // If pricing the base token, use getPrice; otherwise use getCollateralPrice
        if (asset == baseToken) {
            return ICompoundGatewayView(gateway).getPrice(asset);
        }
        return ICompoundGatewayView(gateway).getCollateralPrice(baseToken, asset);
    }

    /// @notice Get Morpho oracle price (36 decimals, collateral/loan ratio)
    /// @dev This is NOT a USD price - it's the exchange rate from collateral to loan token
    /// @param params The Morpho market parameters
    /// @return price Price ratio with 36 decimals
    function getMorphoOraclePrice(MarketParams calldata params) external view returns (uint256) {
        address gateway = gateways[MORPHO_BLUE];
        if (gateway == address(0)) revert GatewayNotSet(MORPHO_BLUE);
        return IMorphoBlueGatewayView(gateway).getOraclePrice(params);
    }

    /// @notice Get asset price via Venus oracle (8 decimals normalized)
    /// @param underlyingToken The underlying asset address
    /// @return price Price in 8 decimals USD
    function getVenusPrice(address underlyingToken) external view returns (uint256) {
        address gateway = gateways[VENUS];
        if (gateway == address(0)) revert GatewayNotSet(VENUS);
        return IVenusGatewayView(gateway).getAssetPrice8(underlyingToken);
    }

    /// @notice Get asset price via Euler oracle (in vault's unit of account)
    /// @param vault The Euler vault address (determines oracle)
    /// @param token The token to price
    /// @return price Price in unit of account decimals
    function getEulerPrice(address vault, address token) external view returns (uint256) {
        address gateway = gateways[EULER_V2];
        if (gateway == address(0)) revert GatewayNotSet(EULER_V2);
        return IEulerGatewayView(gateway).getAssetPrice(vault, token);
    }

    /// @notice Get exchange rate for Euler position (collateral → debt)
    /// @dev Returns 18-decimal exchange rate ready for calculateMinBuyFromRate()
    /// @param borrowVault The borrow vault (debt side)
    /// @param collateralVault The collateral vault
    /// @return rate18 Exchange rate with 18 decimals
    function getEulerExchangeRate(address borrowVault, address collateralVault) external view returns (uint256) {
        address gateway = gateways[EULER_V2];
        if (gateway == address(0)) revert GatewayNotSet(EULER_V2);
        return IEulerGatewayView(gateway).getCollateralToDebtRate(borrowVault, collateralVault);
    }

    // ============ ADL Floor Price Calculation ============

    /// @notice Calculate minimum buy amount using exchange rate
    /// @dev Exchange rate = how many buyTokens per sellToken, scaled by 1e18
    /// @param sellAmount Amount of sellToken (in sellToken decimals)
    /// @param maxSlippageBps Maximum slippage in basis points (e.g., 100 = 1%)
    /// @param exchangeRate18 Exchange rate with 18 decimals (sellToken → buyToken)
    /// @param sellDecimals Decimals of sell token
    /// @param buyDecimals Decimals of buy token
    /// @return minBuyAmount Minimum acceptable buy amount
    function calculateMinBuyFromRate(
        uint256 sellAmount,
        uint256 maxSlippageBps,
        uint256 exchangeRate18,
        uint8 sellDecimals,
        uint8 buyDecimals
    ) external pure returns (uint256 minBuyAmount) {
        if (exchangeRate18 == 0) return 0;

        // buyAmount = sellAmount * exchangeRate / 1e18
        // Then adjust for decimal difference between tokens

        // Step 1: Apply exchange rate (result has sellDecimals precision)
        uint256 rawBuyAmount = (sellAmount * exchangeRate18) / 1e18;

        // Step 2: Adjust for decimal difference
        if (buyDecimals >= sellDecimals) {
            minBuyAmount = rawBuyAmount * (10 ** (buyDecimals - sellDecimals));
        } else {
            minBuyAmount = rawBuyAmount / (10 ** (sellDecimals - buyDecimals));
        }

        // Step 3: Apply slippage tolerance
        minBuyAmount = (minBuyAmount * (10000 - maxSlippageBps)) / 10000;
    }

    /// @notice Convert two USD prices (same decimals) to an 18-decimal exchange rate
    /// @dev exchangeRate18 = sellPriceUsd * 1e18 / buyPriceUsd
    /// @param sellPriceUsd USD price of sell token (any decimals, but same as buyPriceUsd)
    /// @param buyPriceUsd USD price of buy token (any decimals, but same as sellPriceUsd)
    /// @return exchangeRate18 Exchange rate with 18 decimals
    function usdPricesToExchangeRate(
        uint256 sellPriceUsd,
        uint256 buyPriceUsd
    ) external pure returns (uint256 exchangeRate18) {
        if (buyPriceUsd == 0) return 0;
        return (sellPriceUsd * 1e18) / buyPriceUsd;
    }

    /// @notice Calculate minimum buy amount for ADL order with slippage protection
    /// @dev Convenience function that takes USD prices and converts to exchange rate internally
    /// @param sellAmount Amount of sellToken
    /// @param maxSlippageBps Maximum slippage in basis points (e.g., 100 = 1%)
    /// @param sellPriceUsd Price of sellToken (any decimals)
    /// @param buyPriceUsd Price of buyToken (same decimals as sellPriceUsd)
    /// @param sellDecimals Decimals of sell token
    /// @param buyDecimals Decimals of buy token
    /// @return minBuyAmount Minimum acceptable buy amount
    function calculateMinBuyAmount(
        uint256 sellAmount,
        uint256 maxSlippageBps,
        uint256 sellPriceUsd,
        uint256 buyPriceUsd,
        uint8 sellDecimals,
        uint8 buyDecimals
    ) external view returns (uint256 minBuyAmount) {
        if (sellPriceUsd == 0 || buyPriceUsd == 0) return 0;

        // Convert USD prices to exchange rate
        uint256 exchangeRate18 = (sellPriceUsd * 1e18) / buyPriceUsd;

        // Use the exchange rate calculation
        return this.calculateMinBuyFromRate(
            sellAmount,
            maxSlippageBps,
            exchangeRate18,
            sellDecimals,
            buyDecimals
        );
    }

    /// @notice Calculate minimum buy amount for Morpho ADL orders
    /// @dev Morpho oracles return the exchange rate scaled by 1e36
    ///      Formula: loanAmount = collateralAmount * price / 1e36
    ///      The oracle price already accounts for token decimals
    /// @param sellAmount Amount of collateral to sell (in collateral token decimals)
    /// @param maxSlippageBps Maximum slippage in basis points
    /// @param morphoOraclePrice Price from Morpho oracle (36 decimals scale)
    /// @return minBuyAmount Minimum acceptable loan tokens to receive
    function calculateMorphoMinBuyAmount(
        uint256 sellAmount,
        uint256 maxSlippageBps,
        uint256 morphoOraclePrice
    ) external pure returns (uint256 minBuyAmount) {
        if (morphoOraclePrice == 0) return 0;

        // Morpho oracle: loanAmount = collateralAmount * price / ORACLE_PRICE_SCALE
        // The oracle price already incorporates the decimal difference between tokens
        // ORACLE_PRICE_SCALE = 1e36

        minBuyAmount = (sellAmount * morphoOraclePrice) / 1e36;

        // Apply slippage tolerance
        minBuyAmount = (minBuyAmount * (10000 - maxSlippageBps)) / 10000;
    }
}
