// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

contract UiHelper {
    function getDecimals(address[] calldata tokens) public view returns (uint256[] memory) {
        uint256[] memory decimals = new uint256[](tokens.length);
        for (uint256 i = 0; i < tokens.length; i++) {
            decimals[i] = IERC20Metadata(tokens[i]).decimals();
        }
        return decimals;
    }
}

contracts/v2/flashloans/FlashLoanConsume.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

// --- Existing Balancer imports ---
import {IFlashLoanProvider} from "../interfaces/balancer/IFlashLoanProvider.sol";
import {IVaultV3} from "../interfaces/balancer/IVaultV3.sol";

// --- Minimal Aave v3 interfaces (flashLoanSimple) ---
interface IAaveV3Pool {
    function flashLoanSimple(
        address receiverAddress,
        address asset,
        uint256 amount,
        bytes calldata params,
        uint16 referralCode
    ) external;
}

interface IAaveFlashLoanSimpleReceiver {
    function executeOperation(
        address asset,
        uint256 amount,
        uint256 premium,
        address initiator,
        bytes calldata params
    ) external returns (bool);
}

// --- Minimal Uniswap v3 pool interface ---
interface IUniswapV3Pool {
    function token0() external view returns (address);
    function token1() external view returns (address);
    function flash(address recipient, uint256 amount0, uint256 amount1, bytes calldata data) external;
}

/// @notice Abstract base supporting Balancer v2/v3, Aave v3 and Uniswap v3 flash sources.
///         Providers are *configurable*; zero address = disabled (and requests revert).
abstract contract FlashLoanConsumerBase is IAaveFlashLoanSimpleReceiver {
    using SafeERC20 for IERC20;

    // ----------------- Providers (config) -----------------
    IFlashLoanProvider public balancerV2Vault; // Balancer v2 Vault
    IVaultV3 public balancerV3Vault;           // Balancer v3 Vault
    IAaveV3Pool public aaveV3Pool;             // Aave v3 Pool (flashLoanSimple)

    // We gate Uniswap v3 availability by storing a *factory or sentinel address*.
    // The actual pool is passed per-request.
    address public uniswapV3FactoryOrSentinel;

    // ----------------- Internal state guard -----------------
    bool private flashLoanEnabled;
    address private authorizedFlashSender;

    // ----------------- Events (optional, helpful for tracing) -----------------
    event FlashRequested(bytes32 provider, address indexed token, uint256 amount);
    event FlashRepaid(bytes32 provider, address indexed token, uint256 amountWithFee);

    // ----------------- Setters -----------------
    function _setBalancerV2(address provider) internal {
        balancerV2Vault = IFlashLoanProvider(provider);
    }

    function _setBalancerV3(address vault) internal {
        balancerV3Vault = IVaultV3(vault);
    }

    function _setAaveV3(address pool) internal {
        aaveV3Pool = IAaveV3Pool(pool);
    }

    /// @dev Any non-zero address works as an enable switch (factory, router, sentinel).
    function _setUniswapV3Enabled(address factoryOrSentinel) internal {
        uniswapV3FactoryOrSentinel = factoryOrSentinel;
    }

    // ----------------- Enabled flags -----------------
    function balancerV2Enabled() public view returns (bool) {
        return address(balancerV2Vault) != address(0);
    }

    function balancerV3Enabled() public view returns (bool) {
        return address(balancerV3Vault) != address(0);
    }

    function aaveEnabled() public view returns (bool) {
        return address(aaveV3Pool) != address(0);
    }

    function uniswapEnabled() public view returns (bool) {
        return uniswapV3FactoryOrSentinel != address(0);
    }

    // ----------------- Guard modifiers -----------------
    modifier enableFlashLoan() {
        flashLoanEnabled = true;
        _;
        flashLoanEnabled = false;
    }

    modifier flashLoanOnly() {
        require(flashLoanEnabled, "Flash loan not enabled");
        require(msg.sender == authorizedFlashSender, "Unauthorized flash caller");
        _;
    }

    modifier authorizeFlashLoan(address expectedSender) {
        flashLoanEnabled = true;
        authorizedFlashSender = expectedSender;
        _;
        authorizedFlashSender = address(0);
        flashLoanEnabled = false;
    }

    // ============================================================
    //                          BALANCER V2
    // ============================================================

    /// @notice Request a Balancer v2 flash loan (single-asset for simplicity).
    function _requestBalancerV2(address token, uint256 amount, bytes memory userData) internal authorizeFlashLoan(address(balancerV2Vault)) {
        require(balancerV2Enabled(), "Balancer v2 disabled");

        IERC20[] memory tokens = new IERC20[](1);
        tokens[0] = IERC20(token);
        uint256[] memory amounts = new uint256[](1);
        amounts[0] = amount;

        emit FlashRequested("BALANCER_V2", token, amount);
        balancerV2Vault.flashLoan(address(this), tokens, amounts, userData);
    }

    /// @notice Balancer v2 callback.
    function receiveFlashLoan(
        IERC20[] calldata tokens,
        uint256[] calldata amounts,
        uint256[] calldata feeAmounts,
        bytes calldata userData
    ) external flashLoanOnly {
        require(msg.sender == address(balancerV2Vault), "Unauthorized Balancer v2");

        // For single-asset flash loans, pass the repayment amount
        require(tokens.length == 1, "Only single-asset flash loans supported");
        uint256 repayment = amounts[0] + feeAmounts[0];
        _afterFlashLoan(address(tokens[0]), repayment, userData);

        // Repay principal + fee to the vault
        tokens[0].safeTransfer(address(balancerV2Vault), repayment);
        emit FlashRepaid("BALANCER_V2", address(tokens[0]), repayment);
    }

    // ============================================================
    //                          BALANCER V3
    // ============================================================

    /// @notice Request a Balancer v3 "unlocked" context; vault will callback with our calldata.
    function _requestBalancerV3(address token, uint256 amount) internal authorizeFlashLoan(address(balancerV3Vault)) {
        require(balancerV3Enabled(), "Balancer v3 disabled");

        bytes memory userData = abi.encode(token, amount);
        bytes memory callData = abi.encodeWithSelector(this.receiveFlashLoanV3.selector, userData);

        emit FlashRequested("BALANCER_V3", token, amount);
        balancerV3Vault.unlock(callData); // calls back receiveFlashLoanV3 within unlocked context
    }

    /// @notice Balancer v3 callback, runs in unlocked context.
    function receiveFlashLoanV3(bytes calldata userData) external flashLoanOnly {
        require(msg.sender == address(balancerV3Vault), "Unauthorized Balancer v3");

        (address token, uint256 amount) = abi.decode(userData, (address, uint256));

        if (token != address(0) && amount > 0) {
            // Pull funds in unlocked context
            balancerV3Vault.sendTo(token, address(this), amount);
            // For Balancer v3, repayment amount equals the borrowed amount (fee handled by vault accounting)
            _afterFlashLoan(token, amount, userData);
            // Repay + settle (no premium encoded here, handled by vault accounting)
            IERC20(token).safeTransfer(address(balancerV3Vault), amount);
            balancerV3Vault.settle(token, amount);
            emit FlashRepaid("BALANCER_V3", token, amount);
        }
    }

    // ============================================================
    //                            AAVE V3
    // ============================================================

    /// @notice Request an Aave v3 flash loan (simple mode).
    /// @param token Asset to borrow
    /// @param amount Amount to borrow
    /// @param userData Arbitrary data forwarded to executeOperation and to _afterFlashLoan
    function _requestAaveV3(address token, uint256 amount, bytes memory userData) internal authorizeFlashLoan(address(aaveV3Pool)) {
        require(aaveEnabled(), "Aave v3 disabled");
        emit FlashRequested("AAVE_V3", token, amount);
        // referralCode = 0
        aaveV3Pool.flashLoanSimple(address(this), token, amount, userData, 0);
    }

    /// @notice Aave v3 callback. Approve Pool for principal+premium; return true to finalize.
    function executeOperation(
        address asset,
        uint256 amount,
        uint256 premium,
        address initiator,
        bytes calldata params
    ) external override flashLoanOnly returns (bool) {
        require(msg.sender == address(aaveV3Pool), "Unauthorized Aave pool");
        require(initiator == address(this), "Bad initiator");

        uint256 repayment = amount + premium;
        _afterFlashLoan(asset, repayment, params);

        // Repay: approve Pool to pull principal + premium
        // Reset approval to 0 first, then approve new amount (safeApprove pattern for compatibility)
        IERC20(asset).approve(address(aaveV3Pool), 0);
        IERC20(asset).approve(address(aaveV3Pool), repayment);

        emit FlashRepaid("AAVE_V3", asset, repayment);
        return true;
    }

    // ============================================================
    //                          UNISWAP V3
    // ============================================================

    /// @notice Request a Uniswap v3 flash (flash-swap) from a specific pool.
    ///         We enforce global enable via uniswapV3FactoryOrSentinel (non-zero).
    /// @param pool Address of the Uniswap v3 pool (must contain `token`)
    /// @param token The token to borrow (must be token0 or token1 of the pool)
    /// @param amount Amount to borrow
    /// @param userData Your opaque data forwarded to the callback and _afterFlashLoan
    function _requestUniswapV3(
        address pool,
        address token,
        uint256 amount,
        bytes memory userData
    ) internal authorizeFlashLoan(pool) {
        require(uniswapEnabled(), "Uniswap v3 disabled");
        require(pool != address(0), "Pool required");
        require(amount > 0, "Amount=0");

        address t0 = IUniswapV3Pool(pool).token0();
        address t1 = IUniswapV3Pool(pool).token1();
        bool is0 = token == t0;
        bool is1 = token == t1;
        require(is0 || is1, "Token not in pool");

        // Encode pool + token orientation so callback can verify msg.sender and repay the correct leg.
        bytes memory data = abi.encode(pool, token, amount, userData, is0);

        emit FlashRequested("UNISWAP_V3", token, amount);
        IUniswapV3Pool(pool).flash(address(this), is0 ? amount : 0, is1 ? amount : 0, data);
    }

    /// @notice Uniswap v3 flash callback. Must repay (amount + fee) to the *pool* (msg.sender).
    function uniswapV3FlashCallback(
        uint256 fee0,
        uint256 fee1,
        bytes calldata data
    ) external flashLoanOnly {
        (address pool, address token, uint256 amount, bytes memory userData, bool is0) =
            abi.decode(data, (address, address, uint256, bytes, bool));

        require(msg.sender == pool, "Unauthorized Uniswap pool");

        uint256 fee = is0 ? fee0 : fee1;
        uint256 repayment = amount + fee;
        _afterFlashLoan(token, repayment, userData);

        IERC20(token).safeTransfer(msg.sender, repayment);
        emit FlashRepaid("UNISWAP_V3", token, repayment);
    }

    // ============================================================
    //                       ABSTRACT RESUME HOOK
    // ============================================================

    /// @dev Derived contracts implement how to resume execution (swap/repay/move collateral etc.).
    /// @param token The token address that was borrowed
    /// @param repaymentAmount The total amount that must be repaid (principal + fee)
    /// @param userData Your opaque data forwarded from the request
    ///      Accepts bytes memory to support both calldata (from callbacks) and memory (from decoded data).
    function _afterFlashLoan(address token, uint256 repaymentAmount, bytes memory userData) internal virtual;
}

contracts/v2/gateways/aave/AaveGatewayVi.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {IPoolAddressesProvider} from "../../../interfaces/aave/IPoolAddressesProvider.sol";
import {IUiPoolDataProviderV3} from "../../../interfaces/aave/IUiDataProvider.sol";
import {IPoolDataProvider} from "@aave/core-v3/contracts/interfaces/IPoolDataProvider.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";

/**
 * @title AaveGatewayView
 * @notice View-only gateway for Aave v3 protocol
 * @dev Contains all read/view functions from v1, separate from write operations
 */
contract AaveGatewayView {
    IPoolAddressesProvider public immutable poolAddressesProvider;
    IUiPoolDataProviderV3 public immutable uiPoolDataProvider;

    constructor(address _poolAddressesProvider, address _uiPoolDataProvider) {
        poolAddressesProvider = IPoolAddressesProvider(_poolAddressesProvider);
        uiPoolDataProvider = IUiPoolDataProviderV3(_uiPoolDataProvider);
    }

    struct TokenInfo {
        address token;
        uint256 supplyRate;
        uint256 borrowRate;
        string name;
        string symbol;
        uint256 price;
        uint256 borrowBalance;
        uint256 balance;
        address aToken;
        uint8 decimals;
    }

    /// @notice Returns all token info for a given user.
    /// @dev This function caches the reserves and user reserves data to avoid multiple heavy calls.
    function getAllTokensInfo(address user) external view returns (TokenInfo[] memory) {
        // Fetch reserves data once.
        (IUiPoolDataProviderV3.AggregatedReserveData[] memory reserves,) = uiPoolDataProvider.getReservesData(poolAddressesProvider);
        // Fetch user reserves data once.
        (IUiPoolDataProviderV3.UserReserveData[] memory userReserves, ) = 
            uiPoolDataProvider.getUserReservesData(poolAddressesProvider, user);

        TokenInfo[] memory tokens = new TokenInfo[](reserves.length);
        for (uint256 i = 0; i < reserves.length; i++) {
            uint256 balance = _getBalanceFromReserveData(reserves[i], user, userReserves);
            uint256 borrowBalance = _getBorrowBalanceFromReserveData(reserves[i], user, userReserves);
            uint8 dec = 18;
            // Read decimals from the underlying token; default to 18 on failure
            try ERC20(reserves[i].underlyingAsset).decimals() returns (uint8 d) {
                dec = d;
            } catch {
            }
            tokens[i] = TokenInfo({
                token: reserves[i].underlyingAsset,
                supplyRate: reserves[i].liquidityRate,
                borrowRate: reserves[i].variableBorrowRate,
                name: reserves[i].name,
                symbol: reserves[i].symbol,
                price: reserves[i].priceInMarketReferenceCurrency,
                borrowBalance: borrowBalance,
                balance: balance,
                aToken: reserves[i].aTokenAddress,
                decimals: dec
            });
        }
        return tokens;
    }

    function getBorrowRate(address token) external view returns (uint256, bool) {
        (IUiPoolDataProviderV3.AggregatedReserveData[] memory reserves, ) = 
            uiPoolDataProvider.getReservesData(poolAddressesProvider);
        for (uint256 i = 0; i < reserves.length; i++) {
            if (reserves[i].underlyingAsset == token) {
                return (reserves[i].variableBorrowRate, true);
            }
        }
        return (0, false);
    }

    function getSupplyRate(address token) external view returns (uint256, bool) {
        (IUiPoolDataProviderV3.AggregatedReserveData[] memory reserves, ) = 
            uiPoolDataProvider.getReservesData(poolAddressesProvider);
        for (uint256 i = 0; i < reserves.length; i++) {
            if (reserves[i].underlyingAsset == token) {
                return (reserves[i].liquidityRate, true);
            }
        }
        return (0, false);
    }

    /// @notice Gets the balance for a given token and user.
    /// @dev First attempts to call balanceOf on the associated aToken; if that fails, falls back to user reserves data.
    function getBalance(address token, address user) public view returns (uint256) {
        (address aToken, , bool found) = _getReserveAddresses(token);
        if (found && aToken != address(0)) {
            try IERC20(aToken).balanceOf(user) returns (uint256 bal) {
                return bal;
            } catch {
            }
        }
        // Fallback: use user reserves data
        (IUiPoolDataProviderV3.UserReserveData[] memory userReserves, ) = 
            uiPoolDataProvider.getUserReservesData(poolAddressesProvider, user);
        for (uint256 i = 0; i < userReserves.length; i++) {
            if (userReserves[i].underlyingAsset == token) {
                return userReserves[i].scaledATokenBalance;
            }
        }
        return 0;
    }

    /// @notice Gets the borrow balance for a given token and user.
    /// @dev First attempts to call balanceOf on the associated variable debt token; if that fails, falls back to user reserves data.
    function getBorrowBalance(address token, address user) public view returns (uint256) {
        (, address variableDebtToken, bool found) = _getReserveAddresses(token);
        if (found && variableDebtToken != address(0)) {
            try IERC20(variableDebtToken).balanceOf(user) returns (uint256 bal) {
                return bal;
            } catch {
            }
        }
        // Fallback: use user reserves data
        (IUiPoolDataProviderV3.UserReserveData[] memory userReserves, ) = 
            uiPoolDataProvider.getUserReservesData(poolAddressesProvider, user);
        for (uint256 i = 0; i < userReserves.length; i++) {
            if (userReserves[i].underlyingAsset == token) {
                return userReserves[i].scaledVariableDebt;
            }
        }
        return 0;
    }

    function getBorrowBalanceCurrent(address token, address user) external returns (uint256) {
        return getBorrowBalance(token, user);
    }

    /// @notice Returns the list of tokens that the user has borrowed.
    function borrowedTokens(address user) external view returns (address[] memory) {
        (IUiPoolDataProviderV3.UserReserveData[] memory userReserves, ) = 
            uiPoolDataProvider.getUserReservesData(poolAddressesProvider, user);
        uint256 count = 0;
        for (uint256 i = 0; i < userReserves.length; i++) {
            if (userReserves[i].scaledVariableDebt > 0) {
                count++;
            }
        }
        address[] memory tokens = new address[](count);
        uint256 index = 0;
        for (uint256 i = 0; i < userReserves.length; i++) {
            if (userReserves[i].scaledVariableDebt > 0) {
                tokens[index++] = userReserves[i].underlyingAsset;
            }
        }
        return tokens;
    }

    /// @dev Internal helper to get both the aToken and variable debt token for a given underlying asset.
    function _getReserveAddresses(address token) internal view returns (address aToken, address variableDebtToken, bool found) {
        try uiPoolDataProvider.getReservesData(poolAddressesProvider) returns (
            IUiPoolDataProviderV3.AggregatedReserveData[] memory reserves,
            IUiPoolDataProviderV3.BaseCurrencyInfo memory
        ) {
            for (uint256 i = 0; i < reserves.length; i++) {
                if (reserves[i].underlyingAsset == token) {
                    return (reserves[i].aTokenAddress, reserves[i].variableDebtTokenAddress, true);
                }
            }
        } catch {
        }
        return (address(0), address(0), false);
    }

    /// @dev Internal helper to fetch balance from reserve data, with fallback to user reserves.
    function _getBalanceFromReserveData(
        IUiPoolDataProviderV3.AggregatedReserveData memory reserve,
        address user,
        IUiPoolDataProviderV3.UserReserveData[] memory userReserves
    ) internal view returns (uint256) {
        if (reserve.aTokenAddress != address(0)) {
            try IERC20(reserve.aTokenAddress).balanceOf(user) returns (uint256 bal) {
                return bal;
            } catch {
                // Fallback below
            }
        }
        for (uint256 i = 0; i < userReserves.length; i++) {
            if (userReserves[i].underlyingAsset == reserve.underlyingAsset) {
                return userReserves[i].scaledATokenBalance;
            }
        }
        return 0;
    }

    /// @dev Internal helper to fetch borrow balance from reserve data, with fallback to user reserves.
    function _getBorrowBalanceFromReserveData(
        IUiPoolDataProviderV3.AggregatedReserveData memory reserve,
        address user,
        IUiPoolDataProviderV3.UserReserveData[] memory userReserves
    ) internal view returns (uint256) {
        if (reserve.variableDebtTokenAddress != address(0)) {
            try IERC20(reserve.variableDebtTokenAddress).balanceOf(user) returns (uint256 bal) {
                return bal;
            } catch {
                // Fallback below
            }
        }
        for (uint256 i = 0; i < userReserves.length; i++) {
            if (userReserves[i].underlyingAsset == reserve.underlyingAsset) {
                return userReserves[i].scaledVariableDebt;
            }
        }
        return 0;
    }

    function getPossibleCollaterals(address token, address user) external view returns (
        address[] memory collateralAddresses,
        uint256[] memory balances,
        string[] memory symbols,
        uint8[] memory decimals
    ) {
        // If user is zero address, get all possible tokens but with zero balances
        if (user == address(0)) {
            (IUiPoolDataProviderV3.AggregatedReserveData[] memory reserves,) = uiPoolDataProvider.getReservesData(poolAddressesProvider);

            // Initialize arrays with all reserves
            uint256 tokenCountX = reserves.length;
            collateralAddresses = new address[](tokenCountX);
            balances = new uint256[](tokenCountX); // All zeros by default
            symbols = new string[](tokenCountX);
            decimals = new uint8[](tokenCountX);

            // Fill arrays with token data (zero balances)
            for (uint256 i = 0; i < tokenCountX; i++) {
                collateralAddresses[i] = reserves[i].underlyingAsset;
                symbols[i] = reserves[i].symbol;
                // Get decimals directly from token
                try ERC20(reserves[i].underlyingAsset).decimals() returns (uint8 dec) {
                    decimals[i] = dec;
                } catch {
                    decimals[i] = 18; // Default to 18 if call fails
                }
            }

            return (collateralAddresses, balances, symbols, decimals);
        }

        // For connected wallets, get all tokens with actual balances
        TokenInfo[] memory allTokens = this.getAllTokensInfo(user);

        // Count tokens with non-zero balance
        uint256 tokenCount = 0;
        for (uint256 i = 0; i < allTokens.length; i++) {
            if (allTokens[i].balance > 0) {
                tokenCount++;
            }
        }

        // Initialize arrays with the correct size
        collateralAddresses = new address[](tokenCount);
        balances = new uint256[](tokenCount);
        symbols = new string[](tokenCount);
        decimals = new uint8[](tokenCount);

        // Fill arrays with tokens that have balance
        uint256 index = 0;
        for (uint256 i = 0; i < allTokens.length; i++) {
            if (allTokens[i].balance > 0) {
                collateralAddresses[index] = allTokens[i].token;
                balances[index] = allTokens[i].balance;
                symbols[index] = allTokens[i].symbol;
                try ERC20(allTokens[i].token).decimals() returns (uint8 dec) {
                    decimals[index] = dec;
                } catch {
                    decimals[index] = 18; // Default to 18 if call fails
                }
                index++;
            }
        }
        return (collateralAddresses, balances, symbols, decimals);
    }

    function getAToken(address underlyingToken) public view returns (address) {
        IPoolDataProvider dataProvider = IPoolDataProvider(IPoolAddressesProvider(poolAddressesProvider).getPoolDataProvider());
        (address aTokenAddress, , ) = dataProvider.getReserveTokensAddresses(underlyingToken);
        return aTokenAddress;
    }

    function getUnderlyingToken(address aToken) external view returns (address) {
        IPoolDataProvider dataProvider = IPoolDataProvider(IPoolAddressesProvider(poolAddressesProvider).getPoolDataProvider());
        (address underlyingToken, , ) = dataProvider.getReserveTokensAddresses(aToken);
        return underlyingToken;
    }

    function isCollateralSupported(address market, address collateral) external view returns (bool isSupported) {
        // In Aave, we need to check if the token is a supported reserve and if it can be used as collateral
        (IUiPoolDataProviderV3.AggregatedReserveData[] memory reserves,) = uiPoolDataProvider.getReservesData(poolAddressesProvider);

        for (uint256 i = 0; i < reserves.length; i++) {
            if (reserves[i].underlyingAsset == collateral) {
                // Check if the token can be used as collateral in Aave
                return reserves[i].usageAsCollateralEnabled;
            }
        }

        return false;
    }

    function getSupportedCollaterals(address market) external view returns (address[] memory collateralAddresses) {
        // Get all Aave reserves
        (IUiPoolDataProviderV3.AggregatedReserveData[] memory reserves,) = uiPoolDataProvider.getReservesData(poolAddressesProvider);

        // Count eligible collaterals
        uint256 collateralCount = 0;
        for (uint256 i = 0; i < reserves.length; i++) {
            if (reserves[i].usageAsCollateralEnabled) {
                collateralCount++;
            }
        }

        // Create and populate array with eligible collaterals
        collateralAddresses = new address[](collateralCount);
        uint256 index = 0;
        for (uint256 i = 0; i < reserves.length; i++) {
            if (reserves[i].usageAsCollateralEnabled) {
                collateralAddresses[index] = reserves[i].underlyingAsset;
                index++;
            }
        }

        return collateralAddresses;
    }
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {IPoolAddressesProvider} from "../../../interfaces/aave/IPoolAddressesProvider.sol";
import {IPool} from "@aave/core-v3/contracts/interfaces/IPool.sol";
import {IPoolDataProvider} from "@aave/core-v3/contracts/interfaces/IPoolDataProvider.sol";
import {IAaveOracle} from "@aave/core-v3/contracts/interfaces/IAaveOracle.sol";
import {DataTypes} from "@aave/core-v3/contracts/protocol/libraries/types/DataTypes.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";

/**
 * @title AaveGatewayViewBase
 * @notice View-only gateway for Aave v3 protocol on Base. Avoids UiPoolDataProvider struct differences
 *         by reading directly from Pool, DataProvider and Oracle. Constructor signature matches the
 *         generic view so deploy scripts can deploy this under the same name.
 */
contract AaveGatewayViewBase {

    IPoolAddressesProvider public immutable poolAddressesProvider;

    constructor(address _poolAddressesProvider, address /* _uiPoolDataProvider */) {
        poolAddressesProvider = IPoolAddressesProvider(_poolAddressesProvider);
    }

    struct TokenInfo {
        address token;
        uint256 supplyRate;
        uint256 borrowRate;
        string name;
        string symbol;
        uint256 price;
        uint256 borrowBalance;
        uint256 balance;
        address aToken;
        uint8 decimals;
    }

    /// @notice Returns all token info for a given user.
    function getAllTokensInfo(address user) external view returns (TokenInfo[] memory) {
        IPool pool = IPool(poolAddressesProvider.getPool());
        IAaveOracle oracle = IAaveOracle(poolAddressesProvider.getPriceOracle());
        IPoolDataProvider dataProvider = IPoolDataProvider(poolAddressesProvider.getPoolDataProvider());

        address[] memory reserves = pool.getReservesList();
        TokenInfo[] memory tokens = new TokenInfo[](reserves.length);

        for (uint256 i = 0; i < reserves.length; i++) {
            DataTypes.ReserveData memory baseData = pool.getReserveData(reserves[i]);
            (address aToken, , address variableDebtToken) = dataProvider.getReserveTokensAddresses(reserves[i]);

            uint256 balance = 0;
            uint256 borrowBalance = 0;
            if (aToken != address(0)) {
                try IERC20(aToken).balanceOf(user) returns (uint256 bal) { balance = bal; } catch {}
            }
            if (variableDebtToken != address(0)) {
                try IERC20(variableDebtToken).balanceOf(user) returns (uint256 bal) { borrowBalance = bal; } catch {}
            }

            string memory name = "";
            string memory symbol = "";
            try IERC20Metadata(reserves[i]).name() returns (string memory n) { name = n; } catch {}
            try IERC20Metadata(reserves[i]).symbol() returns (string memory s) { symbol = s; } catch {}
            uint8 dec = 18;
            try ERC20(reserves[i]).decimals() returns (uint8 d) { dec = d; } catch {}

            tokens[i] = TokenInfo({
                token: reserves[i],
                supplyRate: baseData.currentLiquidityRate,
                borrowRate: baseData.currentVariableBorrowRate,
                name: name,
                symbol: symbol,
                price: oracle.getAssetPrice(reserves[i]),
                borrowBalance: borrowBalance,
                balance: balance,
                aToken: aToken,
                decimals: dec
            });
        }
        return tokens;
    }

    function getBorrowRate(address token) external view returns (uint256, bool) {
        IPool pool = IPool(poolAddressesProvider.getPool());
        if (!_isReserve(pool, token)) return (0, false);
        DataTypes.ReserveData memory baseData = pool.getReserveData(token);
        return (baseData.currentVariableBorrowRate, true);
    }

    function getSupplyRate(address token) external view returns (uint256, bool) {
        IPool pool = IPool(poolAddressesProvider.getPool());
        if (!_isReserve(pool, token)) return (0, false);
        DataTypes.ReserveData memory baseData = pool.getReserveData(token);
        return (baseData.currentLiquidityRate, true);
    }

    function _isReserve(IPool pool, address token) internal view returns (bool) {
        address[] memory reserves = pool.getReservesList();
        for (uint256 i = 0; i < reserves.length; i++) {
            if (reserves[i] == token) return true;
        }
        return false;
    }

    function getBalance(address token, address user) public view returns (uint256) {
        (address aToken, , ) = _getReserveAddresses(token);
        if (aToken != address(0)) {
            try IERC20(aToken).balanceOf(user) returns (uint256 bal) { return bal; } catch {}
        }
        return 0;
    }

    function getBorrowBalance(address token, address user) public view returns (uint256) {
        (, address variableDebtToken, ) = _getReserveAddresses(token);
        if (variableDebtToken != address(0)) {
            try IERC20(variableDebtToken).balanceOf(user) returns (uint256 bal) { return bal; } catch {}
        }
        return 0;
    }

    function getBorrowBalanceCurrent(address token, address user) external returns (uint256) {
        return getBorrowBalance(token, user);
    }

    function borrowedTokens(address user) external view returns (address[] memory) {
        IPool pool = IPool(poolAddressesProvider.getPool());
        IPoolDataProvider dataProvider = IPoolDataProvider(poolAddressesProvider.getPoolDataProvider());
        address[] memory reserves = pool.getReservesList();

        uint256 count = 0;
        for (uint256 i = 0; i < reserves.length; i++) {
            (, , address variableDebtToken) = dataProvider.getReserveTokensAddresses(reserves[i]);
            if (variableDebtToken != address(0)) {
                try IERC20(variableDebtToken).balanceOf(user) returns (uint256 bal) {
                    if (bal > 0) count++;
                } catch {}
            }
        }

        address[] memory tokens = new address[](count);
        uint256 idx = 0;
        for (uint256 i = 0; i < reserves.length; i++) {
            (, , address variableDebtToken) = dataProvider.getReserveTokensAddresses(reserves[i]);
            if (variableDebtToken != address(0)) {
                try IERC20(variableDebtToken).balanceOf(user) returns (uint256 bal) {
                    if (bal > 0) tokens[idx++] = reserves[i];
                } catch {}
            }
        }
        return tokens;
    }

    function _getReserveAddresses(address token) internal view returns (address aToken, address variableDebtToken, bool found) {
        IPoolDataProvider dataProvider = IPoolDataProvider(poolAddressesProvider.getPoolDataProvider());
        try dataProvider.getReserveTokensAddresses(token) returns (address aT, address /*stableDebtToken*/, address vD) {
            return (aT, vD, true);
        } catch {}
        return (address(0), address(0), false);
    }

    function getPossibleCollaterals(
        address /* market */, 
        address user
    ) external view returns (
        address[] memory collateralAddresses,
        uint256[] memory balances,
        string[] memory symbols,
        uint8[] memory decimals
    ) {
        IPool pool = IPool(poolAddressesProvider.getPool());
        address[] memory reserves = pool.getReservesList();

        if (user == address(0)) {
            uint256 tokenCountX = reserves.length;
            collateralAddresses = new address[](tokenCountX);
            balances = new uint256[](tokenCountX);
            symbols = new string[](tokenCountX);
            decimals = new uint8[](tokenCountX);

            for (uint256 i = 0; i < tokenCountX; i++) {
                collateralAddresses[i] = reserves[i];
                try IERC20Metadata(reserves[i]).symbol() returns (string memory s) { symbols[i] = s; } catch { symbols[i] = ""; }
                try ERC20(reserves[i]).decimals() returns (uint8 dec) { decimals[i] = dec; } catch { decimals[i] = 18; }
            }
            return (collateralAddresses, balances, symbols, decimals);
        }

        // Connected wallet: include only non-zero balances
        IPoolDataProvider dataProvider = IPoolDataProvider(poolAddressesProvider.getPoolDataProvider());
        uint256 tokenCount = 0;
        for (uint256 i = 0; i < reserves.length; i++) {
            (address aToken, , ) = dataProvider.getReserveTokensAddresses(reserves[i]);
            if (aToken != address(0)) {
                try IERC20(aToken).balanceOf(user) returns (uint256 bal) {
                    if (bal > 0) tokenCount++;
                } catch {}
            }
        }

        collateralAddresses = new address[](tokenCount);
        balances = new uint256[](tokenCount);
        symbols = new string[](tokenCount);
        decimals = new uint8[](tokenCount);

        uint256 index = 0;
        for (uint256 i = 0; i < reserves.length; i++) {
            (address aToken, , ) = dataProvider.getReserveTokensAddresses(reserves[i]);
            if (aToken != address(0)) {
                uint256 bal = 0;
                try IERC20(aToken).balanceOf(user) returns (uint256 b) { bal = b; } catch {}
                if (bal > 0) {
                    collateralAddresses[index] = reserves[i];
                    balances[index] = bal;
                    try IERC20Metadata(reserves[i]).symbol() returns (string memory s) { symbols[index] = s; } catch { symbols[index] = ""; }
                    try ERC20(reserves[i]).decimals() returns (uint8 dec) { decimals[index] = dec; } catch { decimals[index] = 18; }
                    index++;
                }
            }
        }
        return (collateralAddresses, balances, symbols, decimals);
    }

    function getAToken(address underlyingToken) public view returns (address) {
        IPoolDataProvider dataProvider = IPoolDataProvider(poolAddressesProvider.getPoolDataProvider());
        (address aTokenAddress, , ) = dataProvider.getReserveTokensAddresses(underlyingToken);
        return aTokenAddress;
    }

    function getUnderlyingToken(address aToken) external view returns (address) {
        IPoolDataProvider dataProvider = IPoolDataProvider(poolAddressesProvider.getPoolDataProvider());
        (address underlyingToken, , ) = dataProvider.getReserveTokensAddresses(aToken);
        return underlyingToken;
    }

    function isCollateralSupported(address /* market */, address collateral) external view returns (bool isSupported) {
        IPoolDataProvider dataProvider = IPoolDataProvider(poolAddressesProvider.getPoolDataProvider());
        (
            ,
            uint256 ltv,
            ,
            ,
            ,
            bool usageAsCollateralEnabled,
            ,
            ,
            ,

        ) = dataProvider.getReserveConfigurationData(collateral);
        return usageAsCollateralEnabled || ltv != 0;
    }

    function getSupportedCollaterals(address /* market */) external view returns (address[] memory collateralAddresses) {
        IPool pool = IPool(poolAddressesProvider.getPool());
        IPoolDataProvider dataProvider = IPoolDataProvider(poolAddressesProvider.getPoolDataProvider());
        address[] memory reserves = pool.getReservesList();

        uint256 collateralCount = 0;
        for (uint256 i = 0; i < reserves.length; i++) {
            (, uint256 ltv, , , , bool usageAsCollateralEnabled, , , , ) = dataProvider.getReserveConfigurationData(reserves[i]);
            if (usageAsCollateralEnabled || ltv != 0) collateralCount++;
        }

        collateralAddresses = new address[](collateralCount);
        uint256 index = 0;
        for (uint256 i = 0; i < reserves.length; i++) {
            (, uint256 ltv, , , , bool usageAsCollateralEnabled, , , , ) = dataProvider.getReserveConfigurationData(reserves[i]);
            if (usageAsCollateralEnabled || ltv != 0) {
                collateralAddresses[index] = reserves[i];
                index++;
            }
        }
        return collateralAddresses;
    }
}

