// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {ComptrollerInterface} from "../../../interfaces/venus/ComptrollerInterface.sol";
import {VTokenInterface} from "../../../interfaces/venus/VTokenInterface.sol";
import {ResilientOracleInterface} from "../../../interfaces/venus/ResilientOracleInterface.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title VenusGatewayView
 * @notice View-only gateway for Venus protocol
 * @dev Contains all read/view functions from v1, separate from write operations
 */
contract VenusGatewayView is Ownable {
    ComptrollerInterface public comptroller;
    ResilientOracleInterface public oracle;

    constructor(address _comptroller, address _oracle, address owner_) Ownable(owner_) {
        comptroller = ComptrollerInterface(_comptroller);
        oracle = ResilientOracleInterface(_oracle);
    }

    function setComptroller(address _comptroller) external onlyOwner {
        comptroller = ComptrollerInterface(_comptroller);
    }

    function setOracle(address _oracle) external onlyOwner {
        oracle = ResilientOracleInterface(_oracle);
    }

    function getVTokenForUnderlying(address underlyingToken) public view returns (address) {
        address[] memory vTokenAddresses = this.getAllMarkets();

        for (uint i = 0; i < vTokenAddresses.length; i++) {
            address vTokenAddress = vTokenAddresses[i];

            try VTokenInterface(vTokenAddress).underlying() returns (address underlying) {
                if (underlying == underlyingToken) {
                    return vTokenAddress;
                }
            } catch {
                // Skip if there's an error (e.g., for vBNB which might not have an underlying() function)
                continue;
            }
        }

        revert("VenusGateway: vToken not found for underlying token");
    }

    function getAssetsIn(address account) external view returns (address[] memory) {
        VTokenInterface[] memory vTokens = comptroller.getAssetsIn(account);
        address[] memory vTokenAddresses = new address[](vTokens.length);

        for (uint i = 0; i < vTokens.length; i++) {
            vTokenAddresses[i] = address(vTokens[i]);
        }

        return vTokenAddresses;
    }

    function checkMembership(address account, address vToken) external view returns (bool) {
        return comptroller.checkMembership(account, vToken);
    }

    function getAllMarkets() external view returns (address[] memory) {
        VTokenInterface[] memory vTokens = comptroller.getAllMarkets();
        address[] memory vTokenAddresses = new address[](vTokens.length);

        for (uint i = 0; i < vTokens.length; i++) {
            vTokenAddresses[i] = address(vTokens[i]);
        }

        return vTokenAddresses;
    }

    function getAccountLiquidity(address account) public view returns (uint, uint, uint) {
        return comptroller.getAccountLiquidity(account);
    }

    function getBalance(address token, address user) external view returns (uint256) {
        address vTokenAddress = getVTokenForUnderlying(token);

        // Get vToken balance
        uint vTokenBalance = VTokenInterface(vTokenAddress).balanceOf(user);

        // Convert to underlying amount using exchange rate
        uint exchangeRate = VTokenInterface(vTokenAddress).exchangeRateStored();

        // Calculate underlying value
        return (vTokenBalance * exchangeRate) / 1e18;
    }

    function getBorrowBalance(address token, address user) external view returns (uint256) {
        address vTokenAddress = getVTokenForUnderlying(token);
        return VTokenInterface(vTokenAddress).borrowBalanceStored(user);
    }

    function getBorrowBalanceCurrent(address token, address user) external returns (uint256) {
        address vTokenAddress = getVTokenForUnderlying(token);
        return VTokenInterface(vTokenAddress).borrowBalanceCurrent(user);
    }

    function getBorrowRate(address token) external view returns (uint256, bool) {
        address vTokenAddress = getVTokenForUnderlying(token);
        uint borrowRate = VTokenInterface(vTokenAddress).borrowRatePerBlock();
        return (borrowRate, true);
    }

    function getSupplyRate(address token) external view returns (uint256, bool) {
        address vTokenAddress = getVTokenForUnderlying(token);
        uint supplyRate = VTokenInterface(vTokenAddress).supplyRatePerBlock();
        return (supplyRate, true);
    }

    function getAllVenusMarkets() external view returns (
        address[] memory vTokens,
        address[] memory tokens,
        string[] memory symbols,
        string[] memory names,
        uint8[] memory decimals,
        uint256[] memory prices
    ) {
        // Get all markets from the comptroller
        vTokens = this.getAllMarkets();
        uint256 marketsCount = vTokens.length;

        // Initialize arrays
        tokens = new address[](marketsCount);
        symbols = new string[](marketsCount);
        names = new string[](marketsCount);
        decimals = new uint8[](marketsCount);
        prices = new uint256[](marketsCount);

        // Populate arrays with basic token information
        for (uint i = 0; i < marketsCount; i++) {
            address vTokenAddress = vTokens[i];
            VTokenInterface vToken = VTokenInterface(vTokenAddress);

            // Get underlying token address directly
            try vToken.underlying() returns (address underlyingToken) {
                tokens[i] = underlyingToken;

                // Get token metadata
                IERC20Metadata underlying = IERC20Metadata(underlyingToken);

                // Try to get symbol from underlying, fallback to vToken
                try underlying.symbol() returns (string memory s) {
                    symbols[i] = s;
                } catch {
                    symbols[i] = vToken.symbol();
                }

                // Try to get name from underlying, fallback to vToken
                try underlying.name() returns (string memory n) {
                    names[i] = n;
                } catch {
                    names[i] = vToken.name();
                }

                // Try to get decimals from underlying, fallback to vToken
                try underlying.decimals() returns (uint8 d) {
                    decimals[i] = d;
                } catch {
                    decimals[i] = vToken.decimals();
                }
            } catch {
                // For tokens like vBNB that might not have an underlying() function
                tokens[i] = address(0);
                symbols[i] = vToken.symbol();
                names[i] = vToken.name();
                decimals[i] = vToken.decimals();
            }
            prices[i] = oracle.getUnderlyingPrice(vTokenAddress);
        }

        return (vTokens, tokens, symbols, names, decimals, prices);
    }

    function getMarketRates(address[] calldata vTokens) external view returns (
        uint256[] memory prices,
        uint256[] memory supplyRates,
        uint256[] memory borrowRates
    ) {
        uint256 marketsCount = vTokens.length;

        // Initialize arrays
        prices = new uint256[](marketsCount);
        supplyRates = new uint256[](marketsCount);
        borrowRates = new uint256[](marketsCount);

        // Populate arrays with rate information
        for (uint i = 0; i < marketsCount; i++) {
            address vTokenAddress = vTokens[i];
            VTokenInterface vToken = VTokenInterface(vTokenAddress);

            // Get rates
            supplyRates[i] = vToken.supplyRatePerBlock();
            borrowRates[i] = vToken.borrowRatePerBlock();
            prices[i] = oracle.getUnderlyingPrice(vTokenAddress);
        }

        return (prices, supplyRates, borrowRates);
    }

    function getUserBalances(address[] calldata vTokens, address account) external view returns (
        uint256[] memory balances,
        uint256[] memory borrowBalances
    ) {
        require(account != address(0), "VenusGateway: zero address not allowed");

        uint256 marketsCount = vTokens.length;

        // Initialize arrays
        balances = new uint256[](marketsCount);
        borrowBalances = new uint256[](marketsCount);

        // Populate arrays with user balance information
        for (uint i = 0; i < marketsCount; i++) {
            address vTokenAddress = vTokens[i];
            VTokenInterface vToken = VTokenInterface(vTokenAddress);

            // Get user balances
            try vToken.balanceOf(account) returns (uint256 b) {
                // Convert vToken balance to underlying using exchange rate
                uint256 exchangeRate = vToken.exchangeRateStored();
                balances[i] = (b * exchangeRate) / 1e18;
            } catch {
                balances[i] = 0;
            }

            try vToken.borrowBalanceStored(account) returns (uint256 bb) {
                borrowBalances[i] = bb;
            } catch {
                borrowBalances[i] = 0;
            }
        }

        return (balances, borrowBalances);
    }

    function getCollateralStatus(address[] calldata vTokens, address account) external view returns (
        bool[] memory isCollateral
    ) {
        require(account != address(0), "VenusGateway: zero address not allowed");

        uint256 marketsCount = vTokens.length;
        isCollateral = new bool[](marketsCount);

        // Get the list of markets the user has entered (used as collateral)
        address[] memory enteredMarkets = this.getAssetsIn(account);

        // Check each market if it's being used as collateral
        for (uint i = 0; i < marketsCount; i++) {
            address vTokenAddress = vTokens[i];

            // Linear search through entered markets
            for (uint j = 0; j < enteredMarkets.length; j++) {
                if (enteredMarkets[j] == vTokenAddress) {
                    isCollateral[i] = true;
                    break;
                }
            }
        }

        return isCollateral;
    }

    function getPossibleCollaterals(address token, address user) external view returns (
        address[] memory collateralAddresses,
        uint256[] memory balances,
        string[] memory symbols,
        uint8[] memory decimals
    ) {
        // Get all Venus markets information
        (
            address[] memory vTokens, 
            address[] memory tokens, 
            string[] memory tokenSymbols, 
            string[] memory names, 
            uint8[] memory tokenDecimals,
            uint256[] memory prices
        ) = this.getAllVenusMarkets();

        // Create arrays for return values
        collateralAddresses = new address[](tokens.length);
        symbols = new string[](tokens.length);
        decimals = new uint8[](tokens.length);
        balances = new uint256[](tokens.length); // Initialize with all zeros by default

        // If user address is provided and not zero, get their balances
        if (user != address(0)) {
            // Get user balances for all markets
            (uint256[] memory userBalances, ) = this.getUserBalances(vTokens, user);
            balances = userBalances;
        }

        // Copy token information to return arrays
        for (uint i = 0; i < tokens.length; i++) {
            collateralAddresses[i] = tokens[i];
            symbols[i] = tokenSymbols[i];
            decimals[i] = tokenDecimals[i];
        }

        return (collateralAddresses, balances, symbols, decimals);
    }

    function isCollateralSupported(address market, address collateral) external view returns (bool isSupported) {
        // Get all Venus markets information
        (
            ,  // vTokens (not needed)
            address[] memory tokens, 
            , // symbols (not needed)
            , // names (not needed)
            , // decimals (not needed)
              // prices (not needed)
        ) = this.getAllVenusMarkets();

        // First check that both market and collateral are valid tokens in Venus
        bool marketFound = false;
        bool collateralFound = false;

        for (uint i = 0; i < tokens.length; i++) {
            if (tokens[i] == market) {
                marketFound = true;
            }
            if (tokens[i] == collateral) {
                collateralFound = true;
            }

            // Early exit if both are found
            if (marketFound && collateralFound) {
                break;
            }
        }

        // In Venus, any valid token can be collateral for any other valid token
        return marketFound && collateralFound;
    }

    function getSupportedCollaterals(address market) external view returns (address[] memory collateralAddresses) {
        // Get all Venus markets information
        (
            ,  // vTokens (not needed)
            address[] memory tokens, 
            ,  // symbols (not needed)
            ,  // names (not needed)
            ,// decimals (not needed)
            // prices (not needed)
        ) = this.getAllVenusMarkets();

        // Check if market is a valid token in Venus
        bool marketFound = false;
        for (uint i = 0; i < tokens.length; i++) {
            if (tokens[i] == market) {
                marketFound = true;
                break;
            }
        }

        // If market is not found, return empty array
        if (!marketFound) {
            return new address[](0);
        }

        // In Venus, all tokens can be collateral for any other token
        // So we return all tokens except the market itself
        collateralAddresses = new address[](tokens.length - 1);

        uint index = 0;
        for (uint i = 0; i < tokens.length; i++) {
            // Skip the market token itself
            if (tokens[i] != market) {
                collateralAddresses[index] = tokens[i];
                index++;
            }
        }

        return collateralAddresses;
    }

    function getLtv(address token, address user) external view returns (uint256) {
        // For Venus, LTV is typically around 50-75% depending on the asset
        // We would ideally get this from the Comptroller's collateralFactors for the specific vToken

        // Get the vToken address for this token
        address vTokenAddress;
        try this.getVTokenForUnderlying(token) returns (address vToken) {
            vTokenAddress = vToken;
        } catch {
            // If token is not found in Venus, return 0 LTV
            return 0;
        }

        // In a real implementation, we would get the collateral factor from Comptroller:
        // (, uint collateralFactorMantissa) = comptroller.markets(vTokenAddress);

        // For simplicity, we'll return a default LTV of 75% (expressed as 0.75 * 1e18)
        // In a production environment, this should be replaced with the actual collateral factor
        return 75 * 1e16; // 75% * 1e18 = 75 * 1e16
    }
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {ProtocolGateway} from "../../../gateways/ProtocolGateway.sol";
import {IGateway} from "../../interfaces/IGateway.sol";
import {ProtocolTypes} from "../../interfaces/ProtocolTypes.sol";

import {ComptrollerInterface} from "../../../interfaces/venus/ComptrollerInterface.sol";
import {VTokenInterface} from "../../../interfaces/venus/VTokenInterface.sol";

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

contract VenusGatewayWrite is IGateway, ProtocolGateway, ReentrancyGuard {
    using SafeERC20 for IERC20;

    ComptrollerInterface immutable public comptroller;

    constructor(address router, address _comptroller) ProtocolGateway(router) {
        comptroller = ComptrollerInterface(_comptroller);
    }

    function processLendingInstruction(ProtocolTypes.Output[] calldata inputs, bytes calldata data)
        external
        returns (ProtocolTypes.Output[] memory outputs)
    {
        ProtocolTypes.LendingInstruction memory ins = abi.decode(data, (ProtocolTypes.LendingInstruction));
        address token = ins.token; uint256 amount = ins.amount;
        if (ins.input.index < inputs.length) { token = inputs[ins.input.index].token; amount = inputs[ins.input.index].amount; }
        if (ins.op == ProtocolTypes.LendingOp.Deposit || ins.op == ProtocolTypes.LendingOp.DepositCollateral) {
            deposit(token, ins.user, amount); outputs = new ProtocolTypes.Output[](0);
        } else if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
            (address u, uint256 amt) = withdrawCollateral(token, ins.user, amount);
            outputs = new ProtocolTypes.Output[](1); outputs[0] = ProtocolTypes.Output({token: u, amount: amt});
        } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
            borrow(token, ins.user, amount); outputs = new ProtocolTypes.Output[](1); outputs[0] = ProtocolTypes.Output({token: token, amount: amount});
        } else if (ins.op == ProtocolTypes.LendingOp.Repay) {
            uint256 refund = repay(token, ins.user, amount); outputs = new ProtocolTypes.Output[](1); outputs[0] = ProtocolTypes.Output({token: token, amount: refund});
        } else if (ins.op == ProtocolTypes.LendingOp.GetBorrowBalance) {
            address vToken = _getVTokenForUnderlying(token); uint256 bal = VTokenInterface(vToken).borrowBalanceCurrent(ins.user);
            outputs = new ProtocolTypes.Output[](1); outputs[0] = ProtocolTypes.Output({token: token, amount: bal});
        } else if (ins.op == ProtocolTypes.LendingOp.GetSupplyBalance) {
            address vToken = _getVTokenForUnderlying(token); uint256 vBal = VTokenInterface(vToken).balanceOf(ins.user); uint256 rate = VTokenInterface(vToken).exchangeRateStored();
            uint256 bal = (vBal * rate) / 1e18; outputs = new ProtocolTypes.Output[](1); outputs[0] = ProtocolTypes.Output({token: token, amount: bal});
        } else { revert("Venus: unknown op"); }
    }

    function deposit(address token, address user, uint256 amount) public onlyRouter nonReentrant {
        address vToken = _getVTokenForUnderlying(token);
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(token).approve(vToken, 0);
        IERC20(token).approve(vToken, amount);
        uint err = VTokenInterface(vToken).mint(amount); require(err == 0, "Venus: mint failed");
        uint vBal = VTokenInterface(vToken).balanceOf(address(this)); require(vBal > 0, "Venus: zero vTokens");
        VTokenInterface(vToken).transfer(user, vBal);
    }

    function withdrawCollateral(address collateral, address user, uint256 underlyingAmount)
        public onlyRouterOrSelf(user) nonReentrant returns (address, uint256)
    {
        address vToken = _getVTokenForUnderlying(collateral);

        uint256 borrowBalance = VTokenInterface(vToken).borrowBalanceCurrent(user);
        if (underlyingAmount >= borrowBalance) {
            underlyingAmount = borrowBalance;
            uint256 balance = VTokenInterface(vToken).balanceOf(user);
            VTokenInterface(vToken).transferFrom(user, address(this), balance);
            uint256 pre = IERC20(collateral).balanceOf(address(this));
            uint err = VTokenInterface(vToken).redeem(balance); require(err == 0, "Venus: redeem failed");
            require(err == 0, "Venus: redeem failed");
            uint256 balanceAfter = IERC20(collateral).balanceOf(address(this));
            uint256 redeemed = balanceAfter - pre;
            IERC20(collateral).safeTransfer(msg.sender, redeemed);
            return (collateral, redeemed);
        }

        uint exchangeRate = VTokenInterface(vToken).exchangeRateCurrent();
        uint requiredV = (underlyingAmount * 1e18 + exchangeRate - 1) / exchangeRate;
        VTokenInterface(vToken).transferFrom(user, address(this), requiredV);

        uint256 balanceBefore = IERC20(collateral).balanceOf(address(this));
        uint err = VTokenInterface(vToken).redeem(requiredV); require(err == 0, "Venus: redeem failed");
        // Transfer the full redeemed amount to avoid dust accumulation
        // The round-up may result in slightly more underlying than requested, which we transfer out
        uint256 actualRedeemed = IERC20(collateral).balanceOf(address(this)) - balanceBefore;
        IERC20(collateral).safeTransfer(msg.sender, actualRedeemed);
        return (collateral, actualRedeemed);
    }

    function borrow(address token, address user, uint256 amount) public onlyRouterOrSelf(user) nonReentrant {
        address vToken = _getVTokenForUnderlying(token);
        uint err = VTokenInterface(vToken).borrowBehalf(user, amount); require(err == 0, "Venus: borrow failed");
        IERC20(token).safeTransfer(msg.sender, amount);
    }

    function repay(address token, address user, uint256 amount) public onlyRouter nonReentrant returns (uint256 refund) {
        address vToken = _getVTokenForUnderlying(token);
        uint256 pre = IERC20(token).balanceOf(address(this));
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(token).approve(vToken, 0);
        IERC20(token).approve(vToken, amount);
        uint err = VTokenInterface(vToken).repayBorrowBehalf(user, amount); require(err == 0, "Venus: repay failed");
        uint256 post = IERC20(token).balanceOf(address(this));
        refund = post > pre ? post - pre : 0;
        if (refund > 0) {
            IERC20(token).safeTransfer(msg.sender, refund);
        }
    }

    function authorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address caller,
        ProtocolTypes.Output[] calldata inputs
    )
        external view returns (address[] memory targets, bytes[] memory data, ProtocolTypes.Output[] memory produced)
    {
        targets = new address[](instrs.length);
        data = new bytes[](instrs.length);
        produced = new ProtocolTypes.Output[](instrs.length);

        uint256 k;
        for (uint256 i; i < instrs.length; i++) {
            ProtocolTypes.LendingInstruction calldata ins = instrs[i];

            // Simulation logic
            address token = ins.token;
            uint256 amount = ins.amount;
            if (ins.input.index < inputs.length) {
                token = inputs[ins.input.index].token;
                amount = inputs[ins.input.index].amount;
            }

            if (ins.op == ProtocolTypes.LendingOp.Deposit) {
                // 1) User token approval to gateway, only if insufficient
                uint256 cur = IERC20(token).allowance(caller, address(this));
                if (amount != 0 && cur >= amount) { targets[k] = address(0); data[k] = bytes(""); }
                else { targets[k] = token; data[k] = abi.encodeWithSelector(IERC20.approve.selector, address(this), amount); }
                k++;
                // 2) Enter market to enable as collateral (idempotent)
                address vToken = _getVTokenForUnderlying(token);
                bool member = false; try comptroller.checkMembership(caller, vToken) returns (bool m) { member = m; } catch {}
                if (member) { targets[k] = address(0); data[k] = bytes(""); }
                else { address[] memory markets = new address[](1); markets[0] = vToken; targets[k] = address(comptroller); data[k] = abi.encodeWithSelector(ComptrollerInterface.enterMarkets.selector, markets); }
                k++;
            } else if (ins.op == ProtocolTypes.LendingOp.Repay) {
                // User token approval to gateway, only if insufficient
                uint256 cur = IERC20(token).allowance(caller, address(this));
                if (amount != 0 && cur >= amount) { targets[k] = address(0); data[k] = bytes(""); }
                else { targets[k] = token; data[k] = abi.encodeWithSelector(IERC20.approve.selector, address(this), amount); }

                // Repay produces refund output (unknown/zero for sim)
                produced[i] = ProtocolTypes.Output({ token: token, amount: 0 });
                k++;
            } else if (ins.op == ProtocolTypes.LendingOp.DepositCollateral) {
                address col = token; address vToken = _getVTokenForUnderlying(col);
                // First: approve gateway to spend tokens (only if insufficient)
                uint256 cur = IERC20(col).allowance(caller, address(this));
                if (amount != 0 && cur >= amount) {
                    targets[k] = address(0); data[k] = bytes("");
                } else {
                    targets[k] = col; data[k] = abi.encodeWithSelector(IERC20.approve.selector, address(this), amount);
                }
                k++;
                // Second: enter market so vTokens can be used as collateral (only if not already a member)
                bool member = false;
                try comptroller.checkMembership(caller, vToken) returns (bool m) { member = m; } catch {}
                if (member) {
                    targets[k] = address(0); data[k] = bytes("");
                } else {
                    address[] memory markets = new address[](1); markets[0] = vToken;
                    targets[k] = address(comptroller); data[k] = abi.encodeWithSelector(ComptrollerInterface.enterMarkets.selector, markets);
                }
                k++;
            } else if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
                address col = token; address vToken = _getVTokenForUnderlying(col);
                // Compute required vTokens from underlying using exchangeRateStored; if amount==0, require max
                uint rate = VTokenInterface(vToken).exchangeRateStored();
                uint requiredV = amount == 0 ? type(uint256).max : (amount * 1e18 + rate - 1) / rate;
                uint256 curV = IERC20(vToken).allowance(caller, address(this));
                if ((amount != 0 && curV >= requiredV) || (amount == 0 && curV == type(uint256).max)) {
                    targets[k] = address(0); data[k] = bytes("");
                } else {
                    targets[k] = vToken; data[k] = abi.encodeWithSelector(IERC20.approve.selector, address(this), requiredV);
                }

                // Simulate output
                produced[i] = ProtocolTypes.Output({ token: token, amount: amount });
                k++;
            } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
                // Only updateDelegate if not already approved
                bool approved = false;
                try comptroller.approvedDelegates(caller, address(this)) returns (bool a) { approved = a; } catch {}
                if (approved) {
                    targets[k] = address(0); data[k] = bytes("");
                } else {
                    targets[k] = address(comptroller); data[k] = abi.encodeWithSelector(ComptrollerInterface.updateDelegate.selector, address(this), true);
                }

                // Simulate output
                produced[i] = ProtocolTypes.Output({ token: token, amount: amount });
                k++;
            } else if (ins.op == ProtocolTypes.LendingOp.GetBorrowBalance) {
                address vToken = _getVTokenForUnderlying(token); uint256 bal = VTokenInterface(vToken).borrowBalanceStored(ins.user);
                produced[i] = ProtocolTypes.Output({ token: token, amount: bal });
                targets[k] = address(0); data[k] = bytes("");
                k++;
            } else if (ins.op == ProtocolTypes.LendingOp.GetSupplyBalance) {
                address vToken = _getVTokenForUnderlying(token); uint256 vBal = VTokenInterface(vToken).balanceOf(ins.user); uint256 rate = VTokenInterface(vToken).exchangeRateStored();
                uint256 bal = (vBal * rate) / 1e18; 
                produced[i] = ProtocolTypes.Output({ token: token, amount: bal });
                targets[k] = address(0); data[k] = bytes("");
                k++;
            }
        }
    }

    function deauthorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address caller
    ) external view returns (address[] memory targets, bytes[] memory data) {
        uint256 count; for (uint256 i; i < instrs.length; i++) {
            ProtocolTypes.LendingInstruction calldata ins = instrs[i];
            if (ins.op == ProtocolTypes.LendingOp.Deposit) count += 2; // approve + enterMarkets
            else if (ins.op == ProtocolTypes.LendingOp.Repay) count++;
            else if (ins.op == ProtocolTypes.LendingOp.DepositCollateral) count += 2; // approve + enterMarkets
            else if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) count++;
            else if (ins.op == ProtocolTypes.LendingOp.Borrow) count++;
        }
        targets = new address[](count); data = new bytes[](count); uint256 k;

        for (uint256 i; i < instrs.length; i++) {
            ProtocolTypes.LendingInstruction calldata ins = instrs[i];

            if (ins.op == ProtocolTypes.LendingOp.Deposit) {
                targets[k] = ins.token; data[k] = abi.encodeWithSelector(IERC20.approve.selector, address(this), 0);
                k++;
                // Cannot "exit market" easily if there are borrows, so we skip deauth for enterMarkets
                targets[k] = address(0); data[k] = bytes("");
                k++;
            } else if (ins.op == ProtocolTypes.LendingOp.Repay) {
                targets[k] = ins.token; data[k] = abi.encodeWithSelector(IERC20.approve.selector, address(this), 0);
                k++;
            } else if (ins.op == ProtocolTypes.LendingOp.DepositCollateral) {
                targets[k] = ins.token; data[k] = abi.encodeWithSelector(IERC20.approve.selector, address(this), 0);
                k++;
                targets[k] = address(0); data[k] = bytes("");
                k++;
            } else if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
                address vToken = _getVTokenForUnderlying(ins.token);
                targets[k] = vToken; data[k] = abi.encodeWithSelector(IERC20.approve.selector, address(this), 0);
                k++;
            } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
                targets[k] = address(comptroller); data[k] = abi.encodeWithSelector(ComptrollerInterface.updateDelegate.selector, address(this), false);
                k++;
            }
        }
    }

    function _getVTokenForUnderlying(address underlying) internal view returns (address) {
        VTokenInterface[] memory vTokens = comptroller.getAllMarkets();
        for (uint i = 0; i < vTokens.length; i++) {
            try vTokens[i].underlying() returns (address u) { if (u == underlying) return address(vTokens[i]); } catch {}
        }
        revert("Venus: vToken not found");
    }
}

