// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

/**
 * @title Venus's VToken Interface
 * @notice Interface for interacting with Venus VTokens
 * @dev Adapted for compatibility with Solidity ^0.8.10 from Venus documentation
 */
interface VTokenInterface {
    /*** Market Events ***/
    event AccrueInterest(uint cashPrior, uint interestAccumulated, uint borrowIndex, uint totalBorrows);
    event Mint(address minter, uint mintAmount, uint mintTokens);
    event Redeem(address redeemer, uint redeemAmount, uint redeemTokens);
    event Borrow(address borrower, uint borrowAmount, uint accountBorrows, uint totalBorrows);
    event RepayBorrow(address payer, address borrower, uint repayAmount, uint accountBorrows, uint totalBorrows);
    event LiquidateBorrow(address liquidator, address borrower, uint repayAmount, address vTokenCollateral, uint seizeTokens);

    /*** VToken State ***/
    function underlying() external view returns (address);
    function decimals() external view returns (uint8);
    function totalSupply() external view returns (uint);
    function totalBorrows() external view returns (uint);
    function totalReserves() external view returns (uint);
    function exchangeRateStored() external view returns (uint);
    function accrualBlockNumber() external view returns (uint);
    function comptroller() external view returns (address);
    function symbol() external view returns (string memory);
    function name() external view returns (string memory);
    function reserveFactorMantissa() external view returns (uint);
    function getCash() external view returns (uint);
    function borrowIndex() external view returns (uint);

    /*** User Account Functions ***/
    function balanceOf(address owner) external view returns (uint);
    function allowance(address owner, address spender) external view returns (uint);
    function approve(address spender, uint amount) external returns (bool);
    function transfer(address dst, uint amount) external returns (bool);
    function transferFrom(address src, address dst, uint amount) external returns (bool);

    /*** VToken Market Functions ***/
    function mint(uint mintAmount) external returns (uint);
    function redeem(uint redeemTokens) external returns (uint);
    function redeemUnderlying(uint redeemAmount) external returns (uint);
    function borrow(uint borrowAmount) external returns (uint);
    function borrowBehalf(address borrower, uint borrowAmount) external returns (uint);
    function repayBorrow(uint repayAmount) external returns (uint);
    function repayBorrowBehalf(address borrower, uint repayAmount) external returns (uint);

    /*** Borrow-Related Functions ***/
    function borrowBalanceCurrent(address account) external returns (uint);
    function borrowBalanceStored(address account) external view returns (uint);
    function supplyRatePerBlock() external view returns (uint);
    function borrowRatePerBlock() external view returns (uint);
    function exchangeRateCurrent() external returns (uint);
    function accrueInterest() external returns (uint);

    /*** Liquidation Functions ***/
    function liquidateBorrow(address borrower, uint repayAmount, address vTokenCollateral) external returns (uint);

    /*** Admin Functions ***/
    function _setReserveFactor(uint newReserveFactorMantissa) external returns (uint);
    function _reduceReserves(uint reduceAmount) external returns (uint);
    function _setInterestRateModel(address newInterestRateModel) external returns (uint);
}

contracts/v2/gateways/aave/AaveGatewayVi.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {IPoolAddressesProvider} from "../../../interfaces/aave/IPoolAddressesProvider.sol";
import {IPool} from "@aave/core-v3/contracts/interfaces/IPool.sol";
import {IPoolDataProvider} from "@aave/core-v3/contracts/interfaces/IPoolDataProvider.sol";
import {IAaveOracle} from "@aave/core-v3/contracts/interfaces/IAaveOracle.sol";
import {DataTypes} from "@aave/core-v3/contracts/protocol/libraries/types/DataTypes.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";

/**
 * @title AaveGatewayViewBase
 * @notice View-only gateway for Aave v3 protocol on Base. Avoids UiPoolDataProvider struct differences
 *         by reading directly from Pool, DataProvider and Oracle. Constructor signature matches the
 *         generic view so deploy scripts can deploy this under the same name.
 */
contract AaveGatewayViewBase {

    IPoolAddressesProvider public immutable poolAddressesProvider;

    constructor(address _poolAddressesProvider, address /* _uiPoolDataProvider */) {
        poolAddressesProvider = IPoolAddressesProvider(_poolAddressesProvider);
    }

    struct TokenInfo {
        address token;
        uint256 supplyRate;
        uint256 borrowRate;
        string name;
        string symbol;
        uint256 price;
        uint256 borrowBalance;
        uint256 balance;
        address aToken;
        uint8 decimals;
    }

    struct EModeInfo {
        uint8 id;
        uint16 ltv;
        uint16 liquidationThreshold;
        uint16 liquidationBonus;
        string label;
        uint128 collateralBitmap;
        uint128 borrowableBitmap;
    }

    /// @notice Reserve configuration for LTV calculations
    struct ReserveConfigData {
        address token;
        uint256 price;              // Price in base currency (8 decimals)
        uint256 ltv;                // Loan-to-value in basis points (0-10000)
        uint256 liquidationThreshold; // Liquidation threshold in basis points
        uint256 liquidationBonus;   // Liquidation bonus in basis points
        uint8 decimals;
        bool usageAsCollateralEnabled;
        bool borrowingEnabled;
    }

    /// @notice Returns the user's current E-Mode category ID
    /// @param user The address of the user
    /// @return categoryId The E-Mode category ID (0 = no E-Mode)
    function getUserEMode(address user) external view returns (uint8 categoryId) {
        IPool pool = IPool(poolAddressesProvider.getPool());
        categoryId = uint8(pool.getUserEMode(user));
    }

    /// @notice Returns all available E-Mode categories
    /// @dev Note: Base doesn't support the UiPoolDataProvider's getEModes, so we return empty for now
    /// @return emodes Array of E-Mode categories with their configurations
    function getEModes() external view returns (EModeInfo[] memory emodes) {
        emodes = new EModeInfo[](0);
    }

    /// @notice Returns all token info for a given user.
    function getAllTokensInfo(address user) external view returns (TokenInfo[] memory) {
        IPool pool = IPool(poolAddressesProvider.getPool());
        IAaveOracle oracle = IAaveOracle(poolAddressesProvider.getPriceOracle());
        IPoolDataProvider dataProvider = IPoolDataProvider(poolAddressesProvider.getPoolDataProvider());

        address[] memory reserves = pool.getReservesList();
        TokenInfo[] memory tokens = new TokenInfo[](reserves.length);

        for (uint256 i = 0; i < reserves.length; i++) {
            DataTypes.ReserveData memory baseData = pool.getReserveData(reserves[i]);
            (address aToken, , address variableDebtToken) = dataProvider.getReserveTokensAddresses(reserves[i]);

            uint256 balance = 0;
            uint256 borrowBalance = 0;
            if (aToken != address(0)) {
                try IERC20(aToken).balanceOf(user) returns (uint256 bal) { balance = bal; } catch {}
            }
            if (variableDebtToken != address(0)) {
                try IERC20(variableDebtToken).balanceOf(user) returns (uint256 bal) { borrowBalance = bal; } catch {}
            }

            string memory name = "";
            string memory symbol = "";
            try IERC20Metadata(reserves[i]).name() returns (string memory n) { name = n; } catch {}
            try IERC20Metadata(reserves[i]).symbol() returns (string memory s) { symbol = s; } catch {}
            uint8 dec = 18;
            try ERC20(reserves[i]).decimals() returns (uint8 d) { dec = d; } catch {}

            // Wrap price fetch in try-catch to handle broken price feeds (e.g., ZeroLend pzETH)
            uint256 price = 0;
            try oracle.getAssetPrice(reserves[i]) returns (uint256 p) { price = p; } catch {}

            tokens[i] = TokenInfo({
                token: reserves[i],
                supplyRate: baseData.currentLiquidityRate,
                borrowRate: baseData.currentVariableBorrowRate,
                name: name,
                symbol: symbol,
                price: price,
                borrowBalance: borrowBalance,
                balance: balance,
                aToken: aToken,
                decimals: dec
            });
        }
        return tokens;
    }

    /// @notice Returns all token info for a user along with their E-Mode category
    /// @param user The address of the user
    /// @return tokens Array of TokenInfo structs
    /// @return userEModeCategory The user's current E-Mode category ID (0 = no E-Mode)
    function getAllTokensInfoWithEMode(address user) external view returns (TokenInfo[] memory tokens, uint8 userEModeCategory) {
        tokens = this.getAllTokensInfo(user);
        userEModeCategory = this.getUserEMode(user);
    }

    /// @notice Get reserve configuration data for multiple tokens (for frontend LTV calculations)
    /// @dev Returns price, LTV, liquidation threshold, and other config for each token
    /// @param tokens Array of token addresses to get config for
    /// @return configs Array of reserve configuration data
    function getReserveConfigs(address[] calldata tokens) external view returns (ReserveConfigData[] memory configs) {
        IAaveOracle oracle = IAaveOracle(poolAddressesProvider.getPriceOracle());
        IPoolDataProvider dataProvider = IPoolDataProvider(poolAddressesProvider.getPoolDataProvider());

        configs = new ReserveConfigData[](tokens.length);

        for (uint256 i = 0; i < tokens.length; i++) {
            address token = tokens[i];

            // Get price
            uint256 price = 0;
            try oracle.getAssetPrice(token) returns (uint256 p) { price = p; } catch {}

            // Get decimals
            uint8 decimals = 18;
            try ERC20(token).decimals() returns (uint8 d) { decimals = d; } catch {}

            // Get reserve configuration
            uint256 ltv = 0;
            uint256 liquidationThreshold = 0;
            uint256 liquidationBonus = 0;
            bool usageAsCollateralEnabled = false;
            bool borrowingEnabled = false;

            try dataProvider.getReserveConfigurationData(token) returns (
                uint256 _decimals,
                uint256 _ltv,
                uint256 _liquidationThreshold,
                uint256 _liquidationBonus,
                uint256 /* reserveFactor */,
                bool _usageAsCollateralEnabled,
                bool _borrowingEnabled,
                bool /* stableBorrowRateEnabled */,
                bool /* isActive */,
                bool /* isFrozen */
            ) {
                ltv = _ltv;
                liquidationThreshold = _liquidationThreshold;
                liquidationBonus = _liquidationBonus;
                usageAsCollateralEnabled = _usageAsCollateralEnabled;
                borrowingEnabled = _borrowingEnabled;
            } catch {}

            configs[i] = ReserveConfigData({
                token: token,
                price: price,
                ltv: ltv,
                liquidationThreshold: liquidationThreshold,
                liquidationBonus: liquidationBonus,
                decimals: decimals,
                usageAsCollateralEnabled: usageAsCollateralEnabled,
                borrowingEnabled: borrowingEnabled
            });
        }
    }

    /// @notice Get reserve config for a single token
    /// @param token The token address
    /// @return config The reserve configuration data
    function getReserveConfig(address token) external view returns (ReserveConfigData memory config) {
        address[] memory tokens = new address[](1);
        tokens[0] = token;
        ReserveConfigData[] memory configs = this.getReserveConfigs(tokens);
        return configs[0];
    }

    function getBorrowRate(address token) external view returns (uint256, bool) {
        IPool pool = IPool(poolAddressesProvider.getPool());
        if (!_isReserve(pool, token)) return (0, false);
        DataTypes.ReserveData memory baseData = pool.getReserveData(token);
        return (baseData.currentVariableBorrowRate, true);
    }

    function getSupplyRate(address token) external view returns (uint256, bool) {
        IPool pool = IPool(poolAddressesProvider.getPool());
        if (!_isReserve(pool, token)) return (0, false);
        DataTypes.ReserveData memory baseData = pool.getReserveData(token);
        return (baseData.currentLiquidityRate, true);
    }

    function _isReserve(IPool pool, address token) internal view returns (bool) {
        address[] memory reserves = pool.getReservesList();
        for (uint256 i = 0; i < reserves.length; i++) {
            if (reserves[i] == token) return true;
        }
        return false;
    }

    function getBalance(address token, address user) public view returns (uint256) {
        (address aToken, , ) = _getReserveAddresses(token);
        if (aToken != address(0)) {
            try IERC20(aToken).balanceOf(user) returns (uint256 bal) { return bal; } catch {}
        }
        return 0;
    }

    function getBorrowBalance(address token, address user) public view returns (uint256) {
        (, address variableDebtToken, ) = _getReserveAddresses(token);
        if (variableDebtToken != address(0)) {
            try IERC20(variableDebtToken).balanceOf(user) returns (uint256 bal) { return bal; } catch {}
        }
        return 0;
    }

    function getBorrowBalanceCurrent(address token, address user) external returns (uint256) {
        return getBorrowBalance(token, user);
    }

    /// @notice Returns the liquidation threshold (LLTV) for a given user in basis points.
    function getMaxLtv(address /* market */, address user) external view returns (uint256) {
        IPool pool = IPool(poolAddressesProvider.getPool());
        (, , , uint256 currentLiquidationThreshold, ,) = pool.getUserAccountData(user);
        return currentLiquidationThreshold;
    }

    /// @notice Returns the configured maximum LTV for the user in basis points.
    /// @dev This is the max borrow power, NOT the current debt/collateral ratio
    function getLtv(address /* market */, address user) external view returns (uint256) {
        IPool pool = IPool(poolAddressesProvider.getPool());
        (, , , , uint256 ltv,) = pool.getUserAccountData(user);
        return ltv;
    }

    /// @notice Returns the current LTV (debt/collateral) in basis points
    /// @dev This is the actual current LTV = totalDebt / totalCollateral * 10000
    /// @param user The user address
    /// @return Current LTV in basis points (e.g., 6500 = 65%)
    function getCurrentLtvBps(address /* market */, address user) external view returns (uint256) {
        IPool pool = IPool(poolAddressesProvider.getPool());
        (uint256 totalCollateralBase, uint256 totalDebtBase,,,,) = pool.getUserAccountData(user);
        if (totalCollateralBase == 0) return 0;
        return (totalDebtBase * 10000) / totalCollateralBase;
    }

    /// @notice Returns the liquidation LTV threshold in basis points
    /// @dev Position is liquidatable when currentLTV >= liquidationLtvBps
    /// @param user The user address
    /// @return Liquidation threshold in basis points
    function getLiquidationLtvBps(address /* market */, address user) external view returns (uint256) {
        IPool pool = IPool(poolAddressesProvider.getPool());
        (, , , uint256 liquidationThreshold,,) = pool.getUserAccountData(user);
        return liquidationThreshold;
    }

    /// @notice Returns full user account data from Aave
    /// @param user The user address
    /// @return totalCollateralBase Total collateral in base currency
    /// @return totalDebtBase Total debt in base currency
    /// @return availableBorrowsBase Available borrows in base currency
    /// @return currentLiquidationThreshold Current liquidation threshold (bps)
    /// @return ltv Current LTV (bps)
    /// @return healthFactor Health factor (1e18 scale)
    function getUserAccountData(address user) external view returns (
        uint256 totalCollateralBase,
        uint256 totalDebtBase,
        uint256 availableBorrowsBase,
        uint256 currentLiquidationThreshold,
        uint256 ltv,
        uint256 healthFactor
    ) {
        IPool pool = IPool(poolAddressesProvider.getPool());
        return pool.getUserAccountData(user);
    }

    function borrowedTokens(address user) external view returns (address[] memory) {
        IPool pool = IPool(poolAddressesProvider.getPool());
        IPoolDataProvider dataProvider = IPoolDataProvider(poolAddressesProvider.getPoolDataProvider());
        address[] memory reserves = pool.getReservesList();

        uint256 count = 0;
        for (uint256 i = 0; i < reserves.length; i++) {
            (, , address variableDebtToken) = dataProvider.getReserveTokensAddresses(reserves[i]);
            if (variableDebtToken != address(0)) {
                try IERC20(variableDebtToken).balanceOf(user) returns (uint256 bal) {
                    if (bal > 0) count++;
                } catch {}
            }
        }

        address[] memory tokens = new address[](count);
        uint256 idx = 0;
        for (uint256 i = 0; i < reserves.length; i++) {
            (, , address variableDebtToken) = dataProvider.getReserveTokensAddresses(reserves[i]);
            if (variableDebtToken != address(0)) {
                try IERC20(variableDebtToken).balanceOf(user) returns (uint256 bal) {
                    if (bal > 0) tokens[idx++] = reserves[i];
                } catch {}
            }
        }
        return tokens;
    }

    function _getReserveAddresses(address token) internal view returns (address aToken, address variableDebtToken, bool found) {
        IPoolDataProvider dataProvider = IPoolDataProvider(poolAddressesProvider.getPoolDataProvider());
        try dataProvider.getReserveTokensAddresses(token) returns (address aT, address /*stableDebtToken*/, address vD) {
            return (aT, vD, true);
        } catch {}
        return (address(0), address(0), false);
    }

    function getPossibleCollaterals(
        address /* market */, 
        address user
    ) external view returns (
        address[] memory collateralAddresses,
        uint256[] memory balances,
        string[] memory symbols,
        uint8[] memory decimals
    ) {
        IPool pool = IPool(poolAddressesProvider.getPool());
        address[] memory reserves = pool.getReservesList();

        if (user == address(0)) {
            uint256 tokenCountX = reserves.length;
            collateralAddresses = new address[](tokenCountX);
            balances = new uint256[](tokenCountX);
            symbols = new string[](tokenCountX);
            decimals = new uint8[](tokenCountX);

            for (uint256 i = 0; i < tokenCountX; i++) {
                collateralAddresses[i] = reserves[i];
                try IERC20Metadata(reserves[i]).symbol() returns (string memory s) { symbols[i] = s; } catch { symbols[i] = ""; }
                try ERC20(reserves[i]).decimals() returns (uint8 dec) { decimals[i] = dec; } catch { decimals[i] = 18; }
            }
            return (collateralAddresses, balances, symbols, decimals);
        }

        // Connected wallet: include only non-zero balances
        IPoolDataProvider dataProvider = IPoolDataProvider(poolAddressesProvider.getPoolDataProvider());
        uint256 tokenCount = 0;
        for (uint256 i = 0; i < reserves.length; i++) {
            (address aToken, , ) = dataProvider.getReserveTokensAddresses(reserves[i]);
            if (aToken != address(0)) {
                try IERC20(aToken).balanceOf(user) returns (uint256 bal) {
                    if (bal > 0) tokenCount++;
                } catch {}
            }
        }

        collateralAddresses = new address[](tokenCount);
        balances = new uint256[](tokenCount);
        symbols = new string[](tokenCount);
        decimals = new uint8[](tokenCount);

        uint256 index = 0;
        for (uint256 i = 0; i < reserves.length; i++) {
            (address aToken, , ) = dataProvider.getReserveTokensAddresses(reserves[i]);
            if (aToken != address(0)) {
                uint256 bal = 0;
                try IERC20(aToken).balanceOf(user) returns (uint256 b) { bal = b; } catch {}
                if (bal > 0) {
                    collateralAddresses[index] = reserves[i];
                    balances[index] = bal;
                    try IERC20Metadata(reserves[i]).symbol() returns (string memory s) { symbols[index] = s; } catch { symbols[index] = ""; }
                    try ERC20(reserves[i]).decimals() returns (uint8 dec) { decimals[index] = dec; } catch { decimals[index] = 18; }
                    index++;
                }
            }
        }
        return (collateralAddresses, balances, symbols, decimals);
    }

    function getAToken(address underlyingToken) public view returns (address) {
        IPoolDataProvider dataProvider = IPoolDataProvider(poolAddressesProvider.getPoolDataProvider());
        (address aTokenAddress, , ) = dataProvider.getReserveTokensAddresses(underlyingToken);
        return aTokenAddress;
    }

    function getUnderlyingToken(address aToken) external view returns (address) {
        IPoolDataProvider dataProvider = IPoolDataProvider(poolAddressesProvider.getPoolDataProvider());
        (address underlyingToken, , ) = dataProvider.getReserveTokensAddresses(aToken);
        return underlyingToken;
    }

    function isCollateralSupported(address /* market */, address collateral) external view returns (bool isSupported) {
        IPoolDataProvider dataProvider = IPoolDataProvider(poolAddressesProvider.getPoolDataProvider());
        (
            ,
            uint256 ltv,
            ,
            ,
            ,
            bool usageAsCollateralEnabled,
            ,
            ,
            ,

        ) = dataProvider.getReserveConfigurationData(collateral);
        return usageAsCollateralEnabled || ltv != 0;
    }

    function getSupportedCollaterals(address /* market */) external view returns (address[] memory collateralAddresses) {
        IPool pool = IPool(poolAddressesProvider.getPool());
        IPoolDataProvider dataProvider = IPoolDataProvider(poolAddressesProvider.getPoolDataProvider());
        address[] memory reserves = pool.getReservesList();

        uint256 collateralCount = 0;
        for (uint256 i = 0; i < reserves.length; i++) {
            (, uint256 ltv, , , , bool usageAsCollateralEnabled, , , , ) = dataProvider.getReserveConfigurationData(reserves[i]);
            if (usageAsCollateralEnabled || ltv != 0) collateralCount++;
        }

        collateralAddresses = new address[](collateralCount);
        uint256 index = 0;
        for (uint256 i = 0; i < reserves.length; i++) {
            (, uint256 ltv, , , , bool usageAsCollateralEnabled, , , , ) = dataProvider.getReserveConfigurationData(reserves[i]);
            if (usageAsCollateralEnabled || ltv != 0) {
                collateralAddresses[index] = reserves[i];
                index++;
            }
        }
        return collateralAddresses;
    }
}

contracts/v2/gateways/compound/CompoundG.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {ICompoundComet} from "../../interfaces/compound/ICompoundComet.sol";
import {AggregatorV3Interface} from "@chainlink/contracts/src/v0.8/shared/interfaces/AggregatorV3Interface.sol";
import {ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";

/// @notice Interface for reading from write gateway
interface IWriteGateway {
    function tokenToComet(address) external view returns (ICompoundComet);
    function allComets() external view returns (address[] memory);
}

/**
 * @title CompoundGatewayView
 * @notice View-only gateway for Compound Comet protocol
 * @dev Contains all read/view functions from v1, separate from write operations
 */
contract CompoundGatewayView is Ownable {
    // base token => Comet
    mapping(address => ICompoundComet) public tokenToComet;
    mapping(address => AggregatorV3Interface) public overrideFeeds;

    // registry for view
    address[] private _comets;
    mapping(address => bool) private _isRegistered;

    // Optional reference to write gateway for syncing Comet registry
    address public writeGateway;

    event CometRegistered(address indexed baseToken, address indexed comet);
    event CometReplaced(address indexed baseToken, address indexed oldComet, address indexed newComet);
    event WriteGatewayUpdated(address indexed oldGateway, address indexed newGateway);

    constructor(address owner_) Ownable(owner_) {}

    modifier whenCometExists(address token) {
        if (address(tokenToComet[token]) != address(0)) {
            _;
        }
    }

    modifier cometMustExist(address token) {
        require(address(tokenToComet[token]) != address(0), "Comet is not set");
        _;
    }

    function addComet(ICompoundComet comet) external onlyOwner {
        address base = comet.baseToken();
        require(base != address(0), "Compound: base=0");
        address prev = address(tokenToComet[base]);

        tokenToComet[base] = comet;
        if (!_isRegistered[address(comet)]) { _isRegistered[address(comet)] = true; _comets.push(address(comet)); }
        emit CometRegistered(base, address(comet));
        if (prev != address(0) && prev != address(comet)) emit CometReplaced(base, prev, address(comet));
    }

    function setCometForBase(address baseToken, address comet_) external onlyOwner {
        require(baseToken != address(0) && comet_ != address(0), "Compound: zero");
        address prev = address(tokenToComet[baseToken]);
        tokenToComet[baseToken] = ICompoundComet(comet_);
        if (!_isRegistered[comet_]) { _isRegistered[comet_] = true; _comets.push(comet_); }
        emit CometRegistered(baseToken, comet_);
        if (prev != address(0) && prev != comet_) emit CometReplaced(baseToken, prev, comet_);
    }

    function allComets() external view returns (address[] memory) { return _comets; }

    /// @notice Get all base tokens for registered Comets
    function allBaseTokens() external view returns (address[] memory) {
        address[] memory baseTokens = new address[](_comets.length);
        for (uint256 i = 0; i < _comets.length; i++) {
            baseTokens[i] = ICompoundComet(_comets[i]).baseToken();
        }
        return baseTokens;
    }

    /// @notice Get all active Comets (union of local + write gateway registries)
    function allActiveComets() public view returns (address[] memory comets) {
        address[] memory local = _comets;
        address[] memory remote = writeGateway == address(0)
            ? new address[](0)
            : IWriteGateway(writeGateway).allComets();

        // Upper bound, then de-dupe
        comets = new address[](local.length + remote.length);
        uint256 k = 0;

        for (uint256 i = 0; i < local.length; i++) comets[k++] = local[i];

        for (uint256 j = 0; j < remote.length; j++) {
            address c = remote[j];
            bool seen = false;
            for (uint256 i = 0; i < k; i++) {
                if (comets[i] == c) { seen = true; break; }
            }
            if (!seen) comets[k++] = c;
        }
        assembly { mstore(comets, k) } // shrink
    }

    /// @notice Get all active base tokens (from allActiveComets)
    function allActiveBaseTokens() external view returns (address[] memory bases) {
        address[] memory comets = allActiveComets();
        bases = new address[](comets.length);
        for (uint256 i = 0; i < comets.length; i++) {
            bases[i] = ICompoundComet(comets[i]).baseToken();
        }
    }

    function overrideFeed(address token, AggregatorV3Interface feed) external onlyOwner {
        overrideFeeds[token] = feed;
    }

    /// @notice Set the write gateway address for syncing Comet registry
    function setWriteGateway(address writeGateway_) external onlyOwner {
        address oldGateway = writeGateway;
        writeGateway = writeGateway_;
        emit WriteGatewayUpdated(oldGateway, writeGateway_);
    }

    /// @notice Sync a Comet from the write gateway to this view gateway
    function syncCometFromWriteGateway(address baseToken) external onlyOwner {
        require(writeGateway != address(0), "Compound: write gateway not set");
        ICompoundComet cometFromWrite = IWriteGateway(writeGateway).tokenToComet(baseToken);
        require(address(cometFromWrite) != address(0), "Compound: comet not in write gateway");

        // Register in this view gateway - inline the logic to avoid ordering issues
        address prev = address(tokenToComet[baseToken]);
        tokenToComet[baseToken] = cometFromWrite;
        if (!_isRegistered[address(cometFromWrite)]) {
            _isRegistered[address(cometFromWrite)] = true;
            _comets.push(address(cometFromWrite));
        }
        emit CometRegistered(baseToken, address(cometFromWrite));
        if (prev != address(0) && prev != address(cometFromWrite)) {
            emit CometReplaced(baseToken, prev, address(cometFromWrite));
        }
    }

    /// @notice Sync all Comets from write gateway
    function syncAllCometsFromWriteGateway() external onlyOwner {
        require(writeGateway != address(0), "Compound: write gateway not set");
        address[] memory writeComets = IWriteGateway(writeGateway).allComets();

        // For each comet, sync it
        for (uint256 i = 0; i < writeComets.length; i++) {
            ICompoundComet comet = ICompoundComet(writeComets[i]);
            address baseToken = comet.baseToken();
            if (address(tokenToComet[baseToken]) != address(comet)) {
                // Inline the logic to avoid ordering issues
                address prev = address(tokenToComet[baseToken]);
                tokenToComet[baseToken] = comet;
                if (!_isRegistered[address(comet)]) {
                    _isRegistered[address(comet)] = true;
                    _comets.push(address(comet));
                }
                emit CometRegistered(baseToken, address(comet));
                if (prev != address(0) && prev != address(comet)) {
                    emit CometReplaced(baseToken, prev, address(comet));
                }
            }
        }
    }

    /// @notice Get Comet for base token, falling back to write gateway if not found locally
    function getComet(address baseToken) public view returns (ICompoundComet) {
        ICompoundComet local = tokenToComet[baseToken];
        if (address(local) != address(0)) {
            return local;
        }
        // Fallback to write gateway if set
        if (writeGateway != address(0)) {
            try IWriteGateway(writeGateway).tokenToComet(baseToken) returns (ICompoundComet comet) {
                if (address(comet) != address(0)) {
                    return comet;
                }
            } catch {
                // Fall through to revert
            }
        }
        revert("Compound: comet not found");
    }

    function getSupplyRate(address token) external view whenCometExists(token) returns (uint256 supplyRate, bool success) {
        ICompoundComet comet = getComet(token);
        supplyRate = comet.getSupplyRate(comet.getUtilization());
        success = true;
    }

    function getBorrowRate(address token) external view whenCometExists(token) returns (uint256 borrowRate, bool success) {
        ICompoundComet comet = getComet(token);
        borrowRate = comet.getBorrowRate(comet.getUtilization());
        success = true;
    }

    function getBaseToken(ICompoundComet comet) external view returns (address) {
        return comet.baseToken();
    }

    function getBalance(address token, address user) external view returns (uint256) {
        ICompoundComet comet = getComet(token);
        return comet.balanceOf(user);
    }

    function getBorrowBalance(address token, address user) public view returns (uint256) {
        ICompoundComet comet = getComet(token);
        return comet.borrowBalanceOf(user);
    }

    function getBorrowBalanceCurrent(address token, address user) external view returns (uint256) {
        return getBorrowBalance(token, user);
    }

    function getLtv(address token, address user) external view returns (uint256) {
        ICompoundComet comet = getComet(token);
        (uint256 totalCollateralValue, uint256 totalBorrowAdjusted) = _collateralTotalsWithFactor(comet, user, false);

        if (totalCollateralValue == 0) return 0;

        return (totalBorrowAdjusted * 10_000) / totalCollateralValue;
    }

    function getMaxLtv(address token, address user) external view returns (uint256) {
        ICompoundComet comet = getComet(token);
        (uint256 totalCollValue, uint256 totalLiqAdjValue) = _collateralTotalsWithFactor(comet, user, true);

        if (totalCollValue == 0) return 0;

        return (totalLiqAdjValue * 10_000) / totalCollValue;
    }

    /// @notice Returns the current LTV (debt/collateral) in basis points
    /// @dev This is the actual current LTV = totalDebt / totalCollateral * 10000
    /// @param token The base token (market identifier)
    /// @param user The user address
    /// @return Current LTV in basis points (e.g., 6500 = 65%)
    function getCurrentLtvBps(address token, address user) external view returns (uint256) {
        ICompoundComet comet = getComet(token);

        // Get raw borrow balance (in base token units)
        uint256 borrowBalance = comet.borrowBalanceOf(user);
        if (borrowBalance == 0) return 0;

        // Get total collateral value (in base token units via oracle)
        (uint256 totalCollateralValue,) = _collateralTotalsWithFactor(comet, user, false);
        if (totalCollateralValue == 0) return 0;

        // Convert borrow balance to same scale as collateral value
        // borrowBalance is in base token units, collateralValue is in price-scaled units
        // We need to scale borrowBalance by the base token's price
        address baseToken = comet.baseToken();
        address basePriceFeed = comet.baseTokenPriceFeed();
        uint256 basePrice = comet.getPrice(basePriceFeed);
        uint256 baseScale = 10 ** IERC20Metadata(baseToken).decimals();

        uint256 borrowValue = (borrowBalance * basePrice) / baseScale;

        return (borrowValue * 10_000) / totalCollateralValue;
    }

    /// @notice Returns the liquidation LTV threshold in basis points
    /// @dev Position is liquidatable when currentLTV >= liquidationLtvBps
    /// @param token The base token (market identifier)
    /// @param user The user address
    /// @return Weighted liquidation threshold in basis points
    function getLiquidationLtvBps(address token, address user) external view returns (uint256) {
        ICompoundComet comet = getComet(token);
        (uint256 totalCollValue, uint256 totalLiqAdjValue) = _collateralTotalsWithFactor(comet, user, true);

        if (totalCollValue == 0) return 0;

        // Liquidation threshold = liquidateCollateralFactor weighted by collateral
        return (totalLiqAdjValue * 10_000) / totalCollValue;
    }

    function getPossibleCollaterals(address token, address user) external view returns (
        address[] memory collateralAddresses,
        uint256[] memory balances,
        string[] memory symbols,
        uint8[] memory decimals
    ) {
        (address[] memory collaterals, uint128[] memory rawBalances, string[] memory displayNames) = getDepositedCollaterals(token, user);

        // Count non-zero balances
        uint256 nonZeroCount = 0;
        for (uint256 i = 0; i < rawBalances.length; i++) {
            if (rawBalances[i] > 0) {
                nonZeroCount++;
            }
        }

        // Create new arrays with only non-zero balances
        collateralAddresses = new address[](nonZeroCount);
        balances = new uint256[](nonZeroCount);
        symbols = new string[](nonZeroCount);
        decimals = new uint8[](nonZeroCount);

        // Fill arrays with non-zero balance entries
        uint256 j = 0;
        for (uint256 i = 0; i < rawBalances.length; i++) {
            if (rawBalances[i] > 0) {
                collateralAddresses[j] = collaterals[i];
                balances[j] = uint256(rawBalances[i]);
                symbols[j] = displayNames[i];
                decimals[j] = IERC20Metadata(collaterals[i]).decimals();
                j++;
            }
        }
    }

    function getDepositedCollaterals(address token, address account)
        public
        view
        returns (address[] memory collaterals, uint128[] memory balances, string[] memory displayNames)
    {
        ICompoundComet comet = getComet(token);
        uint8 n = comet.numAssets();        
        // Allocate arrays of the correct size.
        collaterals = new address[](n);
        displayNames = new string[](n);
        balances = new uint128[](n);

        uint256 index = 0;
        // Populate the arrays with assets that have a nonzero balance.
        for (uint8 i = 0; i < n; i++) {
            ICompoundComet.AssetInfo memory info = comet.getAssetInfo(i);
            (uint128 bal, ) = comet.userCollateral(account, info.asset);
            collaterals[index] = info.asset;
            displayNames[index] = ERC20(info.asset).symbol();
            balances[index] = bal;
            index++;
        }
    }

    function getPrice(address token) public view returns (uint256) {
        if (address(overrideFeeds[token]) != address(0))   {
            (, int256 price,,,) = overrideFeeds[token].latestRoundData();
            return uint256(price);
        }

        ICompoundComet comet = getComet(token);
        address theirFeed = comet.baseTokenPriceFeed();
        if (theirFeed != address(0)) {
            return comet.getPrice(theirFeed);
        }

        return 0;
    }

    function getCollateralPrice(address market, address asset) public view returns (uint256) {
        ICompoundComet comet = getComet(market);
        ICompoundComet.AssetInfo memory info = comet.getAssetInfoByAddress(asset);
        return comet.getPrice(info.priceFeed);
    }

    function getPrices(address market, address[] calldata tokens) public view returns (uint256[] memory) {
        uint256[] memory prices = new uint256[](tokens.length);

        for (uint i = 0; i < tokens.length; i++) {
            prices[i] = getCollateralPrice(market, tokens[i]);
        }

        return prices;
    }

    function getCompoundData(address token, address account)
        external
        view
        returns (
            uint256 supplyRate,
            uint256 borrowRate,
            uint256 balance,
            uint256 borrowBalance,
            uint256 price,
            uint256 priceScale
        )
    {
        ICompoundComet comet = getComet(token);
        supplyRate = comet.getSupplyRate(comet.getUtilization());
        borrowRate = comet.getBorrowRate(comet.getUtilization());
        balance = comet.balanceOf(account);
        borrowBalance = comet.borrowBalanceOf(account);
        price = getPrice(token);
        priceScale = comet.priceScale();
    }

    function isCollateralSupported(address market, address collateral) external view returns (bool isSupported) {
        ICompoundComet comet;
        try this.getComet(market) returns (ICompoundComet c) {
            comet = c;
        } catch {
            return false;
        }

        // Iterate through all assets to find if the collateral is supported
        uint8 numAssets = comet.numAssets();
        for (uint8 i = 0; i < numAssets; i++) {
            ICompoundComet.AssetInfo memory info = comet.getAssetInfo(i);
            if (info.asset == collateral) {
                return true;
            }
        }

        return false;
    }

    function getSupportedCollaterals(address market) external view returns (address[] memory collateralAddresses) {
        ICompoundComet comet;
        try this.getComet(market) returns (ICompoundComet c) {
            comet = c;
        } catch {
            return new address[](0);
        }

        uint8 numAssets = comet.numAssets();
        collateralAddresses = new address[](numAssets);

        for (uint8 i = 0; i < numAssets; i++) {
            ICompoundComet.AssetInfo memory info = comet.getAssetInfo(i);
            collateralAddresses[i] = info.asset;
        }

        return collateralAddresses;
    }

    function getCollateralFactors(address market)
        external
        view
        returns (address[] memory assets, uint256[] memory ltvBps, uint256[] memory lltvBps)
    {
        ICompoundComet comet = getComet(market);
        uint8 numAssets = comet.numAssets();

        assets = new address[](numAssets);
        ltvBps = new uint256[](numAssets);
        lltvBps = new uint256[](numAssets);

        for (uint8 i = 0; i < numAssets; i++) {
            ICompoundComet.AssetInfo memory info = comet.getAssetInfo(i);
            assets[i] = info.asset;
            ltvBps[i] = (uint256(info.borrowCollateralFactor) * 10_000) / 1e18;
            lltvBps[i] = (uint256(info.liquidateCollateralFactor) * 10_000) / 1e18;
        }
    }

    /// @notice Reserve configuration for LTV calculations (matches Aave/Venus pattern)
    struct ReserveConfigData {
        address token;
        uint256 price;              // Price from Comet oracle (8 decimals typically)
        uint256 ltv;                // Borrow collateral factor in basis points (0-10000)
        uint256 liquidationThreshold; // Liquidate collateral factor in basis points
        uint8 decimals;
        uint64 scale;               // Compound's scale for this asset
    }

    /// @notice Get reserve configuration data for a Compound market (for frontend LTV calculations)
    /// @dev Returns price, LTV (borrow collateral factor), liquidation threshold for each collateral
    /// @param market The base token address (e.g., USDC) to identify the Comet
    /// @return configs Array of reserve configuration data for all collaterals in this market
    function getReserveConfigs(address market) external view returns (ReserveConfigData[] memory configs) {
        ICompoundComet comet = getComet(market);
        uint8 numAssets = comet.numAssets();

        configs = new ReserveConfigData[](numAssets);

        for (uint8 i = 0; i < numAssets; i++) {
            ICompoundComet.AssetInfo memory info = comet.getAssetInfo(i);

            // Get price from Comet's oracle
            uint256 price = comet.getPrice(info.priceFeed);

            // Get decimals
            uint8 dec = 18;
            try ERC20(info.asset).decimals() returns (uint8 d) {
                dec = d;
            } catch {}

            configs[i] = ReserveConfigData({
                token: info.asset,
                price: price,
                ltv: (uint256(info.borrowCollateralFactor) * 10_000) / 1e18,
                liquidationThreshold: (uint256(info.liquidateCollateralFactor) * 10_000) / 1e18,
                decimals: dec,
                scale: info.scale
            });
        }
    }

    /// @notice Get base token info for a market (debt token)
    /// @param market The base token address
    /// @return baseToken The base token address
    /// @return price The base token price
    /// @return decimals The base token decimals
    /// @return priceScale The Comet's price scale
    function getBaseTokenInfo(address market) external view returns (
        address baseToken,
        uint256 price,
        uint8 decimals,
        uint256 priceScale
    ) {
        ICompoundComet comet = getComet(market);
        baseToken = comet.baseToken();
        price = getPrice(market);
        priceScale = comet.priceScale();

        try ERC20(baseToken).decimals() returns (uint8 d) {
            decimals = d;
        } catch {
            decimals = 18;
        }
    }

    function _collateralTotalsWithFactor(ICompoundComet comet, address account, bool useLiquidationFactor)
        internal
        view
        returns (uint256 totalCollateralValue, uint256 totalAdjusted)
    {
        uint8 numAssets = comet.numAssets();
        for (uint8 i = 0; i < numAssets; i++) {
            ICompoundComet.AssetInfo memory info = comet.getAssetInfo(i);
            (uint128 colBalance,) = comet.userCollateral(account, info.asset);

            if (colBalance == 0) continue;

            uint256 price = comet.getPrice(info.priceFeed);
            uint256 collateralValue = (uint256(colBalance) * price) / uint256(info.scale);

            totalCollateralValue += collateralValue;
            uint256 factor = useLiquidationFactor
                ? uint256(info.liquidateCollateralFactor)
                : uint256(info.borrowCollateralFactor);
            totalAdjusted += (collateralValue * factor) / 1e18;
        }
    }
}

contracts/v2/gateways/euler/EulerGateway.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { IEulerVault } from "../../interfaces/euler/IEulerVault.sol";
import { IEVC } from "../../interfaces/euler/IEVC.sol";
import { IERC20Metadata } from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

/**
 * @title EulerGatewayView
 * @notice Read-only gateway for Euler V2 lending protocol
 * @dev Provides LTV queries and position data for ADL and frontend
 *
 * Sub-Account Model:
 *   - Each user has 256 possible sub-accounts (index 0-255)
 *   - Sub-account address = (userAddress & 0xFF...FF00) | subAccountIndex
 *   - Each sub-account can have at most 1 controller (debt) + N collaterals
 */
contract EulerGatewayView {
    /// @notice The Ethereum Vault Connector (EVC) singleton
    IEVC public immutable evc;

    error ZeroAddress();

    constructor(address evc_) {
        if (evc_ == address(0)) revert ZeroAddress();
        evc = IEVC(evc_);
    }

    // ============ Sub-Account Helpers ============

    /// @notice Get sub-account address from user and index
    /// @param user The user's main address
    /// @param subAccountIndex The sub-account index (0-255)
    /// @return The sub-account address
    function getSubAccount(address user, uint8 subAccountIndex) public pure returns (address) {
        return address(uint160(uint160(user) ^ subAccountIndex));
    }

    /// @notice Get the user's "main" sub-account (index matches last byte of address)
    /// @param user The user's main address
    /// @return The main sub-account address and its index
    function getMainSubAccount(address user) public pure returns (address, uint8) {
        uint8 mainIndex = uint8(uint160(user) & 0xFF);
        return (getSubAccount(user, mainIndex), mainIndex);
    }

    // ============ LTV Queries (for ADL) ============

    /// @notice Returns the current LTV (debt/collateral) in basis points
    /// @dev Uses Euler's native accountLiquidity function
    /// @param borrowVault The vault where the user has debt
    /// @param user The user address
    /// @param subAccountIndex The sub-account index
    /// @return Current LTV in basis points (e.g., 6500 = 65%)
    function getCurrentLtvBps(
        address borrowVault,
        address user,
        uint8 subAccountIndex
    ) external view returns (uint256) {
        address subAccount = getSubAccount(user, subAccountIndex);

        // Get account liquidity using liquidation=false (borrow LTV)
        // collateralValue is already LTV-adjusted, so we need raw values
        // Use liquidation=true to get values closer to raw (higher LTV means less adjustment)
        (uint256 collateralValueLiq, uint256 liabilityValue) = IEulerVault(borrowVault).accountLiquidity(subAccount, true);

        if (collateralValueLiq == 0 || liabilityValue == 0) return 0;

        // accountLiquidity returns collateral adjusted by LLTV, so:
        // rawCollateral  collateralValueLiq / LLTV
        // currentLTV = liability / rawCollateral = liability * LLTV / collateralValueLiq

        // Get the effective LLTV (minimum across all collaterals)
        address[] memory collaterals = IEulerVault(borrowVault).LTVList();
        uint256 minLltv = 10000; // Default to 100% if no collaterals
        for (uint256 i = 0; i < collaterals.length; i++) {
            uint16 lltv = IEulerVault(borrowVault).LTVLiquidation(collaterals[i]);
            if (lltv > 0 && lltv < minLltv) {
                minLltv = lltv;
            }
        }

        // currentLTV (bps) = (liability * LLTV) / collateralValueLiq
        // Since LLTV is already in 1e4 scale (bps), and we want result in bps:
        // currentLTV = (liability * 10000) / collateralValueLiq * (10000 / LLTV)
        // Simplified: currentLTV = (liability * 10000 * 10000) / (collateralValueLiq * LLTV)

        if (minLltv == 0) return 0;
        return (liabilityValue * 10000 * 10000) / (collateralValueLiq * minLltv);
    }

    /// @notice Returns the current LTV using raw collateral/liability values
    /// @dev Alternative calculation that doesn't rely on LLTV adjustment
    /// @param borrowVault The vault where the user has debt
    /// @param user The user address
    /// @param subAccountIndex The sub-account index
    /// @return Current LTV in basis points
    function getCurrentLtvBpsSimple(
        address borrowVault,
        address user,
        uint8 subAccountIndex
    ) external view returns (uint256) {
        address subAccount = getSubAccount(user, subAccountIndex);

        // Get detailed liquidity to sum raw collateral values
        (
            address[] memory collaterals,
            uint256[] memory collateralValues,
            uint256 liabilityValue
        ) = IEulerVault(borrowVault).accountLiquidityFull(subAccount, true);

        if (liabilityValue == 0) return 0;

        // Sum raw collateral values (un-adjust by dividing by LLTV)
        uint256 rawCollateralValue = 0;
        for (uint256 i = 0; i < collaterals.length; i++) {
            if (collateralValues[i] > 0) {
                uint16 lltv = IEulerVault(borrowVault).LTVLiquidation(collaterals[i]);
                if (lltv > 0) {
                    // rawValue = adjustedValue * 10000 / LLTV
                    rawCollateralValue += (collateralValues[i] * 10000) / lltv;
                }
            }
        }

        if (rawCollateralValue == 0) return 0;

        // currentLTV = liability / rawCollateral * 10000
        return (liabilityValue * 10000) / rawCollateralValue;
    }

    /// @notice Returns the liquidation LTV threshold in basis points
    /// @dev Returns the minimum LLTV across all enabled collaterals
    /// @param borrowVault The vault where the user has debt
    /// @return Minimum liquidation threshold in basis points
    function getLiquidationLtvBps(address borrowVault) external view returns (uint256) {
        address[] memory collaterals = IEulerVault(borrowVault).LTVList();
        if (collaterals.length == 0) return 0;

        uint256 minLltv = type(uint256).max;
        for (uint256 i = 0; i < collaterals.length; i++) {
            uint16 lltv = IEulerVault(borrowVault).LTVLiquidation(collaterals[i]);
            if (lltv > 0 && lltv < minLltv) {
                minLltv = lltv;
            }
        }

        return minLltv == type(uint256).max ? 0 : minLltv;
    }

    /// @notice Returns the borrow LTV limit in basis points
    /// @dev Returns the minimum borrow LTV across all enabled collaterals
    /// @param borrowVault The vault where the user has debt
    /// @return Minimum borrow LTV in basis points
    function getBorrowLtvBps(address borrowVault) external view returns (uint256) {
        address[] memory collaterals = IEulerVault(borrowVault).LTVList();
        if (collaterals.length == 0) return 0;

        uint256 minLtv = type(uint256).max;
        for (uint256 i = 0; i < collaterals.length; i++) {
            uint16 ltv = IEulerVault(borrowVault).LTVBorrow(collaterals[i]);
            if (ltv > 0 && ltv < minLtv) {
                minLtv = ltv;
            }
        }

        return minLtv == type(uint256).max ? 0 : minLtv;
    }

    // ============ Position Queries ============

    /// @notice Check if a position is healthy (not liquidatable)
    /// @param borrowVault The vault where the user has debt
    /// @param user The user address
    /// @param subAccountIndex The sub-account index
    /// @return True if position is healthy
    function isHealthy(
        address borrowVault,
        address user,
        uint8 subAccountIndex
    ) external view returns (bool) {
        address subAccount = getSubAccount(user, subAccountIndex);
        (uint256 collateralValue, uint256 liabilityValue) = IEulerVault(borrowVault).accountLiquidity(subAccount, true);

        // Position is healthy if collateralValue >= liabilityValue (using liquidation LTV)
        return collateralValue >= liabilityValue;
    }

    /// @notice Get health factor (collateral / liability ratio)
    /// @param borrowVault The vault where the user has debt
    /// @param user The user address
    /// @param subAccountIndex The sub-account index
    /// @return Health factor in 1e18 scale (1e18 = 1.0)
    function getHealthFactor(
        address borrowVault,
        address user,
        uint8 subAccountIndex
    ) external view returns (uint256) {
        address subAccount = getSubAccount(user, subAccountIndex);
        (uint256 collateralValue, uint256 liabilityValue) = IEulerVault(borrowVault).accountLiquidity(subAccount, true);

        if (liabilityValue == 0) return type(uint256).max;
        return (collateralValue * 1e18) / liabilityValue;
    }

    /// @notice Get debt balance for a sub-account
    /// @param borrowVault The vault where the user has debt
    /// @param user The user address
    /// @param subAccountIndex The sub-account index
    /// @return Debt in underlying asset units
    function getDebtBalance(
        address borrowVault,
        address user,
        uint8 subAccountIndex
    ) external view returns (uint256) {
        address subAccount = getSubAccount(user, subAccountIndex);
        return IEulerVault(borrowVault).debtOf(subAccount);
    }

    /// @notice Get collateral balance for a sub-account in a specific vault
    /// @param collateralVault The vault where collateral is deposited
    /// @param user The user address
    /// @param subAccountIndex The sub-account index
    /// @return Collateral in underlying asset units
    function getCollateralBalance(
        address collateralVault,
        address user,
        uint8 subAccountIndex
    ) external view returns (uint256) {
        address subAccount = getSubAccount(user, subAccountIndex);
        return IEulerVault(collateralVault).maxWithdraw(subAccount);
    }

    // ============ Vault Info ============

    /// @notice Get vault's underlying asset
    /// @param vault The vault address
    /// @return The underlying asset address
    function getVaultAsset(address vault) external view returns (address) {
        return IEulerVault(vault).asset();
    }

    /// @notice Get vault's name
    /// @param vault The vault address
    /// @return The vault name
    function getVaultName(address vault) external view returns (string memory) {
        return IEulerVault(vault).name();
    }

    /// @notice Get vault's symbol
    /// @param vault The vault address
    /// @return The vault symbol
    function getVaultSymbol(address vault) external view returns (string memory) {
        return IEulerVault(vault).symbol();
    }

    /// @notice Get list of accepted collaterals for a borrow vault
    /// @param borrowVault The borrow vault address
    /// @return Array of collateral vault addresses with configured LTVs
    function getAcceptedCollaterals(address borrowVault) external view returns (address[] memory) {
        return IEulerVault(borrowVault).LTVList();
    }

    /// @notice Get LTV configuration for a specific collateral
    /// @param borrowVault The borrow vault address
    /// @param collateralVault The collateral vault address
    /// @return borrowLTV The borrow LTV in basis points
    /// @return liquidationLTV The liquidation LTV in basis points
    function getCollateralLtv(
        address borrowVault,
        address collateralVault
    ) external view returns (uint256 borrowLTV, uint256 liquidationLTV) {
        borrowLTV = IEulerVault(borrowVault).LTVBorrow(collateralVault);
        liquidationLTV = IEulerVault(borrowVault).LTVLiquidation(collateralVault);
    }
}

contracts/v2/gateways/morpho/MorphoBlueG.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { IMorphoBlue, MarketParams, Market, Position, MorphoLib } from "../../interfaces/morpho/IMorphoBlue.sol";
import { IERC20Metadata } from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";

/// @notice Interface for Morpho Blue oracles
/// @dev Returns price as collateralToken/loanToken with 36 decimals of precision
interface IMorphoOracle {
    function price() external view returns (uint256);
}

/// @notice Interface for Morpho Blue Interest Rate Models
interface IMorphoIrm {
    /// @notice Returns the borrow rate per second (scaled to 1e18)
    function borrowRateView(MarketParams memory marketParams, Market memory market) external view returns (uint256);
}

/**
 * @title MorphoBlueGatewayView
 * @notice View-only gateway for querying Morpho Blue positions and market data
 * @dev Provides utility functions for reading user balances, market state, and risk parameters
 *      Designed for frontend parity with Aave/Compound view gateways
 */
contract MorphoBlueGatewayView is Ownable {
    using MorphoLib for MarketParams;

    /// @notice The Morpho Blue singleton contract
    IMorphoBlue public immutable morpho;

    /// @notice Registry of known markets for enumeration
    bytes32[] private _registeredMarketIds;
    mapping(bytes32 => MarketParams) private _marketParams;
    mapping(bytes32 => bool) private _isRegistered;

    /// @notice Struct for returning comprehensive market info (similar to Aave's TokenInfo)
    struct MarketInfo {
        bytes32 marketId;
        address loanToken;
        address collateralToken;
        string loanSymbol;
        string collateralSymbol;
        uint8 loanDecimals;
        uint8 collateralDecimals;
        uint256 lltv;                  // Max LTV in 18 decimals (e.g., 0.86e18 = 86%)
        uint256 totalSupplyAssets;     // Total supplied to market
        uint256 totalBorrowAssets;     // Total borrowed from market
        uint256 utilizationRate;       // Utilization in 18 decimals
        uint256 borrowRate;            // Borrow APR in 18 decimals (per second * seconds per year)
        uint256 supplyRate;            // Supply APR in 18 decimals
        uint256 oraclePrice;           // Oracle price (collateral/loan, 36 decimals)
    }

    /// @notice Struct for user position in a market
    struct UserPosition {
        bytes32 marketId;
        address loanToken;
        address collateralToken;
        uint256 collateralBalance;     // Collateral in underlying units
        uint256 borrowBalance;         // Borrow debt in underlying units
        uint256 supplyBalance;         // Supply balance (lender side) in underlying units
        uint256 collateralValueUsd;    // Collateral value (scaled to loan token decimals for comparison)
        uint256 borrowValueUsd;        // Borrow value (in loan token units)
        uint256 currentLtv;            // Current LTV in 18 decimals
        uint256 maxLtv;                // Max LTV (LLTV) in 18 decimals
        uint256 healthFactor;          // Health factor in 18 decimals (1e18 = 1.0)
        bool isHealthy;                // Whether position is above liquidation threshold
    }

    /// @notice Reserve config data (similar to Aave's ReserveConfigData)
    struct MarketConfigData {
        bytes32 marketId;
        address loanToken;
        address collateralToken;
        uint256 lltv;                  // Max LTV in 18 decimals
        uint256 oraclePrice;           // Oracle price
        uint8 loanDecimals;
        uint8 collateralDecimals;
        bool isActive;                 // Whether market has any supply
    }

    event MarketRegistered(bytes32 indexed marketId, address loanToken, address collateralToken);
    event MarketRemoved(bytes32 indexed marketId);

    constructor(address morpho_, address owner_) Ownable(owner_) {
        require(morpho_ != address(0), "MorphoBlue: zero address");
        morpho = IMorphoBlue(morpho_);
    }

    // ============ Market Registry ============

    /// @notice Register a market for enumeration
    function registerMarket(MarketParams calldata params) external onlyOwner {
        bytes32 marketId = params.id();
        require(!_isRegistered[marketId], "MorphoBlue: already registered");

        _registeredMarketIds.push(marketId);
        _marketParams[marketId] = params;
        _isRegistered[marketId] = true;

        emit MarketRegistered(marketId, params.loanToken, params.collateralToken);
    }

    /// @notice Register multiple markets at once
    function registerMarkets(MarketParams[] calldata paramsList) external onlyOwner {
        for (uint256 i = 0; i < paramsList.length; i++) {
            bytes32 marketId = paramsList[i].id();
            if (!_isRegistered[marketId]) {
                _registeredMarketIds.push(marketId);
                _marketParams[marketId] = paramsList[i];
                _isRegistered[marketId] = true;
                emit MarketRegistered(marketId, paramsList[i].loanToken, paramsList[i].collateralToken);
            }
        }
    }

    /// @notice Get all registered market IDs
    function getRegisteredMarketIds() external view returns (bytes32[] memory) {
        return _registeredMarketIds;
    }

    /// @notice Get market params by ID
    function getMarketParams(bytes32 marketId) external view returns (MarketParams memory) {
        require(_isRegistered[marketId], "MorphoBlue: not registered");
        return _marketParams[marketId];
    }

    /// @notice Check if a market is registered
    function isMarketRegistered(bytes32 marketId) external view returns (bool) {
        return _isRegistered[marketId];
    }

    // ============ Single Market Queries ============

    /// @notice Get user's collateral balance in a market
    function getCollateralBalance(MarketParams calldata params, address user) external view returns (uint256) {
        bytes32 marketId = params.id();
        Position memory pos = morpho.position(marketId, user);
        return pos.collateral;
    }

    /// @notice Get user's borrow balance in a market (converts shares to assets)
    function getBorrowBalance(MarketParams calldata params, address user) external view returns (uint256) {
        bytes32 marketId = params.id();
        Position memory pos = morpho.position(marketId, user);

        if (pos.borrowShares == 0) return 0;

        Market memory mkt = morpho.market(marketId);
        if (mkt.totalBorrowShares == 0) return 0;

        return (uint256(pos.borrowShares) * uint256(mkt.totalBorrowAssets)) / uint256(mkt.totalBorrowShares);
    }

    /// @notice Get user's supply balance in a market (lender side, converts shares to assets)
    function getSupplyBalance(MarketParams calldata params, address user) external view returns (uint256) {
        bytes32 marketId = params.id();
        Position memory pos = morpho.position(marketId, user);

        if (pos.supplyShares == 0) return 0;

        Market memory mkt = morpho.market(marketId);
        if (mkt.totalSupplyShares == 0) return 0;

        return (pos.supplyShares * uint256(mkt.totalSupplyAssets)) / uint256(mkt.totalSupplyShares);
    }

    /// @notice Get market state
    function getMarketState(
        MarketParams calldata params
    ) external view returns (uint256 totalSupplyAssets, uint256 totalBorrowAssets, uint256 utilizationRate) {
        bytes32 marketId = params.id();
        Market memory mkt = morpho.market(marketId);

        totalSupplyAssets = mkt.totalSupplyAssets;
        totalBorrowAssets = mkt.totalBorrowAssets;

        if (totalSupplyAssets > 0) {
            utilizationRate = (totalBorrowAssets * 1e18) / totalSupplyAssets;
        }
    }

    /// @notice Get oracle price for a market
    /// @dev Returns price as collateral/loan with 36 decimals
    function getOraclePrice(MarketParams calldata params) external view returns (uint256) {
        if (params.oracle == address(0)) return 0;
        try IMorphoOracle(params.oracle).price() returns (uint256 p) {
            return p;
        } catch {
            return 0;
        }
    }

    /// @notice Get borrow rate for a market (per second, scaled to 1e18)
    function getBorrowRate(MarketParams calldata params) external view returns (uint256) {
        if (params.irm == address(0)) return 0;

        bytes32 marketId = params.id();
        Market memory mkt = morpho.market(marketId);

        try IMorphoIrm(params.irm).borrowRateView(params, mkt) returns (uint256 rate) {
            return rate;
        } catch {
            return 0;
        }
    }

    /// @notice Get borrow APR for a market (annualized, scaled to 1e18)
    function getBorrowApr(MarketParams calldata params) external view returns (uint256) {
        uint256 ratePerSecond = this.getBorrowRate(params);
        // APR = rate per second * seconds per year
        return ratePerSecond * 365 days;
    }

    /// @notice Get supply APR for a market (annualized, scaled to 1e18)
    /// @dev Supply APR = Borrow APR * Utilization * (1 - fee)
    function getSupplyApr(MarketParams calldata params) external view returns (uint256) {
        bytes32 marketId = params.id();
        Market memory mkt = morpho.market(marketId);

        if (mkt.totalSupplyAssets == 0) return 0;

        uint256 borrowApr = this.getBorrowApr(params);
        uint256 utilization = (uint256(mkt.totalBorrowAssets) * 1e18) / uint256(mkt.totalSupplyAssets);
        uint256 feeRate = mkt.fee; // Fee is in 18 decimals

        // Supply APR = Borrow APR * Utilization * (1 - fee)
        return (borrowApr * utilization * (1e18 - feeRate)) / 1e18 / 1e18;
    }

    // ============ User Position Queries ============

    /// @notice Get user's full position in a market with risk metrics
    function getPosition(
        MarketParams calldata params,
        address user
    ) external view returns (UserPosition memory pos) {
        bytes32 marketId = params.id();
        Position memory rawPos = morpho.position(marketId, user);
        Market memory mkt = morpho.market(marketId);

        pos.marketId = marketId;
        pos.loanToken = params.loanToken;
        pos.collateralToken = params.collateralToken;
        pos.collateralBalance = rawPos.collateral;
        pos.maxLtv = params.lltv;

        // Convert shares to assets
        if (rawPos.borrowShares > 0 && mkt.totalBorrowShares > 0) {
            pos.borrowBalance = (uint256(rawPos.borrowShares) * uint256(mkt.totalBorrowAssets)) / uint256(mkt.totalBorrowShares);
        }

        if (rawPos.supplyShares > 0 && mkt.totalSupplyShares > 0) {
            pos.supplyBalance = (rawPos.supplyShares * uint256(mkt.totalSupplyAssets)) / uint256(mkt.totalSupplyShares);
        }

        // Calculate LTV and health factor using oracle
        if (params.oracle != address(0) && pos.collateralBalance > 0) {
            try IMorphoOracle(params.oracle).price() returns (uint256 oraclePrice) {
                // Oracle price is collateral/loan with 36 decimals
                // collateralValue (in loan token) = collateral * oraclePrice / 1e36
                uint8 collateralDecimals = _getDecimals(params.collateralToken);
                uint8 loanDecimals = _getDecimals(params.loanToken);

                // Normalize to loan token decimals
                // collateralValue = collateral * price / 10^36 * 10^loanDecimals / 10^collateralDecimals
                uint256 collateralValue = (pos.collateralBalance * oraclePrice) / 1e36;
                if (collateralDecimals > loanDecimals) {
                    collateralValue = collateralValue / (10 ** (collateralDecimals - loanDecimals));
                } else if (loanDecimals > collateralDecimals) {
                    collateralValue = collateralValue * (10 ** (loanDecimals - collateralDecimals));
                }

                pos.collateralValueUsd = collateralValue;
                pos.borrowValueUsd = pos.borrowBalance;

                if (collateralValue > 0) {
                    // Current LTV = borrow / collateralValue
                    pos.currentLtv = (pos.borrowBalance * 1e18) / collateralValue;

                    // Health Factor = (collateralValue * LLTV) / borrow
                    if (pos.borrowBalance > 0) {
                        pos.healthFactor = (collateralValue * params.lltv) / pos.borrowBalance;
                        pos.isHealthy = pos.healthFactor >= 1e18;
                    } else {
                        pos.healthFactor = type(uint256).max;
                        pos.isHealthy = true;
                    }
                }
            } catch {
                // Oracle failed, leave values as 0
            }
        } else if (pos.borrowBalance == 0) {
            pos.healthFactor = type(uint256).max;
            pos.isHealthy = true;
        }
    }

    /// @notice Get positions for a user across all registered markets
    function getAllPositions(address user) external view returns (UserPosition[] memory positions) {
        uint256 count = 0;

        // First pass: count non-empty positions
        for (uint256 i = 0; i < _registeredMarketIds.length; i++) {
            bytes32 marketId = _registeredMarketIds[i];
            Position memory pos = morpho.position(marketId, user);
            if (pos.collateral > 0 || pos.borrowShares > 0 || pos.supplyShares > 0) {
                count++;
            }
        }

        positions = new UserPosition[](count);
        uint256 idx = 0;

        // Second pass: populate positions
        for (uint256 i = 0; i < _registeredMarketIds.length; i++) {
            bytes32 marketId = _registeredMarketIds[i];
            Position memory rawPos = morpho.position(marketId, user);

            if (rawPos.collateral > 0 || rawPos.borrowShares > 0 || rawPos.supplyShares > 0) {
                MarketParams memory params = _marketParams[marketId];
                positions[idx] = this.getPosition(params, user);
                idx++;
            }
        }
    }

    /// @notice Get positions for specific markets
    function getPositionsForMarkets(
        MarketParams[] calldata paramsList,
        address user
    ) external view returns (UserPosition[] memory positions) {
        positions = new UserPosition[](paramsList.length);
        for (uint256 i = 0; i < paramsList.length; i++) {
            positions[i] = this.getPosition(paramsList[i], user);
        }
    }

    // ============ Market Info Queries ============

    /// @notice Get comprehensive info for a single market
    function getMarketInfo(MarketParams calldata params) external view returns (MarketInfo memory info) {
        bytes32 marketId = params.id();
        Market memory mkt = morpho.market(marketId);

        info.marketId = marketId;
        info.loanToken = params.loanToken;
        info.collateralToken = params.collateralToken;
        info.loanDecimals = _getDecimals(params.loanToken);
        info.collateralDecimals = _getDecimals(params.collateralToken);
        info.loanSymbol = _getSymbol(params.loanToken);
        info.collateralSymbol = _getSymbol(params.collateralToken);
        info.lltv = params.lltv;
        info.totalSupplyAssets = mkt.totalSupplyAssets;
        info.totalBorrowAssets = mkt.totalBorrowAssets;

        if (mkt.totalSupplyAssets > 0) {
            info.utilizationRate = (uint256(mkt.totalBorrowAssets) * 1e18) / uint256(mkt.totalSupplyAssets);
        }

        // Get oracle price
        if (params.oracle != address(0)) {
            try IMorphoOracle(params.oracle).price() returns (uint256 p) {
                info.oraclePrice = p;
            } catch {}
        }

        // Get rates
        info.borrowRate = this.getBorrowApr(params);
        info.supplyRate = this.getSupplyApr(params);
    }

    /// @notice Get info for all registered markets
    function getAllMarketsInfo() external view returns (MarketInfo[] memory infos) {
        infos = new MarketInfo[](_registeredMarketIds.length);
        for (uint256 i = 0; i < _registeredMarketIds.length; i++) {
            MarketParams memory params = _marketParams[_registeredMarketIds[i]];
            infos[i] = this.getMarketInfo(params);
        }
    }

    /// @notice Get market config data (similar to Aave's getReserveConfigs)
    function getMarketConfigs(MarketParams[] calldata paramsList) external view returns (MarketConfigData[] memory configs) {
        configs = new MarketConfigData[](paramsList.length);

        for (uint256 i = 0; i < paramsList.length; i++) {
            MarketParams calldata params = paramsList[i];
            bytes32 marketId = params.id();
            Market memory mkt = morpho.market(marketId);

            configs[i].marketId = marketId;
            configs[i].loanToken = params.loanToken;
            configs[i].collateralToken = params.collateralToken;
            configs[i].lltv = params.lltv;
            configs[i].loanDecimals = _getDecimals(params.loanToken);
            configs[i].collateralDecimals = _getDecimals(params.collateralToken);
            configs[i].isActive = mkt.totalSupplyAssets > 0;

            if (params.oracle != address(0)) {
                try IMorphoOracle(params.oracle).price() returns (uint256 p) {
                    configs[i].oraclePrice = p;
                } catch {}
            }
        }
    }

    // ============ LTV Queries (for ADL) ============

    /// @notice Returns the current LTV (debt/collateral) in basis points
    /// @dev Uses the market's oracle to price collateral in loan token terms
    /// @param params The market parameters
    /// @param user The user address
    /// @return Current LTV in basis points (e.g., 6500 = 65%), or 0 if no collateral
    function getCurrentLtvBps(MarketParams calldata params, address user) external view returns (uint256) {
        bytes32 marketId = params.id();
        Position memory rawPos = morpho.position(marketId, user);
        Market memory mkt = morpho.market(marketId);

        if (rawPos.collateral == 0) return 0;

        // Convert borrow shares to assets
        uint256 borrowBalance = 0;
        if (rawPos.borrowShares > 0 && mkt.totalBorrowShares > 0) {
            borrowBalance = (uint256(rawPos.borrowShares) * uint256(mkt.totalBorrowAssets)) / uint256(mkt.totalBorrowShares);
        }
        if (borrowBalance == 0) return 0;

        // Get collateral value in loan token terms using oracle
        if (params.oracle == address(0)) return 0;

        try IMorphoOracle(params.oracle).price() returns (uint256 oraclePrice) {
            // Oracle price is collateral/loan with 36 decimals
            uint8 collateralDecimals = _getDecimals(params.collateralToken);
            uint8 loanDecimals = _getDecimals(params.loanToken);

            uint256 collateralValue = (rawPos.collateral * oraclePrice) / 1e36;
            if (collateralDecimals > loanDecimals) {
                collateralValue = collateralValue / (10 ** (collateralDecimals - loanDecimals));
            } else if (loanDecimals > collateralDecimals) {
                collateralValue = collateralValue * (10 ** (loanDecimals - collateralDecimals));
            }

            if (collateralValue == 0) return 0;

            // LTV in basis points = (borrow / collateralValue) * 10000
            return (borrowBalance * 10000) / collateralValue;
        } catch {
            return 0;
        }
    }

    /// @notice Returns the liquidation LTV threshold in basis points
    /// @dev This is the LLTV from market params, converted from WAD to bps
    /// @param params The market parameters
    /// @return Liquidation threshold in basis points
    function getLiquidationLtvBps(MarketParams calldata params) external pure returns (uint256) {
        // params.lltv is in WAD (1e18), convert to basis points
        return params.lltv / 1e14;
    }

    // ============ Authorization ============

    /// @notice Check if a user is authorized to act on behalf of another
    function isAuthorized(address authorizer, address authorized) external view returns (bool) {
        return morpho.isAuthorized(authorizer, authorized);
    }

    // ============ Utility ============

    /// @notice Compute market ID from params
    function computeMarketId(MarketParams calldata params) external pure returns (bytes32) {
        return params.id();
    }

    /// @notice Get number of registered markets
    function registeredMarketCount() external view returns (uint256) {
        return _registeredMarketIds.length;
    }

    // ============ Internal Helpers ============

    function _getDecimals(address token) internal view returns (uint8) {
        try IERC20Metadata(token).decimals() returns (uint8 d) {
            return d;
        } catch {
            return 18;
        }
    }

    function _getSymbol(address token) internal view returns (string memory) {
        try IERC20Metadata(token).symbol() returns (string memory s) {
            return s;
        } catch {
            return "";
        }
    }
}

contracts/v2/gateways/venus/VenusGateway.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {ComptrollerInterface} from "../../../interfaces/venus/ComptrollerInterface.sol";
import {VTokenInterface} from "../../../interfaces/venus/VTokenInterface.sol";
import {ResilientOracleInterface} from "../../../interfaces/venus/ResilientOracleInterface.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title VenusGatewayView
 * @notice View-only gateway for Venus protocol
 * @dev Contains all read/view functions from v1, separate from write operations
 */
contract VenusGatewayView is Ownable {
    ComptrollerInterface public comptroller;
    ResilientOracleInterface public oracle;

    constructor(address _comptroller, address _oracle, address owner_) Ownable(owner_) {
        comptroller = ComptrollerInterface(_comptroller);
        oracle = ResilientOracleInterface(_oracle);
    }

    function setComptroller(address _comptroller) external onlyOwner {
        comptroller = ComptrollerInterface(_comptroller);
    }

    function setOracle(address _oracle) external onlyOwner {
        oracle = ResilientOracleInterface(_oracle);
    }

    function getVTokenForUnderlying(address underlyingToken) public view returns (address) {
        address[] memory vTokenAddresses = this.getAllMarkets();

        for (uint i = 0; i < vTokenAddresses.length; i++) {
            address vTokenAddress = vTokenAddresses[i];

            try VTokenInterface(vTokenAddress).underlying() returns (address underlying) {
                if (underlying == underlyingToken) {
                    return vTokenAddress;
                }
            } catch {
                // Skip if there's an error (e.g., for vBNB which might not have an underlying() function)
                continue;
            }
        }

        revert("VenusGateway: vToken not found for underlying token");
    }

    function getAssetsIn(address account) external view returns (address[] memory) {
        VTokenInterface[] memory vTokens = comptroller.getAssetsIn(account);
        address[] memory vTokenAddresses = new address[](vTokens.length);

        for (uint i = 0; i < vTokens.length; i++) {
            vTokenAddresses[i] = address(vTokens[i]);
        }

        return vTokenAddresses;
    }

    function checkMembership(address account, address vToken) external view returns (bool) {
        return comptroller.checkMembership(account, vToken);
    }

    function getAllMarkets() external view returns (address[] memory) {
        VTokenInterface[] memory vTokens = comptroller.getAllMarkets();
        address[] memory vTokenAddresses = new address[](vTokens.length);

        for (uint i = 0; i < vTokens.length; i++) {
            vTokenAddresses[i] = address(vTokens[i]);
        }

        return vTokenAddresses;
    }

    function getAccountLiquidity(address account) public view returns (uint, uint, uint) {
        return comptroller.getAccountLiquidity(account);
    }

    function getBalance(address token, address user) external view returns (uint256) {
        address vTokenAddress = getVTokenForUnderlying(token);

        // Get vToken balance
        uint vTokenBalance = VTokenInterface(vTokenAddress).balanceOf(user);

        // Convert to underlying amount using exchange rate
        uint exchangeRate = VTokenInterface(vTokenAddress).exchangeRateStored();

        // Calculate underlying value
        return (vTokenBalance * exchangeRate) / 1e18;
    }

    function getBorrowBalance(address token, address user) external view returns (uint256) {
        address vTokenAddress = getVTokenForUnderlying(token);
        return VTokenInterface(vTokenAddress).borrowBalanceStored(user);
    }

    function getBorrowBalanceCurrent(address token, address user) external returns (uint256) {
        address vTokenAddress = getVTokenForUnderlying(token);
        return VTokenInterface(vTokenAddress).borrowBalanceCurrent(user);
    }

    function getBorrowRate(address token) external view returns (uint256, bool) {
        address vTokenAddress = getVTokenForUnderlying(token);
        uint borrowRate = VTokenInterface(vTokenAddress).borrowRatePerBlock();
        return (borrowRate, true);
    }

    function getSupplyRate(address token) external view returns (uint256, bool) {
        address vTokenAddress = getVTokenForUnderlying(token);
        uint supplyRate = VTokenInterface(vTokenAddress).supplyRatePerBlock();
        return (supplyRate, true);
    }

    function getAllVenusMarkets() external view returns (
        address[] memory vTokens,
        address[] memory tokens,
        string[] memory symbols,
        string[] memory names,
        uint8[] memory decimals,
        uint256[] memory prices
    ) {
        // Get all markets from the comptroller
        vTokens = this.getAllMarkets();
        uint256 marketsCount = vTokens.length;

        // Initialize arrays
        tokens = new address[](marketsCount);
        symbols = new string[](marketsCount);
        names = new string[](marketsCount);
        decimals = new uint8[](marketsCount);
        prices = new uint256[](marketsCount);

        // Populate arrays with basic token information
        for (uint i = 0; i < marketsCount; i++) {
            address vTokenAddress = vTokens[i];
            VTokenInterface vToken = VTokenInterface(vTokenAddress);

            // Get underlying token address directly
            try vToken.underlying() returns (address underlyingToken) {
                tokens[i] = underlyingToken;

                // Get token metadata
                IERC20Metadata underlying = IERC20Metadata(underlyingToken);

                // Try to get symbol from underlying, fallback to vToken
                try underlying.symbol() returns (string memory s) {
                    symbols[i] = s;
                } catch {
                    symbols[i] = vToken.symbol();
                }

                // Try to get name from underlying, fallback to vToken
                try underlying.name() returns (string memory n) {
                    names[i] = n;
                } catch {
                    names[i] = vToken.name();
                }

                // Try to get decimals from underlying, fallback to vToken
                try underlying.decimals() returns (uint8 d) {
                    decimals[i] = d;
                } catch {
                    decimals[i] = vToken.decimals();
                }
            } catch {
                // For tokens like vBNB that might not have an underlying() function
                tokens[i] = address(0);
                symbols[i] = vToken.symbol();
                names[i] = vToken.name();
                decimals[i] = vToken.decimals();
            }
            prices[i] = oracle.getUnderlyingPrice(vTokenAddress);
        }

        return (vTokens, tokens, symbols, names, decimals, prices);
    }

    function getMarketRates(address[] calldata vTokens) external view returns (
        uint256[] memory prices,
        uint256[] memory supplyRates,
        uint256[] memory borrowRates
    ) {
        uint256 marketsCount = vTokens.length;

        // Initialize arrays
        prices = new uint256[](marketsCount);
        supplyRates = new uint256[](marketsCount);
        borrowRates = new uint256[](marketsCount);

        // Populate arrays with rate information
        for (uint i = 0; i < marketsCount; i++) {
            address vTokenAddress = vTokens[i];
            VTokenInterface vToken = VTokenInterface(vTokenAddress);

            // Get rates
            supplyRates[i] = vToken.supplyRatePerBlock();
            borrowRates[i] = vToken.borrowRatePerBlock();
            prices[i] = oracle.getUnderlyingPrice(vTokenAddress);
        }

        return (prices, supplyRates, borrowRates);
    }

    function getUserBalances(address[] calldata vTokens, address account) external view returns (
        uint256[] memory balances,
        uint256[] memory borrowBalances
    ) {
        require(account != address(0), "VenusGateway: zero address not allowed");

        uint256 marketsCount = vTokens.length;

        // Initialize arrays
        balances = new uint256[](marketsCount);
        borrowBalances = new uint256[](marketsCount);

        // Populate arrays with user balance information
        for (uint i = 0; i < marketsCount; i++) {
            address vTokenAddress = vTokens[i];
            VTokenInterface vToken = VTokenInterface(vTokenAddress);

            // Get user balances
            try vToken.balanceOf(account) returns (uint256 b) {
                // Convert vToken balance to underlying using exchange rate
                uint256 exchangeRate = vToken.exchangeRateStored();
                balances[i] = (b * exchangeRate) / 1e18;
            } catch {
                balances[i] = 0;
            }

            try vToken.borrowBalanceStored(account) returns (uint256 bb) {
                borrowBalances[i] = bb;
            } catch {
                borrowBalances[i] = 0;
            }
        }

        return (balances, borrowBalances);
    }

    function getCollateralStatus(address[] calldata vTokens, address account) external view returns (
        bool[] memory isCollateral
    ) {
        require(account != address(0), "VenusGateway: zero address not allowed");

        uint256 marketsCount = vTokens.length;
        isCollateral = new bool[](marketsCount);

        // Get the list of markets the user has entered (used as collateral)
        address[] memory enteredMarkets = this.getAssetsIn(account);

        // Check each market if it's being used as collateral
        for (uint i = 0; i < marketsCount; i++) {
            address vTokenAddress = vTokens[i];

            // Linear search through entered markets
            for (uint j = 0; j < enteredMarkets.length; j++) {
                if (enteredMarkets[j] == vTokenAddress) {
                    isCollateral[i] = true;
                    break;
                }
            }
        }

        return isCollateral;
    }

    function getPossibleCollaterals(address token, address user) external view returns (
        address[] memory collateralAddresses,
        uint256[] memory balances,
        string[] memory symbols,
        uint8[] memory decimals
    ) {
        // Get all Venus markets information
        (
            address[] memory vTokens, 
            address[] memory tokens, 
            string[] memory tokenSymbols, 
            string[] memory names, 
            uint8[] memory tokenDecimals,
            uint256[] memory prices
        ) = this.getAllVenusMarkets();

        // Create arrays for return values
        collateralAddresses = new address[](tokens.length);
        symbols = new string[](tokens.length);
        decimals = new uint8[](tokens.length);
        balances = new uint256[](tokens.length); // Initialize with all zeros by default

        // If user address is provided and not zero, get their balances
        if (user != address(0)) {
            // Get user balances for all markets
            (uint256[] memory userBalances, ) = this.getUserBalances(vTokens, user);
            balances = userBalances;
        }

        // Copy token information to return arrays
        for (uint i = 0; i < tokens.length; i++) {
            collateralAddresses[i] = tokens[i];
            symbols[i] = tokenSymbols[i];
            decimals[i] = tokenDecimals[i];
        }

        return (collateralAddresses, balances, symbols, decimals);
    }

    function isCollateralSupported(address market, address collateral) external view returns (bool isSupported) {
        // Get all Venus markets information
        (
            ,  // vTokens (not needed)
            address[] memory tokens, 
            , // symbols (not needed)
            , // names (not needed)
            , // decimals (not needed)
              // prices (not needed)
        ) = this.getAllVenusMarkets();

        // First check that both market and collateral are valid tokens in Venus
        bool marketFound = false;
        bool collateralFound = false;

        for (uint i = 0; i < tokens.length; i++) {
            if (tokens[i] == market) {
                marketFound = true;
            }
            if (tokens[i] == collateral) {
                collateralFound = true;
            }

            // Early exit if both are found
            if (marketFound && collateralFound) {
                break;
            }
        }

        // In Venus, any valid token can be collateral for any other valid token
        return marketFound && collateralFound;
    }

    function getSupportedCollaterals(address market) external view returns (address[] memory collateralAddresses) {
        // Get all Venus markets information
        (
            ,  // vTokens (not needed)
            address[] memory tokens, 
            ,  // symbols (not needed)
            ,  // names (not needed)
            ,// decimals (not needed)
            // prices (not needed)
        ) = this.getAllVenusMarkets();

        // Check if market is a valid token in Venus
        bool marketFound = false;
        for (uint i = 0; i < tokens.length; i++) {
            if (tokens[i] == market) {
                marketFound = true;
                break;
            }
        }

        // If market is not found, return empty array
        if (!marketFound) {
            return new address[](0);
        }

        // In Venus, all tokens can be collateral for any other token
        // So we return all tokens except the market itself
        collateralAddresses = new address[](tokens.length - 1);

        uint index = 0;
        for (uint i = 0; i < tokens.length; i++) {
            // Skip the market token itself
            if (tokens[i] != market) {
                collateralAddresses[index] = tokens[i];
                index++;
            }
        }

        return collateralAddresses;
    }

    function _weightedCollateralFactorBps(address user) internal view returns (uint256) {
        // Ensure comptroller has code
        if (address(comptroller).code.length == 0) return 0;

        // Get assets directly from comptroller
        VTokenInterface[] memory vTokens;
        try comptroller.getAssetsIn(user) returns (VTokenInterface[] memory v) {
            vTokens = v;
        } catch {
            return 0;
        }

        if (vTokens.length == 0) return 0;

        uint256 totalCollateralValue;
        uint256 totalAllowedBorrow;

        for (uint i = 0; i < vTokens.length; i++) {
            address vTokenAddr = address(vTokens[i]);

            uint256 vBalance;
            try vTokens[i].balanceOf(user) returns (uint256 b) { vBalance = b; } catch { continue; }
            if (vBalance == 0) continue;

            uint256 exchangeRate;
            try vTokens[i].exchangeRateStored() returns (uint256 rate) { exchangeRate = rate; } catch { continue; }

            uint256 underlyingAmount = (vBalance * exchangeRate) / 1e18;

            uint256 price;
            try oracle.getUnderlyingPrice(vTokenAddr) returns (uint256 p) { price = p; } catch { continue; }

            uint256 collateralValue = (underlyingAmount * price) / 1e18;
            totalCollateralValue += collateralValue;

            // Venus v4: markets returns (isListed, collateralFactorMantissa, liquidationThresholdMantissa)
            try comptroller.markets(vTokenAddr) returns (
                bool /* isListed */,
                uint256 collateralFactorMantissa,
                uint256 /* liquidationThresholdMantissa */
            ) {
                totalAllowedBorrow += (collateralValue * collateralFactorMantissa) / 1e18;
            } catch {
                continue;
            }
        }

        if (totalCollateralValue == 0) return 0;

        return (totalAllowedBorrow * 10_000) / totalCollateralValue;
    }

    /// @notice Calculate weighted liquidation threshold (LLTV) across user's collateral positions
    /// @dev Uses Venus V4's liquidationThreshold which is higher than collateralFactor
    function _weightedLiquidationThresholdBps(address user) internal view returns (uint256) {
        // Ensure comptroller has code
        if (address(comptroller).code.length == 0) return 0;

        // Get assets directly from comptroller
        VTokenInterface[] memory vTokens;
        try comptroller.getAssetsIn(user) returns (VTokenInterface[] memory v) {
            vTokens = v;
        } catch {
            return 0;
        }

        if (vTokens.length == 0) return 0;

        uint256 totalCollateralValue;
        uint256 totalLiquidationThreshold;

        for (uint i = 0; i < vTokens.length; i++) {
            address vTokenAddr = address(vTokens[i]);

            uint256 vBalance;
            try vTokens[i].balanceOf(user) returns (uint256 b) { vBalance = b; } catch { continue; }
            if (vBalance == 0) continue;

            uint256 exchangeRate;
            try vTokens[i].exchangeRateStored() returns (uint256 rate) { exchangeRate = rate; } catch { continue; }

            uint256 underlyingAmount = (vBalance * exchangeRate) / 1e18;

            uint256 price;
            try oracle.getUnderlyingPrice(vTokenAddr) returns (uint256 p) { price = p; } catch { continue; }

            uint256 collateralValue = (underlyingAmount * price) / 1e18;
            totalCollateralValue += collateralValue;

            // Venus v4: markets returns (isListed, collateralFactorMantissa, liquidationThresholdMantissa)
            // Use liquidationThresholdMantissa when available, otherwise fall back to collateralFactorMantissa.
            try comptroller.markets(vTokenAddr) returns (
                bool /* isListed */,
                uint256 collateralFactorMantissa,
                uint256 liquidationThresholdMantissa
            ) {
                uint256 liqThreshold = liquidationThresholdMantissa == 0
                    ? collateralFactorMantissa
                    : liquidationThresholdMantissa;

                totalLiquidationThreshold += (collateralValue * liqThreshold) / 1e18;
            } catch {
                continue;
            }
        }

        if (totalCollateralValue == 0) return 0;

        return (totalLiquidationThreshold * 10_000) / totalCollateralValue;
    }

    /// @notice Returns the LTV (borrowing power) for a user in basis points
    /// @dev Uses collateralFactor which determines how much can be borrowed
    function getLtv(address /* token */, address user) external view returns (uint256 result) {
        if (user == address(0)) return 0;
        return _weightedCollateralFactorBps(user);
    }

    /// @notice Returns the LLTV (liquidation threshold) for a user in basis points
    /// @dev Uses liquidationThreshold which is higher than LTV - the point at which liquidation occurs
    function getMaxLtv(address /* token */, address user) external view returns (uint256 result) {
        if (user == address(0)) return 0;
        return _weightedLiquidationThresholdBps(user);
    }

    /// @notice Returns the current LTV (debt/collateral) in basis points
    /// @dev This is the actual current LTV = totalDebt / totalCollateral * 10000
    /// @param user The user address
    /// @return Current LTV in basis points (e.g., 6500 = 65%)
    function getCurrentLtvBps(address /* token */, address user) external view returns (uint256) {
        if (user == address(0)) return 0;

        VTokenInterface[] memory vTokens = comptroller.getAssetsIn(user);
        if (vTokens.length == 0) return 0;

        uint256 totalCollateralValue;
        uint256 totalDebtValue;

        for (uint i = 0; i < vTokens.length; i++) {
            address vTokenAddr = address(vTokens[i]);
            VTokenInterface vToken = VTokenInterface(vTokenAddr);

            // Get oracle price (18 decimals, price per underlying token)
            uint256 price;
            try oracle.getUnderlyingPrice(vTokenAddr) returns (uint256 p) { price = p; } catch { continue; }

            // Calculate collateral value
            uint256 vTokenBalance = vToken.balanceOf(user);
            if (vTokenBalance > 0) {
                uint256 exchangeRate = vToken.exchangeRateStored();
                uint256 underlyingAmount = (vTokenBalance * exchangeRate) / 1e18;
                totalCollateralValue += (underlyingAmount * price) / 1e18;
            }

            // Calculate debt value
            try vToken.borrowBalanceStored(user) returns (uint256 borrowBalance) {
                if (borrowBalance > 0) {
                    totalDebtValue += (borrowBalance * price) / 1e18;
                }
            } catch {}
        }

        if (totalCollateralValue == 0) return 0;
        return (totalDebtValue * 10_000) / totalCollateralValue;
    }

    /// @notice Returns the liquidation LTV threshold in basis points
    /// @dev Position is liquidatable when currentLTV >= liquidationLtvBps
    /// @param user The user address
    /// @return Weighted liquidation threshold in basis points
    function getLiquidationLtvBps(address /* token */, address user) external view returns (uint256) {
        if (user == address(0)) return 0;
        return _weightedLiquidationThresholdBps(user);
    }

    /// @notice Reserve configuration for LTV calculations (matches Aave/Compound pattern)
    struct ReserveConfigData {
        address token;
        address vToken;
        uint256 price;              // Price from oracle (18 decimals)
        uint256 ltv;                // Collateral factor in basis points (0-10000)
        uint256 liquidationThreshold; // Liquidation threshold in basis points
        uint8 decimals;
        bool isListed;
    }

    /// @notice Get reserve configuration data for all Venus markets (for frontend LTV calculations)
    /// @dev Returns price, LTV (collateral factor), liquidation threshold for each market
    /// @return configs Array of reserve configuration data
    function getReserveConfigs() external view returns (ReserveConfigData[] memory configs) {
        VTokenInterface[] memory vTokens = comptroller.getAllMarkets();
        configs = new ReserveConfigData[](vTokens.length);

        for (uint i = 0; i < vTokens.length; i++) {
            address vTokenAddr = address(vTokens[i]);
            VTokenInterface vToken = vTokens[i];

            // Get underlying token
            address underlying;
            try vToken.underlying() returns (address u) {
                underlying = u;
            } catch {
                underlying = address(0); // e.g., vBNB
            }

            // Get decimals
            uint8 dec = 18;
            if (underlying != address(0)) {
                try IERC20Metadata(underlying).decimals() returns (uint8 d) {
                    dec = d;
                } catch {}
            }

            // Get price
            uint256 price = 0;
            try oracle.getUnderlyingPrice(vTokenAddr) returns (uint256 p) {
                price = p;
            } catch {}

            // Get market config (isListed, collateralFactorMantissa, liquidationThresholdMantissa)
            bool isListed = false;
            uint256 ltvBps = 0;
            uint256 liqThresholdBps = 0;

            try comptroller.markets(vTokenAddr) returns (
                bool _isListed,
                uint256 collateralFactorMantissa,
                uint256 liquidationThresholdMantissa
            ) {
                isListed = _isListed;
                // Convert from 1e18 scale to basis points (10000 = 100%)
                ltvBps = (collateralFactorMantissa * 10_000) / 1e18;
                // Use liquidationThreshold if available, otherwise use collateralFactor
                uint256 liqThreshold = liquidationThresholdMantissa == 0 
                    ? collateralFactorMantissa 
                    : liquidationThresholdMantissa;
                liqThresholdBps = (liqThreshold * 10_000) / 1e18;
            } catch {}

            configs[i] = ReserveConfigData({
                token: underlying,
                vToken: vTokenAddr,
                price: price,
                ltv: ltvBps,
                liquidationThreshold: liqThresholdBps,
                decimals: dec,
                isListed: isListed
            });
        }
    }

    /// @notice Get collateral factors for all markets (similar to Compound's getCollateralFactors)
    /// @return assets Array of underlying token addresses
    /// @return ltvBps Array of LTV values in basis points
    /// @return lltvBps Array of liquidation threshold values in basis points
    function getCollateralFactors() external view returns (
        address[] memory assets,
        uint256[] memory ltvBps,
        uint256[] memory lltvBps
    ) {
        VTokenInterface[] memory vTokens = comptroller.getAllMarkets();
        uint256 numMarkets = vTokens.length;

        assets = new address[](numMarkets);
        ltvBps = new uint256[](numMarkets);
        lltvBps = new uint256[](numMarkets);

        for (uint i = 0; i < numMarkets; i++) {
            address vTokenAddr = address(vTokens[i]);

            // Get underlying token
            try vTokens[i].underlying() returns (address u) {
                assets[i] = u;
            } catch {
                assets[i] = address(0);
            }

            // Get market config
            try comptroller.markets(vTokenAddr) returns (
                bool /* isListed */,
                uint256 collateralFactorMantissa,
                uint256 liquidationThresholdMantissa
            ) {
                ltvBps[i] = (collateralFactorMantissa * 10_000) / 1e18;
                uint256 liqThreshold = liquidationThresholdMantissa == 0 
                    ? collateralFactorMantissa 
                    : liquidationThresholdMantissa;
                lltvBps[i] = (liqThreshold * 10_000) / 1e18;
            } catch {}
        }
    }
}

