// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

interface ICompoundComet {
    struct AssetInfo {
        uint8 offset;
        address asset;
        address priceFeed;
        uint64 scale;
        uint64 borrowCollateralFactor;
        uint64 liquidateCollateralFactor;
        uint64 liquidationFactor;
        uint128 supplyCap;
    }

    function baseToken() external view returns (address);
    function allow(address manager, bool isAllowed) external;
    function isAllowed(address owner, address manager) external view returns (bool);

    // Supply/withdraw APIs
    function supplyTo(address dst, address asset, uint amount) external;
    function withdrawFrom(address src, address to, address asset, uint amount) external;

    // Views
    function balanceOf(address owner) external view returns (uint256); // base position
    function borrowBalanceOf(address account) external view returns (uint256);
    function collateralBalanceOf(address account, address asset) external view returns (uint128);

    // View methods for view gateway
    function numAssets() external view returns (uint8);
    function getAssetInfo(uint8 i) external view returns (AssetInfo memory);
    function getAssetInfoByAddress(address asset) external view returns (AssetInfo memory);
    function getPrice(address priceFeed) external view returns (uint128);
    function userCollateral(address account, address asset) external view returns (uint128, uint128);
    function getSupplyRate(uint utilization) external view returns (uint64);
    function getBorrowRate(uint utilization) external view returns (uint64);
    function getUtilization() external view returns (uint);
    function baseTokenPriceFeed() external view returns (address);
    function priceScale() external pure returns (uint64);
}

contracts/v2/interfaces/euler/IEulerVaul.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

/// @title IEulerVault
/// @notice Interface for Euler V2 vaults (EVK - Euler Vault Kit)
/// @dev Extends ERC-4626 with borrowing functionality
interface IEulerVault {
    // ============ ERC-4626 Standard Functions ============

    /// @notice The underlying asset of the vault
    function asset() external view returns (address);

    /// @notice Total assets held by the vault
    function totalAssets() external view returns (uint256);

    /// @notice Convert assets to shares
    function convertToShares(uint256 assets) external view returns (uint256);

    /// @notice Convert shares to assets
    function convertToAssets(uint256 shares) external view returns (uint256);

    /// @notice Deposit assets and receive shares
    /// @param assets Amount of assets to deposit
    /// @param receiver Address to receive shares
    /// @return shares Amount of shares minted
    function deposit(uint256 assets, address receiver) external returns (uint256 shares);

    /// @notice Withdraw assets by burning shares
    /// @param assets Amount of assets to withdraw
    /// @param receiver Address to receive assets
    /// @param owner Address whose shares will be burned
    /// @return shares Amount of shares burned
    function withdraw(uint256 assets, address receiver, address owner) external returns (uint256 shares);

    /// @notice Redeem shares for assets
    /// @param shares Amount of shares to redeem
    /// @param receiver Address to receive assets
    /// @param owner Address whose shares will be burned
    /// @return assets Amount of assets received
    function redeem(uint256 shares, address receiver, address owner) external returns (uint256 assets);

    /// @notice Get share balance of an account
    function balanceOf(address account) external view returns (uint256);

    /// @notice Maximum withdrawable assets for an owner
    function maxWithdraw(address owner) external view returns (uint256);

    /// @notice Maximum redeemable shares for an owner
    function maxRedeem(address owner) external view returns (uint256);

    // ============ EVK Borrowing Extensions ============

    /// @notice Borrow assets from the vault
    /// @param assets Amount of assets to borrow
    /// @param receiver Address to receive borrowed assets
    /// @return Amount actually borrowed
    function borrow(uint256 assets, address receiver) external returns (uint256);

    /// @notice Repay borrowed assets
    /// @param assets Amount of assets to repay
    /// @param receiver Address of the borrower whose debt to repay
    /// @return Amount actually repaid
    function repay(uint256 assets, address receiver) external returns (uint256);

    /// @notice Get debt balance of an account
    /// @param account Address to query
    /// @return Amount of debt in asset units
    function debtOf(address account) external view returns (uint256);

    /// @notice Get debt balance in shares
    /// @param account Address to query
    /// @return Amount of debt shares
    function debtOfExact(address account) external view returns (uint256);

    /// @notice Total borrowed assets
    function totalBorrows() external view returns (uint256);

    // ============ Interest Rate ============

    /// @notice Current interest rate
    function interestRate() external view returns (uint256);

    /// @notice Interest accumulator for debt calculation
    function interestAccumulator() external view returns (uint256);

    // ============ LTV Configuration ============

    /// @notice Get the borrow LTV for a collateral (max LTV for new borrows)
    /// @param collateral The collateral vault address
    /// @return Borrow LTV in 1e4 scale (8000 = 80%)
    function LTVBorrow(address collateral) external view returns (uint16);

    /// @notice Get the liquidation LTV for a collateral (LLTV threshold)
    /// @param collateral The collateral vault address
    /// @return Liquidation LTV in 1e4 scale (9000 = 90%)
    function LTVLiquidation(address collateral) external view returns (uint16);

    /// @notice Get full LTV configuration including ramping parameters
    /// @param collateral The collateral vault address
    /// @return borrowLTV The borrow LTV
    /// @return liquidationLTV The current liquidation LTV
    /// @return initialLiquidationLTV The initial liquidation LTV before ramping
    /// @return targetTimestamp When ramping completes
    /// @return rampDuration Duration of the ramp
    function LTVFull(address collateral) external view returns (
        uint16 borrowLTV,
        uint16 liquidationLTV,
        uint16 initialLiquidationLTV,
        uint48 targetTimestamp,
        uint32 rampDuration
    );

    /// @notice Get list of all collaterals with configured LTVs
    /// @return Array of collateral vault addresses
    function LTVList() external view returns (address[] memory);

    // ============ Account Liquidity ============

    /// @notice Get account liquidity (health check)
    /// @param account The account to check
    /// @param liquidation If true, use liquidation LTV; if false, use borrow LTV
    /// @return collateralValue Total collateral value in unit of account
    /// @return liabilityValue Total liability value in unit of account
    function accountLiquidity(address account, bool liquidation)
        external view returns (uint256 collateralValue, uint256 liabilityValue);

    /// @notice Get detailed account liquidity per collateral
    /// @param account The account to check
    /// @param liquidation If true, use liquidation LTV; if false, use borrow LTV
    /// @return collaterals Array of collateral addresses
    /// @return collateralValues Array of collateral values
    /// @return liabilityValue Total liability value
    function accountLiquidityFull(address account, bool liquidation)
        external view returns (
            address[] memory collaterals,
            uint256[] memory collateralValues,
            uint256 liabilityValue
        );

    // ============ Controller Management ============

    /// @notice Disable this vault as controller for the authenticated account
    /// @dev Must be called through EVC.call() with proper onBehalfOfAccount context
    ///      Reverts if the account has outstanding debt to this vault
    function disableController() external;

    // ============ Vault Info ============

    /// @notice Name of the vault token
    function name() external view returns (string memory);

    /// @notice Symbol of the vault token
    function symbol() external view returns (string memory);

    /// @notice Decimals of the vault token
    function decimals() external view returns (uint8);
}

contracts/v2/interfaces/euler/IEVC.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

/// @title IEVC
/// @notice Interface for the Ethereum Vault Connector (EVC)
/// @dev The EVC mediates between vaults in the Euler V2 protocol
interface IEVC {
    /// @notice Batch operation item
    struct BatchItem {
        address targetContract;
        address onBehalfOfAccount;
        uint256 value;
        bytes data;
    }

    /// @notice Execute a batch of operations
    /// @param items Array of batch items to execute
    function batch(BatchItem[] calldata items) external payable;

    /// @notice Execute a call on behalf of an account
    /// @param targetContract The contract to call
    /// @param onBehalfOfAccount The account context for the call
    /// @param value ETH value to send
    /// @param data The calldata to execute
    /// @return result The return data from the call
    function call(
        address targetContract,
        address onBehalfOfAccount,
        uint256 value,
        bytes calldata data
    ) external payable returns (bytes memory result);

    /// @notice Enable a vault as collateral for an account
    /// @param account The account to enable collateral for
    /// @param vault The vault to enable as collateral
    function enableCollateral(address account, address vault) external;

    /// @notice Disable a vault as collateral for an account
    /// @param account The account to disable collateral for
    /// @param vault The vault to disable as collateral
    function disableCollateral(address account, address vault) external;

    /// @notice Enable a vault as controller for an account (allows borrowing)
    /// @param account The account to enable controller for
    /// @param vault The vault to enable as controller
    function enableController(address account, address vault) external;

    /// @notice Disable controller for an account
    /// @dev Will revert if account has outstanding debt
    /// @param account The account to disable controller for
    function disableController(address account) external;

    /// @notice Check if a vault is enabled as collateral for an account
    /// @param account The account to check
    /// @param vault The vault to check
    /// @return True if the vault is enabled as collateral
    function isCollateralEnabled(address account, address vault) external view returns (bool);

    /// @notice Check if a vault is enabled as controller for an account
    /// @param account The account to check
    /// @param vault The vault to check
    /// @return True if the vault is enabled as controller
    function isControllerEnabled(address account, address vault) external view returns (bool);

    /// @notice Get all collateral vaults for an account
    /// @param account The account to query
    /// @return Array of vault addresses enabled as collateral
    function getCollaterals(address account) external view returns (address[] memory);

    /// @notice Get all controller vaults for an account
    /// @param account The account to query
    /// @return Array of vault addresses enabled as controller
    function getControllers(address account) external view returns (address[] memory);

    /// @notice Set or revoke an operator for an account
    /// @param account The account to set operator for
    /// @param operator The operator address
    /// @param authorized True to authorize, false to revoke
    function setAccountOperator(address account, address operator, bool authorized) external;

    /// @notice Check if an operator is authorized for an account
    /// @param account The account to check
    /// @param operator The operator to check
    /// @return True if the operator is authorized
    function isAccountOperatorAuthorized(address account, address operator) external view returns (bool);

    /// @notice Get the owner of an account (handles sub-accounts)
    /// @param account The account address
    /// @return The owner address
    function getAccountOwner(address account) external view returns (address);
}

contracts/v2/interfaces/morpho/IMorphoBl.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

/// @title IMorphoBlue Interface
/// @notice Interface for Morpho Blue lending protocol
/// @dev See https://github.com/morpho-org/morpho-blue

struct MarketParams {
    address loanToken;
    address collateralToken;
    address oracle;
    address irm;
    uint256 lltv;
}

struct Market {
    uint128 totalSupplyAssets;
    uint128 totalSupplyShares;
    uint128 totalBorrowAssets;
    uint128 totalBorrowShares;
    uint128 lastUpdate;
    uint128 fee;
}

struct Position {
    uint256 supplyShares;
    uint128 borrowShares;
    uint128 collateral;
}

interface IMorphoBlue {
    /// @notice The state of a market (by id)
    function market(bytes32 id) external view returns (Market memory);

    /// @notice The position of a user in a market
    function position(bytes32 id, address user) external view returns (Position memory);

    /// @notice Whether an address is authorized to act on behalf of another
    function isAuthorized(address authorizer, address authorized) external view returns (bool);

    /// @notice The nonce of an authorizer for EIP-712 signatures
    function nonce(address authorizer) external view returns (uint256);

    /// @notice Compute market ID from market params
    function idToMarketParams(bytes32 id) external view returns (MarketParams memory);

    // ============ Write Functions ============

    /// @notice Supply assets to a market (lender side - earns yield)
    /// @param marketParams The market to supply to
    /// @param assets The amount of assets to supply (use 0 if using shares)
    /// @param shares The amount of shares to mint (use 0 if using assets)
    /// @param onBehalf The address to supply on behalf of
    /// @param data Callback data (empty for no callback)
    /// @return assetsSupplied The actual amount of assets supplied
    /// @return sharesSupplied The actual amount of shares minted
    function supply(
        MarketParams memory marketParams,
        uint256 assets,
        uint256 shares,
        address onBehalf,
        bytes memory data
    ) external returns (uint256 assetsSupplied, uint256 sharesSupplied);

    /// @notice Withdraw assets from a market (lender side)
    /// @param marketParams The market to withdraw from
    /// @param assets The amount of assets to withdraw (use 0 if using shares)
    /// @param shares The amount of shares to burn (use 0 if using assets)
    /// @param onBehalf The address to withdraw on behalf of
    /// @param receiver The address to receive the withdrawn assets
    /// @return assetsWithdrawn The actual amount of assets withdrawn
    /// @return sharesWithdrawn The actual amount of shares burned
    function withdraw(
        MarketParams memory marketParams,
        uint256 assets,
        uint256 shares,
        address onBehalf,
        address receiver
    ) external returns (uint256 assetsWithdrawn, uint256 sharesWithdrawn);

    /// @notice Borrow assets from a market
    /// @param marketParams The market to borrow from
    /// @param assets The amount of assets to borrow (use 0 if using shares)
    /// @param shares The amount of borrow shares to mint (use 0 if using assets)
    /// @param onBehalf The address to borrow on behalf of
    /// @param receiver The address to receive the borrowed assets
    /// @return assetsBorrowed The actual amount of assets borrowed
    /// @return sharesBorrowed The actual amount of borrow shares minted
    function borrow(
        MarketParams memory marketParams,
        uint256 assets,
        uint256 shares,
        address onBehalf,
        address receiver
    ) external returns (uint256 assetsBorrowed, uint256 sharesBorrowed);

    /// @notice Repay borrowed assets
    /// @param marketParams The market to repay to
    /// @param assets The amount of assets to repay (use 0 if using shares)
    /// @param shares The amount of borrow shares to burn (use 0 if using assets)
    /// @param onBehalf The address to repay on behalf of
    /// @param data Callback data (empty for no callback)
    /// @return assetsRepaid The actual amount of assets repaid
    /// @return sharesRepaid The actual amount of borrow shares burned
    function repay(
        MarketParams memory marketParams,
        uint256 assets,
        uint256 shares,
        address onBehalf,
        bytes memory data
    ) external returns (uint256 assetsRepaid, uint256 sharesRepaid);

    /// @notice Supply collateral to a market
    /// @param marketParams The market to supply collateral to
    /// @param assets The amount of collateral assets to supply
    /// @param onBehalf The address to supply on behalf of
    /// @param data Callback data (empty for no callback)
    function supplyCollateral(
        MarketParams memory marketParams,
        uint256 assets,
        address onBehalf,
        bytes memory data
    ) external;

    /// @notice Withdraw collateral from a market
    /// @param marketParams The market to withdraw collateral from
    /// @param assets The amount of collateral assets to withdraw
    /// @param onBehalf The address to withdraw on behalf of
    /// @param receiver The address to receive the withdrawn collateral
    function withdrawCollateral(
        MarketParams memory marketParams,
        uint256 assets,
        address onBehalf,
        address receiver
    ) external;

    /// @notice Set authorization for an address to act on behalf of the caller
    /// @param authorized The address to authorize/deauthorize
    /// @param newIsAuthorized Whether to authorize (true) or deauthorize (false)
    function setAuthorization(address authorized, bool newIsAuthorized) external;

    /// @notice Set authorization with signature (EIP-712)
    function setAuthorizationWithSig(
        address authorizer,
        address authorized,
        bool newIsAuthorized,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external;

    /// @notice Accrue interest for a market
    function accrueInterest(MarketParams memory marketParams) external;

    /// @notice Executes a flash loan.
    /// @dev Flash loans have access to the whole balance of the contract (all markets combined).
    /// @dev Zero fees - flashFee is always zero.
    /// @param token The token to flash loan.
    /// @param assets The amount of assets to flash loan.
    /// @param data Arbitrary data to pass to the `onMorphoFlashLoan` callback.
    function flashLoan(address token, uint256 assets, bytes calldata data) external;
}

/// @title IMorphoFlashLoanCallback
/// @notice Interface that users willing to use `flashLoan`'s callback must implement.
interface IMorphoFlashLoanCallback {
    /// @notice Callback called when a flash loan occurs.
    /// @param assets The amount of assets that was flash loaned.
    /// @param data Arbitrary data passed to the `flashLoan` function.
    function onMorphoFlashLoan(uint256 assets, bytes calldata data) external;
}

/// @notice Library for computing Morpho Blue market IDs
library MorphoLib {
    /// @notice Compute the market ID from market params
    function id(MarketParams memory params) internal pure returns (bytes32) {
        return keccak256(abi.encode(params));
    }
}
