import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

contract UiHelper {
    function getDecimals(address[] calldata tokens) public view returns (uint256[] memory) {
        uint256[] memory decimals = new uint256[](tokens.length);
