contracts/v2/cow/KapanOrderHandler.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { IConditionalOrderGenerator, PollTryNextBlock, PollNever, OrderNotValid } from "../interfaces/cow/IConditionalOrder.sol";
import { GPv2Order } from "../interfaces/cow/GPv2Order.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import { KapanOrderManager } from "./KapanOrderManager.sol";

/// @title KapanOrderHandler
/// @notice Generates CoW Protocol orders for Kapan leveraged positions
/// @dev Implements IConditionalOrderGenerator for ComposableCoW integration
contract KapanOrderHandler is IConditionalOrderGenerator {

    // ============ Errors ============
    error InvalidOrderManager();
    error OrderNotActive();

    // ============ State Variables ============

    /// @notice The KapanOrderManager that stores order contexts
    KapanOrderManager public immutable orderManager;

    /// @notice Default order validity duration (30 minutes)
    uint32 public constant DEFAULT_VALIDITY = 30 minutes;

    // ============ Constructor ============

    constructor(address _orderManager) {
        if (_orderManager == address(0)) revert InvalidOrderManager();
        orderManager = KapanOrderManager(_orderManager);
    }

    // ============ IConditionalOrderGenerator Implementation ============

    /// @notice Generate a tradeable order for the current chunk
    /// @param owner The order owner (KapanOrderManager address)
    /// @param sender The caller (usually ComposableCoW)
    /// @param ctx The execution context (unused for single orders)
    /// @param staticInput ABI-encoded orderHash
    /// @param offchainInput Off-chain provided data (unused)
    /// @return order The GPv2Order.Data struct for this chunk
    function getTradeableOrder(
        address owner,
        address sender,
        bytes32 ctx,
        bytes calldata staticInput,
        bytes calldata offchainInput
    ) external view override returns (GPv2Order.Data memory order) {
        // Silence unused variable warnings
        (owner, sender, ctx, offchainInput);

        // Decode the order hash from static input
        bytes32 orderHash = abi.decode(staticInput, (bytes32));

        // Get order context from manager
        KapanOrderManager.OrderContext memory orderCtx = orderManager.getOrder(orderHash);

        // Validate order is active
        if (orderCtx.status != KapanOrderManager.OrderStatus.Active) {
            revert PollNever("order_not_active");
        }

        // Check if order is complete
        if (orderManager.isOrderComplete(orderHash)) {
            revert PollNever("order_complete");
        }

        // Calculate remaining amount
        uint256 remaining = orderCtx.params.preTotalAmount - orderCtx.executedAmount;
        if (remaining == 0) {
            revert PollNever("fully_executed");
        }

        // Calculate this chunk's sell amount
        uint256 sellAmount = remaining < orderCtx.params.chunkSize 
            ? remaining 
            : orderCtx.params.chunkSize;

        // Build the order
        order = GPv2Order.Data({
            sellToken: IERC20(orderCtx.params.sellToken),
            buyToken: IERC20(orderCtx.params.buyToken),
            receiver: address(orderManager),  // Receive tokens at manager for post-hook
            sellAmount: sellAmount,
            buyAmount: orderCtx.params.minBuyPerChunk,
            validTo: uint32(block.timestamp + DEFAULT_VALIDITY),
            appData: orderCtx.params.appDataHash,
            feeAmount: 0,  // Fee is taken from sellAmount in CoW Protocol
            kind: GPv2Order.KIND_SELL,
            partiallyFillable: false,  // Each chunk must fill completely
            sellTokenBalance: GPv2Order.BALANCE_ERC20,
            buyTokenBalance: GPv2Order.BALANCE_ERC20
        });
    }

    /// @notice Verify that a proposed order matches the conditional order
    /// @dev Called by ComposableCoW during signature verification
    function verify(
        address owner,
        address sender,
        bytes32 _hash,
        bytes32 domainSeparator,
        bytes32 ctx,
        bytes calldata staticInput,
        bytes calldata offchainInput,
        GPv2Order.Data calldata order
    ) external view override {
        // Silence unused variable warnings
        (owner, sender, _hash, domainSeparator, ctx, offchainInput);

        // Decode the order hash
        bytes32 orderHash = abi.decode(staticInput, (bytes32));

        // Get order context
        KapanOrderManager.OrderContext memory orderCtx = orderManager.getOrder(orderHash);

        // Validate order is active
        if (orderCtx.status != KapanOrderManager.OrderStatus.Active) {
            revert OrderNotValid("order_not_active");
        }

        // Validate tokens match
        if (address(order.sellToken) != orderCtx.params.sellToken) {
            revert OrderNotValid("sell_token_mismatch");
        }
        if (address(order.buyToken) != orderCtx.params.buyToken) {
            revert OrderNotValid("buy_token_mismatch");
        }

        // Validate receiver is the order manager
        if (order.receiver != address(orderManager)) {
            revert OrderNotValid("invalid_receiver");
        }

        // Validate sell amount doesn't exceed chunk size
        if (order.sellAmount > orderCtx.params.chunkSize) {
            revert OrderNotValid("sell_amount_exceeds_chunk");
        }

        // Validate minimum buy amount
        if (order.buyAmount < orderCtx.params.minBuyPerChunk) {
            revert OrderNotValid("buy_amount_too_low");
        }

        // Validate appData matches
        if (order.appData != orderCtx.params.appDataHash) {
            revert OrderNotValid("appdata_mismatch");
        }

        // Order is valid
    }

    // ============ View Functions ============

    /// @notice Get the current chunk parameters for an order
    /// @param orderHash The order hash
    /// @return sellAmount The sell amount for the next chunk
    /// @return minBuyAmount The minimum buy amount
    /// @return isComplete Whether the order is complete
    function getChunkParams(bytes32 orderHash) external view returns (
        uint256 sellAmount,
        uint256 minBuyAmount,
        bool isComplete
    ) {
        KapanOrderManager.OrderContext memory orderCtx = orderManager.getOrder(orderHash);

        isComplete = orderManager.isOrderComplete(orderHash) || 
                     orderCtx.status != KapanOrderManager.OrderStatus.Active;

        if (isComplete) {
            return (0, 0, true);
        }

        uint256 remaining = orderCtx.params.preTotalAmount - orderCtx.executedAmount;
        sellAmount = remaining < orderCtx.params.chunkSize 
            ? remaining 
            : orderCtx.params.chunkSize;
        minBuyAmount = orderCtx.params.minBuyPerChunk;
    }

    /// @notice Get order progress
    /// @param orderHash The order hash
    /// @return executed Amount executed so far
    /// @return total Total amount to execute
    /// @return iterations Number of iterations completed
    function getProgress(bytes32 orderHash) external view returns (
        uint256 executed,
        uint256 total,
        uint256 iterations
    ) {
        KapanOrderManager.OrderContext memory orderCtx = orderManager.getOrder(orderHash);
        executed = orderCtx.executedAmount;
        total = orderCtx.params.preTotalAmount;
        iterations = orderCtx.iterationCount;
    }
}

contracts/v2/cow/KapanOrderManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import { IComposableCoW, IValueFactory } from "../interfaces/cow/IComposableCoW.sol";
import { IConditionalOrder } from "../interfaces/cow/IConditionalOrder.sol";
import { IERC1271, ERC1271_MAGIC_VALUE } from "../interfaces/cow/IERC1271.sol";
import { GPv2Order } from "../interfaces/cow/GPv2Order.sol";
import { IGPv2Settlement } from "../interfaces/cow/IGPv2Settlement.sol";
import { ProtocolTypes } from "../interfaces/ProtocolTypes.sol";

// Forward declaration for KapanRouter interface
interface IKapanRouter {
    function processProtocolInstructions(ProtocolTypes.ProtocolInstruction[] calldata instructions) external;
    function isAuthorizedFor(address user) external view returns (bool);
}

/// @title KapanOrderManager
/// @notice Manages CoW Protocol orders for Kapan, acting as ERC-1271 signer
/// @dev Singleton contract that holds order context and executes hooks
contract KapanOrderManager is Ownable, ReentrancyGuard, IERC1271 {
    using SafeERC20 for IERC20;

    // ============ Errors ============
    error OrderNotFound();
    error OrderAlreadyExists();
    error InvalidHandler();
    error NotHooksTrampoline();
    error HookExecutionFailed();
    error Unauthorized();
    error InvalidOrderState();

    // ============ Enums ============
    enum CompletionType {
        TargetLTV,          // Complete when target LTV is reached
        TargetBalance,      // Complete when target balance is reached
        Iterations,         // Complete after N iterations
        UntilCancelled      // Run until manually cancelled
    }

    enum OrderStatus {
        None,
        Active,
        Completed,
        Cancelled
    }

    // ============ Structs ============

    /// @notice Order parameters stored for each order
    struct KapanOrderParams {
        address user;

        // Pre-swap instructions (executed in pre-hook)
        bytes preInstructionsData;      // ABI-encoded ProtocolInstruction[]
        uint256 preTotalAmount;         // Total amount to process across all chunks

        // Swap configuration
        address sellToken;
        address buyToken;
        uint256 chunkSize;              // Max sell amount per chunk
        uint256 minBuyPerChunk;         // Minimum buy amount per chunk (slippage protection)

        // Post-swap instructions (executed in post-hook)
        bytes postInstructionsData;     // ABI-encoded ProtocolInstruction[]

        // Completion
        CompletionType completion;
        uint256 targetValue;            // Interpretation depends on CompletionType
        uint256 minHealthFactor;        // Safety: minimum health factor to maintain

        // AppData (pre-computed by frontend, includes hooks)
        bytes32 appDataHash;
    }

    /// @notice Order context stored on-chain
    struct OrderContext {
        KapanOrderParams params;
        OrderStatus status;
        uint256 executedAmount;         // Amount processed so far
        uint256 iterationCount;         // Number of completed iterations
        uint256 createdAt;
    }

    // ============ State Variables ============

    /// @notice The KapanRouter for executing lending operations
    IKapanRouter public router;

    /// @notice The ComposableCoW contract for order registration
    IComposableCoW public composableCoW;

    /// @notice The GPv2Settlement contract
    IGPv2Settlement public settlement;

    /// @notice The HooksTrampoline contract (caller for hooks)
    address public hooksTrampoline;

    /// @notice The KapanOrderHandler for generating orders
    address public orderHandler;

    /// @notice Order contexts by order hash
    mapping(bytes32 => OrderContext) public orders;

    /// @notice Salt used for each order (for ComposableCoW cancellation)
    mapping(bytes32 => bytes32) public orderSalts;

    /// @notice User's active orders
    mapping(address => bytes32[]) public userOrders;

    // ============ Events ============

    event OrderCreated(
        bytes32 indexed orderHash,
        address indexed user,
        address sellToken,
        address buyToken,
        uint256 totalAmount,
        uint256 chunkSize
    );

    event OrderCancelled(bytes32 indexed orderHash, address indexed user);
    event OrderCompleted(bytes32 indexed orderHash, address indexed user, uint256 totalExecuted);
    event ChunkExecuted(bytes32 indexed orderHash, uint256 chunkIndex, uint256 sellAmount, uint256 buyAmount);
    event PreHookExecuted(bytes32 indexed orderHash, uint256 chunkIndex);
    event PostHookExecuted(bytes32 indexed orderHash, uint256 chunkIndex, uint256 receivedAmount);

    // ============ Constructor ============

    constructor(
        address _owner,
        address _router,
        address _composableCoW,
        address _settlement,
        address _hooksTrampoline
    ) Ownable(_owner) {
        router = IKapanRouter(_router);
        composableCoW = IComposableCoW(_composableCoW);
        settlement = IGPv2Settlement(_settlement);
        hooksTrampoline = _hooksTrampoline;

        // Approve vault relayer to spend all tokens (will be set per-token as needed)
    }

    // ============ Admin Functions ============

    function setRouter(address _router) external onlyOwner {
        router = IKapanRouter(_router);
    }

    function setComposableCoW(address _composableCoW) external onlyOwner {
        composableCoW = IComposableCoW(_composableCoW);
    }

    function setHooksTrampoline(address _hooksTrampoline) external onlyOwner {
        hooksTrampoline = _hooksTrampoline;
    }

    function setOrderHandler(address _handler) external onlyOwner {
        orderHandler = _handler;
    }

    /// @notice Approve vault relayer to spend a token
    function approveVaultRelayer(address token) external onlyOwner {
        address vaultRelayer = settlement.vaultRelayer();
        IERC20(token).approve(vaultRelayer, type(uint256).max);
    }

    // ============ Order Creation ============

    /// @notice Create a new CoW order
    /// @param params The order parameters
    /// @param salt Unique salt for the order
    /// @return orderHash The hash identifying this order
    function createOrder(
        KapanOrderParams calldata params,
        bytes32 salt
    ) external nonReentrant returns (bytes32 orderHash) {
        // Validate
        if (params.user != msg.sender) revert Unauthorized();
        if (orderHandler == address(0)) revert InvalidHandler();

        // Compute order hash
        orderHash = keccak256(abi.encode(params, salt, block.timestamp));

        // Check order doesn't exist
        if (orders[orderHash].status != OrderStatus.None) revert OrderAlreadyExists();

        // Store order context
        orders[orderHash] = OrderContext({
            params: params,
            status: OrderStatus.Active,
            executedAmount: 0,
            iterationCount: 0,
            createdAt: block.timestamp
        });

        // Store salt separately for cancellation
        orderSalts[orderHash] = salt;

        // Track user's orders
        userOrders[params.user].push(orderHash);

        // Register with ComposableCoW
        IConditionalOrder.ConditionalOrderParams memory cowParams = IConditionalOrder.ConditionalOrderParams({
            handler: IConditionalOrder(orderHandler),
            salt: salt,
            staticData: abi.encode(orderHash)  // Handler looks up context by hash
        });

        composableCoW.create(cowParams, true);  // dispatch=true for WatchTower

        // Ensure vault relayer can spend sellToken
        _ensureVaultRelayerApproval(params.sellToken);

        emit OrderCreated(
            orderHash,
            params.user,
            params.sellToken,
            params.buyToken,
            params.preTotalAmount,
            params.chunkSize
        );
    }

    // ============ Order Cancellation ============

    /// @notice Cancel an active order
    /// @param orderHash The order to cancel
    function cancelOrder(bytes32 orderHash) external nonReentrant {
        OrderContext storage ctx = orders[orderHash];

        if (ctx.status == OrderStatus.None) revert OrderNotFound();
        if (ctx.params.user != msg.sender) revert Unauthorized();
        if (ctx.status != OrderStatus.Active) revert InvalidOrderState();

        ctx.status = OrderStatus.Cancelled;

        // Remove from ComposableCoW
        // Note: We need to compute the same hash ComposableCoW uses
        bytes32 cowOrderHash = composableCoW.hash(
            IConditionalOrder.ConditionalOrderParams({
                handler: IConditionalOrder(orderHandler),
                salt: orderSalts[orderHash],
                staticData: abi.encode(orderHash)
            })
        );
        composableCoW.remove(cowOrderHash);

        emit OrderCancelled(orderHash, msg.sender);
    }

    // ============ Hook Execution ============

    /// @notice Execute pre-hook (called by HooksTrampoline during CoW settlement)
    /// @dev Prepends a ToOutput instruction with chunkSize, so all stored instructions
    ///      can reference index 0 for the chunk amount
    /// @param orderHash The order being settled
    /// @param chunkIndex The current chunk index
    function executePreHook(bytes32 orderHash, uint256 chunkIndex) external nonReentrant {
        // Only HooksTrampoline can call this
        if (msg.sender != hooksTrampoline) revert NotHooksTrampoline();

        OrderContext storage ctx = orders[orderHash];
        if (ctx.status != OrderStatus.Active) revert InvalidOrderState();

        // Decode stored pre-instructions
        ProtocolTypes.ProtocolInstruction[] memory storedInstructions = 
            abi.decode(ctx.params.preInstructionsData, (ProtocolTypes.ProtocolInstruction[]));

        // Create new array with ToOutput prepended
        ProtocolTypes.ProtocolInstruction[] memory instructions = 
            new ProtocolTypes.ProtocolInstruction[](storedInstructions.length + 1);

        // Prepend ToOutput with chunkSize - this becomes index 0
        instructions[0] = ProtocolTypes.ProtocolInstruction({
            protocolName: "router",
            data: _encodeToOutput(ctx.params.chunkSize, ctx.params.sellToken)
        });

        // Copy stored instructions (their indices shift by 1, but they reference index 0)
        for (uint256 i = 0; i < storedInstructions.length; i++) {
            instructions[i + 1] = storedInstructions[i];
        }

        // Execute via router (router will check delegation)
        router.processProtocolInstructions(instructions);

        emit PreHookExecuted(orderHash, chunkIndex);
    }

    /// @notice Execute post-hook (called by HooksTrampoline during CoW settlement)
    /// @dev Prepends a ToOutput instruction with the actual received amount, so all
    ///      stored instructions can reference index 0 for the swap output
    /// @param orderHash The order being settled
    function executePostHook(bytes32 orderHash) external nonReentrant {
        // Only HooksTrampoline can call this
        if (msg.sender != hooksTrampoline) revert NotHooksTrampoline();

        OrderContext storage ctx = orders[orderHash];
        if (ctx.status != OrderStatus.Active) revert InvalidOrderState();

        // Get actual received amount
        uint256 receivedAmount = IERC20(ctx.params.buyToken).balanceOf(address(this));

        // Transfer tokens to router for processing
        IERC20(ctx.params.buyToken).safeTransfer(address(router), receivedAmount);

        // Decode stored post-instructions
        ProtocolTypes.ProtocolInstruction[] memory storedInstructions = 
            abi.decode(ctx.params.postInstructionsData, (ProtocolTypes.ProtocolInstruction[]));

        // Create new array with ToOutput prepended
        ProtocolTypes.ProtocolInstruction[] memory instructions = 
            new ProtocolTypes.ProtocolInstruction[](storedInstructions.length + 1);

        // Prepend ToOutput with receivedAmount - this becomes index 0
        instructions[0] = ProtocolTypes.ProtocolInstruction({
            protocolName: "router",
            data: _encodeToOutput(receivedAmount, ctx.params.buyToken)
        });

        // Copy stored instructions (their indices shift by 1, but they reference index 0)
        for (uint256 i = 0; i < storedInstructions.length; i++) {
            instructions[i + 1] = storedInstructions[i];
        }

        // Execute via router
        router.processProtocolInstructions(instructions);

        // Update progress
        ctx.executedAmount += ctx.params.chunkSize;
        ctx.iterationCount++;

        // Check completion
        if (_isOrderComplete(ctx)) {
            ctx.status = OrderStatus.Completed;
            emit OrderCompleted(orderHash, ctx.params.user, ctx.executedAmount);
        }

        emit PostHookExecuted(orderHash, ctx.iterationCount, receivedAmount);
    }

    // ============ ERC-1271 Signature Verification ============

    /// @notice Verify signature for CoW Protocol orders
    /// @dev Delegates to ComposableCoW for conditional order verification
    function isValidSignature(
        bytes32 _hash,
        bytes calldata _signature
    ) external view override returns (bytes4) {
        // Decode the payload
        IComposableCoW.PayloadStruct memory payload = abi.decode(_signature, (IComposableCoW.PayloadStruct));

        // Verify via ComposableCoW
        // ComposableCoW will call the handler's verify() function
        try composableCoW.getTradeableOrderWithSignature(
            address(this),
            payload.params,
            payload.offchainInput,
            payload.proof
        ) returns (GPv2Order.Data memory, bytes memory) {
            return ERC1271_MAGIC_VALUE;
        } catch {
            return bytes4(0xffffffff);
        }
    }

    // ============ View Functions ============

    /// @notice Get order context
    function getOrder(bytes32 orderHash) external view returns (OrderContext memory) {
        return orders[orderHash];
    }

    /// @notice Get user's orders
    function getUserOrders(address user) external view returns (bytes32[] memory) {
        return userOrders[user];
    }

    /// @notice Check if an order is complete based on completion criteria
    function isOrderComplete(bytes32 orderHash) external view returns (bool) {
        return _isOrderComplete(orders[orderHash]);
    }

    // ============ Internal Functions ============

    function _isOrderComplete(OrderContext storage ctx) internal view returns (bool) {
        if (ctx.params.completion == CompletionType.Iterations) {
            return ctx.iterationCount >= ctx.params.targetValue;
        }

        if (ctx.params.completion == CompletionType.TargetBalance) {
            // Check remaining amount
            uint256 remaining = ctx.params.preTotalAmount - ctx.executedAmount;
            return remaining == 0 || remaining < ctx.params.chunkSize;
        }

        if (ctx.params.completion == CompletionType.TargetLTV) {
            // TODO: Read actual LTV from lending protocol
            // For now, check if we've executed the total amount
            return ctx.executedAmount >= ctx.params.preTotalAmount;
        }

        // UntilCancelled never completes automatically
        return false;
    }

    /// @notice Encode a ToOutput router instruction
    /// @dev Matches the encoding expected by KapanRouter's RouterInstruction struct
    ///      struct RouterInstruction { uint256 amount; address token; address user; RouterInstructionType instructionType; }
    ///      RouterInstructionType.ToOutput = 3
    function _encodeToOutput(uint256 amount, address token) internal pure returns (bytes memory) {
        // ABI encode as tuple matching RouterInstruction struct layout
        // Note: RouterInstructionType enum is uint8, but in ABI encoding it's padded to 32 bytes
        return abi.encode(amount, token, address(0), uint8(3));
    }

    function _ensureVaultRelayerApproval(address token) internal {
        address vaultRelayer = settlement.vaultRelayer();
        uint256 currentAllowance = IERC20(token).allowance(address(this), vaultRelayer);
        if (currentAllowance < type(uint256).max / 2) {
            IERC20(token).approve(vaultRelayer, type(uint256).max);
        }
    }

    // ============ Emergency Functions ============

    /// @notice Recover stuck tokens (owner only)
    function recoverTokens(address token, address to, uint256 amount) external onlyOwner {
        IERC20(token).safeTransfer(to, amount);
    }
}

contracts/v2/flashloans/FlashLoanConsume.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

// --- Existing Balancer imports ---
import { IFlashLoanProvider } from "../interfaces/balancer/IFlashLoanProvider.sol";
import { IVaultV3 } from "../interfaces/balancer/IVaultV3.sol";

// --- Morpho Blue imports ---
import { IMorphoBlue, IMorphoFlashLoanCallback } from "../interfaces/morpho/IMorphoBlue.sol";

// --- Minimal Aave v3 interfaces (flashLoanSimple) ---
interface IAaveV3Pool {
    function flashLoanSimple(
        address receiverAddress,
        address asset,
        uint256 amount,
        bytes calldata params,
        uint16 referralCode
    ) external;
}

interface IAaveFlashLoanSimpleReceiver {
    function executeOperation(
        address asset,
        uint256 amount,
        uint256 premium,
        address initiator,
        bytes calldata params
    ) external returns (bool);
}

// --- Minimal Uniswap v3 factory/pool interfaces (with auth support) ---
interface IUniswapV3Factory {
    function getPool(address tokenA, address tokenB, uint24 fee) external view returns (address);
}

interface IUniswapV3Pool {
    function token0() external view returns (address);
    function token1() external view returns (address);
    function fee() external view returns (uint24);
    function factory() external view returns (address);

    function flash(address recipient, uint256 amount0, uint256 amount1, bytes calldata data) external;
}

// Custom errors for flash loans
error FlashLoanNotEnabled();
error UnauthorizedFlashCaller();
error NestedFlashNotAllowed();
error ProviderNotConfigured();
error InvalidFlashParams();
error BadInitiator();
error InvalidPool();
error TokenNotInPool();

/// @notice Abstract base supporting Balancer v2/v3, Aave v3 (and forks like ZeroLend), Uniswap v3, and Morpho flash sources.
abstract contract FlashLoanConsumerBase is IAaveFlashLoanSimpleReceiver, IMorphoFlashLoanCallback {
    using SafeERC20 for IERC20;

    // ----------------- Providers (config) -----------------
    IFlashLoanProvider public balancerV2Vault; // Balancer v2 Vault
    IVaultV3 public balancerV3Vault;          // Balancer v3 Vault

    mapping(bytes32 => address) public aaveCompatiblePools; // key hash => pool address
    mapping(address => bool) public isAaveCompatiblePool;   // callback validation allowlist

    // Trusted Uniswap v3 factory (non-zero enables Uniswap v3)
    address public uniswapV3Factory;

    // Morpho Blue singleton (non-zero enables Morpho flash loans)
    address public morphoBlue;

    // ----------------- Callback guard -----------------
    bool private flashLoanEnabled;
    address private authorizedFlashSender;



    // ----------------- Setters -----------------
    function _setBalancerV2(address provider) internal {
        balancerV2Vault = IFlashLoanProvider(provider);
    }

    function _setBalancerV3(address vault) internal {
        balancerV3Vault = IVaultV3(vault);
    }

    function _addAaveCompatiblePool(string memory key, address pool) internal {
        bytes32 keyHash = keccak256(abi.encodePacked(key));
        address oldPool = aaveCompatiblePools[keyHash];
        if (oldPool != address(0)) isAaveCompatiblePool[oldPool] = false;

        aaveCompatiblePools[keyHash] = pool;
        if (pool != address(0)) isAaveCompatiblePool[pool] = true;
    }

    function _setUniswapV3Factory(address factory) internal {
        uniswapV3Factory = factory;
    }

    function _setMorphoBlue(address morpho) internal {
        morphoBlue = morpho;
    }

    // ----------------- Guard modifiers -----------------
    modifier flashLoanOnly() {
        if (!flashLoanEnabled) revert FlashLoanNotEnabled();
        if (msg.sender != authorizedFlashSender) revert UnauthorizedFlashCaller();
        _;
    }

    modifier authorizeFlashLoan(address expectedSender) {
        if (flashLoanEnabled) revert NestedFlashNotAllowed();
        flashLoanEnabled = true;
        authorizedFlashSender = expectedSender;
        _;
        authorizedFlashSender = address(0);
        flashLoanEnabled = false;
    }

    // ============================================================
    //                          BALANCER V2
    // ============================================================

    function _requestBalancerV2(
        address token,
        uint256 amount,
        bytes memory userData
    ) internal authorizeFlashLoan(address(balancerV2Vault)) {
        if (address(balancerV2Vault) == address(0)) revert ProviderNotConfigured();
        if (token == address(0) || amount == 0) revert InvalidFlashParams();

        IERC20[] memory tokens = new IERC20[](1);
        tokens[0] = IERC20(token);
        uint256[] memory amounts = new uint256[](1);
        amounts[0] = amount;

        balancerV2Vault.flashLoan(address(this), tokens, amounts, userData);
    }

    function receiveFlashLoan(
        IERC20[] calldata tokens,
        uint256[] calldata amounts,
        uint256[] calldata feeAmounts,
        bytes calldata userData
    ) external flashLoanOnly {
        uint256 repayment = amounts[0] + feeAmounts[0];
        _afterFlashLoan(address(tokens[0]), repayment, userData);
        tokens[0].safeTransfer(address(balancerV2Vault), repayment);
    }

    // ============================================================
    //                          BALANCER V3
    // ============================================================

    function _requestBalancerV3(address token, uint256 amount)
        internal
        authorizeFlashLoan(address(balancerV3Vault))
    {
        if (address(balancerV3Vault) == address(0)) revert ProviderNotConfigured();
        if (token == address(0) || amount == 0) revert InvalidFlashParams();

        bytes memory userData = abi.encode(token, amount);
        balancerV3Vault.unlock(abi.encodeWithSelector(this.receiveFlashLoanV3.selector, userData));
    }

    function receiveFlashLoanV3(bytes calldata userData) external flashLoanOnly {
        (address token, uint256 amount) = abi.decode(userData, (address, uint256));
        balancerV3Vault.sendTo(token, address(this), amount);
        _afterFlashLoan(token, amount, userData);
        IERC20(token).safeTransfer(address(balancerV3Vault), amount);
        balancerV3Vault.settle(token, amount);
    }

    // ============================================================
    //                   AAVE V3 & COMPATIBLE FORKS
    // ============================================================

    function _requestAaveCompatible(
        string memory poolKey,
        address token,
        uint256 amount,
        bytes memory userData
    ) internal {
        address pool = aaveCompatiblePools[keccak256(abi.encodePacked(poolKey))];
        if (pool == address(0)) revert ProviderNotConfigured();
        _requestAaveCompatibleInternal(pool, token, amount, userData);
    }

    function _requestAaveCompatibleInternal(
        address pool,
        address token,
        uint256 amount,
        bytes memory userData
    ) private authorizeFlashLoan(pool) {
        if (token == address(0) || amount == 0) revert InvalidFlashParams();
        IAaveV3Pool(pool).flashLoanSimple(address(this), token, amount, userData, 0);
    }

    function executeOperation(
        address asset,
        uint256 amount,
        uint256 premium,
        address initiator,
        bytes calldata params
    ) external override flashLoanOnly returns (bool) {
        if (initiator != address(this)) revert BadInitiator();
        uint256 repayment = amount + premium;
        _afterFlashLoan(asset, repayment, params);
        IERC20(asset).forceApprove(msg.sender, repayment);
        return true;
    }

    // ============================================================
    //                          UNISWAP V3
    // ============================================================

    function _requestUniswapV3(
        address pool,
        address token,
        uint256 amount,
        bytes memory userData
    ) internal authorizeFlashLoan(pool) {
        if (uniswapV3Factory == address(0)) revert ProviderNotConfigured();
        if (pool == address(0) || token == address(0) || amount == 0) revert InvalidFlashParams();

        IUniswapV3Pool p = IUniswapV3Pool(pool);
        if (p.factory() != uniswapV3Factory) revert InvalidPool();

        address t0 = p.token0();
        address t1 = p.token1();
        if (IUniswapV3Factory(uniswapV3Factory).getPool(t0, t1, p.fee()) != pool) revert InvalidPool();

        bool is0 = token == t0;
        if (!is0 && token != t1) revert TokenNotInPool();

        p.flash(address(this), is0 ? amount : 0, is0 ? 0 : amount, abi.encode(pool, token, amount, userData, is0));
    }

    function uniswapV3FlashCallback(uint256 fee0, uint256 fee1, bytes calldata data) external flashLoanOnly {
        (address pool, address token, uint256 amount, bytes memory userData, bool is0) =
            abi.decode(data, (address, address, uint256, bytes, bool));
        uint256 repayment = amount + (is0 ? fee0 : fee1);
        _afterFlashLoan(token, repayment, userData);
        IERC20(token).safeTransfer(pool, repayment);
    }

    // ============================================================
    //                          MORPHO BLUE
    // ============================================================

    function _requestMorpho(
        address token,
        uint256 amount,
        bytes memory userData
    ) internal authorizeFlashLoan(morphoBlue) {
        if (morphoBlue == address(0)) revert ProviderNotConfigured();
        if (token == address(0) || amount == 0) revert InvalidFlashParams();
        IMorphoBlue(morphoBlue).flashLoan(token, amount, abi.encode(token, userData));
    }

    function onMorphoFlashLoan(uint256 assets, bytes calldata data) external override flashLoanOnly {
        (address token, bytes memory userData) = abi.decode(data, (address, bytes));
        _afterFlashLoan(token, assets, userData);
        // Morpho uses safeTransferFrom to pull tokens back, so we need to approve
        IERC20(token).forceApprove(morphoBlue, assets);
    }

    // ============================================================
    //                       ABSTRACT RESUME HOOK
    // ============================================================

    function _afterFlashLoan(address token, uint256 repaymentAmount, bytes memory userData) internal virtual;
}

contracts/v2/gateways/aave/AaveGatewayVi.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {IPoolAddressesProvider} from "../../../interfaces/aave/IPoolAddressesProvider.sol";
import {IUiPoolDataProviderV3} from "../../../interfaces/aave/IUiDataProvider.sol";
import {IPool} from "@aave/core-v3/contracts/interfaces/IPool.sol";
import {IPoolDataProvider} from "@aave/core-v3/contracts/interfaces/IPoolDataProvider.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";

/**
 * @title AaveGatewayView
 * @notice View-only gateway for Aave v3 protocol
 * @dev Contains all read/view functions from v1, separate from write operations
 */
contract AaveGatewayView {
    IPoolAddressesProvider public immutable poolAddressesProvider;
    IUiPoolDataProviderV3 public immutable uiPoolDataProvider;

    constructor(address _poolAddressesProvider, address _uiPoolDataProvider) {
        poolAddressesProvider = IPoolAddressesProvider(_poolAddressesProvider);
        uiPoolDataProvider = IUiPoolDataProviderV3(_uiPoolDataProvider);
    }

    struct TokenInfo {
        address token;
        uint256 supplyRate;
        uint256 borrowRate;
        string name;
        string symbol;
        uint256 price;
        uint256 borrowBalance;
        uint256 balance;
        address aToken;
        uint8 decimals;
    }

    struct EModeInfo {
        uint8 id;
        uint16 ltv;
        uint16 liquidationThreshold;
        uint16 liquidationBonus;
        string label;
        uint128 collateralBitmap;
        uint128 borrowableBitmap;
    }

    /// @notice Reserve configuration for LTV calculations
    struct ReserveConfigData {
        address token;
        uint256 price;              // Price in base currency (8 decimals)
        uint256 ltv;                // Loan-to-value in basis points (0-10000)
        uint256 liquidationThreshold; // Liquidation threshold in basis points
        uint256 liquidationBonus;   // Liquidation bonus in basis points
        uint8 decimals;
        bool usageAsCollateralEnabled;
        bool borrowingEnabled;
    }

    /// @notice Returns the user's current E-Mode category ID
    /// @param user The address of the user
    /// @return categoryId The E-Mode category ID (0 = no E-Mode)
    function getUserEMode(address user) external view returns (uint8 categoryId) {
        (, categoryId) = uiPoolDataProvider.getUserReservesData(poolAddressesProvider, user);
    }

    /// @notice Returns all available E-Mode categories
    /// @return emodes Array of E-Mode categories with their configurations
    function getEModes() external view returns (EModeInfo[] memory emodes) {
        IUiPoolDataProviderV3.Emode[] memory rawEmodes = uiPoolDataProvider.getEModes(poolAddressesProvider);
        emodes = new EModeInfo[](rawEmodes.length);
        for (uint256 i = 0; i < rawEmodes.length; i++) {
            emodes[i] = EModeInfo({
                id: rawEmodes[i].id,
                ltv: rawEmodes[i].eMode.ltv,
                liquidationThreshold: rawEmodes[i].eMode.liquidationThreshold,
                liquidationBonus: rawEmodes[i].eMode.liquidationBonus,
                label: rawEmodes[i].eMode.label,
                collateralBitmap: rawEmodes[i].eMode.collateralBitmap,
                borrowableBitmap: rawEmodes[i].eMode.borrowableBitmap
            });
        }
    }

    function getAllTokensInfo(address user) external view returns (TokenInfo[] memory) {
        // Fetch reserves data once.
        (IUiPoolDataProviderV3.AggregatedReserveData[] memory reserves,) = uiPoolDataProvider.getReservesData(poolAddressesProvider);
        // Fetch user reserves data once (including E-Mode category)
        (IUiPoolDataProviderV3.UserReserveData[] memory userReserves, /* uint8 userEModeCategory */) = 
            uiPoolDataProvider.getUserReservesData(poolAddressesProvider, user);

        TokenInfo[] memory tokens = new TokenInfo[](reserves.length);
        for (uint256 i = 0; i < reserves.length; i++) {
            uint256 balance = _getBalanceFromReserveData(reserves[i], user, userReserves);
            uint256 borrowBalance = _getBorrowBalanceFromReserveData(reserves[i], user, userReserves);
            uint8 dec = 18;
            // Read decimals from the underlying token; default to 18 on failure
            try ERC20(reserves[i].underlyingAsset).decimals() returns (uint8 d) {
                dec = d;
            } catch {
            }
            tokens[i] = TokenInfo({
                token: reserves[i].underlyingAsset,
                supplyRate: reserves[i].liquidityRate,
                borrowRate: reserves[i].variableBorrowRate,
                name: reserves[i].name,
                symbol: reserves[i].symbol,
                price: reserves[i].priceInMarketReferenceCurrency,
                borrowBalance: borrowBalance,
                balance: balance,
                aToken: reserves[i].aTokenAddress,
                decimals: dec
            });
        }
        return tokens;
    }

    /// @notice Returns all token info for a user along with their E-Mode category
    /// @param user The address of the user
    /// @return tokens Array of TokenInfo structs
    /// @return userEModeCategory The user's current E-Mode category ID (0 = no E-Mode)
    function getAllTokensInfoWithEMode(address user) external view returns (TokenInfo[] memory tokens, uint8 userEModeCategory) {
        // Fetch reserves data once.
        (IUiPoolDataProviderV3.AggregatedReserveData[] memory reserves,) = uiPoolDataProvider.getReservesData(poolAddressesProvider);
        // Fetch user reserves data once (including E-Mode category)
        (IUiPoolDataProviderV3.UserReserveData[] memory userReserves, uint8 eModeCat) = 
            uiPoolDataProvider.getUserReservesData(poolAddressesProvider, user);
        userEModeCategory = eModeCat;

        tokens = new TokenInfo[](reserves.length);
        for (uint256 i = 0; i < reserves.length; i++) {
            uint256 balance = _getBalanceFromReserveData(reserves[i], user, userReserves);
            uint256 borrowBalance = _getBorrowBalanceFromReserveData(reserves[i], user, userReserves);
            uint8 dec = 18;
            try ERC20(reserves[i].underlyingAsset).decimals() returns (uint8 d) {
                dec = d;
            } catch {
            }
            tokens[i] = TokenInfo({
                token: reserves[i].underlyingAsset,
                supplyRate: reserves[i].liquidityRate,
                borrowRate: reserves[i].variableBorrowRate,
                name: reserves[i].name,
                symbol: reserves[i].symbol,
                price: reserves[i].priceInMarketReferenceCurrency,
                borrowBalance: borrowBalance,
                balance: balance,
                aToken: reserves[i].aTokenAddress,
                decimals: dec
            });
        }
    }

    /// @notice Get reserve configuration data for multiple tokens (for frontend LTV calculations)
    /// @dev Returns price, LTV, liquidation threshold, and other config for each token
    /// @param tokens Array of token addresses to get config for
    /// @return configs Array of reserve configuration data
    function getReserveConfigs(address[] calldata tokens) external view returns (ReserveConfigData[] memory configs) {
        IPoolDataProvider dataProvider = IPoolDataProvider(IPoolAddressesProvider(poolAddressesProvider).getPoolDataProvider());
        (IUiPoolDataProviderV3.AggregatedReserveData[] memory reserves,) = uiPoolDataProvider.getReservesData(poolAddressesProvider);

        configs = new ReserveConfigData[](tokens.length);

        for (uint256 i = 0; i < tokens.length; i++) {
            address token = tokens[i];

            // Find price from reserves data
            uint256 price = 0;
            for (uint256 j = 0; j < reserves.length; j++) {
                if (reserves[j].underlyingAsset == token) {
                    price = reserves[j].priceInMarketReferenceCurrency;
                    break;
                }
            }

            // Get decimals
            uint8 decimals = 18;
            try ERC20(token).decimals() returns (uint8 d) { decimals = d; } catch {}

            // Get reserve configuration
            uint256 ltv = 0;
            uint256 liquidationThreshold = 0;
            uint256 liquidationBonus = 0;
            bool usageAsCollateralEnabled = false;
            bool borrowingEnabled = false;

            try dataProvider.getReserveConfigurationData(token) returns (
                uint256 /* _decimals */,
                uint256 _ltv,
                uint256 _liquidationThreshold,
                uint256 _liquidationBonus,
                uint256 /* reserveFactor */,
                bool _usageAsCollateralEnabled,
                bool _borrowingEnabled,
                bool /* stableBorrowRateEnabled */,
                bool /* isActive */,
                bool /* isFrozen */
            ) {
                ltv = _ltv;
                liquidationThreshold = _liquidationThreshold;
                liquidationBonus = _liquidationBonus;
                usageAsCollateralEnabled = _usageAsCollateralEnabled;
                borrowingEnabled = _borrowingEnabled;
            } catch {}

            configs[i] = ReserveConfigData({
                token: token,
                price: price,
                ltv: ltv,
                liquidationThreshold: liquidationThreshold,
                liquidationBonus: liquidationBonus,
                decimals: decimals,
                usageAsCollateralEnabled: usageAsCollateralEnabled,
                borrowingEnabled: borrowingEnabled
            });
        }
    }

    /// @notice Get reserve config for a single token
    /// @param token The token address
    /// @return config The reserve configuration data
    function getReserveConfig(address token) external view returns (ReserveConfigData memory config) {
        address[] memory tokens = new address[](1);
        tokens[0] = token;
        ReserveConfigData[] memory configs = this.getReserveConfigs(tokens);
        return configs[0];
    }

    function getBorrowRate(address token) external view returns (uint256, bool) {
        (IUiPoolDataProviderV3.AggregatedReserveData[] memory reserves, ) = 
            uiPoolDataProvider.getReservesData(poolAddressesProvider);
        for (uint256 i = 0; i < reserves.length; i++) {
            if (reserves[i].underlyingAsset == token) {
                return (reserves[i].variableBorrowRate, true);
            }
        }
        return (0, false);
    }

    function getSupplyRate(address token) external view returns (uint256, bool) {
        (IUiPoolDataProviderV3.AggregatedReserveData[] memory reserves, ) = 
            uiPoolDataProvider.getReservesData(poolAddressesProvider);
        for (uint256 i = 0; i < reserves.length; i++) {
            if (reserves[i].underlyingAsset == token) {
                return (reserves[i].liquidityRate, true);
            }
        }
        return (0, false);
    }

    /// @notice Gets the balance for a given token and user.
    function getBalance(address token, address user) public view returns (uint256) {
        (address aToken, , bool found) = _getReserveAddresses(token);
        if (found && aToken != address(0)) {
            try IERC20(aToken).balanceOf(user) returns (uint256 bal) {
                return bal;
            } catch {
            }
        }
        // Fallback: use user reserves data
        (IUiPoolDataProviderV3.UserReserveData[] memory userReserves, ) = 
            uiPoolDataProvider.getUserReservesData(poolAddressesProvider, user);
        for (uint256 i = 0; i < userReserves.length; i++) {
            if (userReserves[i].underlyingAsset == token) {
                return userReserves[i].scaledATokenBalance;
            }
        }
        return 0;
    }

    /// @notice Gets the borrow balance for a given token and user.
    function getBorrowBalance(address token, address user) public view returns (uint256) {
        (, address variableDebtToken, bool found) = _getReserveAddresses(token);
        if (found && variableDebtToken != address(0)) {
            try IERC20(variableDebtToken).balanceOf(user) returns (uint256 bal) {
                return bal;
            } catch {
            }
        }
        // Fallback: use user reserves data
        (IUiPoolDataProviderV3.UserReserveData[] memory userReserves, ) = 
            uiPoolDataProvider.getUserReservesData(poolAddressesProvider, user);
        for (uint256 i = 0; i < userReserves.length; i++) {
            if (userReserves[i].underlyingAsset == token) {
                return userReserves[i].scaledVariableDebt;
            }
        }
        return 0;
    }

    function getBorrowBalanceCurrent(address token, address user) external returns (uint256) {
        return getBorrowBalance(token, user);
    }

    /// @notice Returns the liquidation threshold (LLTV) for a given user in basis points.
    function getMaxLtv(address /* market */, address user) external view returns (uint256) {
        IPool pool = IPool(poolAddressesProvider.getPool());
        (, , , uint256 currentLiquidationThreshold, ,) = pool.getUserAccountData(user);
        return currentLiquidationThreshold;
    }

    /// @notice Returns the protocol-level maximum LTV configuration for the user in basis points.
    function getLtv(address /* market */, address user) external view returns (uint256) {
        IPool pool = IPool(poolAddressesProvider.getPool());
        (, , , , uint256 ltv,) = pool.getUserAccountData(user);
        return ltv;
    }

    /// @notice Returns full user account data from Aave
    /// @param user The user address
    /// @return totalCollateralBase Total collateral in base currency
    /// @return totalDebtBase Total debt in base currency
    /// @return availableBorrowsBase Available borrows in base currency
    /// @return currentLiquidationThreshold Current liquidation threshold (bps)
    /// @return ltv Current LTV (bps)
    /// @return healthFactor Health factor (1e18 scale)
    function getUserAccountData(address user) external view returns (
        uint256 totalCollateralBase,
        uint256 totalDebtBase,
        uint256 availableBorrowsBase,
        uint256 currentLiquidationThreshold,
        uint256 ltv,
        uint256 healthFactor
    ) {
        IPool pool = IPool(poolAddressesProvider.getPool());
        return pool.getUserAccountData(user);
    }

    /// @notice Returns the list of tokens that the user has borrowed.
    function borrowedTokens(address user) external view returns (address[] memory) {
        (IUiPoolDataProviderV3.UserReserveData[] memory userReserves, ) = 
            uiPoolDataProvider.getUserReservesData(poolAddressesProvider, user);
        uint256 count = 0;
        for (uint256 i = 0; i < userReserves.length; i++) {
            if (userReserves[i].scaledVariableDebt > 0) {
                count++;
            }
        }
        address[] memory tokens = new address[](count);
        uint256 index = 0;
        for (uint256 i = 0; i < userReserves.length; i++) {
            if (userReserves[i].scaledVariableDebt > 0) {
                tokens[index++] = userReserves[i].underlyingAsset;
            }
        }
        return tokens;
    }

    /// @dev Internal helper to get both the aToken and variable debt token for a given underlying asset.
    function _getReserveAddresses(address token) internal view returns (address aToken, address variableDebtToken, bool found) {
        try uiPoolDataProvider.getReservesData(poolAddressesProvider) returns (
            IUiPoolDataProviderV3.AggregatedReserveData[] memory reserves,
            IUiPoolDataProviderV3.BaseCurrencyInfo memory
        ) {
            for (uint256 i = 0; i < reserves.length; i++) {
                if (reserves[i].underlyingAsset == token) {
                    return (reserves[i].aTokenAddress, reserves[i].variableDebtTokenAddress, true);
                }
            }
        } catch {
        }
        return (address(0), address(0), false);
    }

    /// @dev Internal helper to fetch balance from reserve data, with fallback to user reserves.
    function _getBalanceFromReserveData(
        IUiPoolDataProviderV3.AggregatedReserveData memory reserve,
        address user,
        IUiPoolDataProviderV3.UserReserveData[] memory userReserves
    ) internal view returns (uint256) {
        if (reserve.aTokenAddress != address(0)) {
            try IERC20(reserve.aTokenAddress).balanceOf(user) returns (uint256 bal) {
                return bal;
            } catch {
                // Fallback below
            }
        }
        for (uint256 i = 0; i < userReserves.length; i++) {
            if (userReserves[i].underlyingAsset == reserve.underlyingAsset) {
                return userReserves[i].scaledATokenBalance;
            }
        }
        return 0;
    }

    /// @dev Internal helper to fetch borrow balance from reserve data, with fallback to user reserves.
    function _getBorrowBalanceFromReserveData(
        IUiPoolDataProviderV3.AggregatedReserveData memory reserve,
        address user,
        IUiPoolDataProviderV3.UserReserveData[] memory userReserves
    ) internal view returns (uint256) {
        if (reserve.variableDebtTokenAddress != address(0)) {
            try IERC20(reserve.variableDebtTokenAddress).balanceOf(user) returns (uint256 bal) {
                return bal;
            } catch {
                // Fallback below
            }
        }
        for (uint256 i = 0; i < userReserves.length; i++) {
            if (userReserves[i].underlyingAsset == reserve.underlyingAsset) {
                return userReserves[i].scaledVariableDebt;
            }
        }
        return 0;
    }

    function getPossibleCollaterals(address token, address user) external view returns (
        address[] memory collateralAddresses,
        uint256[] memory balances,
        string[] memory symbols,
        uint8[] memory decimals
    ) {
        // If user is zero address, get all possible tokens but with zero balances
        if (user == address(0)) {
            (IUiPoolDataProviderV3.AggregatedReserveData[] memory reserves,) = uiPoolDataProvider.getReservesData(poolAddressesProvider);

            // Initialize arrays with all reserves
            uint256 tokenCountX = reserves.length;
            collateralAddresses = new address[](tokenCountX);
            balances = new uint256[](tokenCountX); // All zeros by default
            symbols = new string[](tokenCountX);
            decimals = new uint8[](tokenCountX);

            // Fill arrays with token data (zero balances)
            for (uint256 i = 0; i < tokenCountX; i++) {
                collateralAddresses[i] = reserves[i].underlyingAsset;
                symbols[i] = reserves[i].symbol;
                // Get decimals directly from token
                try ERC20(reserves[i].underlyingAsset).decimals() returns (uint8 dec) {
                    decimals[i] = dec;
                } catch {
                    decimals[i] = 18; // Default to 18 if call fails
                }
            }

            return (collateralAddresses, balances, symbols, decimals);
        }

        // For connected wallets, get all tokens with actual balances
        TokenInfo[] memory allTokens = this.getAllTokensInfo(user);

        // Count tokens with non-zero balance
        uint256 tokenCount = 0;
        for (uint256 i = 0; i < allTokens.length; i++) {
            if (allTokens[i].balance > 0) {
                tokenCount++;
            }
        }

        // Initialize arrays with the correct size
        collateralAddresses = new address[](tokenCount);
        balances = new uint256[](tokenCount);
        symbols = new string[](tokenCount);
        decimals = new uint8[](tokenCount);

        // Fill arrays with tokens that have balance
        uint256 index = 0;
        for (uint256 i = 0; i < allTokens.length; i++) {
            if (allTokens[i].balance > 0) {
                collateralAddresses[index] = allTokens[i].token;
                balances[index] = allTokens[i].balance;
                symbols[index] = allTokens[i].symbol;
                try ERC20(allTokens[i].token).decimals() returns (uint8 dec) {
                    decimals[index] = dec;
                } catch {
                    decimals[index] = 18; // Default to 18 if call fails
                }
                index++;
            }
        }
        return (collateralAddresses, balances, symbols, decimals);
    }

    function getAToken(address underlyingToken) public view returns (address) {
        IPoolDataProvider dataProvider = IPoolDataProvider(IPoolAddressesProvider(poolAddressesProvider).getPoolDataProvider());
        (address aTokenAddress, , ) = dataProvider.getReserveTokensAddresses(underlyingToken);
        return aTokenAddress;
    }

    function getUnderlyingToken(address aToken) external view returns (address) {
        IPoolDataProvider dataProvider = IPoolDataProvider(IPoolAddressesProvider(poolAddressesProvider).getPoolDataProvider());
        (address underlyingToken, , ) = dataProvider.getReserveTokensAddresses(aToken);
        return underlyingToken;
    }

    function isCollateralSupported(address market, address collateral) external view returns (bool isSupported) {
        // In Aave, we need to check if the token is a supported reserve and if it can be used as collateral
        (IUiPoolDataProviderV3.AggregatedReserveData[] memory reserves,) = uiPoolDataProvider.getReservesData(poolAddressesProvider);

        for (uint256 i = 0; i < reserves.length; i++) {
            if (reserves[i].underlyingAsset == collateral) {
                // Check if the token can be used as collateral in Aave
                return reserves[i].usageAsCollateralEnabled;
            }
        }

        return false;
    }

    function getSupportedCollaterals(address market) external view returns (address[] memory collateralAddresses) {
        // Get all Aave reserves
        (IUiPoolDataProviderV3.AggregatedReserveData[] memory reserves,) = uiPoolDataProvider.getReservesData(poolAddressesProvider);

        // Count eligible collaterals
        uint256 collateralCount = 0;
        for (uint256 i = 0; i < reserves.length; i++) {
            if (reserves[i].usageAsCollateralEnabled) {
                collateralCount++;
            }
        }

        // Create and populate array with eligible collaterals
        collateralAddresses = new address[](collateralCount);
        uint256 index = 0;
        for (uint256 i = 0; i < reserves.length; i++) {
            if (reserves[i].usageAsCollateralEnabled) {
                collateralAddresses[index] = reserves[i].underlyingAsset;
                index++;
            }
        }

        return collateralAddresses;
    }
}

