// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/// @title GPv2Order - CoW Protocol order library
/// @notice Data structures and helpers for CoW Protocol orders
library GPv2Order {
    /// @dev The complete order data that is signed by the user
    struct Data {
        IERC20 sellToken;
        IERC20 buyToken;
        address receiver;
        uint256 sellAmount;
        uint256 buyAmount;
        uint32 validTo;
        bytes32 appData;
        uint256 feeAmount;
        bytes32 kind;
        bool partiallyFillable;
        bytes32 sellTokenBalance;
        bytes32 buyTokenBalance;
    }

    /// @dev Order kind constants
    bytes32 internal constant KIND_SELL =
        keccak256("sell");
    bytes32 internal constant KIND_BUY =
        keccak256("buy");

    /// @dev Token balance constants
    bytes32 internal constant BALANCE_ERC20 =
        keccak256("erc20");
    bytes32 internal constant BALANCE_EXTERNAL =
        keccak256("external");
    bytes32 internal constant BALANCE_INTERNAL =
        keccak256("internal");

    /// @dev The order EIP-712 type hash
    bytes32 internal constant TYPE_HASH =
        keccak256(
            "Order("
            "address sellToken,"
            "address buyToken,"
            "address receiver,"
            "uint256 sellAmount,"
            "uint256 buyAmount,"
            "uint32 validTo,"
            "bytes32 appData,"
            "uint256 feeAmount,"
            "string kind,"
            "bool partiallyFillable,"
            "string sellTokenBalance,"
            "string buyTokenBalance"
            ")"
        );

    /// @dev Computes the order hash for the given order data
    /// @param order The order to hash
    /// @param domainSeparator The EIP-712 domain separator
    /// @return orderDigest The order digest
    function hash(Data memory order, bytes32 domainSeparator) internal pure returns (bytes32 orderDigest) {
        bytes32 structHash = keccak256(
            abi.encode(
                TYPE_HASH,
                order.sellToken,
                order.buyToken,
                order.receiver,
                order.sellAmount,
                order.buyAmount,
                order.validTo,
                order.appData,
                order.feeAmount,
                order.kind,
                order.partiallyFillable,
                order.sellTokenBalance,
                order.buyTokenBalance
            )
        );
        orderDigest = keccak256(abi.encodePacked("\x19\x01", domainSeparator, structHash));
    }
}

contracts/v2/interfaces/cow/IComposableC.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { IConditionalOrder } from "./IConditionalOrder.sol";
import { GPv2Order } from "./GPv2Order.sol";

/// @title IComposableCoW - Interface for the ComposableCoW contract
/// @notice Registry for conditional orders on CoW Protocol
interface IComposableCoW {
    /// @dev Proof location for merkle roots
    struct Proof {
        uint256 location;
        bytes data;
    }

    /// @dev Payload passed for signature verification
    struct PayloadStruct {
        bytes32[] proof;
        IConditionalOrder.ConditionalOrderParams params;
        bytes offchainInput;
    }

    // ============ Events ============

    /// @notice Emitted when a conditional order is created
    event ConditionalOrderCreated(
        address indexed owner,
        IConditionalOrder.ConditionalOrderParams params
    );

    /// @notice Emitted when a merkle root is set
    event MerkleRootSet(
        address indexed owner,
        bytes32 root,
        Proof proof
    );

    // ============ State Variables ============

    /// @notice Get the merkle root for an owner
    function roots(address owner) external view returns (bytes32);

    /// @notice Check if a single order exists
    function singleOrders(address owner, bytes32 singleOrderHash) external view returns (bool);

    /// @notice Get order-specific storage
    function cabinet(address owner, bytes32 ctx) external view returns (bytes32);

    // ============ User Functions ============

    /// @notice Create a single conditional order
    /// @param params The conditional order parameters
    /// @param dispatch If true, emit ConditionalOrderCreated event
    function create(
        IConditionalOrder.ConditionalOrderParams calldata params,
        bool dispatch
    ) external;

    /// @notice Create a conditional order with context
    /// @param params The conditional order parameters
    /// @param factory Factory to generate context value
    /// @param data Data passed to factory
    /// @param dispatch If true, emit ConditionalOrderCreated event
    function createWithContext(
        IConditionalOrder.ConditionalOrderParams calldata params,
        IValueFactory factory,
        bytes calldata data,
        bool dispatch
    ) external;

    /// @notice Remove a single conditional order
    /// @param singleOrderHash Hash of the order to remove: H(ConditionalOrderParams)
    function remove(bytes32 singleOrderHash) external;

    /// @notice Set the merkle root for conditional orders
    /// @param root The merkle root
    /// @param proof Proof data for the root
    function setRoot(bytes32 root, Proof calldata proof) external;

    /// @notice Set the merkle root with context
    /// @param root The merkle root
    /// @param proof Proof data for the root
    /// @param factory Factory to generate context value
    /// @param data Data passed to factory
    function setRootWithContext(
        bytes32 root,
        Proof calldata proof,
        IValueFactory factory,
        bytes calldata data
    ) external;

    /// @notice Set cabinet value for an order
    /// @param ctx The context (order hash)
    /// @param value The value to store
    function setCabinet(bytes32 ctx, bytes32 value) external;

    // ============ Watch-tower Functions ============

    /// @notice Get a tradeable order with signature for submission to CoW API
    /// @param owner The owner of the conditional order
    /// @param params The conditional order parameters
    /// @param offchainInput Off-chain provided data
    /// @param proof Merkle proof (empty for single orders)
    /// @return order The tradeable GPv2Order
    /// @return signature The ERC-1271 signature for the order
    function getTradeableOrderWithSignature(
        address owner,
        IConditionalOrder.ConditionalOrderParams calldata params,
        bytes calldata offchainInput,
        bytes32[] calldata proof
    ) external view returns (GPv2Order.Data memory order, bytes memory signature);

    /// @notice Compute the hash of conditional order params
    /// @param params The conditional order parameters
    /// @return The keccak256 hash
    function hash(IConditionalOrder.ConditionalOrderParams memory params) external pure returns (bytes32);
}

/// @title IValueFactory - Interface for context value factories
interface IValueFactory {
    function getValue(bytes calldata data) external view returns (bytes32);
}

contracts/v2/interfaces/cow/IConditional.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { GPv2Order } from "./GPv2Order.sol";

/// @title IConditionalOrder - Interface for conditional order handlers
/// @notice Defines the interface for creating conditional/programmatic orders on CoW Protocol
interface IConditionalOrder {
    /// @dev Conditional order parameters stored on-chain
    struct ConditionalOrderParams {
        IConditionalOrder handler;
        bytes32 salt;
        bytes staticData;
    }

    /// @notice Verifies that a discrete order is valid for the conditional order
    /// @param owner The owner of the conditional order
    /// @param sender The msg.sender calling isValidSignature
    /// @param _hash The EIP-712 order digest
    /// @param domainSeparator The EIP-712 domain separator
    /// @param ctx The execution context (H(params) for single, merkle root for tree)
    /// @param staticInput Order-specific data known at creation time
    /// @param offchainInput Order-specific data NOT known at creation time
    /// @param order The proposed discrete order
    function verify(
        address owner,
        address sender,
        bytes32 _hash,
        bytes32 domainSeparator,
        bytes32 ctx,
        bytes calldata staticInput,
        bytes calldata offchainInput,
        GPv2Order.Data calldata order
    ) external view;
}

/// @title IConditionalOrderGenerator - Interface for conditional order generators
/// @notice Extends IConditionalOrder to generate tradeable orders
interface IConditionalOrderGenerator is IConditionalOrder {
    /// @notice Generates a tradeable GPv2Order based on the current state
    /// @param owner The owner of the conditional order
    /// @param sender The msg.sender (usually ComposableCoW)
    /// @param ctx The execution context
    /// @param staticInput Order-specific data known at creation time
    /// @param offchainInput Order-specific data provided by the watch-tower
    /// @return order The generated GPv2Order.Data struct
    function getTradeableOrder(
        address owner,
        address sender,
        bytes32 ctx,
        bytes calldata staticInput,
        bytes calldata offchainInput
    ) external view returns (GPv2Order.Data memory order);
}

/// @title Conditional Order Errors
/// @notice Custom errors for signaling to watch-towers

/// @dev Signal to try polling again at the next block
error PollTryNextBlock(string reason);

/// @dev Signal to try polling at a specific block
error PollTryAtBlock(uint256 blockNumber, string reason);

/// @dev Signal to try polling at a specific timestamp
error PollTryAtEpoch(uint256 timestamp, string reason);

/// @dev Signal to stop polling this order (completed or cancelled)
error PollNever(string reason);

/// @dev Signal that the order parameters are invalid
error OrderNotValid(string reason);

/// @dev Signal that the order is not currently tradeable
error OrderNotReady(string reason);

