// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {ICompoundComet} from "../../interfaces/compound/ICompoundComet.sol";
import {AggregatorV3Interface} from "@chainlink/contracts/src/v0.8/shared/interfaces/AggregatorV3Interface.sol";
import {ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";

/// @notice Interface for reading from write gateway
interface IWriteGateway {
    function tokenToComet(address) external view returns (ICompoundComet);
    function allComets() external view returns (address[] memory);
}

/**
 * @title CompoundGatewayView
 * @notice View-only gateway for Compound Comet protocol
 * @dev Contains all read/view functions from v1, separate from write operations
 */
contract CompoundGatewayView is Ownable {
    // base token => Comet
    mapping(address => ICompoundComet) public tokenToComet;
    mapping(address => AggregatorV3Interface) public overrideFeeds;

    // registry for view
    address[] private _comets;
    mapping(address => bool) private _isRegistered;

    // Optional reference to write gateway for syncing Comet registry
    address public writeGateway;

    event CometRegistered(address indexed baseToken, address indexed comet);
    event CometReplaced(address indexed baseToken, address indexed oldComet, address indexed newComet);
    event WriteGatewayUpdated(address indexed oldGateway, address indexed newGateway);

    constructor(address owner_) Ownable(owner_) {}

    modifier whenCometExists(address token) {
        if (address(tokenToComet[token]) != address(0)) {
            _;
        }
    }

    modifier cometMustExist(address token) {
        require(address(tokenToComet[token]) != address(0), "Comet is not set");
        _;
    }

    function addComet(ICompoundComet comet) external onlyOwner {
        address base = comet.baseToken();
        require(base != address(0), "Compound: base=0");
        address prev = address(tokenToComet[base]);

        tokenToComet[base] = comet;
        if (!_isRegistered[address(comet)]) { _isRegistered[address(comet)] = true; _comets.push(address(comet)); }
        emit CometRegistered(base, address(comet));
        if (prev != address(0) && prev != address(comet)) emit CometReplaced(base, prev, address(comet));
    }

    function setCometForBase(address baseToken, address comet_) external onlyOwner {
        require(baseToken != address(0) && comet_ != address(0), "Compound: zero");
        address prev = address(tokenToComet[baseToken]);
        tokenToComet[baseToken] = ICompoundComet(comet_);
        if (!_isRegistered[comet_]) { _isRegistered[comet_] = true; _comets.push(comet_); }
        emit CometRegistered(baseToken, comet_);
        if (prev != address(0) && prev != comet_) emit CometReplaced(baseToken, prev, comet_);
    }

    function allComets() external view returns (address[] memory) { return _comets; }

    /// @notice Get all base tokens for registered Comets
    function allBaseTokens() external view returns (address[] memory) {
        address[] memory baseTokens = new address[](_comets.length);
        for (uint256 i = 0; i < _comets.length; i++) {
            baseTokens[i] = ICompoundComet(_comets[i]).baseToken();
        }
        return baseTokens;
    }

    /// @notice Get all active Comets (union of local + write gateway registries)
    function allActiveComets() public view returns (address[] memory comets) {
        address[] memory local = _comets;
        address[] memory remote = writeGateway == address(0)
            ? new address[](0)
            : IWriteGateway(writeGateway).allComets();

        // Upper bound, then de-dupe
        comets = new address[](local.length + remote.length);
        uint256 k = 0;

        for (uint256 i = 0; i < local.length; i++) comets[k++] = local[i];

        for (uint256 j = 0; j < remote.length; j++) {
            address c = remote[j];
            bool seen = false;
            for (uint256 i = 0; i < k; i++) {
                if (comets[i] == c) { seen = true; break; }
            }
            if (!seen) comets[k++] = c;
        }
        assembly { mstore(comets, k) } // shrink
    }

    /// @notice Get all active base tokens (from allActiveComets)
    function allActiveBaseTokens() external view returns (address[] memory bases) {
        address[] memory comets = allActiveComets();
        bases = new address[](comets.length);
        for (uint256 i = 0; i < comets.length; i++) {
            bases[i] = ICompoundComet(comets[i]).baseToken();
        }
    }

    function overrideFeed(address token, AggregatorV3Interface feed) external onlyOwner {
        overrideFeeds[token] = feed;
    }

    /// @notice Set the write gateway address for syncing Comet registry
    function setWriteGateway(address writeGateway_) external onlyOwner {
        address oldGateway = writeGateway;
        writeGateway = writeGateway_;
        emit WriteGatewayUpdated(oldGateway, writeGateway_);
    }

    /// @notice Sync a Comet from the write gateway to this view gateway
    function syncCometFromWriteGateway(address baseToken) external onlyOwner {
        require(writeGateway != address(0), "Compound: write gateway not set");
        ICompoundComet cometFromWrite = IWriteGateway(writeGateway).tokenToComet(baseToken);
        require(address(cometFromWrite) != address(0), "Compound: comet not in write gateway");

        // Register in this view gateway - inline the logic to avoid ordering issues
        address prev = address(tokenToComet[baseToken]);
        tokenToComet[baseToken] = cometFromWrite;
        if (!_isRegistered[address(cometFromWrite)]) {
            _isRegistered[address(cometFromWrite)] = true;
            _comets.push(address(cometFromWrite));
        }
        emit CometRegistered(baseToken, address(cometFromWrite));
        if (prev != address(0) && prev != address(cometFromWrite)) {
            emit CometReplaced(baseToken, prev, address(cometFromWrite));
        }
    }

    /// @notice Sync all Comets from write gateway
    function syncAllCometsFromWriteGateway() external onlyOwner {
        require(writeGateway != address(0), "Compound: write gateway not set");
        address[] memory writeComets = IWriteGateway(writeGateway).allComets();

        // For each comet, sync it
        for (uint256 i = 0; i < writeComets.length; i++) {
            ICompoundComet comet = ICompoundComet(writeComets[i]);
            address baseToken = comet.baseToken();
            if (address(tokenToComet[baseToken]) != address(comet)) {
                // Inline the logic to avoid ordering issues
                address prev = address(tokenToComet[baseToken]);
                tokenToComet[baseToken] = comet;
                if (!_isRegistered[address(comet)]) {
                    _isRegistered[address(comet)] = true;
                    _comets.push(address(comet));
                }
                emit CometRegistered(baseToken, address(comet));
                if (prev != address(0) && prev != address(comet)) {
                    emit CometReplaced(baseToken, prev, address(comet));
                }
            }
        }
    }

    /// @notice Get Comet for base token, falling back to write gateway if not found locally
    function getComet(address baseToken) public view returns (ICompoundComet) {
        ICompoundComet local = tokenToComet[baseToken];
        if (address(local) != address(0)) {
            return local;
        }
        // Fallback to write gateway if set
        if (writeGateway != address(0)) {
            try IWriteGateway(writeGateway).tokenToComet(baseToken) returns (ICompoundComet comet) {
                if (address(comet) != address(0)) {
                    return comet;
                }
            } catch {
                // Fall through to revert
            }
        }
        revert("Compound: comet not found");
    }

    function getSupplyRate(address token) external view whenCometExists(token) returns (uint256 supplyRate, bool success) {
        ICompoundComet comet = getComet(token);
        supplyRate = comet.getSupplyRate(comet.getUtilization());
        success = true;
    }

    function getBorrowRate(address token) external view whenCometExists(token) returns (uint256 borrowRate, bool success) {
        ICompoundComet comet = getComet(token);
        borrowRate = comet.getBorrowRate(comet.getUtilization());
        success = true;
    }

    function getBaseToken(ICompoundComet comet) external view returns (address) {
        return comet.baseToken();
    }

    function getBalance(address token, address user) external view returns (uint256) {
        ICompoundComet comet = getComet(token);
        return comet.balanceOf(user);
    }

    function getBorrowBalance(address token, address user) public view returns (uint256) {
        ICompoundComet comet = getComet(token);
        return comet.borrowBalanceOf(user);
    }

    function getBorrowBalanceCurrent(address token, address user) external view returns (uint256) {
        return getBorrowBalance(token, user);
    }

    function getLtv(address /* token */, address /* user */) external pure returns (uint256) {
        // TODO: Implement
        return 0;
    }

    function getMaxLtv(address /* token */) external pure returns (uint256) {
        // TODO: Implement
        return 0;
    }

    function getPossibleCollaterals(address token, address user) external view returns (
        address[] memory collateralAddresses,
        uint256[] memory balances,
        string[] memory symbols,
        uint8[] memory decimals
    ) {
        (address[] memory collaterals, uint128[] memory rawBalances, string[] memory displayNames) = getDepositedCollaterals(token, user);

        // Count non-zero balances
        uint256 nonZeroCount = 0;
        for (uint256 i = 0; i < rawBalances.length; i++) {
            if (rawBalances[i] > 0) {
                nonZeroCount++;
            }
        }

        // Create new arrays with only non-zero balances
        collateralAddresses = new address[](nonZeroCount);
        balances = new uint256[](nonZeroCount);
        symbols = new string[](nonZeroCount);
        decimals = new uint8[](nonZeroCount);

        // Fill arrays with non-zero balance entries
        uint256 j = 0;
        for (uint256 i = 0; i < rawBalances.length; i++) {
            if (rawBalances[i] > 0) {
                collateralAddresses[j] = collaterals[i];
                balances[j] = uint256(rawBalances[i]);
                symbols[j] = displayNames[i];
                decimals[j] = IERC20Metadata(collaterals[i]).decimals();
                j++;
            }
        }
    }

    function getDepositedCollaterals(address token, address account)
        public
        view
        returns (address[] memory collaterals, uint128[] memory balances, string[] memory displayNames)
    {
        ICompoundComet comet = getComet(token);
        uint8 n = comet.numAssets();        
        // Allocate arrays of the correct size.
        collaterals = new address[](n);
        displayNames = new string[](n);
        balances = new uint128[](n);

        uint256 index = 0;
        // Populate the arrays with assets that have a nonzero balance.
        for (uint8 i = 0; i < n; i++) {
            ICompoundComet.AssetInfo memory info = comet.getAssetInfo(i);
            (uint128 bal, ) = comet.userCollateral(account, info.asset);
            collaterals[index] = info.asset;
            displayNames[index] = ERC20(info.asset).symbol();
            balances[index] = bal;
            index++;
        }
    }

    function getPrice(address token) public view returns (uint256) {
        if (address(overrideFeeds[token]) != address(0))   {
            (, int256 price,,,) = overrideFeeds[token].latestRoundData();
            return uint256(price);
        }

        ICompoundComet comet = getComet(token);
        address theirFeed = comet.baseTokenPriceFeed();
        if (theirFeed != address(0)) {
            return comet.getPrice(theirFeed);
        }

        return 0;
    }

    function getCollateralPrice(address market, address asset) public view returns (uint256) {
        ICompoundComet comet = getComet(market);
        ICompoundComet.AssetInfo memory info = comet.getAssetInfoByAddress(asset);
        return comet.getPrice(info.priceFeed);
    }

    function getPrices(address market, address[] calldata tokens) public view returns (uint256[] memory) {
        uint256[] memory prices = new uint256[](tokens.length);

        for (uint i = 0; i < tokens.length; i++) {
            prices[i] = getCollateralPrice(market, tokens[i]);
        }

        return prices;
    }

    function getCompoundData(address token, address account)
        external
        view
        returns (
            uint256 supplyRate,
            uint256 borrowRate,
            uint256 balance,
            uint256 borrowBalance,
            uint256 price,
            uint256 priceScale
        )
    {
        ICompoundComet comet = getComet(token);
        supplyRate = comet.getSupplyRate(comet.getUtilization());
        borrowRate = comet.getBorrowRate(comet.getUtilization());
        balance = comet.balanceOf(account);
        borrowBalance = comet.borrowBalanceOf(account);
        price = getPrice(token);
        priceScale = comet.priceScale();
    }

    function isCollateralSupported(address market, address collateral) external view returns (bool isSupported) {
        ICompoundComet comet;
        try this.getComet(market) returns (ICompoundComet c) {
            comet = c;
        } catch {
            return false;
        }

        // Iterate through all assets to find if the collateral is supported
        uint8 numAssets = comet.numAssets();
        for (uint8 i = 0; i < numAssets; i++) {
            ICompoundComet.AssetInfo memory info = comet.getAssetInfo(i);
            if (info.asset == collateral) {
                return true;
            }
        }

        return false;
    }

    function getSupportedCollaterals(address market) external view returns (address[] memory collateralAddresses) {
        ICompoundComet comet;
        try this.getComet(market) returns (ICompoundComet c) {
            comet = c;
        } catch {
            return new address[](0);
        }

        uint8 numAssets = comet.numAssets();
        collateralAddresses = new address[](numAssets);

        for (uint8 i = 0; i < numAssets; i++) {
            ICompoundComet.AssetInfo memory info = comet.getAssetInfo(i);
            collateralAddresses[i] = info.asset;
        }

        return collateralAddresses;
    }
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {ProtocolGateway} from "../../../gateways/ProtocolGateway.sol";
import {IGateway} from "../../interfaces/IGateway.sol";
import {ProtocolTypes} from "../../interfaces/ProtocolTypes.sol";
import {ICompoundComet} from "../../interfaces/compound/ICompoundComet.sol";

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";

import "hardhat/console.sol";

contract CompoundGatewayWrite is IGateway, ProtocolGateway, Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    // base token => Comet
    mapping(address => ICompoundComet) public tokenToComet;

    // registry for view
    address[] private _comets;
    mapping(address => bool) private _isRegistered;

    event CometRegistered(address indexed baseToken, address indexed comet);
    event CometReplaced(address indexed baseToken, address indexed oldComet, address indexed newComet);

    constructor(address router, address owner_) ProtocolGateway(router) Ownable(owner_) {}

    function addComet(ICompoundComet comet) external onlyOwner {
        address base = comet.baseToken();
        require(base != address(0), "Compound: base=0");
        address prev = address(tokenToComet[base]);

        tokenToComet[base] = comet;
        if (!_isRegistered[address(comet)]) { _isRegistered[address(comet)] = true; _comets.push(address(comet)); }
        emit CometRegistered(base, address(comet));
        if (prev != address(0) && prev != address(comet)) emit CometReplaced(base, prev, address(comet));
    }

    function setCometForBase(address baseToken, address comet_) external onlyOwner {
        require(baseToken != address(0) && comet_ != address(0), "Compound: zero");
        address prev = address(tokenToComet[baseToken]);
        tokenToComet[baseToken] = ICompoundComet(comet_);
        if (!_isRegistered[comet_]) { _isRegistered[comet_] = true; _comets.push(comet_); }
        emit CometRegistered(baseToken, comet_);
        if (prev != address(0) && prev != comet_) emit CometReplaced(baseToken, prev, comet_);
    }

    function allComets() external view returns (address[] memory) { return _comets; }



    function processLendingInstruction(ProtocolTypes.Output[] calldata inputs, bytes calldata data)
        external
        returns (ProtocolTypes.Output[] memory outputs)
    {
        console.log("CompoundGatewayWrite: processLendingInstruction called");
        console.log("CompoundGatewayWrite: inputs count", inputs.length);
        for (uint256 i = 0; i < inputs.length; i++) {
            console.log("CompoundGatewayWrite: input[", i, "] token", uint256(uint160(inputs[i].token)));
            console.log("CompoundGatewayWrite: input[", i, "] amount", inputs[i].amount);
        }
        console.log("CompoundGatewayWrite: data length", data.length);

        ProtocolTypes.LendingInstruction memory ins = abi.decode(data, (ProtocolTypes.LendingInstruction));
        console.log("CompoundGatewayWrite: op", uint256(ins.op));
        console.log("CompoundGatewayWrite: token", uint256(uint160(ins.token)));
        console.log("CompoundGatewayWrite: user", uint256(uint160(ins.user)));
        console.log("CompoundGatewayWrite: amount", ins.amount);
        console.log("CompoundGatewayWrite: input.index", ins.input.index);

        address market = _decodeMarket(ins.context);
        console.log("CompoundGatewayWrite: market", uint256(uint160(market)));
        address token = ins.token;
        uint256 amount = ins.amount;
        if (ins.input.index < inputs.length) {
            token = inputs[ins.input.index].token;
            amount = inputs[ins.input.index].amount;
            console.log("CompoundGatewayWrite: using UTXO - token", uint256(uint160(token)));
            console.log("CompoundGatewayWrite: using UTXO - amount", amount);
        } else {
            console.log("CompoundGatewayWrite: using instruction params");
        }

        if (ins.op == ProtocolTypes.LendingOp.Deposit) {
            console.log("CompoundGatewayWrite: executing Deposit");
            if (market != address(0) && market != token) depositCollateral(market, token, amount, ins.user);
            else deposit(token, ins.user, amount);
            outputs = new ProtocolTypes.Output[](0);
            console.log("CompoundGatewayWrite: Deposit completed, 0 outputs");
        } else if (ins.op == ProtocolTypes.LendingOp.DepositCollateral) {
            console.log("CompoundGatewayWrite: executing DepositCollateral");
            address base = market != address(0) ? market : token;
            depositCollateral(base, token, amount, ins.user);
            outputs = new ProtocolTypes.Output[](0);
            console.log("CompoundGatewayWrite: DepositCollateral completed, 0 outputs");
        } else if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
            console.log("CompoundGatewayWrite: executing WithdrawCollateral");
            address base = market != address(0) ? market : token;
            (address u, uint256 amt) = withdrawCollateral(base, token, ins.user, amount);
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({token: u, amount: amt});
            console.log("CompoundGatewayWrite: WithdrawCollateral completed, token", uint256(uint160(u)), "amount", amt);
        } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
            console.log("CompoundGatewayWrite: executing Borrow");
            borrow(token, ins.user, amount);
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({token: token, amount: amount});
            console.log("CompoundGatewayWrite: Borrow completed, token", uint256(uint160(token)), "amount", amount);
        } else if (ins.op == ProtocolTypes.LendingOp.Repay) {
            console.log("CompoundGatewayWrite: executing Repay");
            repay(token, ins.user, amount);
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({token: token, amount: 0});
            console.log("CompoundGatewayWrite: Repay completed, 0 refund");
        } else if (ins.op == ProtocolTypes.LendingOp.GetBorrowBalance) {
            console.log("CompoundGatewayWrite: executing GetBorrowBalance");
            ICompoundComet comet = tokenToComet[token];
            uint256 bal = address(comet) == address(0) ? 0 : comet.borrowBalanceOf(ins.user);
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({token: token, amount: bal});
            console.log("CompoundGatewayWrite: GetBorrowBalance completed, balance", bal);
        } else if (ins.op == ProtocolTypes.LendingOp.GetSupplyBalance) {
            console.log("CompoundGatewayWrite: executing GetSupplyBalance");
            uint256 bal;
            if (market != address(0) && market != token) {
                ICompoundComet comet = tokenToComet[market];
                bal = address(comet) == address(0) ? 0 : uint256(comet.collateralBalanceOf(ins.user, token));
            } else {
                ICompoundComet comet = tokenToComet[token];
                bal = address(comet) == address(0) ? 0 : comet.balanceOf(ins.user);
            }
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({token: token, amount: bal});
            console.log("CompoundGatewayWrite: GetSupplyBalance completed, balance", bal);
        } else {
            console.log("CompoundGatewayWrite: ERROR - unknown op", uint256(ins.op));
            revert("Compound: unknown op");
        }
    }

    function deposit(address token, address onBehalfOf, uint256 amount) public onlyRouter nonReentrant {
        ICompoundComet comet = tokenToComet[token];
        require(address(comet) != address(0), "Compound: base comet not found");
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(token).approve(address(comet), 0);
        IERC20(token).approve(address(comet), amount);
        comet.supplyTo(onBehalfOf, token, amount);
    }

    function depositCollateral(address market, address collateral, uint256 amount, address receiver) public onlyRouter nonReentrant {
        ICompoundComet comet = tokenToComet[market];
        require(address(comet) != address(0), "Compound: market comet not found");
        IERC20(collateral).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(collateral).approve(address(comet), 0);
        IERC20(collateral).approve(address(comet), amount);
        comet.supplyTo(receiver, collateral, amount);
    }

    function withdrawCollateral(address market, address collateral, address user, uint256 amount)
        public onlyRouterOrSelf(user) nonReentrant returns (address, uint256)
    {
        ICompoundComet comet = tokenToComet[market];
        require(address(comet) != address(0), "Compound: market comet not found");
        comet.withdrawFrom(user, address(this), collateral, amount);
        IERC20(collateral).safeTransfer(msg.sender, amount);
        return (collateral, amount);
    }

    function borrow(address token, address user, uint256 amount) public onlyRouterOrSelf(user) nonReentrant {
        ICompoundComet comet = tokenToComet[token];
        require(address(comet) != address(0), "Compound: base comet not found");
        comet.withdrawFrom(user, address(this), token, amount);
        IERC20(token).safeTransfer(msg.sender, amount);
    }

    function repay(address token, address user, uint256 amount) public onlyRouter nonReentrant {
        ICompoundComet comet = tokenToComet[token];
        require(address(comet) != address(0), "Compound: base comet not found");
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(token).approve(address(comet), 0);
        IERC20(token).approve(address(comet), amount);
        comet.supplyTo(user, token, amount);
    }

    function authorize(ProtocolTypes.LendingInstruction[] calldata instrs, address caller)
        external view returns (address[] memory targets, bytes[] memory data)
    {
        uint256 count;
        for (uint256 i; i < instrs.length; i++) {
            ProtocolTypes.LendingInstruction calldata ins = instrs[i];
            address market = _decodeMarket(ins.context);
            if (ins.op == ProtocolTypes.LendingOp.Deposit || ins.op == ProtocolTypes.LendingOp.Repay) count++;
            else if (ins.op == ProtocolTypes.LendingOp.DepositCollateral || (ins.op == ProtocolTypes.LendingOp.Deposit && market != address(0) && market != ins.token)) count++;
            else if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral || ins.op == ProtocolTypes.LendingOp.Borrow) count++;
        }
        targets = new address[](count); data = new bytes[](count); uint256 k;
        for (uint256 i; i < instrs.length; i++) {
            ProtocolTypes.LendingInstruction calldata ins = instrs[i];
            address market = _decodeMarket(ins.context);
            if (ins.op == ProtocolTypes.LendingOp.Deposit || ins.op == ProtocolTypes.LendingOp.Repay) {
                // Approve underlying to gateway only if insufficient allowance
                uint256 cur = IERC20(ins.token).allowance(caller, address(this));
                if (ins.amount != 0 && cur >= ins.amount) { targets[k] = address(0); data[k] = bytes(""); }
                else { targets[k] = ins.token; data[k] = abi.encodeWithSelector(IERC20.approve.selector, address(this), ins.amount); }
                k++;
            } else if (ins.op == ProtocolTypes.LendingOp.DepositCollateral || (ins.op == ProtocolTypes.LendingOp.Deposit && market != address(0) && market != ins.token)) {
                address col = ins.token;
                uint256 cur = IERC20(col).allowance(caller, address(this));
                if (ins.amount != 0 && cur >= ins.amount) { targets[k] = address(0); data[k] = bytes(""); }
                else { targets[k] = col; data[k] = abi.encodeWithSelector(IERC20.approve.selector, address(this), ins.amount); }
                k++;
            } else if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
                address base = market != address(0) ? market : ins.token; ICompoundComet comet = tokenToComet[base];
                bool permitted = comet.isAllowed(caller, address(this));
                if (permitted) { targets[k] = address(0); data[k] = bytes(""); }
                else { targets[k] = address(comet); data[k] = abi.encodeWithSelector(ICompoundComet.allow.selector, address(this), true); }
                k++;
            } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
                ICompoundComet comet = tokenToComet[ins.token];
                bool permitted = comet.isAllowed(caller, address(this));
                if (permitted) { targets[k] = address(0); data[k] = bytes(""); }
                else { targets[k] = address(comet); data[k] = abi.encodeWithSelector(ICompoundComet.allow.selector, address(this), true); }
                k++;
            }
        }
    }

    function _decodeMarket(bytes memory ctx) internal pure returns (address market) {
        if (ctx.length >= 32) { assembly { market := mload(add(ctx, 32)) } }
    }
}

contracts/v2/gateways/venus/VenusGateway.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import {ProtocolGateway} from "../../../gateways/ProtocolGateway.sol";
import {IGateway} from "../../interfaces/IGateway.sol";
import {ProtocolTypes} from "../../interfaces/ProtocolTypes.sol";

import {ComptrollerInterface} from "../../../interfaces/venus/ComptrollerInterface.sol";
import {VTokenInterface} from "../../../interfaces/venus/VTokenInterface.sol";

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";

contract VenusGatewayWrite is IGateway, ProtocolGateway, Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    ComptrollerInterface public comptroller;

    constructor(address _comptroller, address router, address owner_) ProtocolGateway(router) Ownable(owner_) {
        comptroller = ComptrollerInterface(_comptroller);
    }

    function setComptroller(address _comptroller) external onlyOwner { comptroller = ComptrollerInterface(_comptroller); }



    function processLendingInstruction(ProtocolTypes.Output[] calldata inputs, bytes calldata data)
        external
        returns (ProtocolTypes.Output[] memory outputs)
    {
        ProtocolTypes.LendingInstruction memory ins = abi.decode(data, (ProtocolTypes.LendingInstruction));
        address token = ins.token; uint256 amount = ins.amount;
        if (ins.input.index < inputs.length) { token = inputs[ins.input.index].token; amount = inputs[ins.input.index].amount; }
        if (ins.op == ProtocolTypes.LendingOp.Deposit || ins.op == ProtocolTypes.LendingOp.DepositCollateral) {
            deposit(token, ins.user, amount); outputs = new ProtocolTypes.Output[](0);
        } else if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
            (address u, uint256 amt) = withdrawCollateral(token, ins.user, amount);
            outputs = new ProtocolTypes.Output[](1); outputs[0] = ProtocolTypes.Output({token: u, amount: amt});
        } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
            borrow(token, ins.user, amount); outputs = new ProtocolTypes.Output[](1); outputs[0] = ProtocolTypes.Output({token: token, amount: amount});
        } else if (ins.op == ProtocolTypes.LendingOp.Repay) {
            uint256 refund = repay(token, ins.user, amount); outputs = new ProtocolTypes.Output[](1); outputs[0] = ProtocolTypes.Output({token: token, amount: refund});
        } else if (ins.op == ProtocolTypes.LendingOp.GetBorrowBalance) {
            address vToken = _getVTokenForUnderlying(token); uint256 bal = VTokenInterface(vToken).borrowBalanceCurrent(ins.user);
            outputs = new ProtocolTypes.Output[](1); outputs[0] = ProtocolTypes.Output({token: token, amount: bal});
        } else if (ins.op == ProtocolTypes.LendingOp.GetSupplyBalance) {
            address vToken = _getVTokenForUnderlying(token); uint256 vBal = VTokenInterface(vToken).balanceOf(ins.user); uint256 rate = VTokenInterface(vToken).exchangeRateStored();
            uint256 bal = (vBal * rate) / 1e18; outputs = new ProtocolTypes.Output[](1); outputs[0] = ProtocolTypes.Output({token: token, amount: bal});
        } else { revert("Venus: unknown op"); }
    }

    function deposit(address token, address user, uint256 amount) public onlyRouter nonReentrant {
        address vToken = _getVTokenForUnderlying(token);
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(token).approve(vToken, 0);
        IERC20(token).approve(vToken, amount);
        uint err = VTokenInterface(vToken).mint(amount); require(err == 0, "Venus: mint failed");
        uint vBal = VTokenInterface(vToken).balanceOf(address(this)); require(vBal > 0, "Venus: zero vTokens");
        VTokenInterface(vToken).transfer(user, vBal);
    }

    function withdrawCollateral(address collateral, address user, uint256 underlyingAmount)
        public onlyRouterOrSelf(user) nonReentrant returns (address, uint256)
    {
        address vToken = _getVTokenForUnderlying(collateral);
        uint exchangeRate = VTokenInterface(vToken).exchangeRateCurrent();
        uint requiredV = (underlyingAmount * 1e18 + exchangeRate - 1) / exchangeRate;
        VTokenInterface(vToken).transferFrom(user, address(this), requiredV);
        uint err = VTokenInterface(vToken).redeem(requiredV); require(err == 0, "Venus: redeem failed");
        IERC20(collateral).safeTransfer(msg.sender, underlyingAmount);
        return (collateral, underlyingAmount);
    }

    function borrow(address token, address user, uint256 amount) public onlyRouterOrSelf(user) nonReentrant {
        address vToken = _getVTokenForUnderlying(token);
        uint err = VTokenInterface(vToken).borrowBehalf(user, amount); require(err == 0, "Venus: borrow failed");
        IERC20(token).safeTransfer(msg.sender, amount);
    }

    function repay(address token, address user, uint256 amount) public onlyRouter nonReentrant returns (uint256 refund) {
        address vToken = _getVTokenForUnderlying(token);
        uint256 pre = IERC20(token).balanceOf(address(this));
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(token).approve(vToken, 0);
        IERC20(token).approve(vToken, amount);
        uint err = VTokenInterface(vToken).repayBorrowBehalf(user, amount); require(err == 0, "Venus: repay failed");
        uint256 post = IERC20(token).balanceOf(address(this));
        refund = post > pre ? post - pre : 0;
        if (refund > 0) {
            IERC20(token).safeTransfer(msg.sender, refund);
        }
    }

    function authorize(ProtocolTypes.LendingInstruction[] calldata instrs, address caller)
        external view returns (address[] memory targets, bytes[] memory data)
    {
        uint256 count; for (uint256 i; i < instrs.length; i++) {
            ProtocolTypes.LendingInstruction calldata ins = instrs[i];
            if (ins.op == ProtocolTypes.LendingOp.Deposit || ins.op == ProtocolTypes.LendingOp.Repay) count++;
            else if (ins.op == ProtocolTypes.LendingOp.DepositCollateral) count += 2; // approve + enterMarkets
            else if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) count++;
            else if (ins.op == ProtocolTypes.LendingOp.Borrow) count++;
        }
        targets = new address[](count); data = new bytes[](count); uint256 k;
        for (uint256 i; i < instrs.length; i++) {
            ProtocolTypes.LendingInstruction calldata ins = instrs[i];
            if (ins.op == ProtocolTypes.LendingOp.Deposit || ins.op == ProtocolTypes.LendingOp.Repay) {
                // User token approval to gateway, only if insufficient
                uint256 cur = IERC20(ins.token).allowance(caller, address(this));
                if (ins.amount != 0 && cur >= ins.amount) {
                    targets[k] = address(0); data[k] = bytes("");
                } else {
                    targets[k] = ins.token; data[k] = abi.encodeWithSelector(IERC20.approve.selector, address(this), ins.amount);
                }
                k++;
            } else if (ins.op == ProtocolTypes.LendingOp.DepositCollateral) {
                address col = ins.token; address vToken = _getVTokenForUnderlying(col);
                // First: approve gateway to spend tokens (only if insufficient)
                uint256 cur = IERC20(col).allowance(caller, address(this));
                if (ins.amount != 0 && cur >= ins.amount) {
                    targets[k] = address(0); data[k] = bytes("");
                } else {
                    targets[k] = col; data[k] = abi.encodeWithSelector(IERC20.approve.selector, address(this), ins.amount);
                }
                k++;
                // Second: enter market so vTokens can be used as collateral (only if not already a member)
                bool member = false;
                try comptroller.checkMembership(caller, vToken) returns (bool m) { member = m; } catch {}
                if (member) {
                    targets[k] = address(0); data[k] = bytes("");
                } else {
                    address[] memory markets = new address[](1); markets[0] = vToken;
                    targets[k] = address(comptroller); data[k] = abi.encodeWithSelector(ComptrollerInterface.enterMarkets.selector, markets);
                }
                k++;
            } else if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
                address col = ins.token; address vToken = _getVTokenForUnderlying(col);
                // Compute required vTokens from underlying using exchangeRateStored; if amount==0, require max
                uint rate = VTokenInterface(vToken).exchangeRateStored();
                uint requiredV = ins.amount == 0 ? type(uint256).max : (ins.amount * 1e18 + rate - 1) / rate;
                uint256 curV = IERC20(vToken).allowance(caller, address(this));
                if ((ins.amount != 0 && curV >= requiredV) || (ins.amount == 0 && curV == type(uint256).max)) {
                    targets[k] = address(0); data[k] = bytes("");
                } else {
                    targets[k] = vToken; data[k] = abi.encodeWithSelector(IERC20.approve.selector, address(this), type(uint256).max);
                }
                k++;
            } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
                // Only updateDelegate if not already approved
                bool approved = false;
                try comptroller.approvedDelegates(caller, address(this)) returns (bool a) { approved = a; } catch {}
                if (approved) {
                    targets[k] = address(0); data[k] = bytes("");
                } else {
                    targets[k] = address(comptroller); data[k] = abi.encodeWithSelector(ComptrollerInterface.updateDelegate.selector, address(this), true);
                }
                k++;
            }
        }
    }

    function _getVTokenForUnderlying(address underlying) internal view returns (address) {
        VTokenInterface[] memory vTokens = comptroller.getAllMarkets();
        for (uint i = 0; i < vTokens.length; i++) {
            try vTokens[i].underlying() returns (address u) { if (u == underlying) return address(vTokens[i]); } catch {}
        }
        revert("Venus: vToken not found");
    }
}

