// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { ProtocolTypes } from "./ProtocolTypes.sol";

interface IGateway {
    function processLendingInstruction(
        ProtocolTypes.Output[] calldata inputs,
        bytes calldata data
    ) external returns (ProtocolTypes.Output[] memory outputs);

    /// @notice Return user-side authorization calls for the given lending instructions.
    ///         One element per input (or (address(0), bytes("")) if none needed).
    ///         Also returns the produced outputs from the simulation of these instructions.
    function authorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address caller,
        ProtocolTypes.Output[] calldata inputs
    ) external view returns (address[] memory targets, bytes[] memory data, ProtocolTypes.Output[] memory produced);

    /// @notice Return user-side deauthorization calls for the given lending instructions.
    ///         Used to revoke permissions after execution.
    function deauthorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address caller,
        ProtocolTypes.Output[] calldata inputs
    ) external view returns (address[] memory targets, bytes[] memory data);
}

contracts/v2/interfaces/ProtocolTypes.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

library ProtocolTypes {
    struct ProtocolInstruction {
        string protocolName;
        bytes data;
    }

    struct Output {
        address token;
        uint256 amount;
    }

    // Common lending instruction schema for gateways (deposit/withdraw/borrow/repay)
    enum LendingOp {
        Deposit,
        DepositCollateral,
        WithdrawCollateral,
        Borrow,
        Repay,
        GetBorrowBalance,
        GetSupplyBalance,
        Swap,
        SwapExactOut
    }
    struct InputPtr {
        uint256 index;
    }
    /**
     * Cross-protocol instruction.
     * - For Compound you can pass `context = abi.encode(address marketBaseToken)`
     *   which tells the gateway which Comet (market) to use. Empty means infer from token.
     */
    struct LendingInstruction {
        LendingOp op;
        address token; // underlying being acted on (base or collateral)
        address user; // end user on whose account we operate
        uint256 amount; // amount in underlying units
        bytes context; // (optional) protocol-specific blob (e.g., Compound marketBaseToken)
        InputPtr input; // (optional) pointer to prior output (UTXO); use if index < inputs.length
    }
}
