pragma solidity ^0.8.30;

import { IConditionalOrder, IConditionalOrderGenerator, PollTryNextBlock, PollNever, OrderNotValid } from "../interfaces/cow/IConditionalOrder.sol";
import { GPv2Order } from "../interfaces/cow/GPv2Order.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { IERC165 } from "@openzeppelin/contracts/utils/introspection/IERC165.sol";

import { KapanOrderManager } from "./KapanOrderManager.sol";

/// @title KapanOrderHandler
/// @notice Generates CoW Protocol orders for Kapan leveraged positions
/// @dev Implements IConditionalOrderGenerator for ComposableCoW integration
contract KapanOrderHandler is IConditionalOrderGenerator, IERC165 {

    // ============ Errors ============
    error InvalidOrderManager();
    error OrderNotActive();

    // ============ State Variables ============

    /// @notice The KapanOrderManager that stores order contexts
    KapanOrderManager public immutable orderManager;

    /// @notice Chunk window duration - each chunk is valid for this period
    /// @dev Using fixed windows ensures deterministic validTo = same order hash during window
    uint256 public constant CHUNK_WINDOW = 30 minutes;

    // ============ Constructor ============

    constructor(address _orderManager) {
        if (_orderManager == address(0)) revert InvalidOrderManager();
        orderManager = KapanOrderManager(_orderManager);
    }

    // ============ IConditionalOrderGenerator Implementation ============

    /// @notice Generate a tradeable order for the current chunk
    /// @param owner The order owner (KapanOrderManager address)
    /// @param sender The caller (usually ComposableCoW)
    /// @param ctx The execution context (unused for single orders)
    /// @param staticInput ABI-encoded orderHash
    /// @param offchainInput Off-chain provided data (unused)
    /// @return order The GPv2Order.Data struct for this chunk
    function getTradeableOrder(
        address owner,
        address sender,
        bytes32 ctx,
        bytes calldata staticInput,
        bytes calldata offchainInput
    ) external view override returns (GPv2Order.Data memory order) {
        // Silence unused variable warnings
        (owner, sender, ctx, offchainInput);

        // Decode the order hash from static input
        bytes32 orderHash = abi.decode(staticInput, (bytes32));

        // Get order context from manager
        KapanOrderManager.OrderContext memory orderCtx = orderManager.getOrder(orderHash);

        // Validate order is active
        if (orderCtx.status != KapanOrderManager.OrderStatus.Active) {
            revert PollNever("order_not_active");
        }

        // Check if order is complete
        if (orderManager.isOrderComplete(orderHash)) {
            revert PollNever("order_complete");
        }

        // Calculate remaining amount
        uint256 remaining = orderCtx.params.preTotalAmount - orderCtx.executedAmount;
        if (remaining == 0) {
            revert PollNever("fully_executed");
        }

        // Calculate this chunk's sell amount
        uint256 sellAmount = remaining < orderCtx.params.chunkSize 
            ? remaining 
            : orderCtx.params.chunkSize;

        // Calculate deterministic validTo based on chunk windows (TWAP-style)
        // This ensures the same order hash is returned during a window period,
        // preventing order spam where each poll creates a new CoW order.
        uint256 validTo = _calculateValidTo(orderCtx.createdAt, orderCtx.iterationCount);

        // ============================================================================
        // CRITICAL: RECEIVER MUST ALWAYS BE ORDER MANAGER - DO NOT CHANGE THIS!
        // ============================================================================
        // The receiver is where CoW Protocol sends the bought tokens after the swap.
        // OrderManager._executePostHook() checks balanceOf(address(this)) to determine
        // how much was received, then processes post-instructions with that amount.
        //
        // If receiver != OrderManager, the tokens go elsewhere and post-hook fails
        // because OrderManager has 0 balance of the bought token.
        //
        // DO NOT set receiver = Settlement for "autopilot balance filtering bypass".
        // That approach is BROKEN - it sends tokens to Settlement but post-hook
        // expects them at OrderManager. The correct fix for autopilot filtering
        // is in appData.flashloan.receiver (for API validation), NOT GPv2Order.receiver.
        // ============================================================================
        address receiver = address(orderManager);

        // Build the order with deterministic validTo
        // KIND_SELL: sellAmount = exact amount to sell, buyAmount = minimum to receive
        // KIND_BUY: sellAmount = maximum to sell, buyAmount = exact amount to receive
        order = GPv2Order.Data({
            sellToken: IERC20(orderCtx.params.sellToken),
            buyToken: IERC20(orderCtx.params.buyToken),
            receiver: receiver,
            sellAmount: sellAmount,
            buyAmount: orderCtx.params.minBuyPerChunk,
            validTo: uint32(validTo),
            appData: orderCtx.params.appDataHash,
            feeAmount: 0,  // Fee is taken from sellAmount in CoW Protocol
            kind: orderCtx.params.isKindBuy ? GPv2Order.KIND_BUY : GPv2Order.KIND_SELL,
            partiallyFillable: false,  // Each chunk must fill completely
            sellTokenBalance: GPv2Order.BALANCE_ERC20,
            buyTokenBalance: GPv2Order.BALANCE_ERC20
        });
    }

    /// @notice Calculate deterministic validTo timestamp for a chunk
    /// @dev Uses fixed time windows so multiple polls return the same validTo.
    ///      If chunk doesn't fill within its window, we extend to the current window.
    /// @param createdAt Order creation timestamp
    /// @param iterationCount Current chunk index (0-based)
    /// @return validTo The validTo timestamp for this chunk's order
    function _calculateValidTo(uint256 createdAt, uint256 iterationCount) internal view returns (uint256) {
        // Calculate the ideal window for this chunk
        uint256 chunkWindowStart = createdAt + (iterationCount * CHUNK_WINDOW);
        uint256 chunkWindowEnd = chunkWindowStart + CHUNK_WINDOW - 1;

        // If we're still within the chunk's ideal window, use it
        if (block.timestamp <= chunkWindowEnd) {
            return chunkWindowEnd;
        }

        // Chunk didn't fill in time - extend to current window
        // This allows the order to remain valid and retry
        uint256 elapsedSinceCreate = block.timestamp - createdAt;
        uint256 currentWindowIndex = elapsedSinceCreate / CHUNK_WINDOW;
        return createdAt + ((currentWindowIndex + 1) * CHUNK_WINDOW) - 1;
    }

    /// @notice Verify that a proposed order matches the conditional order
    /// @dev Called by ComposableCoW during signature verification
    function verify(
        address owner,
        address sender,
        bytes32 _hash,
        bytes32 domainSeparator,
        bytes32 ctx,
        bytes calldata staticInput,
        bytes calldata offchainInput,
        GPv2Order.Data calldata order
    ) external view override {
        // Silence unused variable warnings
        (owner, sender, _hash, domainSeparator, ctx, offchainInput);

        // Decode the order hash
        bytes32 orderHash = abi.decode(staticInput, (bytes32));

        // Get order context
        KapanOrderManager.OrderContext memory orderCtx = orderManager.getOrder(orderHash);

        // Validate order is active
        if (orderCtx.status != KapanOrderManager.OrderStatus.Active) {
            revert OrderNotValid("order_not_active");
        }

        // Validate tokens match
        if (address(order.sellToken) != orderCtx.params.sellToken) {
            revert OrderNotValid("sell_token_mismatch");
        }
        if (address(order.buyToken) != orderCtx.params.buyToken) {
            revert OrderNotValid("buy_token_mismatch");
        }

        // Validate receiver - MUST always be OrderManager for post-hook token handling
        // See comment in getTradeableOrder() for why this must never be Settlement
        if (order.receiver != address(orderManager)) {
            revert OrderNotValid("invalid_receiver");
        }

        // Validate amounts based on order kind
        if (orderCtx.params.isKindBuy) {
            // KIND_BUY: buyAmount must match exactly, sellAmount can be up to max
            if (order.buyAmount != orderCtx.params.minBuyPerChunk) {
                revert OrderNotValid("buy_amount_mismatch");
            }
            if (order.sellAmount > orderCtx.params.chunkSize) {
                revert OrderNotValid("sell_amount_exceeds_max");
            }
        } else {
            // KIND_SELL: sellAmount can be up to chunk size, buyAmount must meet minimum
            if (order.sellAmount > orderCtx.params.chunkSize) {
                revert OrderNotValid("sell_amount_exceeds_chunk");
            }
            if (order.buyAmount < orderCtx.params.minBuyPerChunk) {
                revert OrderNotValid("buy_amount_too_low");
            }
        }

        // Validate appData matches
        if (order.appData != orderCtx.params.appDataHash) {
            revert OrderNotValid("appdata_mismatch");
        }

        // Order is valid
    }

    // ============ View Functions ============

    /// @notice Get the current chunk parameters for an order
    /// @param orderHash The order hash
    /// @return sellAmount For KIND_SELL: exact sell amount. For KIND_BUY: max sell amount.
    /// @return buyAmount For KIND_SELL: min buy amount. For KIND_BUY: exact buy amount.
    /// @return isComplete Whether the order is complete
    /// @return isKindBuy True if this is a KIND_BUY order
    function getChunkParams(bytes32 orderHash) external view returns (
        uint256 sellAmount,
        uint256 buyAmount,
        bool isComplete,
        bool isKindBuy
    ) {
        KapanOrderManager.OrderContext memory orderCtx = orderManager.getOrder(orderHash);

        isComplete = orderManager.isOrderComplete(orderHash) || 
                     orderCtx.status != KapanOrderManager.OrderStatus.Active;

        if (isComplete) {
            return (0, 0, true, false);
        }

        uint256 remaining = orderCtx.params.preTotalAmount - orderCtx.executedAmount;
        sellAmount = remaining < orderCtx.params.chunkSize 
            ? remaining 
            : orderCtx.params.chunkSize;
        buyAmount = orderCtx.params.minBuyPerChunk;
        isKindBuy = orderCtx.params.isKindBuy;
    }

    /// @notice Get order progress
    /// @param orderHash The order hash
    /// @return executed Amount executed so far
    /// @return total Total amount to execute
    /// @return iterations Number of iterations completed
    function getProgress(bytes32 orderHash) external view returns (
        uint256 executed,
        uint256 total,
        uint256 iterations
    ) {
        KapanOrderManager.OrderContext memory orderCtx = orderManager.getOrder(orderHash);
        executed = orderCtx.executedAmount;
        total = orderCtx.params.preTotalAmount;
        iterations = orderCtx.iterationCount;
    }

    // ============ ERC-165 Implementation ============

    /// @notice Check if contract supports an interface
    /// @dev Required for ComposableCoW to verify handler compatibility
    function supportsInterface(bytes4 interfaceId) external pure override returns (bool) {
        return interfaceId == type(IConditionalOrderGenerator).interfaceId ||
               interfaceId == type(IConditionalOrder).interfaceId ||
               interfaceId == type(IERC165).interfaceId;
    }
}
","keccak256":"0xf65b0992b7d5f0477044fea3604f079448db64f19a6df77114596990b0dd992c","license":"MIT"},"contracts/v2/cow/KapanOrderManager.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import { IComposableCoW, IValueFactory } from "../interfaces/cow/IComposableCoW.sol";
import { IConditionalOrder } from "../interfaces/cow/IConditionalOrder.sol";
import { IERC1271, ERC1271_MAGIC_VALUE } from "../interfaces/cow/IERC1271.sol";
import { GPv2Order } from "../interfaces/cow/GPv2Order.sol";
import { IGPv2Settlement } from "../interfaces/cow/IGPv2Settlement.sol";
import { ProtocolTypes } from "../interfaces/ProtocolTypes.sol";

// Forward declaration for KapanRouter interface
interface IKapanRouter {
    function processProtocolInstructions(ProtocolTypes.ProtocolInstruction[] calldata instructions) external;
    function isAuthorizedFor(address user) external view returns (bool);
}

// Forward declaration for KapanCowAdapter interface
interface IKapanCowAdapter {
    function onPreHook(bytes32 orderHash) external;
    function onPostHook(bytes32 orderHash) external;
}

/// @title KapanOrderManager
/// @notice Manages CoW Protocol orders for Kapan, acting as ERC-1271 signer
/// @dev Singleton contract that holds order context and executes hooks
contract KapanOrderManager is Ownable, ReentrancyGuard, IERC1271 {
    using SafeERC20 for IERC20;

    // ============ Errors ============
    error OrderNotFound();
    error OrderAlreadyExists();
    error InvalidHandler();
    error NotHooksTrampoline();
    error HookExecutionFailed();
    error Unauthorized();
    error InvalidOrderState();
    error ZeroAddress();
    error InstructionUserMismatch(address expected, address actual);
    error PreHookAlreadyExecuted();
    error PreHookNotExecuted();
    error CannotCancelMidExecution();

    // ============ Enums ============
    enum CompletionType {
        TargetLTV,          // Complete when target LTV is reached
        TargetBalance,      // Complete when target balance is reached
        Iterations,         // Complete after N iterations
        UntilCancelled      // Run until manually cancelled
    }

    enum OrderStatus {
        None,
        Active,
        Completed,
        Cancelled
    }

    // Router instruction types (must match KapanRouter)
    enum RouterInstructionType {
        FlashLoan,
        PullToken,
        PushToken,
        ToOutput,
        Approve,
        Split,
        Add,
        Subtract
    }

    // Router instruction struct (must match KapanRouter)
    struct RouterInstruction {
        uint256 amount;
        address token;
        address user;
        RouterInstructionType instructionType;
    }

    // ============ Structs ============

    /// @notice Order parameters stored for each order
    struct KapanOrderParams {
        address user;

        // Pre-swap instructions (executed in pre-hook) - per iteration
        // Array of ABI-encoded ProtocolInstruction[] for each iteration
        // If fewer entries than iterations, last entry is reused
        bytes[] preInstructionsPerIteration;
        uint256 preTotalAmount;         // Total amount to process across all chunks

        // Swap configuration
        address sellToken;
        address buyToken;
        uint256 chunkSize;              // Max sell amount per chunk
        uint256 minBuyPerChunk;         // Minimum buy amount per chunk (slippage protection)

        // Post-swap instructions (executed in post-hook) - per iteration
        // Array of ABI-encoded ProtocolInstruction[] for each iteration
        // If fewer entries than iterations, last entry is reused
        // Typically: [0..N-2] = deposit+borrow+push, [N-1] = deposit only
        bytes[] postInstructionsPerIteration;

        // Completion
        CompletionType completion;
        uint256 targetValue;            // Interpretation depends on CompletionType
        uint256 minHealthFactor;        // Safety: minimum health factor to maintain

        // AppData (pre-computed by frontend, includes hooks)
        bytes32 appDataHash;

        // Flash loan mode: indicates order uses CoW flash loan for funding
        // NOTE: receiver is ALWAYS OrderManager regardless of this flag
        bool isFlashLoanOrder;

        // Order kind: if true, creates KIND_BUY order instead of KIND_SELL
        // KIND_SELL: chunkSize = exact sell, minBuyPerChunk = minimum buy (slippage protection)
        // KIND_BUY: chunkSize = max sell (slippage protection), minBuyPerChunk = exact buy amount
        bool isKindBuy;
    }

    /// @notice Order context stored on-chain
    struct OrderContext {
        KapanOrderParams params;
        OrderStatus status;
        uint256 executedAmount;         // Amount processed so far
        uint256 iterationCount;         // Number of completed iterations
        uint256 createdAt;
    }

    // ============ State Variables ============

    /// @notice The KapanRouter for executing lending operations
    IKapanRouter public router;

    /// @notice The ComposableCoW contract for order registration
    IComposableCoW public composableCoW;

    /// @notice The GPv2Settlement contract
    IGPv2Settlement public settlement;

    /// @notice The HooksTrampoline contract (caller for hooks)
    address public hooksTrampoline;

    /// @notice The KapanOrderHandler for generating orders
    address public orderHandler;

    /// @notice The KapanCowAdapter for flash loan order security
    IKapanCowAdapter public cowAdapter;

    /// @notice Order contexts by order hash
    mapping(bytes32 => OrderContext) public orders;

    /// @notice Salt used for each order (for ComposableCoW cancellation)
    mapping(bytes32 => bytes32) public orderSalts;

    /// @notice User's active orders
    mapping(address => bytes32[]) public userOrders;

    /// @notice Lookup order hash by (user, salt) - enables pre-computing appData
    mapping(address => mapping(bytes32 => bytes32)) public userSaltToOrderHash;

    /// @notice Track pre-hook execution per order to prevent re-execution
    /// @dev Stores (iterationCount + 1) when pre-hook executed, 0 means no pre-hook pending
    mapping(bytes32 => uint256) public preHookExecutedForIteration;

    /// @notice Track seed tokens deposited per order for accurate refunds on cancel
    mapping(bytes32 => uint256) public orderSeedBalance;

    /// @notice Track token balances at end of pre-hook for delta calculation in post-hook
    /// @dev Prevents leftover token accumulation from affecting subsequent orders
    mapping(bytes32 => uint256) public preHookSellBalance;
    mapping(bytes32 => uint256) public preHookBuyBalance;

    // ============ Events ============

    event OrderCreated(
        bytes32 indexed orderHash,
        address indexed user,
        address sellToken,
        address buyToken,
        uint256 totalAmount,
        uint256 chunkSize
    );

    event OrderCancelled(bytes32 indexed orderHash, address indexed user);
    event OrderCompleted(bytes32 indexed orderHash, address indexed user, uint256 totalExecuted);
    event ChunkExecuted(bytes32 indexed orderHash, uint256 chunkIndex, uint256 sellAmount, uint256 buyAmount);
    event PreHookExecuted(bytes32 indexed orderHash, uint256 chunkIndex);
    event PostHookExecuted(bytes32 indexed orderHash, uint256 chunkIndex, uint256 receivedAmount);

    // Admin events
    event RouterUpdated(address indexed oldRouter, address indexed newRouter);
    event ComposableCoWUpdated(address indexed oldComposableCoW, address indexed newComposableCoW);
    event HooksTrampolineUpdated(address indexed oldTrampoline, address indexed newTrampoline);
    event OrderHandlerUpdated(address indexed oldHandler, address indexed newHandler);
    event CowAdapterUpdated(address indexed oldAdapter, address indexed newAdapter);

    // ============ Constructor ============

    constructor(
        address _owner,
        address _router,
        address _composableCoW,
        address _settlement,
        address _hooksTrampoline
    ) Ownable(_owner) {
        if (_router == address(0)) revert ZeroAddress();
        if (_composableCoW == address(0)) revert ZeroAddress();
        if (_settlement == address(0)) revert ZeroAddress();
        if (_hooksTrampoline == address(0)) revert ZeroAddress();

        router = IKapanRouter(_router);
        composableCoW = IComposableCoW(_composableCoW);
        settlement = IGPv2Settlement(_settlement);
        hooksTrampoline = _hooksTrampoline;
    }

    // ============ Admin Functions ============

    function setRouter(address _router) external onlyOwner {
        if (_router == address(0)) revert ZeroAddress();
        address oldRouter = address(router);
        router = IKapanRouter(_router);
        emit RouterUpdated(oldRouter, _router);
    }

    function setComposableCoW(address _composableCoW) external onlyOwner {
        if (_composableCoW == address(0)) revert ZeroAddress();
        address oldComposableCoW = address(composableCoW);
        composableCoW = IComposableCoW(_composableCoW);
        emit ComposableCoWUpdated(oldComposableCoW, _composableCoW);
    }

    function setHooksTrampoline(address _hooksTrampoline) external onlyOwner {
        if (_hooksTrampoline == address(0)) revert ZeroAddress();
        address oldTrampoline = hooksTrampoline;
        hooksTrampoline = _hooksTrampoline;
        emit HooksTrampolineUpdated(oldTrampoline, _hooksTrampoline);
    }

    function setOrderHandler(address _handler) external onlyOwner {
        if (_handler == address(0)) revert ZeroAddress();
        address oldHandler = orderHandler;
        orderHandler = _handler;
        emit OrderHandlerUpdated(oldHandler, _handler);
    }

    function setCowAdapter(address _cowAdapter) external onlyOwner {
        address oldAdapter = address(cowAdapter);
        cowAdapter = IKapanCowAdapter(_cowAdapter);
        emit CowAdapterUpdated(oldAdapter, _cowAdapter);
    }

    /// @notice Approve vault relayer to spend a token
    function approveVaultRelayer(address token) external onlyOwner {
        if (token == address(0)) revert ZeroAddress();
        address vaultRelayer = settlement.vaultRelayer();
        IERC20(token).forceApprove(vaultRelayer, type(uint256).max);
    }

    // ============ Order Creation ============

    /// @notice Create a new CoW order with seed tokens for first chunk
    /// @dev User must approve this contract to pull seedAmount of sellToken before calling
    /// @param params The order parameters
    /// @param salt Unique salt for the order
    /// @param seedAmount Amount of sell tokens to pull from user for first chunk (0 to skip)
    /// @return orderHash The hash identifying this order
    function createOrder(
        KapanOrderParams calldata params,
        bytes32 salt,
        uint256 seedAmount
    ) external nonReentrant returns (bytes32 orderHash) {
        // Validate caller is the order user
        if (params.user != msg.sender) revert Unauthorized();
        if (orderHandler == address(0)) revert InvalidHandler();

        // Validate all instructions target the calling user (prevents attacking other users' positions)
        // Pass address(this) as second arg to allow PullToken from OrderManager (for KIND_BUY post-hooks)
        _validateInstructionUsers(params.preInstructionsPerIteration, msg.sender, address(this));
        _validateInstructionUsers(params.postInstructionsPerIteration, msg.sender, address(this));

        // Compute order hash
        orderHash = keccak256(abi.encode(params, salt, block.timestamp));

        // Check order doesn't exist
        if (orders[orderHash].status != OrderStatus.None) revert OrderAlreadyExists();

        // Pull seed tokens from user if provided
        // This ensures OrderManager has balance for CoW API's balance check
        if (seedAmount > 0) {
            IERC20(params.sellToken).safeTransferFrom(msg.sender, address(this), seedAmount);
            orderSeedBalance[orderHash] = seedAmount;
        }

        // Store order context
        orders[orderHash] = OrderContext({
            params: params,
            status: OrderStatus.Active,
            executedAmount: 0,
            iterationCount: 0,
            createdAt: block.timestamp
        });

        // Store salt separately for cancellation
        orderSalts[orderHash] = salt;

        // Track user's orders
        userOrders[params.user].push(orderHash);

        // Enable lookup by (user, salt) for hook resolution
        userSaltToOrderHash[params.user][salt] = orderHash;

        // Register with ComposableCoW
        IConditionalOrder.ConditionalOrderParams memory cowParams = IConditionalOrder.ConditionalOrderParams({
            handler: IConditionalOrder(orderHandler),
            salt: salt,
            staticData: abi.encode(orderHash)  // Handler looks up context by hash
        });

        composableCoW.create(cowParams, true);  // dispatch=true for WatchTower

        // Ensure vault relayer can spend sellToken
        _ensureVaultRelayerApproval(params.sellToken);

        emit OrderCreated(
            orderHash,
            params.user,
            params.sellToken,
            params.buyToken,
            params.preTotalAmount,
            params.chunkSize
        );
    }

    // ============ Order Cancellation ============

    /// @notice Cancel an active order and refund any remaining seed tokens
    /// @param orderHash The order to cancel
    function cancelOrder(bytes32 orderHash) external nonReentrant {
        OrderContext storage ctx = orders[orderHash];

        if (ctx.status == OrderStatus.None) revert OrderNotFound();
        if (ctx.params.user != msg.sender) revert Unauthorized();
        if (ctx.status != OrderStatus.Active) revert InvalidOrderState();

        // Cannot cancel if pre-hook has executed but post-hook hasn't (mid-execution)
        if (preHookExecutedForIteration[orderHash] != 0) revert CannotCancelMidExecution();

        ctx.status = OrderStatus.Cancelled;

        // Remove from ComposableCoW
        // Note: We need to compute the same hash ComposableCoW uses
        bytes32 cowOrderHash = composableCoW.hash(
            IConditionalOrder.ConditionalOrderParams({
                handler: IConditionalOrder(orderHandler),
                salt: orderSalts[orderHash],
                staticData: abi.encode(orderHash)
            })
        );
        composableCoW.remove(cowOrderHash);

        // Only refund the tracked seed balance for THIS order
        // This prevents stealing tokens belonging to other orders
        uint256 seedToRefund = orderSeedBalance[orderHash];
        if (seedToRefund > 0) {
            orderSeedBalance[orderHash] = 0;
            IERC20(ctx.params.sellToken).safeTransfer(ctx.params.user, seedToRefund);
        }

        // Note: We don't refund buyTokens here because:
        // - If not mid-execution, no buyTokens should be present for this order
        // - If iterations completed (iterationCount > 0), buyTokens were processed through router
        // - Any stuck buyTokens can be recovered via owner's recoverTokens()

        emit OrderCancelled(orderHash, msg.sender);
    }

    // ============ Hook Execution ============

    /// @notice Execute pre-hook (called by HooksTrampoline during CoW settlement)
    /// @dev Prepends a ToOutput instruction with chunkSize, so all stored instructions
    ///      can reference index 0 for the chunk amount. Chunk index is read from iterationCount.
    /// @param orderHash The order being settled
    function executePreHook(bytes32 orderHash) external nonReentrant {
        _executePreHook(orderHash);
    }

    /// @notice Execute post-hook (called by HooksTrampoline during CoW settlement)
    /// @dev Prepends a ToOutput instruction with the actual received amount, so all
    ///      stored instructions can reference index 0 for the swap output
    /// @param orderHash The order being settled
    function executePostHook(bytes32 orderHash) external nonReentrant {
        _executePostHook(orderHash);
    }

    /// @notice Execute pre-hook using (user, salt) lookup
    /// @dev Allows pre-computing appData before order creation. Chunk index is determined
    ///      from the order's iterationCount, so the same appData works for all chunks.
    /// @param user The order owner
    /// @param salt The order salt (known before tx)
    function executePreHookBySalt(address user, bytes32 salt) external nonReentrant {
        bytes32 orderHash = userSaltToOrderHash[user][salt];
        if (orderHash == bytes32(0)) revert OrderNotFound();
        _executePreHook(orderHash);
    }

    /// @notice Execute post-hook using (user, salt) lookup
    /// @dev Allows pre-computing appData before order creation
    /// @param user The order owner
    /// @param salt The order salt (known before tx)
    function executePostHookBySalt(address user, bytes32 salt) external nonReentrant {
        bytes32 orderHash = userSaltToOrderHash[user][salt];
        if (orderHash == bytes32(0)) revert OrderNotFound();
        _executePostHook(orderHash);
    }

    /// @notice Internal pre-hook execution
    /// @dev Chunk index is read from ctx.iterationCount so the same appData works for all chunks
    function _executePreHook(bytes32 orderHash) internal {
        // Only HooksTrampoline can call this
        if (msg.sender != hooksTrampoline) revert NotHooksTrampoline();

        OrderContext storage ctx = orders[orderHash];
        if (ctx.status != OrderStatus.Active) revert InvalidOrderState();

        // SECURITY: For flash loan orders, validate with CowAdapter
        // This prevents hook hijacking - attacker can't trigger victim's hooks
        // because fundOrder must have been called with this orderHash first
        if (ctx.params.isFlashLoanOrder && address(cowAdapter) != address(0)) {
            cowAdapter.onPreHook(orderHash);
        }

        // Guard: Pre-hook can only run once per iteration
        // Value of 0 means no pre-hook pending, non-zero means pre-hook already executed
        if (preHookExecutedForIteration[orderHash] != 0) revert PreHookAlreadyExecuted();

        // Mark pre-hook as executed for current iteration
        // Store (iterationCount + 1) so that 0 remains the "not executed" sentinel
        preHookExecutedForIteration[orderHash] = ctx.iterationCount + 1;

        // Get pre-instructions for current iteration
        // If fewer entries than iterations, reuse the last entry
        bytes memory preInstructionsData = _getInstructionsForIteration(
            ctx.params.preInstructionsPerIteration,
            ctx.iterationCount
        );

        // Decode stored pre-instructions
        ProtocolTypes.ProtocolInstruction[] memory storedInstructions = 
            abi.decode(preInstructionsData, (ProtocolTypes.ProtocolInstruction[]));

        // Create new array with ToOutput prepended
        ProtocolTypes.ProtocolInstruction[] memory instructions = 
            new ProtocolTypes.ProtocolInstruction[](storedInstructions.length + 1);

        // Prepend ToOutput with chunkSize - this becomes index 0
        instructions[0] = ProtocolTypes.ProtocolInstruction({
            protocolName: "router",
            data: _encodeToOutput(ctx.params.chunkSize, ctx.params.sellToken)
        });

        // Copy stored instructions (their indices shift by 1, but they reference index 0)
        for (uint256 i = 0; i < storedInstructions.length; i++) {
            instructions[i + 1] = storedInstructions[i];
        }

        // Execute via router (router will check delegation)
        router.processProtocolInstructions(instructions);

        // Record balances at end of pre-hook for delta calculation in post-hook
        // This prevents leftover tokens from previous orders affecting this order
        preHookSellBalance[orderHash] = IERC20(ctx.params.sellToken).balanceOf(address(this));
        preHookBuyBalance[orderHash] = IERC20(ctx.params.buyToken).balanceOf(address(this));

        // Emit with current iteration count (chunk index is self-determined from state)
        emit PreHookExecuted(orderHash, ctx.iterationCount);
    }

    /// @notice Internal post-hook execution
    /// @dev For KIND_SELL: Prepends 1 UTXO (received buyToken amount)
    ///      For KIND_BUY: Prepends 2 UTXOs (actual sell amount, leftover sell amount)
    function _executePostHook(bytes32 orderHash) internal {
        // Only HooksTrampoline can call this
        if (msg.sender != hooksTrampoline) revert NotHooksTrampoline();

        OrderContext storage ctx = orders[orderHash];
        if (ctx.status != OrderStatus.Active) revert InvalidOrderState();

        // Guard: Post-hook requires pre-hook to have executed for this iteration
        // preHookExecutedForIteration stores (iterationCount + 1) when pre-hook ran
        if (preHookExecutedForIteration[orderHash] != ctx.iterationCount + 1) revert PreHookNotExecuted();

        // Reset the pre-hook flag (allows next iteration's pre-hook to run)
        preHookExecutedForIteration[orderHash] = 0;

        // Get post-instructions for current iteration
        bytes memory postInstructionsData = _getInstructionsForIteration(
            ctx.params.postInstructionsPerIteration,
            ctx.iterationCount
        );

        // Decode stored post-instructions
        ProtocolTypes.ProtocolInstruction[] memory storedInstructions = 
            abi.decode(postInstructionsData, (ProtocolTypes.ProtocolInstruction[]));

        // Calculate deltas using pre-hook recorded balances
        // This prevents leftover tokens from previous orders affecting this order
        uint256 sellBefore = preHookSellBalance[orderHash];
        uint256 buyBefore = preHookBuyBalance[orderHash];
        uint256 sellAfter = IERC20(ctx.params.sellToken).balanceOf(address(this));
        uint256 buyAfter = IERC20(ctx.params.buyToken).balanceOf(address(this));

        // Clear stored balances (no longer needed, saves gas on subsequent reads)
        delete preHookSellBalance[orderHash];
        delete preHookBuyBalance[orderHash];

        // Calculate actual amounts using deltas
        uint256 actualSellAmount = sellBefore > sellAfter ? sellBefore - sellAfter : 0;
        uint256 actualBuyAmount = buyAfter > buyBefore ? buyAfter - buyBefore : 0;

        ProtocolTypes.ProtocolInstruction[] memory instructions;

        if (ctx.params.isKindBuy) {
            // KIND_BUY: actualSellAmount is the delta, actualBuyAmount should match minBuyPerChunk
            // Calculate remaining from intended chunkSize, not from balance (avoids leftover issues)
            uint256 remainingSellToken = actualSellAmount < ctx.params.chunkSize
                ? ctx.params.chunkSize - actualSellAmount
                : 0;

            // Transfer only the calculated remaining to router (not any pre-existing leftovers)
            if (remainingSellToken > 0 && sellAfter >= remainingSellToken) {
                IERC20(ctx.params.sellToken).safeTransfer(address(router), remainingSellToken);
            }

            // Approve router to pull buyToken (for PullToken instruction in post-hook)
            // Only approve the delta amount, not any pre-existing balance
            if (actualBuyAmount > 0) {
                IERC20(ctx.params.buyToken).forceApprove(address(router), actualBuyAmount);
            }

            // Prepend TWO UTXOs for KIND_BUY:
            // UTXO[0] = actual sell amount (what we paid)
            // UTXO[1] = leftover amount (what wasn't used, now at router)
            instructions = new ProtocolTypes.ProtocolInstruction[](storedInstructions.length + 2);
            instructions[0] = ProtocolTypes.ProtocolInstruction({
                protocolName: "router",
                data: _encodeToOutput(actualSellAmount, ctx.params.sellToken)
            });
            instructions[1] = ProtocolTypes.ProtocolInstruction({
                protocolName: "router",
                data: _encodeToOutput(remainingSellToken, ctx.params.sellToken)
            });

            // Copy stored instructions (indices shift by 2)
            for (uint256 i = 0; i < storedInstructions.length; i++) {
                instructions[i + 2] = storedInstructions[i];
            }
        } else {
            // KIND_SELL: actualBuyAmount is the delta (what we received from swap)
            // Transfer only the delta to router (not any pre-existing leftovers)
            if (actualBuyAmount > 0) {
                IERC20(ctx.params.buyToken).safeTransfer(address(router), actualBuyAmount);
            }

            // Prepend ONE UTXO for KIND_SELL:
            // UTXO[0] = received amount (delta - what we got from swap)
            instructions = new ProtocolTypes.ProtocolInstruction[](storedInstructions.length + 1);
            instructions[0] = ProtocolTypes.ProtocolInstruction({
                protocolName: "router",
                data: _encodeToOutput(actualBuyAmount, ctx.params.buyToken)
            });

            // Copy stored instructions (indices shift by 1)
            for (uint256 i = 0; i < storedInstructions.length; i++) {
                instructions[i + 1] = storedInstructions[i];
            }
        }

        // Execute via router
        router.processProtocolInstructions(instructions);

        // Update progress with actual amounts
        ctx.executedAmount += actualSellAmount;
        ctx.iterationCount++;

        // Clear seed balance after first iteration (seed was consumed by the swap)
        // This ensures cancel won't try to refund already-used tokens
        if (orderSeedBalance[orderHash] > 0) {
            orderSeedBalance[orderHash] = 0;
        }

        // Emit chunk execution event with actual sell/buy amounts for frontend tracking
        emit ChunkExecuted(orderHash, ctx.iterationCount, actualSellAmount, actualBuyAmount);

        // Check completion
        if (_isOrderComplete(ctx)) {
            ctx.status = OrderStatus.Completed;
            emit OrderCompleted(orderHash, ctx.params.user, ctx.executedAmount);
        }

        emit PostHookExecuted(orderHash, ctx.iterationCount, actualBuyAmount);

        // SECURITY: For flash loan orders, finalize with CowAdapter
        // This clears the adapter state, ensuring proper cleanup
        if (ctx.params.isFlashLoanOrder && address(cowAdapter) != address(0)) {
            cowAdapter.onPostHook(orderHash);
        }
    }

    // ============ ERC-1271 Signature Verification ============

    /// @notice Verify signature for CoW Protocol orders
    /// @dev For non-Safe ERC-1271 contracts, ComposableCoW returns signature as:
    ///      abi.encode(GPv2Order.Data, PayloadStruct)
    ///      We decode this and verify the order matches what the handler generates.
    function isValidSignature(
        bytes32 _hash,
        bytes calldata _signature
    ) external view override returns (bytes4) {
        // For ERC-1271 forwarder pattern (non-Safe), signature = abi.encode(order, payload)
        // We need to decode both parts
        (GPv2Order.Data memory order, IComposableCoW.PayloadStruct memory payload) =
            abi.decode(_signature, (GPv2Order.Data, IComposableCoW.PayloadStruct));

        // Verify the hash matches the computed order hash
        bytes32 computedHash = GPv2Order.hash(order, settlement.domainSeparator());
        if (_hash != computedHash) {
            return bytes4(0xffffffff);
        }

        // Verify via ComposableCoW - this checks authorization and generates the expected order
        // ComposableCoW will call the handler's verify() function
        try composableCoW.getTradeableOrderWithSignature(
            address(this),
            payload.params,
            payload.offchainInput,
            payload.proof
        ) returns (GPv2Order.Data memory expectedOrder, bytes memory) {
            // Verify the provided order matches the expected order
            // The hash should match what we expect for this order
            if (_orderMatches(order, expectedOrder)) {
                return ERC1271_MAGIC_VALUE;
            }
            return bytes4(0xffffffff);
        } catch {
            return bytes4(0xffffffff);
        }
    }

    /// @dev Check if two GPv2Orders match (excluding validTo which changes)
    function _orderMatches(GPv2Order.Data memory a, GPv2Order.Data memory b) internal pure returns (bool) {
        return address(a.sellToken) == address(b.sellToken) &&
               address(a.buyToken) == address(b.buyToken) &&
               a.receiver == b.receiver &&
               a.sellAmount == b.sellAmount &&
               a.buyAmount == b.buyAmount &&
               a.appData == b.appData &&
               a.feeAmount == b.feeAmount &&
               a.kind == b.kind &&
               a.partiallyFillable == b.partiallyFillable &&
               a.sellTokenBalance == b.sellTokenBalance &&
               a.buyTokenBalance == b.buyTokenBalance;
    }

    // ============ View Functions ============

    /// @notice Get order context
    function getOrder(bytes32 orderHash) external view returns (OrderContext memory) {
        return orders[orderHash];
    }

    /// @notice Get user's orders
    function getUserOrders(address user) external view returns (bytes32[] memory) {
        return userOrders[user];
    }

    /// @notice Check if an order is complete based on completion criteria
    function isOrderComplete(bytes32 orderHash) external view returns (bool) {
        return _isOrderComplete(orders[orderHash]);
    }

    // ============ Internal Functions ============

    function _isOrderComplete(OrderContext storage ctx) internal view returns (bool) {
        if (ctx.params.completion == CompletionType.Iterations) {
            return ctx.iterationCount >= ctx.params.targetValue;
        }

        if (ctx.params.completion == CompletionType.TargetBalance) {
            // Check remaining amount
            uint256 remaining = ctx.params.preTotalAmount - ctx.executedAmount;
            return remaining == 0 || remaining < ctx.params.chunkSize;
        }

        if (ctx.params.completion == CompletionType.TargetLTV) {
            // TODO: Read actual LTV from lending protocol
            // For now, check if we've executed the total amount
            return ctx.executedAmount >= ctx.params.preTotalAmount;
        }

        // UntilCancelled never completes automatically
        return false;
    }

    // ============ Instruction Validation ============

    /// @dev Constant hash for "router" protocol name comparison
    bytes32 private constant ROUTER_PROTOCOL_HASH = keccak256(abi.encodePacked("router"));

    /// @notice Validate that all instructions in all iterations target the expected user
    /// @dev Prevents attackers from encoding instructions that operate on other users' positions
    ///      Note: Only certain instruction types require strict validation (PullToken, lending ops)
    /// @param instructionsPerIteration Array of encoded ProtocolInstruction[] per iteration
    /// @param expectedUser The user who must be the target of security-sensitive instructions
    function _validateInstructionUsers(
        bytes[] calldata instructionsPerIteration,
        address expectedUser,
        address selfAddress
    ) internal pure {
        for (uint256 i = 0; i < instructionsPerIteration.length; i++) {
            ProtocolTypes.ProtocolInstruction[] memory instructions =
                abi.decode(instructionsPerIteration[i], (ProtocolTypes.ProtocolInstruction[]));

            for (uint256 j = 0; j < instructions.length; j++) {
                address instrUser = _extractUserFromInstruction(instructions[j], expectedUser, selfAddress);
                if (instrUser != expectedUser) {
                    revert InstructionUserMismatch(expectedUser, instrUser);
                }
            }
        }
    }

    /// @notice Extract the user address from a protocol instruction that requires validation
    /// @dev Returns expectedUser for instructions that don't need user validation (PushToken, ToOutput, etc.)
    ///      Only PullToken and lending instructions need strict user validation
    /// @param instruction The protocol instruction to extract user from
    /// @param expectedUser The expected user (returned for instructions that don't need validation)
    /// @param selfAddress The OrderManager's address (allowed as source for PullToken in KIND_BUY post-hooks)
    /// @return user The user address to validate (or expectedUser if no validation needed)
    function _extractUserFromInstruction(
        ProtocolTypes.ProtocolInstruction memory instruction,
        address expectedUser,
        address selfAddress
    ) internal pure returns (address user) {
        bytes32 protocolHash = keccak256(abi.encodePacked(instruction.protocolName));

        if (protocolHash == ROUTER_PROTOCOL_HASH) {
            // Decode RouterInstruction (same method as KapanRouter)
            RouterInstruction memory routerInstruction = abi.decode(instruction.data, (RouterInstruction));

            // Only PullToken needs strict user validation (prevents stealing from other users)
            // Other instruction types have different semantics for the user field:
            // - PushToken: user = recipient (can be OrderManager, adapters, etc.)
            // - ToOutput: user = unused (always address(0))
            // - Approve: user = unused (always address(0))
            // - FlashLoan: user = unused (always address(0))
            // - Add/Subtract/Split: user = unused (always address(0))
            if (routerInstruction.instructionType == RouterInstructionType.PullToken) {
                // Allow PullToken from self (OrderManager) for KIND_BUY post-hooks
                // where tokens land in OrderManager from CoW settlement
                if (routerInstruction.user == selfAddress) {
                    user = expectedUser; // Skip validation for self
                } else {
                    user = routerInstruction.user;
                }
            } else {
                // Skip validation for other router instructions
                user = expectedUser;
            }
        } else {
            // Lending instruction (gateway): struct LendingInstruction { op, token, user, amount, context, input }
            // All lending operations need strict user validation
            ProtocolTypes.LendingInstruction memory lending =
                abi.decode(instruction.data, (ProtocolTypes.LendingInstruction));
            user = lending.user;
        }
    }

    /// @notice Encode a ToOutput router instruction
    /// @dev Matches the encoding expected by KapanRouter's RouterInstruction struct
    ///      struct RouterInstruction { uint256 amount; address token; address user; RouterInstructionType instructionType; }
    ///      RouterInstructionType.ToOutput = 3
    function _encodeToOutput(uint256 amount, address token) internal pure returns (bytes memory) {
        // ABI encode as tuple matching RouterInstruction struct layout
        // Note: RouterInstructionType enum is uint8, but in ABI encoding it's padded to 32 bytes
        return abi.encode(amount, token, address(0), uint8(3));
    }

    /// @notice Get instructions for a specific iteration
    /// @dev If the array has fewer entries than the iteration index, returns the last entry
    ///      This allows specifying different instructions for early iterations vs later ones
    ///      e.g., [depositAndBorrow, depositAndBorrow, depositOnly] for 3 iterations
    /// @param instructionsArray Array of encoded instructions per iteration
    /// @param iteration Current iteration index (0-based)
    /// @return The encoded instructions for this iteration
    function _getInstructionsForIteration(
        bytes[] storage instructionsArray,
        uint256 iteration
    ) internal view returns (bytes memory) {
        if (instructionsArray.length == 0) {
            // Return empty instructions if none provided
            return abi.encode(new ProtocolTypes.ProtocolInstruction[](0));
        }

        // If iteration index exceeds array length, use the last entry
        uint256 index = iteration < instructionsArray.length 
            ? iteration 
            : instructionsArray.length - 1;

        return instructionsArray[index];
    }

    function _ensureVaultRelayerApproval(address token) internal {
        address vaultRelayer = settlement.vaultRelayer();
        uint256 currentAllowance = IERC20(token).allowance(address(this), vaultRelayer);
        if (currentAllowance < type(uint256).max / 2) {
            IERC20(token).forceApprove(vaultRelayer, type(uint256).max);
        }
    }

    // ============ Emergency Functions ============

    /// @notice Recover stuck tokens (owner only)
    function recoverTokens(address token, address to, uint256 amount) external onlyOwner {
        IERC20(token).safeTransfer(to, amount);
    }
}
","keccak256":"0x6401f7f587b4ec2f77077ba24ed0035419c6ae335d1552d3fce79d65c46b2264","license":"MIT"},"contracts/v2/interfaces/ProtocolTypes.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

library ProtocolTypes {
    struct ProtocolInstruction {
        string protocolName;
        bytes data;
    }

    struct Output {
        address token;
        uint256 amount;
    }

    // Common lending instruction schema for gateways (deposit/withdraw/borrow/repay)
    enum LendingOp {
        Deposit,
        DepositCollateral,
        WithdrawCollateral,
        Borrow,
        Repay,
        GetBorrowBalance,
        GetSupplyBalance,
        Swap,
        SwapExactOut,
        SetEMode  // Aave V3 E-Mode: amount encodes categoryId (0 = disable E-Mode)
    }
    struct InputPtr {
        uint256 index;
    }
    /**
     * Cross-protocol instruction.
     * - For Compound you can pass `context = abi.encode(address marketBaseToken)`
     *   which tells the gateway which Comet (market) to use. Empty means infer from token.
     */
    struct LendingInstruction {
        LendingOp op;
        address token; // underlying being acted on (base or collateral)
        address user; // end user on whose account we operate
        uint256 amount; // amount in underlying units
        bytes context; // (optional) protocol-specific blob (e.g., Compound marketBaseToken)
        InputPtr input; // (optional) pointer to prior output (UTXO); use if index < inputs.length
    }
}
","keccak256":"0x6b3d27ea42baef69a30052ada687149250a56be5942577d654f2f2142a5510c6","license":"MIT"},"contracts/v2/interfaces/cow/GPv2Order.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/// @title GPv2Order - CoW Protocol order library
/// @notice Data structures and helpers for CoW Protocol orders
library GPv2Order {
    /// @dev The complete order data that is signed by the user
    struct Data {
        IERC20 sellToken;
        IERC20 buyToken;
        address receiver;
        uint256 sellAmount;
        uint256 buyAmount;
        uint32 validTo;
        bytes32 appData;
        uint256 feeAmount;
        bytes32 kind;
        bool partiallyFillable;
        bytes32 sellTokenBalance;
        bytes32 buyTokenBalance;
    }

    /// @dev Order kind constants
    bytes32 internal constant KIND_SELL =
        keccak256("sell");
    bytes32 internal constant KIND_BUY =
        keccak256("buy");

    /// @dev Token balance constants
    bytes32 internal constant BALANCE_ERC20 =
        keccak256("erc20");
    bytes32 internal constant BALANCE_EXTERNAL =
        keccak256("external");
    bytes32 internal constant BALANCE_INTERNAL =
        keccak256("internal");

    /// @dev The order EIP-712 type hash
    bytes32 internal constant TYPE_HASH =
        keccak256(
            "Order("
            "address sellToken,"
            "address buyToken,"
            "address receiver,"
            "uint256 sellAmount,"
            "uint256 buyAmount,"
            "uint32 validTo,"
            "bytes32 appData,"
            "uint256 feeAmount,"
            "string kind,"
            "bool partiallyFillable,"
            "string sellTokenBalance,"
            "string buyTokenBalance"
            ")"
        );

    /// @dev Computes the order hash for the given order data
    /// @param order The order to hash
    /// @param domainSeparator The EIP-712 domain separator
    /// @return orderDigest The order digest
    function hash(Data memory order, bytes32 domainSeparator) internal pure returns (bytes32 orderDigest) {
        bytes32 structHash = keccak256(
            abi.encode(
                TYPE_HASH,
                order.sellToken,
                order.buyToken,
                order.receiver,
                order.sellAmount,
                order.buyAmount,
                order.validTo,
                order.appData,
                order.feeAmount,
                order.kind,
                order.partiallyFillable,
                order.sellTokenBalance,
                order.buyTokenBalance
            )
        );
        orderDigest = keccak256(abi.encodePacked("\x19\x01", domainSeparator, structHash));
    }
}
