contracts/gateways/ProtocolGateway.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

/// @notice Shared base for v2 GatewayWrite/GatewayView contracts.
///         Holds immutable router address and common access modifiers.
abstract contract ProtocolGateway {
    /// @notice Immutable router address that can call protected functions
    address public immutable ROUTER;

    /// @notice Restrict function access to only the router contract
    modifier onlyRouter() {
        require(msg.sender == ROUTER, "Only router can call");
        _;
    }

    /// @notice Restrict access to router or the user themselves
    /// @param user The user the function is being called for
    modifier onlyRouterOrSelf(address user) {
        require(msg.sender == ROUTER || msg.sender == user, "Only router or self can call");
        _;
    }

    /// @param router The address of the router contract that can call protected functions
    constructor(address router) {
        require(router != address(0), "router=0");
        ROUTER = router;
    }
}

contracts/interfaces/venus/ComptrollerIn.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

import "./VTokenInterface.sol";

/**
 * @title Venus's Comptroller Interface
 * @notice Interface for interacting with Venus Comptroller
 * @dev Adapted for compatibility with Solidity ^0.8.10 from Venus v4 documentation
 * @dev Source: https://docs-v4.venus.io/technical-reference/reference-core-pool/comptroller/diamond/facets/market-facet
 */
interface ComptrollerInterface {
    /**
     * @notice Indicator that this is a Comptroller contract (for inspection)
     */
    function isComptroller() external pure returns (bool);

    /**
     * @notice Enters a list of markets (vTokens)
     * @param vTokens The list of addresses of the vToken markets to enter
     * @return success A list of error codes (0=success, otherwise a failure)
     */
    function enterMarkets(address[] calldata vTokens) external returns (uint[] memory);

    /**
     * @notice Exits a vToken market
     * @param vTokenAddress Address of the vToken market to exit
     * @return success 0=success, otherwise a failure
     */
    function exitMarket(address vTokenAddress) external returns (uint);

    /**
     * @notice Returns the list of vToken markets an account has entered
     * @param account The address of the account to query
     * @return The list of vToken markets the account has entered
     */
    function getAssetsIn(address account) external view returns (VTokenInterface[] memory);

    /**
     * @notice Checks if an account is entered into a specific market
     * @param account The address of the account to check
     * @param vToken The vToken market to check
     * @return True if the account is in the market, otherwise false
     */
    function checkMembership(address account, address vToken) external view returns (bool);

    /**
     * @notice Returns a list of all vToken markets
     * @return A list of all vToken market addresses
     */
    function getAllMarkets() external view returns (VTokenInterface[] memory);

    /**
     * @notice Determine the amount of vToken that could be seized in a liquidation
     * @param vTokenBorrowed The borrowed vToken
     * @param vTokenCollateral The collateral vToken
     * @param actualRepayAmount The amount of vTokenBorrowed underlying to repay
     * @return errorCode 0=success, otherwise a failure
     * @return seizeTokens The number of vTokenCollateral tokens to seize
     */
    function liquidateCalculateSeizeTokens(
        address vTokenBorrowed,
        address vTokenCollateral,
        uint actualRepayAmount
    ) external view returns (uint, uint);

    /**
     * @notice Calculate amount of tokens to seize in a VAI liquidation
     * @param vTokenCollateral The collateral vToken
     * @param actualRepayAmount The amount of VAI to repay
     * @return errorCode 0=success, otherwise a failure
     * @return seizeTokens The number of vTokenCollateral tokens to seize
     */
    function liquidateVAICalculateSeizeTokens(address vTokenCollateral, uint actualRepayAmount) external view returns (uint, uint);

    /**
     * @notice Update delegate status to allow/disallow borrowing on behalf
     * @param delegate The delegate address
     * @param allowBorrows Whether to allow the delegate to borrow
     */
    function updateDelegate(address delegate, bool allowBorrows) external;

    function approvedDelegates(address user, address delegate) external view returns (bool);

    /**
     * @notice Return information about a specific market
     * @dev On Venus v4 core pool, the third value is the liquidation threshold,
     *      NOT an "isComped" flag as on some older Comptroller variants.
     * @param vToken The vToken address to get market data for
     * @return isListed Whether the market is listed
     * @return collateralFactorMantissa The collateral factor (LTV) for the market (scaled by 1e18)
     * @return liquidationThresholdMantissa The liquidation threshold (LLTV) for the market (scaled by 1e18)
     */
    function markets(address vToken)
        external
        view
        returns (
            bool isListed,
            uint256 collateralFactorMantissa,
            uint256 liquidationThresholdMantissa
        );

    /**
     * @notice Get the account liquidity information
     * @param account The address of the account
     * @return error Error code (0=success, otherwise a failure)
     * @return liquidity The USD value borrowable by the account
     * @return shortfall The USD value of collateral needed to meet obligations
     */
    function getAccountLiquidity(address account) external view returns (uint, uint, uint);

    /**
     * @notice Check if a specific action is allowed
     * @param vToken The vToken market to check
     * @param action The action to check (mint=1, redeem=2, borrow=3, repay=4, etc.)
     * @return True if the action is allowed
     */
    function actionAllowed(address vToken, uint8 action) external view returns (bool);

    /**
     * @notice Checks if a borrow is allowed
     * @param vToken The vToken market to check
     * @param borrower The account borrowing
     * @param borrowAmount The amount of underlying to borrow
     * @return 0=success, otherwise a failure
     */
    function borrowAllowed(address vToken, address borrower, uint borrowAmount) external returns (uint);

    /**
     * @notice Checks if a mint is allowed
     * @param vToken The vToken market to check
     * @param minter The account minting
     * @param mintAmount The amount of underlying to mint
     * @return 0=success, otherwise a failure
     */
    function mintAllowed(address vToken, address minter, uint mintAmount) external returns (uint);

    /**
     * @notice Checks if a redeem is allowed
     * @param vToken The vToken market to check
     * @param redeemer The account redeeming
     * @param redeemTokens The amount of vTokens to redeem
     * @return 0=success, otherwise a failure
     */
    function redeemAllowed(address vToken, address redeemer, uint redeemTokens) external returns (uint);

    /**
     * @notice Checks if a repay is allowed
     * @param vToken The vToken market to check
     * @param payer The account paying
     * @param borrower The account having their loan repaid
     * @param repayAmount The amount of underlying to repay
     * @return 0=success, otherwise a failure
     */
    function repayBorrowAllowed(address vToken, address payer, address borrower, uint repayAmount) external returns (uint);

    /**
     * @notice Returns the price oracle used by the comptroller
     * @return The address of the oracle
     */
    function oracle() external view returns (address);
}

contracts/interfaces/venus/VTokenInterfa.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

/**
 * @title Venus's VToken Interface
 * @notice Interface for interacting with Venus VTokens
 * @dev Adapted for compatibility with Solidity ^0.8.10 from Venus documentation
 */
interface VTokenInterface {
    /*** Market Events ***/
    event AccrueInterest(uint cashPrior, uint interestAccumulated, uint borrowIndex, uint totalBorrows);
    event Mint(address minter, uint mintAmount, uint mintTokens);
    event Redeem(address redeemer, uint redeemAmount, uint redeemTokens);
    event Borrow(address borrower, uint borrowAmount, uint accountBorrows, uint totalBorrows);
    event RepayBorrow(address payer, address borrower, uint repayAmount, uint accountBorrows, uint totalBorrows);
    event LiquidateBorrow(address liquidator, address borrower, uint repayAmount, address vTokenCollateral, uint seizeTokens);

    /*** VToken State ***/
    function underlying() external view returns (address);
    function decimals() external view returns (uint8);
    function totalSupply() external view returns (uint);
    function totalBorrows() external view returns (uint);
    function totalReserves() external view returns (uint);
    function exchangeRateStored() external view returns (uint);
    function accrualBlockNumber() external view returns (uint);
    function comptroller() external view returns (address);
    function symbol() external view returns (string memory);
    function name() external view returns (string memory);
    function reserveFactorMantissa() external view returns (uint);
    function getCash() external view returns (uint);
    function borrowIndex() external view returns (uint);

    /*** User Account Functions ***/
    function balanceOf(address owner) external view returns (uint);
    function allowance(address owner, address spender) external view returns (uint);
    function approve(address spender, uint amount) external returns (bool);
    function transfer(address dst, uint amount) external returns (bool);
    function transferFrom(address src, address dst, uint amount) external returns (bool);

    /*** VToken Market Functions ***/
    function mint(uint mintAmount) external returns (uint);
    function redeem(uint redeemTokens) external returns (uint);
    function redeemUnderlying(uint redeemAmount) external returns (uint);
    function borrow(uint borrowAmount) external returns (uint);
    function borrowBehalf(address borrower, uint borrowAmount) external returns (uint);
    function repayBorrow(uint repayAmount) external returns (uint);
    function repayBorrowBehalf(address borrower, uint repayAmount) external returns (uint);

    /*** Borrow-Related Functions ***/
    function borrowBalanceCurrent(address account) external returns (uint);
    function borrowBalanceStored(address account) external view returns (uint);
    function supplyRatePerBlock() external view returns (uint);
    function borrowRatePerBlock() external view returns (uint);
    function exchangeRateCurrent() external returns (uint);
    function accrueInterest() external returns (uint);

    /*** Liquidation Functions ***/
    function liquidateBorrow(address borrower, uint repayAmount, address vTokenCollateral) external returns (uint);

    /*** Admin Functions ***/
    function _setReserveFactor(uint newReserveFactorMantissa) external returns (uint);
    function _reduceReserves(uint reduceAmount) external returns (uint);
    function _setInterestRateModel(address newInterestRateModel) external returns (uint);
}

contracts/v2/cow/KapanOrderHandler.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { IConditionalOrder, IConditionalOrderGenerator, PollTryNextBlock, PollNever, OrderNotValid } from "../interfaces/cow/IConditionalOrder.sol";
import { GPv2Order } from "../interfaces/cow/GPv2Order.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { IERC165 } from "@openzeppelin/contracts/utils/introspection/IERC165.sol";

import { KapanOrderManager } from "./KapanOrderManager.sol";

/// @title KapanOrderHandler
/// @notice Generates CoW Protocol orders for Kapan leveraged positions
/// @dev Implements IConditionalOrderGenerator for ComposableCoW integration
contract KapanOrderHandler is IConditionalOrderGenerator, IERC165 {

    // ============ Errors ============
    error InvalidOrderManager();
    error OrderNotActive();

    // ============ State Variables ============

    /// @notice The KapanOrderManager that stores order contexts
    KapanOrderManager public immutable orderManager;

    /// @notice Chunk window duration - each chunk is valid for this period
    /// @dev Using fixed windows ensures deterministic validTo = same order hash during window
    uint256 public constant CHUNK_WINDOW = 30 minutes;

    // ============ Constructor ============

    constructor(address _orderManager) {
        if (_orderManager == address(0)) revert InvalidOrderManager();
        orderManager = KapanOrderManager(_orderManager);
    }

    // ============ IConditionalOrderGenerator Implementation ============

    /// @notice Generate a tradeable order for the current chunk
    /// @param owner The order owner (KapanOrderManager address)
    /// @param sender The caller (usually ComposableCoW)
    /// @param ctx The execution context (unused for single orders)
    /// @param staticInput ABI-encoded orderHash
    /// @param offchainInput Off-chain provided data (unused)
    /// @return order The GPv2Order.Data struct for this chunk
    function getTradeableOrder(
        address owner,
        address sender,
        bytes32 ctx,
        bytes calldata staticInput,
        bytes calldata offchainInput
    ) external view override returns (GPv2Order.Data memory order) {
        // Silence unused variable warnings
        (owner, sender, ctx, offchainInput);

        // Decode the order hash from static input
        bytes32 orderHash = abi.decode(staticInput, (bytes32));

        // Get order context from manager
        KapanOrderManager.OrderContext memory orderCtx = orderManager.getOrder(orderHash);

        // Validate order is active
        if (orderCtx.status != KapanOrderManager.OrderStatus.Active) {
            revert PollNever("order_not_active");
        }

        // Check if order is complete
        if (orderManager.isOrderComplete(orderHash)) {
            revert PollNever("order_complete");
        }

        // Calculate remaining amount
        uint256 remaining = orderCtx.params.preTotalAmount - orderCtx.executedAmount;
        if (remaining == 0) {
            revert PollNever("fully_executed");
        }

        // Calculate this chunk's sell amount
        uint256 sellAmount = remaining < orderCtx.params.chunkSize 
            ? remaining 
            : orderCtx.params.chunkSize;

        // Calculate deterministic validTo based on chunk windows (TWAP-style)
        // This ensures the same order hash is returned during a window period,
        // preventing order spam where each poll creates a new CoW order.
        uint256 validTo = _calculateValidTo(orderCtx.createdAt, orderCtx.iterationCount);

        // Receiver is always OrderManager - it needs the bought tokens for post-hook execution
        // (Previously had flash loan special case for Settlement, but that was incorrect -
        // Aave's working implementation shows receiver should be where post-hook runs)
        address receiver = address(orderManager);

        // Build the order with deterministic validTo
        order = GPv2Order.Data({
            sellToken: IERC20(orderCtx.params.sellToken),
            buyToken: IERC20(orderCtx.params.buyToken),
            receiver: receiver,
            sellAmount: sellAmount,
            buyAmount: orderCtx.params.minBuyPerChunk,
            validTo: uint32(validTo),
            appData: orderCtx.params.appDataHash,
            feeAmount: 0,  // Fee is taken from sellAmount in CoW Protocol
            kind: GPv2Order.KIND_SELL,
            partiallyFillable: false,  // Each chunk must fill completely
            sellTokenBalance: GPv2Order.BALANCE_ERC20,
            buyTokenBalance: GPv2Order.BALANCE_ERC20
        });
    }

    /// @notice Calculate deterministic validTo timestamp for a chunk
    /// @dev Uses fixed time windows so multiple polls return the same validTo.
    ///      If chunk doesn't fill within its window, we extend to the current window.
    /// @param createdAt Order creation timestamp
    /// @param iterationCount Current chunk index (0-based)
    /// @return validTo The validTo timestamp for this chunk's order
    function _calculateValidTo(uint256 createdAt, uint256 iterationCount) internal view returns (uint256) {
        // Calculate the ideal window for this chunk
        uint256 chunkWindowStart = createdAt + (iterationCount * CHUNK_WINDOW);
        uint256 chunkWindowEnd = chunkWindowStart + CHUNK_WINDOW - 1;

        // If we're still within the chunk's ideal window, use it
        if (block.timestamp <= chunkWindowEnd) {
            return chunkWindowEnd;
        }

        // Chunk didn't fill in time - extend to current window
        // This allows the order to remain valid and retry
        uint256 elapsedSinceCreate = block.timestamp - createdAt;
        uint256 currentWindowIndex = elapsedSinceCreate / CHUNK_WINDOW;
        return createdAt + ((currentWindowIndex + 1) * CHUNK_WINDOW) - 1;
    }

    /// @notice Verify that a proposed order matches the conditional order
    /// @dev Called by ComposableCoW during signature verification
    function verify(
        address owner,
        address sender,
        bytes32 _hash,
        bytes32 domainSeparator,
        bytes32 ctx,
        bytes calldata staticInput,
        bytes calldata offchainInput,
        GPv2Order.Data calldata order
    ) external view override {
        // Silence unused variable warnings
        (owner, sender, _hash, domainSeparator, ctx, offchainInput);

        // Decode the order hash
        bytes32 orderHash = abi.decode(staticInput, (bytes32));

        // Get order context
        KapanOrderManager.OrderContext memory orderCtx = orderManager.getOrder(orderHash);

        // Validate order is active
        if (orderCtx.status != KapanOrderManager.OrderStatus.Active) {
            revert OrderNotValid("order_not_active");
        }

        // Validate tokens match
        if (address(order.sellToken) != orderCtx.params.sellToken) {
            revert OrderNotValid("sell_token_mismatch");
        }
        if (address(order.buyToken) != orderCtx.params.buyToken) {
            revert OrderNotValid("buy_token_mismatch");
        }

        // Validate receiver - must always be OrderManager for post-hook token handling
        if (order.receiver != address(orderManager)) {
            revert OrderNotValid("invalid_receiver");
        }

        // Validate sell amount doesn't exceed chunk size
        if (order.sellAmount > orderCtx.params.chunkSize) {
            revert OrderNotValid("sell_amount_exceeds_chunk");
        }

        // Validate minimum buy amount
        if (order.buyAmount < orderCtx.params.minBuyPerChunk) {
            revert OrderNotValid("buy_amount_too_low");
        }

        // Validate appData matches
        if (order.appData != orderCtx.params.appDataHash) {
            revert OrderNotValid("appdata_mismatch");
        }

        // Order is valid
    }

    // ============ View Functions ============

    /// @notice Get the current chunk parameters for an order
    /// @param orderHash The order hash
    /// @return sellAmount The sell amount for the next chunk
    /// @return minBuyAmount The minimum buy amount
    /// @return isComplete Whether the order is complete
    function getChunkParams(bytes32 orderHash) external view returns (
        uint256 sellAmount,
        uint256 minBuyAmount,
        bool isComplete
    ) {
        KapanOrderManager.OrderContext memory orderCtx = orderManager.getOrder(orderHash);

        isComplete = orderManager.isOrderComplete(orderHash) || 
                     orderCtx.status != KapanOrderManager.OrderStatus.Active;

        if (isComplete) {
            return (0, 0, true);
        }

        uint256 remaining = orderCtx.params.preTotalAmount - orderCtx.executedAmount;
        sellAmount = remaining < orderCtx.params.chunkSize 
            ? remaining 
            : orderCtx.params.chunkSize;
        minBuyAmount = orderCtx.params.minBuyPerChunk;
    }

    /// @notice Get order progress
    /// @param orderHash The order hash
    /// @return executed Amount executed so far
    /// @return total Total amount to execute
    /// @return iterations Number of iterations completed
    function getProgress(bytes32 orderHash) external view returns (
        uint256 executed,
        uint256 total,
        uint256 iterations
    ) {
        KapanOrderManager.OrderContext memory orderCtx = orderManager.getOrder(orderHash);
        executed = orderCtx.executedAmount;
        total = orderCtx.params.preTotalAmount;
        iterations = orderCtx.iterationCount;
    }

    // ============ ERC-165 Implementation ============

    /// @notice Check if contract supports an interface
    /// @dev Required for ComposableCoW to verify handler compatibility
    function supportsInterface(bytes4 interfaceId) external pure override returns (bool) {
        return interfaceId == type(IConditionalOrderGenerator).interfaceId ||
               interfaceId == type(IConditionalOrder).interfaceId ||
               interfaceId == type(IERC165).interfaceId;
    }
}

contracts/v2/cow/KapanOrderManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import { IComposableCoW, IValueFactory } from "../interfaces/cow/IComposableCoW.sol";
import { IConditionalOrder } from "../interfaces/cow/IConditionalOrder.sol";
import { IERC1271, ERC1271_MAGIC_VALUE } from "../interfaces/cow/IERC1271.sol";
import { GPv2Order } from "../interfaces/cow/GPv2Order.sol";
import { IGPv2Settlement } from "../interfaces/cow/IGPv2Settlement.sol";
import { ProtocolTypes } from "../interfaces/ProtocolTypes.sol";

import "hardhat/console.sol";

// Forward declaration for KapanRouter interface
interface IKapanRouter {
    function processProtocolInstructions(ProtocolTypes.ProtocolInstruction[] calldata instructions) external;
    function isAuthorizedFor(address user) external view returns (bool);
}

/// @title KapanOrderManager
/// @notice Manages CoW Protocol orders for Kapan, acting as ERC-1271 signer
/// @dev Singleton contract that holds order context and executes hooks
contract KapanOrderManager is Ownable, ReentrancyGuard, IERC1271 {
    using SafeERC20 for IERC20;

    // ============ Errors ============
    error OrderNotFound();
    error OrderAlreadyExists();
    error InvalidHandler();
    error NotHooksTrampoline();
    error HookExecutionFailed();
    error Unauthorized();
    error InvalidOrderState();
    error ZeroAddress();
    error InstructionUserMismatch(address expected, address actual);

    // ============ Enums ============
    enum CompletionType {
        TargetLTV,          // Complete when target LTV is reached
        TargetBalance,      // Complete when target balance is reached
        Iterations,         // Complete after N iterations
        UntilCancelled      // Run until manually cancelled
    }

    enum OrderStatus {
        None,
        Active,
        Completed,
        Cancelled
    }

    // Router instruction types (must match KapanRouter)
    enum RouterInstructionType {
        FlashLoan,
        PullToken,
        PushToken,
        ToOutput,
        Approve,
        Split,
        Add,
        Subtract
    }

    // Router instruction struct (must match KapanRouter)
    struct RouterInstruction {
        uint256 amount;
        address token;
        address user;
        RouterInstructionType instructionType;
    }

    // ============ Structs ============

    /// @notice Order parameters stored for each order
    struct KapanOrderParams {
        address user;

        // Pre-swap instructions (executed in pre-hook) - per iteration
        // Array of ABI-encoded ProtocolInstruction[] for each iteration
        // If fewer entries than iterations, last entry is reused
        bytes[] preInstructionsPerIteration;
        uint256 preTotalAmount;         // Total amount to process across all chunks

        // Swap configuration
        address sellToken;
        address buyToken;
        uint256 chunkSize;              // Max sell amount per chunk
        uint256 minBuyPerChunk;         // Minimum buy amount per chunk (slippage protection)

        // Post-swap instructions (executed in post-hook) - per iteration
        // Array of ABI-encoded ProtocolInstruction[] for each iteration
        // If fewer entries than iterations, last entry is reused
        // Typically: [0..N-2] = deposit+borrow+push, [N-1] = deposit only
        bytes[] postInstructionsPerIteration;

        // Completion
        CompletionType completion;
        uint256 targetValue;            // Interpretation depends on CompletionType
        uint256 minHealthFactor;        // Safety: minimum health factor to maintain

        // AppData (pre-computed by frontend, includes hooks)
        bytes32 appDataHash;

        // Flash loan mode: when true, order.receiver = Settlement (required by CoW solvers)
        // When false (multi-chunk mode), order.receiver = OrderManager
        bool isFlashLoanOrder;
    }

    /// @notice Order context stored on-chain
    struct OrderContext {
        KapanOrderParams params;
        OrderStatus status;
        uint256 executedAmount;         // Amount processed so far
        uint256 iterationCount;         // Number of completed iterations
        uint256 createdAt;
    }

    // ============ State Variables ============

    /// @notice The KapanRouter for executing lending operations
    IKapanRouter public router;

    /// @notice The ComposableCoW contract for order registration
    IComposableCoW public composableCoW;

    /// @notice The GPv2Settlement contract
    IGPv2Settlement public settlement;

    /// @notice The HooksTrampoline contract (caller for hooks)
    address public hooksTrampoline;

    /// @notice The KapanOrderHandler for generating orders
    address public orderHandler;

    /// @notice Order contexts by order hash
    mapping(bytes32 => OrderContext) public orders;

    /// @notice Salt used for each order (for ComposableCoW cancellation)
    mapping(bytes32 => bytes32) public orderSalts;

    /// @notice User's active orders
    mapping(address => bytes32[]) public userOrders;

    /// @notice Lookup order hash by (user, salt) - enables pre-computing appData
    mapping(address => mapping(bytes32 => bytes32)) public userSaltToOrderHash;

    // ============ Events ============

    event OrderCreated(
        bytes32 indexed orderHash,
        address indexed user,
        address sellToken,
        address buyToken,
        uint256 totalAmount,
        uint256 chunkSize
    );

    event OrderCancelled(bytes32 indexed orderHash, address indexed user);
    event OrderCompleted(bytes32 indexed orderHash, address indexed user, uint256 totalExecuted);
    event ChunkExecuted(bytes32 indexed orderHash, uint256 chunkIndex, uint256 sellAmount, uint256 buyAmount);
    event PreHookExecuted(bytes32 indexed orderHash, uint256 chunkIndex);
    event PostHookExecuted(bytes32 indexed orderHash, uint256 chunkIndex, uint256 receivedAmount);

    // Admin events
    event RouterUpdated(address indexed oldRouter, address indexed newRouter);
    event ComposableCoWUpdated(address indexed oldComposableCoW, address indexed newComposableCoW);
    event HooksTrampolineUpdated(address indexed oldTrampoline, address indexed newTrampoline);
    event OrderHandlerUpdated(address indexed oldHandler, address indexed newHandler);

    // ============ Constructor ============

    constructor(
        address _owner,
        address _router,
        address _composableCoW,
        address _settlement,
        address _hooksTrampoline
    ) Ownable(_owner) {
        if (_router == address(0)) revert ZeroAddress();
        if (_composableCoW == address(0)) revert ZeroAddress();
        if (_settlement == address(0)) revert ZeroAddress();
        if (_hooksTrampoline == address(0)) revert ZeroAddress();

        router = IKapanRouter(_router);
        composableCoW = IComposableCoW(_composableCoW);
        settlement = IGPv2Settlement(_settlement);
        hooksTrampoline = _hooksTrampoline;
    }

    // ============ Admin Functions ============

    function setRouter(address _router) external onlyOwner {
        if (_router == address(0)) revert ZeroAddress();
        address oldRouter = address(router);
        router = IKapanRouter(_router);
        emit RouterUpdated(oldRouter, _router);
    }

    function setComposableCoW(address _composableCoW) external onlyOwner {
        if (_composableCoW == address(0)) revert ZeroAddress();
        address oldComposableCoW = address(composableCoW);
        composableCoW = IComposableCoW(_composableCoW);
        emit ComposableCoWUpdated(oldComposableCoW, _composableCoW);
    }

    function setHooksTrampoline(address _hooksTrampoline) external onlyOwner {
        if (_hooksTrampoline == address(0)) revert ZeroAddress();
        address oldTrampoline = hooksTrampoline;
        hooksTrampoline = _hooksTrampoline;
        emit HooksTrampolineUpdated(oldTrampoline, _hooksTrampoline);
    }

    function setOrderHandler(address _handler) external onlyOwner {
        if (_handler == address(0)) revert ZeroAddress();
        address oldHandler = orderHandler;
        orderHandler = _handler;
        emit OrderHandlerUpdated(oldHandler, _handler);
    }

    /// @notice Approve vault relayer to spend a token
    function approveVaultRelayer(address token) external onlyOwner {
        if (token == address(0)) revert ZeroAddress();
        address vaultRelayer = settlement.vaultRelayer();
        IERC20(token).forceApprove(vaultRelayer, type(uint256).max);
    }

    // ============ Order Creation ============

    /// @notice Create a new CoW order with seed tokens for first chunk
    /// @dev User must approve this contract to pull seedAmount of sellToken before calling
    /// @param params The order parameters
    /// @param salt Unique salt for the order
    /// @param seedAmount Amount of sell tokens to pull from user for first chunk (0 to skip)
    /// @return orderHash The hash identifying this order
    function createOrder(
        KapanOrderParams calldata params,
        bytes32 salt,
        uint256 seedAmount
    ) external nonReentrant returns (bytes32 orderHash) {
        // Validate caller is the order user
        if (params.user != msg.sender) revert Unauthorized();
        if (orderHandler == address(0)) revert InvalidHandler();

        // TODO: Re-enable after debugging - temporarily disabled to isolate issue
        // Validate all instructions target the caller (prevents encoding instructions for other users)
        // _validateInstructionUsers(params.preInstructionsPerIteration, msg.sender);
        // _validateInstructionUsers(params.postInstructionsPerIteration, msg.sender);

        // Compute order hash
        orderHash = keccak256(abi.encode(params, salt, block.timestamp));

        // Check order doesn't exist
        if (orders[orderHash].status != OrderStatus.None) revert OrderAlreadyExists();

        // Pull seed tokens from user if provided
        // This ensures OrderManager has balance for CoW API's balance check
        if (seedAmount > 0) {
            IERC20(params.sellToken).safeTransferFrom(msg.sender, address(this), seedAmount);
        }

        // Store order context
        orders[orderHash] = OrderContext({
            params: params,
            status: OrderStatus.Active,
            executedAmount: 0,
            iterationCount: 0,
            createdAt: block.timestamp
        });

        // Store salt separately for cancellation
        orderSalts[orderHash] = salt;

        // Track user's orders
        userOrders[params.user].push(orderHash);

        // Enable lookup by (user, salt) for hook resolution
        userSaltToOrderHash[params.user][salt] = orderHash;

        // Register with ComposableCoW
        IConditionalOrder.ConditionalOrderParams memory cowParams = IConditionalOrder.ConditionalOrderParams({
            handler: IConditionalOrder(orderHandler),
            salt: salt,
            staticData: abi.encode(orderHash)  // Handler looks up context by hash
        });

        composableCoW.create(cowParams, true);  // dispatch=true for WatchTower

        // Ensure vault relayer can spend sellToken
        _ensureVaultRelayerApproval(params.sellToken);

        emit OrderCreated(
            orderHash,
            params.user,
            params.sellToken,
            params.buyToken,
            params.preTotalAmount,
            params.chunkSize
        );
    }

    // ============ Order Cancellation ============

    /// @notice Cancel an active order and refund any remaining seed tokens
    /// @param orderHash The order to cancel
    function cancelOrder(bytes32 orderHash) external nonReentrant {
        OrderContext storage ctx = orders[orderHash];

        if (ctx.status == OrderStatus.None) revert OrderNotFound();
        if (ctx.params.user != msg.sender) revert Unauthorized();
        if (ctx.status != OrderStatus.Active) revert InvalidOrderState();

        ctx.status = OrderStatus.Cancelled;

        // Remove from ComposableCoW
        // Note: We need to compute the same hash ComposableCoW uses
        bytes32 cowOrderHash = composableCoW.hash(
            IConditionalOrder.ConditionalOrderParams({
                handler: IConditionalOrder(orderHandler),
                salt: orderSalts[orderHash],
                staticData: abi.encode(orderHash)
            })
        );
        composableCoW.remove(cowOrderHash);

        // Refund any remaining tokens to user
        // This handles cases where:
        // 1. Seed tokens were provided but order never filled
        // 2. Partial execution left some sell tokens
        // 3. A chunk was in-flight and buy tokens arrived but couldn't be processed
        uint256 sellRemaining = IERC20(ctx.params.sellToken).balanceOf(address(this));
        if (sellRemaining > 0) {
            IERC20(ctx.params.sellToken).safeTransfer(ctx.params.user, sellRemaining);
        }

        // Also refund any buy tokens that might be stuck (from partial chunk execution)
        uint256 buyRemaining = IERC20(ctx.params.buyToken).balanceOf(address(this));
        if (buyRemaining > 0) {
            IERC20(ctx.params.buyToken).safeTransfer(ctx.params.user, buyRemaining);
        }

        emit OrderCancelled(orderHash, msg.sender);
    }

    // ============ Hook Execution ============

    /// @notice Execute pre-hook (called by HooksTrampoline during CoW settlement)
    /// @dev Prepends a ToOutput instruction with chunkSize, so all stored instructions
    ///      can reference index 0 for the chunk amount. Chunk index is read from iterationCount.
    /// @param orderHash The order being settled
    function executePreHook(bytes32 orderHash) external nonReentrant {
        _executePreHook(orderHash);
    }

    /// @notice Execute post-hook (called by HooksTrampoline during CoW settlement)
    /// @dev Prepends a ToOutput instruction with the actual received amount, so all
    ///      stored instructions can reference index 0 for the swap output
    /// @param orderHash The order being settled
    function executePostHook(bytes32 orderHash) external nonReentrant {
        _executePostHook(orderHash);
    }

    /// @notice Execute pre-hook using (user, salt) lookup
    /// @dev Allows pre-computing appData before order creation. Chunk index is determined
    ///      from the order's iterationCount, so the same appData works for all chunks.
    /// @param user The order owner
    /// @param salt The order salt (known before tx)
    function executePreHookBySalt(address user, bytes32 salt) external nonReentrant {
        bytes32 orderHash = userSaltToOrderHash[user][salt];
        if (orderHash == bytes32(0)) revert OrderNotFound();
        _executePreHook(orderHash);
    }

    /// @notice Execute post-hook using (user, salt) lookup
    /// @dev Allows pre-computing appData before order creation
    /// @param user The order owner
    /// @param salt The order salt (known before tx)
    function executePostHookBySalt(address user, bytes32 salt) external nonReentrant {
        bytes32 orderHash = userSaltToOrderHash[user][salt];
        if (orderHash == bytes32(0)) revert OrderNotFound();
        _executePostHook(orderHash);
    }

    /// @notice Internal pre-hook execution
    /// @dev Chunk index is read from ctx.iterationCount so the same appData works for all chunks
    function _executePreHook(bytes32 orderHash) internal {
        // Only HooksTrampoline can call this
        if (msg.sender != hooksTrampoline) revert NotHooksTrampoline();

        OrderContext storage ctx = orders[orderHash];
        if (ctx.status != OrderStatus.Active) revert InvalidOrderState();

        // Get pre-instructions for current iteration
        // If fewer entries than iterations, reuse the last entry
        bytes memory preInstructionsData = _getInstructionsForIteration(
            ctx.params.preInstructionsPerIteration,
            ctx.iterationCount
        );

        // Decode stored pre-instructions
        ProtocolTypes.ProtocolInstruction[] memory storedInstructions = 
            abi.decode(preInstructionsData, (ProtocolTypes.ProtocolInstruction[]));

        // Create new array with ToOutput prepended
        ProtocolTypes.ProtocolInstruction[] memory instructions = 
            new ProtocolTypes.ProtocolInstruction[](storedInstructions.length + 1);

        // Prepend ToOutput with chunkSize - this becomes index 0
        instructions[0] = ProtocolTypes.ProtocolInstruction({
            protocolName: "router",
            data: _encodeToOutput(ctx.params.chunkSize, ctx.params.sellToken)
        });

        // Copy stored instructions (their indices shift by 1, but they reference index 0)
        for (uint256 i = 0; i < storedInstructions.length; i++) {
            instructions[i + 1] = storedInstructions[i];
        }

        // Execute via router (router will check delegation)
        router.processProtocolInstructions(instructions);

        // Emit with current iteration count (chunk index is self-determined from state)
        emit PreHookExecuted(orderHash, ctx.iterationCount);
    }

    /// @notice Internal post-hook execution
    function _executePostHook(bytes32 orderHash) internal {
        console.log("_executePostHook: START");

        // Only HooksTrampoline can call this
        if (msg.sender != hooksTrampoline) revert NotHooksTrampoline();

        console.log("_executePostHook: caller is HooksTrampoline");

        OrderContext storage ctx = orders[orderHash];
        if (ctx.status != OrderStatus.Active) revert InvalidOrderState();

        // Get actual received amount
        uint256 receivedAmount = IERC20(ctx.params.buyToken).balanceOf(address(this));
        console.log("_executePostHook: receivedAmount =", receivedAmount);

        // Transfer tokens to router for processing
        IERC20(ctx.params.buyToken).safeTransfer(address(router), receivedAmount);
        console.log("_executePostHook: transferred to router");

        // Get post-instructions for current iteration
        // If fewer entries than iterations, reuse the last entry
        bytes memory postInstructionsData = _getInstructionsForIteration(
            ctx.params.postInstructionsPerIteration,
            ctx.iterationCount
        );

        // Decode stored post-instructions
        ProtocolTypes.ProtocolInstruction[] memory storedInstructions = 
            abi.decode(postInstructionsData, (ProtocolTypes.ProtocolInstruction[]));

        // Create new array with ToOutput prepended
        ProtocolTypes.ProtocolInstruction[] memory instructions = 
            new ProtocolTypes.ProtocolInstruction[](storedInstructions.length + 1);

        // Prepend ToOutput with receivedAmount - this becomes index 0
        instructions[0] = ProtocolTypes.ProtocolInstruction({
            protocolName: "router",
            data: _encodeToOutput(receivedAmount, ctx.params.buyToken)
        });

        // Copy stored instructions (their indices shift by 1, but they reference index 0)
        for (uint256 i = 0; i < storedInstructions.length; i++) {
            instructions[i + 1] = storedInstructions[i];
        }

        console.log("_executePostHook: instruction count =", instructions.length);
        console.log("_executePostHook: calling router.processProtocolInstructions");

        // Execute via router
        try router.processProtocolInstructions(instructions) {
            console.log("_executePostHook: router call SUCCESS");
        } catch Error(string memory reason) {
            console.log("_executePostHook: router call FAILED with reason:", reason);
            revert(reason);
        } catch (bytes memory lowLevelData) {
            console.log("_executePostHook: router call FAILED with low-level error");
            console.logBytes(lowLevelData);
            revert("Router call failed");
        }

        // Update progress
        uint256 chunkSellAmount = ctx.params.chunkSize;
        ctx.executedAmount += chunkSellAmount;
        ctx.iterationCount++;

        // Emit chunk execution event with sell/buy amounts for frontend tracking
        emit ChunkExecuted(orderHash, ctx.iterationCount, chunkSellAmount, receivedAmount);

        // Check completion
        if (_isOrderComplete(ctx)) {
            ctx.status = OrderStatus.Completed;
            emit OrderCompleted(orderHash, ctx.params.user, ctx.executedAmount);
        }

        emit PostHookExecuted(orderHash, ctx.iterationCount, receivedAmount);
        console.log("_executePostHook: COMPLETE");
    }

    // ============ ERC-1271 Signature Verification ============

    /// @notice Verify signature for CoW Protocol orders
    /// @dev For non-Safe ERC-1271 contracts, ComposableCoW returns signature as:
    ///      abi.encode(GPv2Order.Data, PayloadStruct)
    ///      We decode this and verify the order matches what the handler generates.
    function isValidSignature(
        bytes32 _hash,
        bytes calldata _signature
    ) external view override returns (bytes4) {
        // For ERC-1271 forwarder pattern (non-Safe), signature = abi.encode(order, payload)
        // We need to decode both parts
        (GPv2Order.Data memory order, IComposableCoW.PayloadStruct memory payload) = 
            abi.decode(_signature, (GPv2Order.Data, IComposableCoW.PayloadStruct));

        // Verify via ComposableCoW - this checks authorization and generates the expected order
        // ComposableCoW will call the handler's verify() function
        try composableCoW.getTradeableOrderWithSignature(
            address(this),
            payload.params,
            payload.offchainInput,
            payload.proof
        ) returns (GPv2Order.Data memory expectedOrder, bytes memory) {
            // Verify the provided order matches the expected order
            // The hash should match what we expect for this order
            if (_orderMatches(order, expectedOrder)) {
                return ERC1271_MAGIC_VALUE;
            }
            return bytes4(0xffffffff);
        } catch {
            return bytes4(0xffffffff);
        }
    }

    /// @dev Check if two GPv2Orders match (excluding validTo which changes)
    function _orderMatches(GPv2Order.Data memory a, GPv2Order.Data memory b) internal pure returns (bool) {
        return address(a.sellToken) == address(b.sellToken) &&
               address(a.buyToken) == address(b.buyToken) &&
               a.receiver == b.receiver &&
               a.sellAmount == b.sellAmount &&
               a.buyAmount == b.buyAmount &&
               a.appData == b.appData &&
               a.feeAmount == b.feeAmount &&
               a.kind == b.kind &&
               a.partiallyFillable == b.partiallyFillable &&
               a.sellTokenBalance == b.sellTokenBalance &&
               a.buyTokenBalance == b.buyTokenBalance;
    }

    // ============ View Functions ============

    /// @notice Get order context
    function getOrder(bytes32 orderHash) external view returns (OrderContext memory) {
        return orders[orderHash];
    }

    /// @notice Get user's orders
    function getUserOrders(address user) external view returns (bytes32[] memory) {
        return userOrders[user];
    }

    /// @notice Check if an order is complete based on completion criteria
    function isOrderComplete(bytes32 orderHash) external view returns (bool) {
        return _isOrderComplete(orders[orderHash]);
    }

    // ============ Internal Functions ============

    function _isOrderComplete(OrderContext storage ctx) internal view returns (bool) {
        if (ctx.params.completion == CompletionType.Iterations) {
            return ctx.iterationCount >= ctx.params.targetValue;
        }

        if (ctx.params.completion == CompletionType.TargetBalance) {
            // Check remaining amount
            uint256 remaining = ctx.params.preTotalAmount - ctx.executedAmount;
            return remaining == 0 || remaining < ctx.params.chunkSize;
        }

        if (ctx.params.completion == CompletionType.TargetLTV) {
            // TODO: Read actual LTV from lending protocol
            // For now, check if we've executed the total amount
            return ctx.executedAmount >= ctx.params.preTotalAmount;
        }

        // UntilCancelled never completes automatically
        return false;
    }

    // ============ Instruction Validation ============

    /// @dev Constant hash for "router" protocol name comparison
    bytes32 private constant ROUTER_PROTOCOL_HASH = keccak256(abi.encodePacked("router"));

    /// @notice Validate that all instructions in all iterations target the expected user
    /// @dev Prevents attackers from encoding instructions that operate on other users' positions
    ///      Note: Only certain instruction types require strict validation (PullToken, lending ops)
    /// @param instructionsPerIteration Array of encoded ProtocolInstruction[] per iteration
    /// @param expectedUser The user who must be the target of security-sensitive instructions
    function _validateInstructionUsers(
        bytes[] calldata instructionsPerIteration,
        address expectedUser
    ) internal pure {
        for (uint256 i = 0; i < instructionsPerIteration.length; i++) {
            ProtocolTypes.ProtocolInstruction[] memory instructions = 
                abi.decode(instructionsPerIteration[i], (ProtocolTypes.ProtocolInstruction[]));

            for (uint256 j = 0; j < instructions.length; j++) {
                address instrUser = _extractUserFromInstruction(instructions[j], expectedUser);
                if (instrUser != expectedUser) {
                    revert InstructionUserMismatch(expectedUser, instrUser);
                }
            }
        }
    }

    /// @notice Extract the user address from a protocol instruction that requires validation
    /// @dev Returns expectedUser for instructions that don't need user validation (PushToken, ToOutput, etc.)
    ///      Only PullToken and lending instructions need strict user validation
    /// @param instruction The protocol instruction to extract user from
    /// @param expectedUser The expected user (returned for instructions that don't need validation)
    /// @return user The user address to validate (or expectedUser if no validation needed)
    function _extractUserFromInstruction(
        ProtocolTypes.ProtocolInstruction memory instruction,
        address expectedUser
    ) internal pure returns (address user) {
        bytes32 protocolHash = keccak256(abi.encodePacked(instruction.protocolName));

        if (protocolHash == ROUTER_PROTOCOL_HASH) {
            // Decode RouterInstruction (same method as KapanRouter)
            RouterInstruction memory routerInstruction = abi.decode(instruction.data, (RouterInstruction));

            // Only PullToken needs strict user validation (prevents stealing from other users)
            // Other instruction types have different semantics for the user field:
            // - PushToken: user = recipient (can be OrderManager, adapters, etc.)
            // - ToOutput: user = unused (always address(0))
            // - Approve: user = unused (always address(0))
            // - FlashLoan: user = unused (always address(0))
            // - Add/Subtract/Split: user = unused (always address(0))
            if (routerInstruction.instructionType == RouterInstructionType.PullToken) {
                user = routerInstruction.user;
            } else {
                // Skip validation for other router instructions
                user = expectedUser;
            }
        } else {
            // Lending instruction (gateway): struct LendingInstruction { op, token, user, amount, context, input }
            // All lending operations need strict user validation
            ProtocolTypes.LendingInstruction memory lending = 
                abi.decode(instruction.data, (ProtocolTypes.LendingInstruction));
            user = lending.user;
        }
    }

    /// @notice Encode a ToOutput router instruction
    /// @dev Matches the encoding expected by KapanRouter's RouterInstruction struct
    ///      struct RouterInstruction { uint256 amount; address token; address user; RouterInstructionType instructionType; }
    ///      RouterInstructionType.ToOutput = 3
    function _encodeToOutput(uint256 amount, address token) internal pure returns (bytes memory) {
        // ABI encode as tuple matching RouterInstruction struct layout
        // Note: RouterInstructionType enum is uint8, but in ABI encoding it's padded to 32 bytes
        return abi.encode(amount, token, address(0), uint8(3));
    }

    /// @notice Get instructions for a specific iteration
    /// @dev If the array has fewer entries than the iteration index, returns the last entry
    ///      This allows specifying different instructions for early iterations vs later ones
    ///      e.g., [depositAndBorrow, depositAndBorrow, depositOnly] for 3 iterations
    /// @param instructionsArray Array of encoded instructions per iteration
    /// @param iteration Current iteration index (0-based)
    /// @return The encoded instructions for this iteration
    function _getInstructionsForIteration(
        bytes[] storage instructionsArray,
        uint256 iteration
    ) internal view returns (bytes memory) {
        if (instructionsArray.length == 0) {
            // Return empty instructions if none provided
            return abi.encode(new ProtocolTypes.ProtocolInstruction[](0));
        }

        // If iteration index exceeds array length, use the last entry
        uint256 index = iteration < instructionsArray.length 
            ? iteration 
            : instructionsArray.length - 1;

        return instructionsArray[index];
    }

    function _ensureVaultRelayerApproval(address token) internal {
        address vaultRelayer = settlement.vaultRelayer();
        uint256 currentAllowance = IERC20(token).allowance(address(this), vaultRelayer);
        if (currentAllowance < type(uint256).max / 2) {
            IERC20(token).forceApprove(vaultRelayer, type(uint256).max);
        }
    }

    // ============ Emergency Functions ============

    /// @notice Recover stuck tokens (owner only)
    function recoverTokens(address token, address to, uint256 amount) external onlyOwner {
        IERC20(token).safeTransfer(to, amount);
    }
}

contracts/v2/flashloans/FlashLoanConsume.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

// --- Existing Balancer imports ---
import { IFlashLoanProvider } from "../interfaces/balancer/IFlashLoanProvider.sol";
import { IVaultV3 } from "../interfaces/balancer/IVaultV3.sol";

// --- Morpho Blue imports ---
import { IMorphoBlue, IMorphoFlashLoanCallback } from "../interfaces/morpho/IMorphoBlue.sol";

// --- Minimal Aave v3 interfaces (flashLoanSimple) ---
interface IAaveV3Pool {
    function flashLoanSimple(
        address receiverAddress,
        address asset,
        uint256 amount,
        bytes calldata params,
        uint16 referralCode
    ) external;
}

interface IAaveFlashLoanSimpleReceiver {
    function executeOperation(
        address asset,
        uint256 amount,
        uint256 premium,
        address initiator,
        bytes calldata params
    ) external returns (bool);
}

// --- Minimal Uniswap v3 factory/pool interfaces (with auth support) ---
interface IUniswapV3Factory {
    function getPool(address tokenA, address tokenB, uint24 fee) external view returns (address);
}

interface IUniswapV3Pool {
    function token0() external view returns (address);
    function token1() external view returns (address);
    function fee() external view returns (uint24);
    function factory() external view returns (address);

    function flash(address recipient, uint256 amount0, uint256 amount1, bytes calldata data) external;
}

// Custom errors for flash loans
error FlashLoanNotEnabled();
error UnauthorizedFlashCaller();
error NestedFlashNotAllowed();
error ProviderNotConfigured();
error InvalidFlashParams();
error BadInitiator();
error InvalidPool();
error TokenNotInPool();

/// @notice Abstract base supporting Balancer v2/v3, Aave v3 (and forks like ZeroLend), Uniswap v3, and Morpho flash sources.
abstract contract FlashLoanConsumerBase is IAaveFlashLoanSimpleReceiver, IMorphoFlashLoanCallback {
    using SafeERC20 for IERC20;

    // ----------------- Providers (config) -----------------
    IFlashLoanProvider public balancerV2Vault; // Balancer v2 Vault
    IVaultV3 public balancerV3Vault;          // Balancer v3 Vault

    mapping(bytes32 => address) public aaveCompatiblePools; // key hash => pool address
    mapping(address => bool) public isAaveCompatiblePool;   // callback validation allowlist

    // Trusted Uniswap v3 factory (non-zero enables Uniswap v3)
    address public uniswapV3Factory;

    // Morpho Blue singleton (non-zero enables Morpho flash loans)
    address public morphoBlue;

    // ----------------- Callback guard -----------------
    bool private flashLoanEnabled;
    address private authorizedFlashSender;



    // ----------------- Setters -----------------
    function _setBalancerV2(address provider) internal {
        balancerV2Vault = IFlashLoanProvider(provider);
    }

    function _setBalancerV3(address vault) internal {
        balancerV3Vault = IVaultV3(vault);
    }

    function _addAaveCompatiblePool(string memory key, address pool) internal {
        bytes32 keyHash = keccak256(abi.encodePacked(key));
        address oldPool = aaveCompatiblePools[keyHash];
        if (oldPool != address(0)) isAaveCompatiblePool[oldPool] = false;

        aaveCompatiblePools[keyHash] = pool;
        if (pool != address(0)) isAaveCompatiblePool[pool] = true;
    }

    function _setUniswapV3Factory(address factory) internal {
        uniswapV3Factory = factory;
    }

    function _setMorphoBlue(address morpho) internal {
        morphoBlue = morpho;
    }

    // ----------------- Guard modifiers -----------------
    modifier flashLoanOnly() {
        if (!flashLoanEnabled) revert FlashLoanNotEnabled();
        if (msg.sender != authorizedFlashSender) revert UnauthorizedFlashCaller();
        _;
    }

    modifier authorizeFlashLoan(address expectedSender) {
        if (flashLoanEnabled) revert NestedFlashNotAllowed();
        flashLoanEnabled = true;
        authorizedFlashSender = expectedSender;
        _;
        authorizedFlashSender = address(0);
        flashLoanEnabled = false;
    }

    // ============================================================
    //                          BALANCER V2
    // ============================================================

    function _requestBalancerV2(
        address token,
        uint256 amount,
        bytes memory userData
    ) internal authorizeFlashLoan(address(balancerV2Vault)) {
        if (address(balancerV2Vault) == address(0)) revert ProviderNotConfigured();
        if (token == address(0) || amount == 0) revert InvalidFlashParams();

        IERC20[] memory tokens = new IERC20[](1);
        tokens[0] = IERC20(token);
        uint256[] memory amounts = new uint256[](1);
        amounts[0] = amount;

        balancerV2Vault.flashLoan(address(this), tokens, amounts, userData);
    }

    function receiveFlashLoan(
        IERC20[] calldata tokens,
        uint256[] calldata amounts,
        uint256[] calldata feeAmounts,
        bytes calldata userData
    ) external flashLoanOnly {
        uint256 repayment = amounts[0] + feeAmounts[0];
        _afterFlashLoan(address(tokens[0]), repayment, userData);
        tokens[0].safeTransfer(address(balancerV2Vault), repayment);
    }

    // ============================================================
    //                          BALANCER V3
    // ============================================================

    function _requestBalancerV3(address token, uint256 amount)
        internal
        authorizeFlashLoan(address(balancerV3Vault))
    {
        if (address(balancerV3Vault) == address(0)) revert ProviderNotConfigured();
        if (token == address(0) || amount == 0) revert InvalidFlashParams();

        bytes memory userData = abi.encode(token, amount);
        balancerV3Vault.unlock(abi.encodeWithSelector(this.receiveFlashLoanV3.selector, userData));
    }

    function receiveFlashLoanV3(bytes calldata userData) external flashLoanOnly {
        (address token, uint256 amount) = abi.decode(userData, (address, uint256));
        balancerV3Vault.sendTo(token, address(this), amount);
        _afterFlashLoan(token, amount, userData);
        IERC20(token).safeTransfer(address(balancerV3Vault), amount);
        balancerV3Vault.settle(token, amount);
    }

    // ============================================================
    //                   AAVE V3 & COMPATIBLE FORKS
    // ============================================================

    function _requestAaveCompatible(
        string memory poolKey,
        address token,
        uint256 amount,
        bytes memory userData
    ) internal {
        address pool = aaveCompatiblePools[keccak256(abi.encodePacked(poolKey))];
        if (pool == address(0)) revert ProviderNotConfigured();
        _requestAaveCompatibleInternal(pool, token, amount, userData);
    }

    function _requestAaveCompatibleInternal(
        address pool,
        address token,
        uint256 amount,
        bytes memory userData
    ) private authorizeFlashLoan(pool) {
        if (token == address(0) || amount == 0) revert InvalidFlashParams();
        IAaveV3Pool(pool).flashLoanSimple(address(this), token, amount, userData, 0);
    }

    function executeOperation(
        address asset,
        uint256 amount,
        uint256 premium,
        address initiator,
        bytes calldata params
    ) external override flashLoanOnly returns (bool) {
        if (initiator != address(this)) revert BadInitiator();
        uint256 repayment = amount + premium;
        _afterFlashLoan(asset, repayment, params);
        IERC20(asset).forceApprove(msg.sender, repayment);
        return true;
    }

    // ============================================================
    //                          UNISWAP V3
    // ============================================================

    function _requestUniswapV3(
        address pool,
        address token,
        uint256 amount,
        bytes memory userData
    ) internal authorizeFlashLoan(pool) {
        if (uniswapV3Factory == address(0)) revert ProviderNotConfigured();
        if (pool == address(0) || token == address(0) || amount == 0) revert InvalidFlashParams();

        IUniswapV3Pool p = IUniswapV3Pool(pool);
        if (p.factory() != uniswapV3Factory) revert InvalidPool();

        address t0 = p.token0();
        address t1 = p.token1();
        if (IUniswapV3Factory(uniswapV3Factory).getPool(t0, t1, p.fee()) != pool) revert InvalidPool();

        bool is0 = token == t0;
        if (!is0 && token != t1) revert TokenNotInPool();

        p.flash(address(this), is0 ? amount : 0, is0 ? 0 : amount, abi.encode(pool, token, amount, userData, is0));
    }

    function uniswapV3FlashCallback(uint256 fee0, uint256 fee1, bytes calldata data) external flashLoanOnly {
        (address pool, address token, uint256 amount, bytes memory userData, bool is0) =
            abi.decode(data, (address, address, uint256, bytes, bool));
        uint256 repayment = amount + (is0 ? fee0 : fee1);
        _afterFlashLoan(token, repayment, userData);
        IERC20(token).safeTransfer(pool, repayment);
    }

    // ============================================================
    //                          MORPHO BLUE
    // ============================================================

    function _requestMorpho(
        address token,
        uint256 amount,
        bytes memory userData
    ) internal authorizeFlashLoan(morphoBlue) {
        if (morphoBlue == address(0)) revert ProviderNotConfigured();
        if (token == address(0) || amount == 0) revert InvalidFlashParams();
        IMorphoBlue(morphoBlue).flashLoan(token, amount, abi.encode(token, userData));
    }

    function onMorphoFlashLoan(uint256 assets, bytes calldata data) external override flashLoanOnly {
        (address token, bytes memory userData) = abi.decode(data, (address, bytes));
        _afterFlashLoan(token, assets, userData);
        // Morpho uses safeTransferFrom to pull tokens back, so we need to approve
        IERC20(token).forceApprove(morphoBlue, assets);
    }

    // ============================================================
    //                       ABSTRACT RESUME HOOK
    // ============================================================

    function _afterFlashLoan(address token, uint256 repaymentAmount, bytes memory userData) internal virtual;
}

contracts/v2/gateways/aave/AaveGatewayWr.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { ProtocolGateway } from "../../../gateways/ProtocolGateway.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import { IGateway } from "../../interfaces/IGateway.sol";
import { ProtocolTypes } from "../../interfaces/ProtocolTypes.sol";

import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { IPool } from "@aave/core-v3/contracts/interfaces/IPool.sol";
import { IPoolAddressesProvider } from "@aave/core-v3/contracts/interfaces/IPoolAddressesProvider.sol";
import { IPoolDataProvider } from "@aave/core-v3/contracts/interfaces/IPoolDataProvider.sol";

interface IVariableDebtToken {
    function borrowAllowance(address fromUser, address spender) external view returns (uint256);
}

contract AaveGatewayWrite is IGateway, ProtocolGateway, ReentrancyGuard {
    using SafeERC20 for IERC20;

    IPoolAddressesProvider public immutable poolAddressesProvider;
    uint16 public immutable REFERRAL_CODE;

    constructor(address router, address _poolAddressesProvider, uint16 _referralCode) ProtocolGateway(router) {
        poolAddressesProvider = IPoolAddressesProvider(_poolAddressesProvider);
        REFERRAL_CODE = _referralCode;
    }

    function processLendingInstruction(
        ProtocolTypes.Output[] calldata inputs,
        bytes calldata data
    ) external onlyRouter returns (ProtocolTypes.Output[] memory outputs) {
        ProtocolTypes.LendingInstruction memory instr = abi.decode(data, (ProtocolTypes.LendingInstruction));
        address token = instr.token;
        uint256 amount = instr.amount;
        if (instr.input.index < inputs.length) {
            token = inputs[instr.input.index].token;
            amount = inputs[instr.input.index].amount;
        }
        if (instr.op == ProtocolTypes.LendingOp.Deposit || instr.op == ProtocolTypes.LendingOp.DepositCollateral) {
            deposit(token, instr.user, amount);
            outputs = new ProtocolTypes.Output[](0);
        } else if (instr.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
            (address u, uint256 amt) = withdraw(token, instr.user, amount);
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: u, amount: amt });
        } else if (instr.op == ProtocolTypes.LendingOp.Borrow) {
            borrow(token, instr.user, amount);
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: amount });
        } else if (instr.op == ProtocolTypes.LendingOp.Repay) {
            uint256 refund = repay(token, instr.user, amount);
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: refund });
        } else if (instr.op == ProtocolTypes.LendingOp.GetBorrowBalance) {
            uint256 bal = _getBorrowBalance(token, instr.user);
            // NO BUFFER in execution
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: bal });
        } else if (instr.op == ProtocolTypes.LendingOp.GetSupplyBalance) {
            uint256 bal = _getSupplyBalance(token, instr.user);
            // NO BUFFER in execution
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: bal });
        } else if (instr.op == ProtocolTypes.LendingOp.SetEMode) {
            // Note: SetEMode is handled specially - it produces approval targets
            // for the user to call the Pool directly (see authorize function)
            outputs = new ProtocolTypes.Output[](0);
        } else {
            revert("Unknown op");
        }
    }

    function deposit(address token, address onBehalfOf, uint256 amount) internal nonReentrant {
        address pool = poolAddressesProvider.getPool();
        require(pool != address(0), "Pool not set");
        // Router should approve this gateway to pull from router balance
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(token).forceApprove(pool, amount);
        IPool(pool).supply(token, amount, onBehalfOf, REFERRAL_CODE);
    }

    function withdraw(
        address underlying,
        address user,
        uint256 amount
    ) internal nonReentrant returns (address, uint256) {
        address aToken = _getAToken(underlying);
        require(aToken != address(0), "aToken not found");
        IERC20 a = IERC20(aToken);
        uint256 balance = a.balanceOf(user);
        // Clamp amount to balance if requested amount > balance
        // This handles rounding errors (e.g. 1 wei less) and type(uint256).max
        if (amount > balance) {
            amount = balance;
        }
        uint256 allowance = a.allowance(user, address(this));
        require(allowance >= amount, "aToken: allowance");
        a.safeTransferFrom(user, address(this), amount);
        address pool = poolAddressesProvider.getPool();
        require(pool != address(0), "Pool not set");
        uint256 amountOut = IPool(pool).withdraw(underlying, amount, address(this));
        IERC20(underlying).safeTransfer(msg.sender, amountOut);
        return (underlying, amountOut);
    }

    function borrow(address token, address user, uint256 amount) internal nonReentrant {
        address pool = poolAddressesProvider.getPool();
        require(pool != address(0), "Pool not set");
        (, , address vDebt) = _getReserveTokens(token);
        require(vDebt != address(0), "vDebt not found");
        uint256 allowance = IVariableDebtToken(vDebt).borrowAllowance(user, address(this));
        require(allowance >= amount, "borrow allowance");
        IPool(pool).borrow(token, amount, 2, REFERRAL_CODE, user);
        IERC20(token).safeTransfer(msg.sender, amount);
    }

    function repay(
        address token,
        address user,
        uint256 amount
    ) internal nonReentrant returns (uint256 refund) {
        address pool = poolAddressesProvider.getPool();
        require(pool != address(0), "Pool not set");
        uint256 pre = IERC20(token).balanceOf(address(this));
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(token).forceApprove(pool, amount);
        uint256 repaid = IPool(pool).repay(token, amount, 2, user);
        uint256 post = IERC20(token).balanceOf(address(this));
        // Prefer protocol's returned amount when available
        uint256 spent = repaid > 0 && repaid <= amount ? repaid : (pre + amount > post ? pre + amount - post : amount);
        refund = amount > spent ? amount - spent : 0;
        if (refund > 0) {
            IERC20(token).safeTransfer(msg.sender, refund);
        }
    }

    function authorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address caller,
        ProtocolTypes.Output[] calldata inputs
    ) external view returns (address[] memory targets, bytes[] memory data, ProtocolTypes.Output[] memory produced) {
        targets = new address[](instrs.length);
        data = new bytes[](instrs.length);

        // 1. Calculate exact output count to match execution logic
        uint256 outCount = 0;
        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingOp op = instrs[i].op;
            if (
                op == ProtocolTypes.LendingOp.WithdrawCollateral ||
                op == ProtocolTypes.LendingOp.Borrow ||
                op == ProtocolTypes.LendingOp.GetBorrowBalance ||
                op == ProtocolTypes.LendingOp.GetSupplyBalance ||
                op == ProtocolTypes.LendingOp.Repay
            ) {
                outCount++;
            }
        }
        produced = new ProtocolTypes.Output[](outCount);
        uint256 pIdx = 0;

        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingInstruction calldata ins = instrs[i];

            // Simulation logic to determine actual token and amount
            address token = ins.token;
            uint256 amount = ins.amount;
            if (ins.input.index < inputs.length) {
                token = inputs[ins.input.index].token;
                amount = inputs[ins.input.index].amount;
            }

            if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
                address aToken = _getAToken(token);
                uint256 cur = IERC20(aToken).allowance(caller, address(this));
                uint256 required = amount;

                // Add to produced
                produced[pIdx] = ProtocolTypes.Output({ token: token, amount: amount });
                pIdx++;

                if (amount != 0 && cur >= required) {
                    targets[i] = address(0);
                    data[i] = bytes("");
                } else {
                    targets[i] = aToken;
                    data[i] = abi.encodeWithSelector(IERC20.approve.selector, address(this), required);
                }
            } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
                (, , address vDebt) = _getReserveTokens(token);

                // Add to produced
                produced[pIdx] = ProtocolTypes.Output({ token: token, amount: amount });
                pIdx++;

                if (vDebt == address(0)) {
                    targets[i] = address(0);
                    data[i] = bytes("");
                } else {
                    uint256 cur = IVariableDebtToken(vDebt).borrowAllowance(caller, address(this));
                    uint256 required = amount; // Exact amount

                    if (cur >= required && amount != 0) {
                        targets[i] = address(0);
                        data[i] = bytes("");
                    } else {
                        targets[i] = vDebt;
                        data[i] = abi.encodeWithSignature(
                            "approveDelegation(address,uint256)",
                            address(this),
                            type(uint256).max
                        );
                    }
                }
            } else if (ins.op == ProtocolTypes.LendingOp.GetBorrowBalance) {
                uint256 bal = _getBorrowBalance(token, ins.user);
                bal = (bal * 1001) / 1000; // Buffer
                produced[pIdx] = ProtocolTypes.Output({ token: token, amount: bal });
                pIdx++;
                targets[i] = address(0);
                data[i] = bytes("");
            } else if (ins.op == ProtocolTypes.LendingOp.GetSupplyBalance) {
                uint256 bal = _getSupplyBalance(token, ins.user);
                bal = (bal * 1001) / 1000; // Buffer
                produced[pIdx] = ProtocolTypes.Output({ token: token, amount: bal });
                pIdx++;
                targets[i] = address(0);
                data[i] = bytes("");
            } else if (ins.op == ProtocolTypes.LendingOp.Repay) {
                // Repay produces a refund output (usually 0)
                produced[pIdx] = ProtocolTypes.Output({ token: token, amount: 0 });
                pIdx++;
                targets[i] = address(0);
                data[i] = bytes("");
            } else if (ins.op == ProtocolTypes.LendingOp.SetEMode) {
                // SetEMode: user calls Pool.setUserEMode(categoryId) directly
                // amount encodes the categoryId
                address pool = poolAddressesProvider.getPool();
                targets[i] = pool;
                data[i] = abi.encodeWithSignature("setUserEMode(uint8)", uint8(amount));
            } else {
                // Deposit / DepositCollateral produce NO output
                targets[i] = address(0);
                data[i] = bytes("");
            }
        }
    }

    function deauthorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address /*caller*/,
        ProtocolTypes.Output[] calldata inputs
    ) external view override returns (address[] memory targets, bytes[] memory data) {
        targets = new address[](instrs.length);
        data = new bytes[](instrs.length);

        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingInstruction calldata ins = instrs[i];
            address token = ins.token;
            if (ins.input.index < inputs.length) {
                token = inputs[ins.input.index].token;
            }

            if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
                // Revoke aToken approval
                address aToken = _getAToken(token);
                targets[i] = aToken;
                data[i] = abi.encodeWithSelector(IERC20.approve.selector, address(this), 0);
            } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
                // Revoke Credit Delegation
                (, , address vDebt) = _getReserveTokens(token);
                if (vDebt != address(0)) {
                    targets[i] = vDebt;
                    data[i] = abi.encodeWithSignature("approveDelegation(address,uint256)", address(this), 0);
                }
            } else {
                // SetEMode and other ops don't need deauthorization
                targets[i] = address(0);
                data[i] = bytes("");
            }
        }
    }

    /// @notice Returns the Aave Pool address for direct calls (e.g., setUserEMode)
    function getPool() external view returns (address) {
        return poolAddressesProvider.getPool();
    }

    /// @notice Helper to encode setUserEMode calldata
    /// @param categoryId The E-Mode category ID (0 = disable E-Mode)
    /// @return target The Aave Pool address
    /// @return callData The encoded function call
    function encodeSetEMode(uint8 categoryId) external view returns (address target, bytes memory callData) {
        target = poolAddressesProvider.getPool();
        callData = abi.encodeWithSignature("setUserEMode(uint8)", categoryId);
    }

    function _getAToken(address underlying) internal view returns (address) {
        IPoolDataProvider data = IPoolDataProvider(poolAddressesProvider.getPoolDataProvider());
        (address aToken, , ) = data.getReserveTokensAddresses(underlying);
        return aToken;
    }

    function _getReserveTokens(
        address underlying
    ) internal view returns (address aToken, address sDebt, address vDebt) {
        IPoolDataProvider data = IPoolDataProvider(poolAddressesProvider.getPoolDataProvider());
        (aToken, sDebt, vDebt) = data.getReserveTokensAddresses(underlying);
    }

    function _getBorrowBalance(address token, address user) internal view returns (uint256) {
        (, , address vDebt) = _getReserveTokens(token);
        return vDebt == address(0) ? 0 : IERC20(vDebt).balanceOf(user);
    }

    function _getSupplyBalance(address token, address user) internal view returns (uint256) {
        (address aToken, , ) = _getReserveTokens(token);
        return aToken == address(0) ? 0 : IERC20(aToken).balanceOf(user);
    }

    // ============ Emergency Recovery ============

    /// @notice Recover stuck tokens (only callable by router's owner)
    function recoverTokens(address token, address to, uint256 amount) external {
        require(msg.sender == Ownable(ROUTER).owner(), "Only router owner");
        uint256 balance = IERC20(token).balanceOf(address(this));
        uint256 toRecover = amount == type(uint256).max ? balance : amount;
        if (toRecover > balance) toRecover = balance;
        if (toRecover > 0) {
            IERC20(token).safeTransfer(to, toRecover);
        }
    }
}

contracts/v2/gateways/compound/CompoundG.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { ProtocolGateway } from "../../../gateways/ProtocolGateway.sol";
import { IGateway } from "../../interfaces/IGateway.sol";
import { ProtocolTypes } from "../../interfaces/ProtocolTypes.sol";
import { ICompoundComet } from "../../interfaces/compound/ICompoundComet.sol";

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";

contract CompoundGatewayWrite is IGateway, ProtocolGateway, Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    // base token => Comet
    mapping(address => ICompoundComet) public tokenToComet;

    // registry for view
    address[] private _comets;
    mapping(address => bool) private _isRegistered;

    event CometRegistered(address indexed baseToken, address indexed comet);
    event CometReplaced(address indexed baseToken, address indexed oldComet, address indexed newComet);

    constructor(address router, address owner_) ProtocolGateway(router) Ownable(owner_) {}

    function addComet(ICompoundComet comet) external onlyOwner {
        address base = comet.baseToken();
        require(base != address(0), "Compound: base=0");
        require(!_isRegistered[address(comet)], "Compound: already registered");

        tokenToComet[base] = comet;
        _isRegistered[address(comet)] = true;
        _comets.push(address(comet));
        emit CometRegistered(base, address(comet));
    }

    function setCometForBase(address baseToken, address comet_) external onlyOwner {
        require(baseToken != address(0) && comet_ != address(0), "Compound: zero");
        require(address(tokenToComet[baseToken]) == address(0), "Compound: comet already set");
        require(!_isRegistered[comet_], "Compound: comet already registered");

        tokenToComet[baseToken] = ICompoundComet(comet_);
        emit CometRegistered(baseToken, comet_);
    }

    function allComets() external view returns (address[] memory) {
        return _comets;
    }

    function processLendingInstruction(
        ProtocolTypes.Output[] calldata inputs,
        bytes calldata data
    ) external onlyRouter returns (ProtocolTypes.Output[] memory outputs) {
        ProtocolTypes.LendingInstruction memory ins = abi.decode(data, (ProtocolTypes.LendingInstruction));
        address market = _decodeMarket(ins.context);
        address token = ins.token;
        uint256 amount = ins.amount;
        if (ins.input.index < inputs.length) {
            token = inputs[ins.input.index].token;
            amount = inputs[ins.input.index].amount;
        }

        if (ins.op == ProtocolTypes.LendingOp.Deposit) {
            if (market != address(0) && market != token) depositCollateral(market, token, amount, ins.user);
            else deposit(token, ins.user, amount);
            outputs = new ProtocolTypes.Output[](0);
        } else if (ins.op == ProtocolTypes.LendingOp.DepositCollateral) {
            address base = market != address(0) ? market : token;
            depositCollateral(base, token, amount, ins.user);
            outputs = new ProtocolTypes.Output[](0);
        } else if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
            address base = market != address(0) ? market : token;
            (address u, uint256 amt) = withdrawCollateral(base, token, ins.user, amount);
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: u, amount: amt });
        } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
            borrow(token, ins.user, amount);
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: amount });
        } else if (ins.op == ProtocolTypes.LendingOp.Repay) {
            repay(token, ins.user, amount);
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: 0 });
        } else if (ins.op == ProtocolTypes.LendingOp.GetBorrowBalance) {
            ICompoundComet comet = tokenToComet[token];
            uint256 bal = address(comet) == address(0) ? 0 : comet.borrowBalanceOf(ins.user);
            // NO BUFFER in execution
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: bal });
        } else if (ins.op == ProtocolTypes.LendingOp.GetSupplyBalance) {
            uint256 bal;
            if (market != address(0) && market != token) {
                ICompoundComet comet = tokenToComet[market];
                bal = address(comet) == address(0) ? 0 : uint256(comet.collateralBalanceOf(ins.user, token));
            } else {
                ICompoundComet comet = tokenToComet[token];
                bal = address(comet) == address(0) ? 0 : comet.balanceOf(ins.user);
            }
            // NO BUFFER in execution (Fixes Withdraw revert)
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: bal });
        } else {
            revert("Compound: unknown op");
        }
    }

    function deposit(address token, address onBehalfOf, uint256 amount) internal nonReentrant {
        ICompoundComet comet = tokenToComet[token];
        require(address(comet) != address(0), "Compound: base comet not found");
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(token).forceApprove(address(comet), amount);
        comet.supplyTo(onBehalfOf, token, amount);
    }

    function depositCollateral(
        address market,
        address collateral,
        uint256 amount,
        address receiver
    ) internal nonReentrant {
        ICompoundComet comet = tokenToComet[market];
        require(address(comet) != address(0), "Compound: market comet not found");
        IERC20(collateral).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(collateral).forceApprove(address(comet), amount);
        comet.supplyTo(receiver, collateral, amount);
    }

    function withdrawCollateral(
        address market,
        address collateral,
        address user,
        uint256 amount
    ) internal nonReentrant returns (address, uint256) {
        ICompoundComet comet = tokenToComet[market];
        require(address(comet) != address(0), "Compound: market comet not found");

        // Fix: Use msg.sender (Router) as receiver to simplify flow and ensure authorization alignment
        comet.withdrawFrom(user, msg.sender, collateral, amount);

        return (collateral, amount);
    }

    function borrow(address token, address user, uint256 amount) internal nonReentrant {
        ICompoundComet comet = tokenToComet[token];
        require(address(comet) != address(0), "Compound: base comet not found");
        comet.withdrawFrom(user, address(this), token, amount);
        IERC20(token).safeTransfer(msg.sender, amount);
    }

    function repay(address token, address user, uint256 amount) internal nonReentrant {
        ICompoundComet comet = tokenToComet[token];
        require(address(comet) != address(0), "Compound: base comet not found");
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(token).forceApprove(address(comet), amount);
        comet.supplyTo(user, token, amount);
    }

    function authorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address caller,
        ProtocolTypes.Output[] calldata inputs
    ) external view returns (address[] memory targets, bytes[] memory data, ProtocolTypes.Output[] memory produced) {
        targets = new address[](instrs.length);
        data = new bytes[](instrs.length);
        produced = new ProtocolTypes.Output[](_countOutputs(instrs));
        uint256 pIdx = 0;

        for (uint256 i = 0; i < instrs.length; i++) {
            (address target, bytes memory callData, ProtocolTypes.Output memory output, bool hasOutput) = 
                _processAuthorizeOp(instrs[i], caller, inputs);
            targets[i] = target;
            data[i] = callData;
            if (hasOutput) {
                produced[pIdx++] = output;
            }
        }
    }

    function _countOutputs(ProtocolTypes.LendingInstruction[] calldata instrs) private pure returns (uint256 count) {
        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingOp op = instrs[i].op;
            if (
                op == ProtocolTypes.LendingOp.WithdrawCollateral ||
                op == ProtocolTypes.LendingOp.Borrow ||
                op == ProtocolTypes.LendingOp.Repay ||
                op == ProtocolTypes.LendingOp.GetBorrowBalance ||
                op == ProtocolTypes.LendingOp.GetSupplyBalance
            ) {
                count++;
            }
        }
    }

    function _processAuthorizeOp(
        ProtocolTypes.LendingInstruction calldata ins,
        address caller,
        ProtocolTypes.Output[] calldata inputs
    ) private view returns (address target, bytes memory callData, ProtocolTypes.Output memory output, bool hasOutput) {
        address market = _decodeMarket(ins.context);
        address token = ins.token;
        uint256 amount = ins.amount;
        if (ins.input.index < inputs.length) {
            token = inputs[ins.input.index].token;
            amount = inputs[ins.input.index].amount;
        }

        if (ins.op == ProtocolTypes.LendingOp.Deposit || ins.op == ProtocolTypes.LendingOp.Repay) {
            if (ins.op == ProtocolTypes.LendingOp.Repay) {
                output = ProtocolTypes.Output({ token: token, amount: 0 });
                hasOutput = true;
            }
        } else if (
            ins.op == ProtocolTypes.LendingOp.DepositCollateral ||
            (ins.op == ProtocolTypes.LendingOp.Deposit && market != address(0) && market != token)
        ) {
            // No output
        } else if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
            (target, callData) = _getWithdrawAuth(market, token, caller);
            output = ProtocolTypes.Output({ token: token, amount: amount });
            hasOutput = true;
        } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
            (target, callData) = _getBorrowAuth(token, caller);
            output = ProtocolTypes.Output({ token: token, amount: amount });
            hasOutput = true;
        } else if (ins.op == ProtocolTypes.LendingOp.GetBorrowBalance) {
            uint256 bal = _getBorrowBalance(token, ins.user);
            output = ProtocolTypes.Output({ token: token, amount: (bal * 1001) / 1000 });
            hasOutput = true;
        } else if (ins.op == ProtocolTypes.LendingOp.GetSupplyBalance) {
            uint256 bal = _getSupplyBalance(token, market, ins.user);
            output = ProtocolTypes.Output({ token: token, amount: (bal * 1001) / 1000 });
            hasOutput = true;
        }
    }

    function _getWithdrawAuth(address market, address token, address caller) private view returns (address target, bytes memory callData) {
        address base = market != address(0) ? market : token;
        ICompoundComet comet = tokenToComet[base];
        if (!comet.isAllowed(caller, address(this))) {
            target = address(comet);
            callData = abi.encodeWithSelector(ICompoundComet.allow.selector, address(this), true);
        }
    }

    function _getBorrowAuth(address token, address caller) private view returns (address target, bytes memory callData) {
        ICompoundComet comet = tokenToComet[token];
        if (!comet.isAllowed(caller, address(this))) {
            target = address(comet);
            callData = abi.encodeWithSelector(ICompoundComet.allow.selector, address(this), true);
        }
    }

    function _getBorrowBalance(address token, address user) private view returns (uint256) {
        ICompoundComet comet = tokenToComet[token];
        return address(comet) == address(0) ? 0 : comet.borrowBalanceOf(user);
    }

    function _getSupplyBalance(address token, address market, address user) private view returns (uint256) {
        if (market != address(0) && market != token) {
            ICompoundComet comet = tokenToComet[market];
            return address(comet) == address(0) ? 0 : uint256(comet.collateralBalanceOf(user, token));
        } else {
            ICompoundComet comet = tokenToComet[token];
            return address(comet) == address(0) ? 0 : comet.balanceOf(user);
        }
    }

    function deauthorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address /*caller*/,
        ProtocolTypes.Output[] calldata inputs
    ) external view override returns (address[] memory targets, bytes[] memory data) {
        targets = new address[](instrs.length);
        data = new bytes[](instrs.length);

        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingInstruction calldata ins = instrs[i];
            address market = _decodeMarket(ins.context);
            address token = ins.token;
            if (ins.input.index < inputs.length) {
                token = inputs[ins.input.index].token;
            }

            if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
                address base = market != address(0) ? market : token;
                ICompoundComet comet = tokenToComet[base];
                if (address(comet) != address(0)) {
                    targets[i] = address(comet);
                    // allow(manager, isAllowed) -> false
                    data[i] = abi.encodeWithSelector(ICompoundComet.allow.selector, address(this), false);
                }
            } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
                ICompoundComet comet = tokenToComet[token];
                if (address(comet) != address(0)) {
                    targets[i] = address(comet);
                    data[i] = abi.encodeWithSelector(ICompoundComet.allow.selector, address(this), false);
                }
            } else {
                targets[i] = address(0);
                data[i] = bytes("");
            }
        }
    }

    function _decodeMarket(bytes memory ctx) internal pure returns (address market) {
        if (ctx.length >= 32) {
            assembly {
                market := mload(add(ctx, 32))
            }
        }
    }

    // ============ Emergency Recovery ============

    /// @notice Recover stuck tokens (owner only)
    function recoverTokens(address token, address to, uint256 amount) external onlyOwner {
        uint256 balance = IERC20(token).balanceOf(address(this));
        uint256 toRecover = amount == type(uint256).max ? balance : amount;
        if (toRecover > balance) toRecover = balance;
        if (toRecover > 0) {
            IERC20(token).safeTransfer(to, toRecover);
        }
    }
}

contracts/v2/gateways/morpho/MorphoBlueG.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { ProtocolGateway } from "../../../gateways/ProtocolGateway.sol";
import { IGateway } from "../../interfaces/IGateway.sol";
import { ProtocolTypes } from "../../interfaces/ProtocolTypes.sol";
import { IMorphoBlue, MarketParams, Market, Position, MorphoLib } from "../../interfaces/morpho/IMorphoBlue.sol";

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title MorphoBlueGatewayWrite
 * @notice Gateway for Morpho Blue lending protocol
 * @dev Morpho Blue uses market-based lending where each market is identified by:
 *      - loanToken: The token being borrowed
 *      - collateralToken: The token used as collateral
 *      - oracle: Price oracle address
 *      - irm: Interest rate model address
 *      - lltv: Liquidation loan-to-value ratio
 *
 * Context encoding for LendingInstruction:
 *   - bytes context = abi.encode(MarketParams) for full market params
 *   - The token field should be the collateral token for collateral ops,
 *     or the loan token for borrow/repay ops
 */
contract MorphoBlueGatewayWrite is IGateway, ProtocolGateway, Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;
    using MorphoLib for MarketParams;

    /// @notice The Morpho Blue singleton contract
    IMorphoBlue public immutable morpho;

    /// @notice Registered markets by their ID
    mapping(bytes32 => MarketParams) public registeredMarkets;

    /// @notice List of registered market IDs for enumeration
    bytes32[] public marketIds;

    /// @notice Whether a market ID is registered
    mapping(bytes32 => bool) public isMarketRegistered;

    event MarketRegistered(bytes32 indexed marketId, address loanToken, address collateralToken);

    error MarketNotRegistered(bytes32 marketId);
    error InvalidMarketParams();
    error ZeroAddress();

    constructor(
        address router,
        address owner_,
        address morpho_
    ) ProtocolGateway(router) Ownable(owner_) {
        if (morpho_ == address(0)) revert ZeroAddress();
        morpho = IMorphoBlue(morpho_);
    }

    /// @notice Register a market for use with this gateway
    /// @param params The market parameters
    function registerMarket(MarketParams calldata params) external onlyOwner {
        if (params.loanToken == address(0) || params.collateralToken == address(0)) {
            revert InvalidMarketParams();
        }

        bytes32 marketId = params.id();

        if (!isMarketRegistered[marketId]) {
            registeredMarkets[marketId] = params;
            marketIds.push(marketId);
            isMarketRegistered[marketId] = true;
            emit MarketRegistered(marketId, params.loanToken, params.collateralToken);
        }
    }

    /// @notice Get all registered market IDs
    function getAllMarketIds() external view returns (bytes32[] memory) {
        return marketIds;
    }

    /// @notice Get market params by ID
    function getMarketParams(bytes32 marketId) external view returns (MarketParams memory) {
        if (!isMarketRegistered[marketId]) revert MarketNotRegistered(marketId);
        return registeredMarkets[marketId];
    }

    function processLendingInstruction(
        ProtocolTypes.Output[] calldata inputs,
        bytes calldata data
    ) external onlyRouter returns (ProtocolTypes.Output[] memory) {
        ProtocolTypes.LendingInstruction memory ins = abi.decode(data, (ProtocolTypes.LendingInstruction));
        MarketParams memory params = _decodeMarketParams(ins.context);

        // Resolve amount from input if referenced
        uint256 amount = ins.input.index < inputs.length ? inputs[ins.input.index].amount : ins.amount;

        return _dispatch(ins.op, params, amount, ins.user);
    }

    function _dispatch(
        ProtocolTypes.LendingOp op,
        MarketParams memory params,
        uint256 amount,
        address user
    ) internal returns (ProtocolTypes.Output[] memory) {
        // Deposit operations - no output
        if (op == ProtocolTypes.LendingOp.Deposit) {
            _supply(params, amount, user);
            return _noOutput();
        }
        if (op == ProtocolTypes.LendingOp.DepositCollateral) {
            _supplyCollateral(params, amount, user);
            return _noOutput();
        }

        // Collateral operations - output collateral token
        if (op == ProtocolTypes.LendingOp.WithdrawCollateral) {
            return _output(params.collateralToken, _withdrawCollateral(params, amount, user));
        }
        if (op == ProtocolTypes.LendingOp.GetSupplyBalance) {
            return _output(params.collateralToken, _getCollateralBalance(params, user));
        }

        // Loan operations - output loan token
        if (op == ProtocolTypes.LendingOp.Borrow) {
            return _output(params.loanToken, _borrow(params, amount, user));
        }
        if (op == ProtocolTypes.LendingOp.Repay) {
            return _output(params.loanToken, _repay(params, amount, user));
        }
        if (op == ProtocolTypes.LendingOp.GetBorrowBalance) {
            return _output(params.loanToken, _getBorrowBalance(params, user));
        }

        revert("MorphoBlue: unknown op");
    }

    function _noOutput() internal pure returns (ProtocolTypes.Output[] memory) {
        return new ProtocolTypes.Output[](0);
    }

    function _output(address token, uint256 amount) internal pure returns (ProtocolTypes.Output[] memory outputs) {
        outputs = new ProtocolTypes.Output[](1);
        outputs[0] = ProtocolTypes.Output({ token: token, amount: amount });
    }

    // ============ Internal Write Functions ============

    function _supply(MarketParams memory params, uint256 amount, address onBehalfOf) internal {
        IERC20(params.loanToken).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(params.loanToken).forceApprove(address(morpho), amount);

        morpho.supply(params, amount, 0, onBehalfOf, "");
    }

    function _supplyCollateral(MarketParams memory params, uint256 amount, address onBehalfOf) internal {
        IERC20(params.collateralToken).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(params.collateralToken).forceApprove(address(morpho), amount);

        morpho.supplyCollateral(params, amount, onBehalfOf, "");
    }

    function _withdrawCollateral(
        MarketParams memory params,
        uint256 amount,
        address user
    ) internal returns (uint256) {
        bytes32 marketId = params.id();
        Position memory pos = morpho.position(marketId, user);

        // Clamp to available collateral
        if (amount > pos.collateral) {
            amount = pos.collateral;
        }

        // Withdraw to router (msg.sender)
        morpho.withdrawCollateral(params, amount, user, msg.sender);

        return amount;
    }

    function _borrow(MarketParams memory params, uint256 amount, address user) internal returns (uint256) {
        // Borrow to this contract, then forward to router
        (uint256 borrowed, ) = morpho.borrow(params, amount, 0, user, address(this));
        IERC20(params.loanToken).safeTransfer(msg.sender, borrowed);
        return borrowed;
    }

    function _repay(MarketParams memory params, uint256 amount, address user) internal returns (uint256 refund) {
        IERC20 loanToken = IERC20(params.loanToken);
        uint256 pre = loanToken.balanceOf(address(this));

        loanToken.safeTransferFrom(msg.sender, address(this), amount);
        loanToken.forceApprove(address(morpho), amount);

        // Get user's actual debt to decide repay strategy
        bytes32 marketId = params.id();
        Position memory pos = morpho.position(marketId, user);

        uint256 repaid;
        if (pos.borrowShares > 0) {
            // Calculate current debt in assets
            Market memory mkt = morpho.market(marketId);
            uint256 debtAssets = mkt.totalBorrowShares > 0
                ? (uint256(pos.borrowShares) * uint256(mkt.totalBorrowAssets) + uint256(mkt.totalBorrowShares) - 1) / uint256(mkt.totalBorrowShares)
                : 0;

            if (amount >= debtAssets) {
                // Full repay: use shares to avoid overflow/rounding issues
                (repaid, ) = morpho.repay(params, 0, pos.borrowShares, user, "");
            } else {
                // Partial repay: use assets
                (repaid, ) = morpho.repay(params, amount, 0, user, "");
            }
        }

        uint256 post = loanToken.balanceOf(address(this));
        refund = post > pre ? post - pre : 0;

        if (refund > 0) {
            loanToken.safeTransfer(msg.sender, refund);
        }
    }

    // ============ View Functions ============

    function _getBorrowBalance(MarketParams memory params, address user) internal returns (uint256) {
        // IMPORTANT: Accrue interest first to get accurate debt including pending interest
        // Morpho Blue lazily accrues interest - position() returns stale values without this
        morpho.accrueInterest(params);

        return _getBorrowBalanceView(params, user);
    }

    /// @dev View version of _getBorrowBalance for use in authorize() - may be slightly stale
    function _getBorrowBalanceView(MarketParams memory params, address user) internal view returns (uint256) {
        bytes32 marketId = params.id();
        Position memory pos = morpho.position(marketId, user);

        if (pos.borrowShares == 0) return 0;

        // Convert shares to assets using market state
        // borrowAssets = borrowShares * totalBorrowAssets / totalBorrowShares
        // Round UP with extra buffer to ensure full debt repayment
        Market memory mkt = morpho.market(marketId);
        if (mkt.totalBorrowShares == 0) return 0;

        uint256 numerator = uint256(pos.borrowShares) * uint256(mkt.totalBorrowAssets);
        uint256 denominator = uint256(mkt.totalBorrowShares);
        // Round up: (a + b - 1) / b, then add 1 extra wei for double-rounding safety
        return ((numerator + denominator - 1) / denominator) + 1;
    }

    function _getCollateralBalance(MarketParams memory params, address user) internal view returns (uint256) {
        bytes32 marketId = params.id();
        Position memory pos = morpho.position(marketId, user);
        return pos.collateral;
    }

    // ============ Authorization ============

    function authorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address caller,
        ProtocolTypes.Output[] calldata inputs
    ) external view returns (address[] memory targets, bytes[] memory data, ProtocolTypes.Output[] memory produced) {
        targets = new address[](instrs.length);
        data = new bytes[](instrs.length);

        // Count outputs
        uint256 outCount = 0;
        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingOp op = instrs[i].op;
            if (
                op == ProtocolTypes.LendingOp.WithdrawCollateral ||
                op == ProtocolTypes.LendingOp.Borrow ||
                op == ProtocolTypes.LendingOp.Repay ||
                op == ProtocolTypes.LendingOp.GetBorrowBalance ||
                op == ProtocolTypes.LendingOp.GetSupplyBalance
            ) {
                outCount++;
            }
        }
        produced = new ProtocolTypes.Output[](outCount);
        uint256 pIdx = 0;

        // Check if caller has authorized this gateway on Morpho
        bool isAuth = morpho.isAuthorized(caller, address(this));
        bool authEmitted = false; // Only emit setAuthorization once

        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingInstruction calldata ins = instrs[i];
            MarketParams memory params = _decodeMarketParams(ins.context);

            address token = ins.token;
            uint256 amount = ins.amount;
            if (ins.input.index < inputs.length) {
                token = inputs[ins.input.index].token;
                amount = inputs[ins.input.index].amount;
            }

            if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
                produced[pIdx] = ProtocolTypes.Output({ token: params.collateralToken, amount: amount });
                pIdx++;

                if (isAuth || authEmitted) {
                    targets[i] = address(0);
                    data[i] = "";
                } else {
                    targets[i] = address(morpho);
                    data[i] = abi.encodeWithSelector(IMorphoBlue.setAuthorization.selector, address(this), true);
                    authEmitted = true;
                }
            } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
                produced[pIdx] = ProtocolTypes.Output({ token: params.loanToken, amount: amount });
                pIdx++;

                if (isAuth || authEmitted) {
                    targets[i] = address(0);
                    data[i] = "";
                } else {
                    targets[i] = address(morpho);
                    data[i] = abi.encodeWithSelector(IMorphoBlue.setAuthorization.selector, address(this), true);
                    authEmitted = true;
                }
            } else if (ins.op == ProtocolTypes.LendingOp.GetBorrowBalance) {
                uint256 bal = _getBorrowBalanceView(params, ins.user);
                bal = (bal * 1001) / 1000; // Buffer
                produced[pIdx] = ProtocolTypes.Output({ token: params.loanToken, amount: bal });
                pIdx++;
                targets[i] = address(0);
                data[i] = "";
            } else if (ins.op == ProtocolTypes.LendingOp.GetSupplyBalance) {
                uint256 bal = _getCollateralBalance(params, ins.user);
                bal = (bal * 1001) / 1000; // Buffer
                produced[pIdx] = ProtocolTypes.Output({ token: params.collateralToken, amount: bal });
                pIdx++;
                targets[i] = address(0);
                data[i] = "";
            } else if (ins.op == ProtocolTypes.LendingOp.Repay) {
                produced[pIdx] = ProtocolTypes.Output({ token: params.loanToken, amount: 0 });
                pIdx++;
                targets[i] = address(0);
                data[i] = "";
            } else {
                // Deposit / DepositCollateral - no auth needed, no output
                targets[i] = address(0);
                data[i] = "";
            }
        }
    }

    function deauthorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address /*caller*/,
        ProtocolTypes.Output[] calldata /*inputs*/
    ) external view override returns (address[] memory targets, bytes[] memory data) {
        targets = new address[](instrs.length);
        data = new bytes[](instrs.length);

        bool needsDeauth = false;
        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingOp op = instrs[i].op;
            if (op == ProtocolTypes.LendingOp.WithdrawCollateral || op == ProtocolTypes.LendingOp.Borrow) {
                needsDeauth = true;
                break;
            }
        }

        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingOp op = instrs[i].op;
            if (needsDeauth && (op == ProtocolTypes.LendingOp.WithdrawCollateral || op == ProtocolTypes.LendingOp.Borrow)) {
                // Only need to deauth once, put it on first sensitive op
                targets[i] = address(morpho);
                data[i] = abi.encodeWithSelector(IMorphoBlue.setAuthorization.selector, address(this), false);
                needsDeauth = false; // Only emit once
            } else {
                targets[i] = address(0);
                data[i] = "";
            }
        }
    }

    // ============ Helpers ============

    function _decodeMarketParams(bytes memory ctx) internal pure returns (MarketParams memory params) {
        if (ctx.length >= 160) {
            // Full MarketParams: 5 * 32 bytes = 160
            params = abi.decode(ctx, (MarketParams));
        } else {
            revert("MorphoBlue: invalid context - expected MarketParams");
        }
    }

    // ============ Emergency Recovery ============

    /// @notice Recover stuck tokens (owner only)
    function recoverTokens(address token, address to, uint256 amount) external onlyOwner {
        uint256 balance = IERC20(token).balanceOf(address(this));
        uint256 toRecover = amount == type(uint256).max ? balance : amount;
        if (toRecover > balance) toRecover = balance;
        if (toRecover > 0) {
            IERC20(token).safeTransfer(to, toRecover);
        }
    }
}

contracts/v2/gateways/venus/VenusGateway.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { ProtocolGateway } from "../../../gateways/ProtocolGateway.sol";
import { IGateway } from "../../interfaces/IGateway.sol";
import { ProtocolTypes } from "../../interfaces/ProtocolTypes.sol";

import { ComptrollerInterface } from "../../../interfaces/venus/ComptrollerInterface.sol";
import { VTokenInterface } from "../../../interfaces/venus/VTokenInterface.sol";

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";

contract VenusGatewayWrite is IGateway, ProtocolGateway, ReentrancyGuard {
    using SafeERC20 for IERC20;

    ComptrollerInterface public immutable comptroller;

    constructor(address router, address _comptroller) ProtocolGateway(router) {
        comptroller = ComptrollerInterface(_comptroller);
    }

    function processLendingInstruction(
        ProtocolTypes.Output[] calldata inputs,
        bytes calldata data
    ) external onlyRouter returns (ProtocolTypes.Output[] memory outputs) {
        ProtocolTypes.LendingInstruction memory ins = abi.decode(data, (ProtocolTypes.LendingInstruction));
        address token = ins.token;
        uint256 amount = ins.amount;
        if (ins.input.index < inputs.length) {
            token = inputs[ins.input.index].token;
            amount = inputs[ins.input.index].amount;
        }

        if (ins.op == ProtocolTypes.LendingOp.Deposit || ins.op == ProtocolTypes.LendingOp.DepositCollateral) {
            deposit(token, ins.user, amount);
            outputs = new ProtocolTypes.Output[](0);
        } else if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
            (address u, uint256 amt) = withdrawCollateral(token, ins.user, amount);
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: u, amount: amt });
        } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
            borrow(token, ins.user, amount);
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: amount });
        } else if (ins.op == ProtocolTypes.LendingOp.Repay) {
            uint256 refund = repay(token, ins.user, amount);
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: refund });
        } else if (ins.op == ProtocolTypes.LendingOp.GetBorrowBalance) {
            address vToken = _getVTokenForUnderlying(token);
            uint256 bal = VTokenInterface(vToken).borrowBalanceCurrent(ins.user);
            // NO BUFFER in execution
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: bal });
        } else if (ins.op == ProtocolTypes.LendingOp.GetSupplyBalance) {
            address vToken = _getVTokenForUnderlying(token);
            uint256 vBal = VTokenInterface(vToken).balanceOf(ins.user);
            uint256 rate = VTokenInterface(vToken).exchangeRateStored();
            uint256 bal = (vBal * rate) / 1e18;
            // NO BUFFER in execution
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: bal });
        } else {
            revert("Venus: unknown op");
        }
    }

    function deposit(address token, address user, uint256 amount) internal nonReentrant {
        address vToken = _getVTokenForUnderlying(token);
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(token).forceApprove(vToken, amount);
        uint err = VTokenInterface(vToken).mint(amount);
        require(err == 0, "Venus: mint failed");
        uint vBal = VTokenInterface(vToken).balanceOf(address(this));
        require(vBal > 0, "Venus: zero vTokens");
        VTokenInterface(vToken).transfer(user, vBal);
    }

    function withdrawCollateral(
        address collateral,
        address user,
        uint256 underlyingAmount
    ) internal nonReentrant returns (address, uint256) {
        address vToken = _getVTokenForUnderlying(collateral);

        // Get user's supply balance in vTokens and convert to underlying
        uint256 vBalance = VTokenInterface(vToken).balanceOf(user);
        uint256 exchangeRate = VTokenInterface(vToken).exchangeRateCurrent();
        uint256 supplyBalance = (vBalance * exchangeRate) / 1e18;

        // Clamp to supply balance if requesting more than available
        if (underlyingAmount >= supplyBalance || underlyingAmount == 0) {
            // Withdraw all - transfer all vTokens and redeem
            VTokenInterface(vToken).transferFrom(user, address(this), vBalance);
            uint256 pre = IERC20(collateral).balanceOf(address(this));
            uint err = VTokenInterface(vToken).redeem(vBalance);
            require(err == 0, "Venus: redeem failed");
            uint256 balanceAfter = IERC20(collateral).balanceOf(address(this));
            uint256 redeemed = balanceAfter - pre;
            IERC20(collateral).safeTransfer(msg.sender, redeemed);
            return (collateral, redeemed);
        }

        // Partial withdraw - calculate required vTokens for desired underlying
        uint requiredV = (underlyingAmount * 1e18 + exchangeRate - 1) / exchangeRate;
        VTokenInterface(vToken).transferFrom(user, address(this), requiredV);

        uint256 balanceBefore = IERC20(collateral).balanceOf(address(this));
        uint256 err2 = VTokenInterface(vToken).redeem(requiredV);
        require(err2 == 0, "Venus: redeem failed");
        uint256 actualRedeemed = IERC20(collateral).balanceOf(address(this)) - balanceBefore;
        IERC20(collateral).safeTransfer(msg.sender, actualRedeemed);
        return (collateral, actualRedeemed);
    }

    function borrow(address token, address user, uint256 amount) internal nonReentrant {
        address vToken = _getVTokenForUnderlying(token);
        uint err = VTokenInterface(vToken).borrowBehalf(user, amount);
        require(err == 0, "Venus: borrow failed");
        IERC20(token).safeTransfer(msg.sender, amount);
    }

    function repay(
        address token,
        address user,
        uint256 amount
    ) internal nonReentrant returns (uint256 refund) {
        address vToken = _getVTokenForUnderlying(token);
        uint256 pre = IERC20(token).balanceOf(address(this));
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(token).forceApprove(vToken, amount);
        uint err = VTokenInterface(vToken).repayBorrowBehalf(user, amount);
        require(err == 0, "Venus: repay failed");
        uint256 post = IERC20(token).balanceOf(address(this));
        refund = post > pre ? post - pre : 0;
        if (refund > 0) {
            IERC20(token).safeTransfer(msg.sender, refund);
        }
    }

    /// @dev Helper struct to reduce stack depth in authorize
    struct AuthCounts {
        uint256 outCount;
        uint256 authCount;
    }

    function authorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address caller,
        ProtocolTypes.Output[] calldata inputs
    ) external view returns (address[] memory targets, bytes[] memory data, ProtocolTypes.Output[] memory produced) {
        // PASS 1: Count needed outputs and auth targets
        AuthCounts memory counts = _countAuthRequirements(instrs, caller, inputs);

        // Allocate exact sizes
        targets = new address[](counts.authCount);
        data = new bytes[](counts.authCount);
        produced = new ProtocolTypes.Output[](counts.outCount);

        // PASS 2: Fill Arrays
        _fillAuthArrays(instrs, caller, inputs, targets, data, produced);
    }

    function _countAuthRequirements(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address caller,
        ProtocolTypes.Output[] calldata inputs
    ) private view returns (AuthCounts memory counts) {
        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingInstruction calldata ins = instrs[i];

            // Output counting
            if (_producesOutput(ins.op)) {
                counts.outCount++;
            }

            // Auth counting
            counts.authCount += _countAuthForInstruction(ins, caller, inputs);
        }
    }

    function _producesOutput(ProtocolTypes.LendingOp op) private pure returns (bool) {
        return op == ProtocolTypes.LendingOp.WithdrawCollateral ||
               op == ProtocolTypes.LendingOp.Borrow ||
               op == ProtocolTypes.LendingOp.Repay ||
               op == ProtocolTypes.LendingOp.GetBorrowBalance ||
               op == ProtocolTypes.LendingOp.GetSupplyBalance;
    }

    function _countAuthForInstruction(
        ProtocolTypes.LendingInstruction calldata ins,
        address caller,
        ProtocolTypes.Output[] calldata inputs
    ) private view returns (uint256) {
        (address token, uint256 amount) = _resolveTokenAmount(ins, inputs);

        if (ins.op == ProtocolTypes.LendingOp.Deposit || ins.op == ProtocolTypes.LendingOp.DepositCollateral) {
            return _needsEnterMarkets(token, caller) ? 1 : 0;
        } else if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
            return _needsWithdrawApproval(token, amount, caller) ? 1 : 0;
        } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
            return _needsBorrowDelegation(caller) ? 1 : 0;
        }
        return 0;
    }

    function _resolveTokenAmount(
        ProtocolTypes.LendingInstruction calldata ins,
        ProtocolTypes.Output[] calldata inputs
    ) private pure returns (address token, uint256 amount) {
        token = ins.token;
        amount = ins.amount;
        if (ins.input.index < inputs.length) {
            token = inputs[ins.input.index].token;
            amount = inputs[ins.input.index].amount;
        }
    }

    function _needsEnterMarkets(address token, address caller) private view returns (bool) {
        address vToken = _getVTokenForUnderlying(token);
        try comptroller.checkMembership(caller, vToken) returns (bool m) {
            return !m;
        } catch {
            return true;
        }
    }

    function _needsWithdrawApproval(address token, uint256 amount, address caller) private view returns (bool) {
        address vToken = _getVTokenForUnderlying(token);
        uint256 rate = VTokenInterface(vToken).exchangeRateStored();
        uint256 requiredV = amount == 0 ? type(uint256).max : (amount * 1e18 + rate - 1) / rate;
        uint256 curV = IERC20(vToken).allowance(caller, address(this));
        return amount == 0 || curV < requiredV;
    }

    function _needsBorrowDelegation(address caller) private view returns (bool) {
        try comptroller.approvedDelegates(caller, address(this)) returns (bool a) {
            return !a;
        } catch {
            return true;
        }
    }

    function _fillAuthArrays(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address caller,
        ProtocolTypes.Output[] calldata inputs,
        address[] memory targets,
        bytes[] memory data,
        ProtocolTypes.Output[] memory produced
    ) private view {
        uint256 k = 0; // auth index
        uint256 p = 0; // output index

        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingInstruction calldata ins = instrs[i];
            (address token, uint256 amount) = _resolveTokenAmount(ins, inputs);

            if (ins.op == ProtocolTypes.LendingOp.Deposit || ins.op == ProtocolTypes.LendingOp.DepositCollateral) {
                if (_needsEnterMarkets(token, caller)) {
                    address vToken = _getVTokenForUnderlying(token);
                    address[] memory markets = new address[](1);
                    markets[0] = vToken;
                    targets[k] = address(comptroller);
                    data[k] = abi.encodeWithSelector(ComptrollerInterface.enterMarkets.selector, markets);
                    k++;
                }
            } else if (ins.op == ProtocolTypes.LendingOp.Repay) {
                produced[p++] = ProtocolTypes.Output({ token: token, amount: 0 });
            } else if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
                if (_needsWithdrawApproval(token, amount, caller)) {
                    address vToken = _getVTokenForUnderlying(token);
                    uint256 rate = VTokenInterface(vToken).exchangeRateStored();
                    uint256 requiredV = amount == 0 ? type(uint256).max : (amount * 1e18 + rate - 1) / rate;
                    targets[k] = vToken;
                    data[k] = abi.encodeWithSelector(IERC20.approve.selector, address(this), requiredV);
                    k++;
                }
                produced[p++] = ProtocolTypes.Output({ token: token, amount: amount });
            } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
                if (_needsBorrowDelegation(caller)) {
                    targets[k] = address(comptroller);
                    data[k] = abi.encodeWithSelector(ComptrollerInterface.updateDelegate.selector, address(this), true);
                    k++;
                }
                produced[p++] = ProtocolTypes.Output({ token: token, amount: amount });
            } else if (ins.op == ProtocolTypes.LendingOp.GetBorrowBalance) {
                address vToken = _getVTokenForUnderlying(token);
                uint256 bal = VTokenInterface(vToken).borrowBalanceStored(ins.user);
                produced[p++] = ProtocolTypes.Output({ token: token, amount: (bal * 1001) / 1000 });
            } else if (ins.op == ProtocolTypes.LendingOp.GetSupplyBalance) {
                address vToken = _getVTokenForUnderlying(token);
                uint256 vBal = VTokenInterface(vToken).balanceOf(ins.user);
                uint256 rate = VTokenInterface(vToken).exchangeRateStored();
                uint256 bal = (vBal * rate) / 1e18;
                produced[p++] = ProtocolTypes.Output({ token: token, amount: (bal * 1001) / 1000 });
            }
        }
    }

    function deauthorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address /*caller*/,
        ProtocolTypes.Output[] calldata inputs
    ) external view override returns (address[] memory targets, bytes[] memory data) {
        // Venus can generate multiple revokes per instruction, but standard revokes are 1-to-1
        // We intentionally do NOT exit markets (too disruptive).
        targets = new address[](instrs.length);
        data = new bytes[](instrs.length);

        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingInstruction calldata ins = instrs[i];
            address token = ins.token;
            if (ins.input.index < inputs.length) {
                token = inputs[ins.input.index].token;
            }

            if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
                address vToken = _getVTokenForUnderlying(token);
                targets[i] = vToken;
                data[i] = abi.encodeWithSelector(IERC20.approve.selector, address(this), 0);
            } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
                // Revoke delegate
                targets[i] = address(comptroller);
                data[i] = abi.encodeWithSelector(ComptrollerInterface.updateDelegate.selector, address(this), false);
            } else {
                targets[i] = address(0);
                data[i] = bytes("");
            }
        }
    }

    function _getVTokenForUnderlying(address underlying) internal view returns (address) {
        VTokenInterface[] memory vTokens = comptroller.getAllMarkets();
        for (uint i = 0; i < vTokens.length; i++) {
            try vTokens[i].underlying() returns (address u) {
                if (u == underlying) return address(vTokens[i]);
            } catch {}
        }
        revert("Venus: vToken not found");
    }

    // ============ Emergency Recovery ============

    /// @notice Recover stuck tokens (only callable by router's owner)
    function recoverTokens(address token, address to, uint256 amount) external {
        require(msg.sender == Ownable(ROUTER).owner(), "Only router owner");
        uint256 balance = IERC20(token).balanceOf(address(this));
        uint256 toRecover = amount == type(uint256).max ? balance : amount;
        if (toRecover > balance) toRecover = balance;
        if (toRecover > 0) {
            IERC20(token).safeTransfer(to, toRecover);
        }
    }
}

