contracts/gateways/ProtocolGateway.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

/// @notice Shared base for v2 GatewayWrite/GatewayView contracts.
///         Holds immutable router address and common access modifiers.
abstract contract ProtocolGateway {
    /// @notice Immutable router address that can call protected functions
    address public immutable ROUTER;

    /// @notice Restrict function access to only the router contract
    modifier onlyRouter() {
        require(msg.sender == ROUTER, "Only router can call");
        _;
    }

    /// @notice Restrict access to router or the user themselves
    /// @param user The user the function is being called for
    modifier onlyRouterOrSelf(address user) {
        require(msg.sender == ROUTER || msg.sender == user, "Only router or self can call");
        _;
    }

    /// @param router The address of the router contract that can call protected functions
    constructor(address router) {
        require(router != address(0), "router=0");
        ROUTER = router;
    }
}

contracts/interfaces/venus/ComptrollerIn.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

import "./VTokenInterface.sol";

/**
 * @title Venus's Comptroller Interface
 * @notice Interface for interacting with Venus Comptroller
 * @dev Adapted for compatibility with Solidity ^0.8.10 from Venus v4 documentation
 * @dev Source: https://docs-v4.venus.io/technical-reference/reference-core-pool/comptroller/diamond/facets/market-facet
 */
interface ComptrollerInterface {
    /**
     * @notice Indicator that this is a Comptroller contract (for inspection)
     */
    function isComptroller() external pure returns (bool);

    /**
     * @notice Enters a list of markets (vTokens)
     * @param vTokens The list of addresses of the vToken markets to enter
     * @return success A list of error codes (0=success, otherwise a failure)
     */
    function enterMarkets(address[] calldata vTokens) external returns (uint[] memory);

    /**
     * @notice Exits a vToken market
     * @param vTokenAddress Address of the vToken market to exit
     * @return success 0=success, otherwise a failure
     */
    function exitMarket(address vTokenAddress) external returns (uint);

    /**
     * @notice Returns the list of vToken markets an account has entered
     * @param account The address of the account to query
     * @return The list of vToken markets the account has entered
     */
    function getAssetsIn(address account) external view returns (VTokenInterface[] memory);

    /**
     * @notice Checks if an account is entered into a specific market
     * @param account The address of the account to check
     * @param vToken The vToken market to check
     * @return True if the account is in the market, otherwise false
     */
    function checkMembership(address account, address vToken) external view returns (bool);

    /**
     * @notice Returns a list of all vToken markets
     * @return A list of all vToken market addresses
     */
    function getAllMarkets() external view returns (VTokenInterface[] memory);

    /**
     * @notice Determine the amount of vToken that could be seized in a liquidation
     * @param vTokenBorrowed The borrowed vToken
     * @param vTokenCollateral The collateral vToken
     * @param actualRepayAmount The amount of vTokenBorrowed underlying to repay
     * @return errorCode 0=success, otherwise a failure
     * @return seizeTokens The number of vTokenCollateral tokens to seize
     */
    function liquidateCalculateSeizeTokens(
        address vTokenBorrowed,
        address vTokenCollateral,
        uint actualRepayAmount
    ) external view returns (uint, uint);

    /**
     * @notice Calculate amount of tokens to seize in a VAI liquidation
     * @param vTokenCollateral The collateral vToken
     * @param actualRepayAmount The amount of VAI to repay
     * @return errorCode 0=success, otherwise a failure
     * @return seizeTokens The number of vTokenCollateral tokens to seize
     */
    function liquidateVAICalculateSeizeTokens(address vTokenCollateral, uint actualRepayAmount) external view returns (uint, uint);

    /**
     * @notice Update delegate status to allow/disallow borrowing on behalf
     * @param delegate The delegate address
     * @param allowBorrows Whether to allow the delegate to borrow
     */
    function updateDelegate(address delegate, bool allowBorrows) external;

    function approvedDelegates(address user, address delegate) external view returns (bool);

    /**
     * @notice Return information about a specific market
     * @param vToken The vToken address to get market data for
     * @return isListed Whether the market is listed
     * @return collateralFactorMantissa The collateral factor for the market
     * @return isComped Whether the market is included in XVS distribution
     */
    function markets(address vToken) external view returns (bool isListed, uint collateralFactorMantissa, bool isComped);

    /**
     * @notice Get the account liquidity information
     * @param account The address of the account
     * @return error Error code (0=success, otherwise a failure)
     * @return liquidity The USD value borrowable by the account
     * @return shortfall The USD value of collateral needed to meet obligations
     */
    function getAccountLiquidity(address account) external view returns (uint, uint, uint);

    /**
     * @notice Check if a specific action is allowed
     * @param vToken The vToken market to check
     * @param action The action to check (mint=1, redeem=2, borrow=3, repay=4, etc.)
     * @return True if the action is allowed
     */
    function actionAllowed(address vToken, uint8 action) external view returns (bool);

    /**
     * @notice Checks if a borrow is allowed
     * @param vToken The vToken market to check
     * @param borrower The account borrowing
     * @param borrowAmount The amount of underlying to borrow
     * @return 0=success, otherwise a failure
     */
    function borrowAllowed(address vToken, address borrower, uint borrowAmount) external returns (uint);

    /**
     * @notice Checks if a mint is allowed
     * @param vToken The vToken market to check
     * @param minter The account minting
     * @param mintAmount The amount of underlying to mint
     * @return 0=success, otherwise a failure
     */
    function mintAllowed(address vToken, address minter, uint mintAmount) external returns (uint);

    /**
     * @notice Checks if a redeem is allowed
     * @param vToken The vToken market to check
     * @param redeemer The account redeeming
     * @param redeemTokens The amount of vTokens to redeem
     * @return 0=success, otherwise a failure
     */
    function redeemAllowed(address vToken, address redeemer, uint redeemTokens) external returns (uint);

    /**
     * @notice Checks if a repay is allowed
     * @param vToken The vToken market to check
     * @param payer The account paying
     * @param borrower The account having their loan repaid
     * @param repayAmount The amount of underlying to repay
     * @return 0=success, otherwise a failure
     */
    function repayBorrowAllowed(address vToken, address payer, address borrower, uint repayAmount) external returns (uint);

    /**
     * @notice Returns the price oracle used by the comptroller
     * @return The address of the oracle
     */
    function oracle() external view returns (address);
}

contracts/interfaces/venus/VTokenInterfa.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

/**
 * @title Venus's VToken Interface
 * @notice Interface for interacting with Venus VTokens
 * @dev Adapted for compatibility with Solidity ^0.8.10 from Venus documentation
 */
interface VTokenInterface {
    /*** Market Events ***/
    event AccrueInterest(uint cashPrior, uint interestAccumulated, uint borrowIndex, uint totalBorrows);
    event Mint(address minter, uint mintAmount, uint mintTokens);
    event Redeem(address redeemer, uint redeemAmount, uint redeemTokens);
    event Borrow(address borrower, uint borrowAmount, uint accountBorrows, uint totalBorrows);
    event RepayBorrow(address payer, address borrower, uint repayAmount, uint accountBorrows, uint totalBorrows);
    event LiquidateBorrow(address liquidator, address borrower, uint repayAmount, address vTokenCollateral, uint seizeTokens);

    /*** VToken State ***/
    function underlying() external view returns (address);
    function decimals() external view returns (uint8);
    function totalSupply() external view returns (uint);
    function totalBorrows() external view returns (uint);
    function totalReserves() external view returns (uint);
    function exchangeRateStored() external view returns (uint);
    function accrualBlockNumber() external view returns (uint);
    function comptroller() external view returns (address);
    function symbol() external view returns (string memory);
    function name() external view returns (string memory);
    function reserveFactorMantissa() external view returns (uint);
    function getCash() external view returns (uint);
    function borrowIndex() external view returns (uint);

    /*** User Account Functions ***/
    function balanceOf(address owner) external view returns (uint);
    function allowance(address owner, address spender) external view returns (uint);
    function approve(address spender, uint amount) external returns (bool);
    function transfer(address dst, uint amount) external returns (bool);
    function transferFrom(address src, address dst, uint amount) external returns (bool);

    /*** VToken Market Functions ***/
    function mint(uint mintAmount) external returns (uint);
    function redeem(uint redeemTokens) external returns (uint);
    function redeemUnderlying(uint redeemAmount) external returns (uint);
    function borrow(uint borrowAmount) external returns (uint);
    function borrowBehalf(address borrower, uint borrowAmount) external returns (uint);
    function repayBorrow(uint repayAmount) external returns (uint);
    function repayBorrowBehalf(address borrower, uint repayAmount) external returns (uint);

    /*** Borrow-Related Functions ***/
    function borrowBalanceCurrent(address account) external returns (uint);
    function borrowBalanceStored(address account) external view returns (uint);
    function supplyRatePerBlock() external view returns (uint);
    function borrowRatePerBlock() external view returns (uint);
    function exchangeRateCurrent() external returns (uint);
    function accrueInterest() external returns (uint);

    /*** Liquidation Functions ***/
    function liquidateBorrow(address borrower, uint repayAmount, address vTokenCollateral) external returns (uint);

    /*** Admin Functions ***/
    function _setReserveFactor(uint newReserveFactorMantissa) external returns (uint);
    function _reduceReserves(uint reduceAmount) external returns (uint);
    function _setInterestRateModel(address newInterestRateModel) external returns (uint);
}

contracts/v2/flashloans/FlashLoanConsume.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "hardhat/console.sol";

// --- Existing Balancer imports ---
import { IFlashLoanProvider } from "../interfaces/balancer/IFlashLoanProvider.sol";
import { IVaultV3 } from "../interfaces/balancer/IVaultV3.sol";

// --- Minimal Aave v3 interfaces (flashLoanSimple) ---
interface IAaveV3Pool {
    function flashLoanSimple(
        address receiverAddress,
        address asset,
        uint256 amount,
        bytes calldata params,
        uint16 referralCode
    ) external;
}

interface IAaveFlashLoanSimpleReceiver {
    function executeOperation(
        address asset,
        uint256 amount,
        uint256 premium,
        address initiator,
        bytes calldata params
    ) external returns (bool);
}

// --- Minimal Uniswap v3 pool interface ---
interface IUniswapV3Pool {
    function token0() external view returns (address);

    function token1() external view returns (address);

    function flash(address recipient, uint256 amount0, uint256 amount1, bytes calldata data) external;
}

/// @notice Abstract base supporting Balancer v2/v3, Aave v3 and Uniswap v3 flash sources.
///         Providers are *configurable*; zero address = disabled (and requests revert).
abstract contract FlashLoanConsumerBase is IAaveFlashLoanSimpleReceiver {
    using SafeERC20 for IERC20;

    // ----------------- Providers (config) -----------------
    IFlashLoanProvider public balancerV2Vault; // Balancer v2 Vault
    IVaultV3 public balancerV3Vault; // Balancer v3 Vault
    IAaveV3Pool public aaveV3Pool; // Aave v3 Pool (flashLoanSimple)

    // We gate Uniswap v3 availability by storing a *factory or sentinel address*.
    // The actual pool is passed per-request.
    address public uniswapV3FactoryOrSentinel;

    // ----------------- Internal state guard -----------------
    bool private flashLoanEnabled;
    address private authorizedFlashSender;

    // ----------------- Events (optional, helpful for tracing) -----------------
    event FlashRequested(bytes32 provider, address indexed token, uint256 amount);
    event FlashRepaid(bytes32 provider, address indexed token, uint256 amountWithFee);

    // ----------------- Setters -----------------
    function _setBalancerV2(address provider) internal {
        balancerV2Vault = IFlashLoanProvider(provider);
    }

    function _setBalancerV3(address vault) internal {
        balancerV3Vault = IVaultV3(vault);
    }

    function _setAaveV3(address pool) internal {
        aaveV3Pool = IAaveV3Pool(pool);
    }

    /// @dev Any non-zero address works as an enable switch (factory, router, sentinel).
    function _setUniswapV3Enabled(address factoryOrSentinel) internal {
        uniswapV3FactoryOrSentinel = factoryOrSentinel;
    }

    // ----------------- Enabled flags -----------------
    function balancerV2Enabled() public view returns (bool) {
        return address(balancerV2Vault) != address(0);
    }

    function balancerV3Enabled() public view returns (bool) {
        return address(balancerV3Vault) != address(0);
    }

    function aaveEnabled() public view returns (bool) {
        return address(aaveV3Pool) != address(0);
    }

    function uniswapEnabled() public view returns (bool) {
        return uniswapV3FactoryOrSentinel != address(0);
    }

    // ----------------- Guard modifiers -----------------
    modifier enableFlashLoan() {
        flashLoanEnabled = true;
        _;
        flashLoanEnabled = false;
    }

    modifier flashLoanOnly() {
        require(flashLoanEnabled, "Flash loan not enabled");
        require(msg.sender == authorizedFlashSender, "Unauthorized flash caller");
        _;
    }

    modifier authorizeFlashLoan(address expectedSender) {
        flashLoanEnabled = true;
        authorizedFlashSender = expectedSender;
        _;
        authorizedFlashSender = address(0);
        flashLoanEnabled = false;
    }

    // ============================================================
    //                          BALANCER V2
    // ============================================================

    /// @notice Request a Balancer v2 flash loan (single-asset for simplicity).
    function _requestBalancerV2(
        address token,
        uint256 amount,
        bytes memory userData
    ) internal authorizeFlashLoan(address(balancerV2Vault)) {
        require(balancerV2Enabled(), "Balancer v2 disabled");

        IERC20[] memory tokens = new IERC20[](1);
        tokens[0] = IERC20(token);
        uint256[] memory amounts = new uint256[](1);
        amounts[0] = amount;

        emit FlashRequested("BALANCER_V2", token, amount);
        balancerV2Vault.flashLoan(address(this), tokens, amounts, userData);
    }

    /// @notice Balancer v2 callback.
    function receiveFlashLoan(
        IERC20[] calldata tokens,
        uint256[] calldata amounts,
        uint256[] calldata feeAmounts,
        bytes calldata userData
    ) external flashLoanOnly {
        require(msg.sender == address(balancerV2Vault), "Unauthorized Balancer v2");

        // For single-asset flash loans, pass the repayment amount
        require(tokens.length == 1, "Only single-asset flash loans supported");
        uint256 repayment = amounts[0] + feeAmounts[0];
        _afterFlashLoan(address(tokens[0]), repayment, userData);

        // Repay principal + fee to the vault
        tokens[0].safeTransfer(address(balancerV2Vault), repayment);
        emit FlashRepaid("BALANCER_V2", address(tokens[0]), repayment);
    }

    // ============================================================
    //                          BALANCER V3
    // ============================================================

    /// @notice Request a Balancer v3 "unlocked" context; vault will callback with our calldata.
    function _requestBalancerV3(address token, uint256 amount) internal authorizeFlashLoan(address(balancerV3Vault)) {
        require(balancerV3Enabled(), "Balancer v3 disabled");

        bytes memory userData = abi.encode(token, amount);
        bytes memory callData = abi.encodeWithSelector(this.receiveFlashLoanV3.selector, userData);

        emit FlashRequested("BALANCER_V3", token, amount);
        balancerV3Vault.unlock(callData); // calls back receiveFlashLoanV3 within unlocked context
    }

    /// @notice Balancer v3 callback, runs in unlocked context.
    function receiveFlashLoanV3(bytes calldata userData) external flashLoanOnly {
        require(msg.sender == address(balancerV3Vault), "Unauthorized Balancer v3");

        (address token, uint256 amount) = abi.decode(userData, (address, uint256));

        if (token != address(0) && amount > 0) {
            // Pull funds in unlocked context
            balancerV3Vault.sendTo(token, address(this), amount);
            // For Balancer v3, repayment amount equals the borrowed amount (fee handled by vault accounting)
            _afterFlashLoan(token, amount, userData);
            // Repay + settle (no premium encoded here, handled by vault accounting)
            IERC20(token).safeTransfer(address(balancerV3Vault), amount);
            balancerV3Vault.settle(token, amount);
            emit FlashRepaid("BALANCER_V3", token, amount);
        }
    }

    // ============================================================
    //                            AAVE V3
    // ============================================================

    /// @notice Request an Aave v3 flash loan (simple mode).
    /// @param token Asset to borrow
    /// @param amount Amount to borrow
    /// @param userData Arbitrary data forwarded to executeOperation and to _afterFlashLoan
    function _requestAaveV3(
        address token,
        uint256 amount,
        bytes memory userData
    ) internal authorizeFlashLoan(address(aaveV3Pool)) {
        require(aaveEnabled(), "Aave v3 disabled");
        emit FlashRequested("AAVE_V3", token, amount);
        // referralCode = 0
        aaveV3Pool.flashLoanSimple(address(this), token, amount, userData, 0);
    }

    /// @notice Aave v3 callback. Approve Pool for principal+premium; return true to finalize.
    function executeOperation(
        address asset,
        uint256 amount,
        uint256 premium,
        address initiator,
        bytes calldata params
    ) external override flashLoanOnly returns (bool) {
        require(msg.sender == address(aaveV3Pool), "Unauthorized Aave pool");
        require(initiator == address(this), "Bad initiator");

        uint256 repayment = amount + premium;
        _afterFlashLoan(asset, repayment, params);

        // Repay: approve Pool to pull principal + premium
        // Reset approval to 0 first, then approve new amount (safeApprove pattern for compatibility)
        IERC20(asset).approve(address(aaveV3Pool), 0);
        IERC20(asset).approve(address(aaveV3Pool), repayment);

        emit FlashRepaid("AAVE_V3", asset, repayment);
        console.log("FlashLoanConsumerBase: AaveV3 executeOperation after repayment");
        return true;
    }

    // ============================================================
    //                          UNISWAP V3
    // ============================================================

    /// @notice Request a Uniswap v3 flash (flash-swap) from a specific pool.
    ///         We enforce global enable via uniswapV3FactoryOrSentinel (non-zero).
    /// @param pool Address of the Uniswap v3 pool (must contain `token`)
    /// @param token The token to borrow (must be token0 or token1 of the pool)
    /// @param amount Amount to borrow
    /// @param userData Your opaque data forwarded to the callback and _afterFlashLoan
    function _requestUniswapV3(
        address pool,
        address token,
        uint256 amount,
        bytes memory userData
    ) internal authorizeFlashLoan(pool) {
        require(uniswapEnabled(), "Uniswap v3 disabled");
        require(pool != address(0), "Pool required");
        require(amount > 0, "Amount=0");

        address t0 = IUniswapV3Pool(pool).token0();
        address t1 = IUniswapV3Pool(pool).token1();
        bool is0 = token == t0;
        bool is1 = token == t1;
        require(is0 || is1, "Token not in pool");

        // Encode pool + token orientation so callback can verify msg.sender and repay the correct leg.
        bytes memory data = abi.encode(pool, token, amount, userData, is0);

        emit FlashRequested("UNISWAP_V3", token, amount);
        IUniswapV3Pool(pool).flash(address(this), is0 ? amount : 0, is1 ? amount : 0, data);
    }

    /// @notice Uniswap v3 flash callback. Must repay (amount + fee) to the *pool* (msg.sender).
    function uniswapV3FlashCallback(uint256 fee0, uint256 fee1, bytes calldata data) external flashLoanOnly {
        (address pool, address token, uint256 amount, bytes memory userData, bool is0) = abi.decode(
            data,
            (address, address, uint256, bytes, bool)
        );

        require(msg.sender == pool, "Unauthorized Uniswap pool");

        uint256 fee = is0 ? fee0 : fee1;
        uint256 repayment = amount + fee;
        _afterFlashLoan(token, repayment, userData);

        IERC20(token).safeTransfer(msg.sender, repayment);
        emit FlashRepaid("UNISWAP_V3", token, repayment);
    }

    // ============================================================
    //                       ABSTRACT RESUME HOOK
    // ============================================================

    /// @dev Derived contracts implement how to resume execution (swap/repay/move collateral etc.).
    /// @param token The token address that was borrowed
    /// @param repaymentAmount The total amount that must be repaid (principal + fee)
    /// @param userData Your opaque data forwarded from the request
    ///      Accepts bytes memory to support both calldata (from callbacks) and memory (from decoded data).
    function _afterFlashLoan(address token, uint256 repaymentAmount, bytes memory userData) internal virtual;
}

contracts/v2/gateways/venus/VenusGateway.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { ProtocolGateway } from "../../../gateways/ProtocolGateway.sol";
import { IGateway } from "../../interfaces/IGateway.sol";
import { ProtocolTypes } from "../../interfaces/ProtocolTypes.sol";

import { ComptrollerInterface } from "../../../interfaces/venus/ComptrollerInterface.sol";
import { VTokenInterface } from "../../../interfaces/venus/VTokenInterface.sol";

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

contract VenusGatewayWrite is IGateway, ProtocolGateway, ReentrancyGuard {
    using SafeERC20 for IERC20;

    ComptrollerInterface public immutable comptroller;

    constructor(address router, address _comptroller) ProtocolGateway(router) {
        comptroller = ComptrollerInterface(_comptroller);
    }

    function processLendingInstruction(
        ProtocolTypes.Output[] calldata inputs,
        bytes calldata data
    ) external returns (ProtocolTypes.Output[] memory outputs) {
        ProtocolTypes.LendingInstruction memory ins = abi.decode(data, (ProtocolTypes.LendingInstruction));
        address token = ins.token;
        uint256 amount = ins.amount;
        if (ins.input.index < inputs.length) {
            token = inputs[ins.input.index].token;
            amount = inputs[ins.input.index].amount;
        }

        if (ins.op == ProtocolTypes.LendingOp.Deposit || ins.op == ProtocolTypes.LendingOp.DepositCollateral) {
            deposit(token, ins.user, amount);
            outputs = new ProtocolTypes.Output[](0);
        } else if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
            (address u, uint256 amt) = withdrawCollateral(token, ins.user, amount);
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: u, amount: amt });
        } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
            borrow(token, ins.user, amount);
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: amount });
        } else if (ins.op == ProtocolTypes.LendingOp.Repay) {
            uint256 refund = repay(token, ins.user, amount);
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: refund });
        } else if (ins.op == ProtocolTypes.LendingOp.GetBorrowBalance) {
            address vToken = _getVTokenForUnderlying(token);
            uint256 bal = VTokenInterface(vToken).borrowBalanceCurrent(ins.user);
            // NO BUFFER in execution
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: bal });
        } else if (ins.op == ProtocolTypes.LendingOp.GetSupplyBalance) {
            address vToken = _getVTokenForUnderlying(token);
            uint256 vBal = VTokenInterface(vToken).balanceOf(ins.user);
            uint256 rate = VTokenInterface(vToken).exchangeRateStored();
            uint256 bal = (vBal * rate) / 1e18;
            // NO BUFFER in execution
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: bal });
        } else {
            revert("Venus: unknown op");
        }
    }

    function deposit(address token, address user, uint256 amount) public onlyRouter nonReentrant {
        address vToken = _getVTokenForUnderlying(token);
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(token).approve(vToken, 0);
        IERC20(token).approve(vToken, amount);
        uint err = VTokenInterface(vToken).mint(amount);
        require(err == 0, "Venus: mint failed");
        uint vBal = VTokenInterface(vToken).balanceOf(address(this));
        require(vBal > 0, "Venus: zero vTokens");
        VTokenInterface(vToken).transfer(user, vBal);
    }

    function withdrawCollateral(
        address collateral,
        address user,
        uint256 underlyingAmount
    ) public onlyRouterOrSelf(user) nonReentrant returns (address, uint256) {
        address vToken = _getVTokenForUnderlying(collateral);

        uint256 borrowBalance = VTokenInterface(vToken).borrowBalanceCurrent(user);
        if (underlyingAmount >= borrowBalance) {
            underlyingAmount = borrowBalance;
            uint256 balance = VTokenInterface(vToken).balanceOf(user);
            VTokenInterface(vToken).transferFrom(user, address(this), balance);
            uint256 pre = IERC20(collateral).balanceOf(address(this));
            uint err = VTokenInterface(vToken).redeem(balance);
            require(err == 0, "Venus: redeem failed");
            uint256 balanceAfter = IERC20(collateral).balanceOf(address(this));
            uint256 redeemed = balanceAfter - pre;
            IERC20(collateral).safeTransfer(msg.sender, redeemed);
            return (collateral, redeemed);
        }

        uint exchangeRate = VTokenInterface(vToken).exchangeRateCurrent();
        uint requiredV = (underlyingAmount * 1e18 + exchangeRate - 1) / exchangeRate;
        VTokenInterface(vToken).transferFrom(user, address(this), requiredV);

        uint256 balanceBefore = IERC20(collateral).balanceOf(address(this));
        uint err = VTokenInterface(vToken).redeem(requiredV);
        require(err == 0, "Venus: redeem failed");
        uint256 actualRedeemed = IERC20(collateral).balanceOf(address(this)) - balanceBefore;
        IERC20(collateral).safeTransfer(msg.sender, actualRedeemed);
        return (collateral, actualRedeemed);
    }

    function borrow(address token, address user, uint256 amount) public onlyRouterOrSelf(user) nonReentrant {
        address vToken = _getVTokenForUnderlying(token);
        uint err = VTokenInterface(vToken).borrowBehalf(user, amount);
        require(err == 0, "Venus: borrow failed");
        IERC20(token).safeTransfer(msg.sender, amount);
    }

    function repay(
        address token,
        address user,
        uint256 amount
    ) public onlyRouter nonReentrant returns (uint256 refund) {
        address vToken = _getVTokenForUnderlying(token);
        uint256 pre = IERC20(token).balanceOf(address(this));
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(token).approve(vToken, 0);
        IERC20(token).approve(vToken, amount);
        uint err = VTokenInterface(vToken).repayBorrowBehalf(user, amount);
        require(err == 0, "Venus: repay failed");
        uint256 post = IERC20(token).balanceOf(address(this));
        refund = post > pre ? post - pre : 0;
        if (refund > 0) {
            IERC20(token).safeTransfer(msg.sender, refund);
        }
    }

    function authorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address caller,
        ProtocolTypes.Output[] calldata inputs
    ) external view returns (address[] memory targets, bytes[] memory data, ProtocolTypes.Output[] memory produced) {
        // PASS 1: Count needed outputs and needed auth targets
        // We MUST do this because 1 Venus instruction can generate 2 auth steps (Approve + EnterMarkets)
        uint256 outCount = 0;
        uint256 authCount = 0;

        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingInstruction calldata ins = instrs[i];

            // Output counting
            if (
                ins.op == ProtocolTypes.LendingOp.WithdrawCollateral ||
                ins.op == ProtocolTypes.LendingOp.Borrow ||
                ins.op == ProtocolTypes.LendingOp.Repay ||
                ins.op == ProtocolTypes.LendingOp.GetBorrowBalance ||
                ins.op == ProtocolTypes.LendingOp.GetSupplyBalance
            ) {
                outCount++;
            }

            // Auth counting logic
            address token = ins.token;
            uint256 amount = ins.amount;
            if (ins.input.index < inputs.length) {
                token = inputs[ins.input.index].token;
                amount = inputs[ins.input.index].amount;
            }

            if (
                ins.op == ProtocolTypes.LendingOp.Deposit ||
                ins.op == ProtocolTypes.LendingOp.DepositCollateral
            ) {
                // Enter Markets - required for collateral to count
                address vToken = _getVTokenForUnderlying(token);
                bool member = false;
                try comptroller.checkMembership(caller, vToken) returns (bool m) {
                    member = m;
                } catch {}
                if (!member) {
                    authCount++;
                }
            } else if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
                address vToken = _getVTokenForUnderlying(token);
                uint rate = VTokenInterface(vToken).exchangeRateStored();
                uint requiredV = amount == 0 ? type(uint256).max : (amount * 1e18 + rate - 1) / rate;
                uint256 curV = IERC20(vToken).allowance(caller, address(this));
                if (amount == 0 || curV < requiredV) {
                    authCount++;
                }
            } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
                bool approved = false;
                try comptroller.approvedDelegates(caller, address(this)) returns (bool a) {
                    approved = a;
                } catch {}
                if (!approved) {
                    authCount++;
                }
            }
            // Get* ops require 0 auth
        }

        // Allocate exact sizes
        targets = new address[](authCount);
        data = new bytes[](authCount);
        produced = new ProtocolTypes.Output[](outCount);

        uint256 k = 0; // auth index
        uint256 p = 0; // output index

        // PASS 2: Fill Arrays
        for (uint256 i; i < instrs.length; i++) {
            ProtocolTypes.LendingInstruction calldata ins = instrs[i];

            address token = ins.token;
            uint256 amount = ins.amount;
            if (ins.input.index < inputs.length) {
                token = inputs[ins.input.index].token;
                amount = inputs[ins.input.index].amount;
            }

            if (
                ins.op == ProtocolTypes.LendingOp.Deposit ||
                ins.op == ProtocolTypes.LendingOp.DepositCollateral
            ) {
                // Enter Markets - required for collateral to count
                address vToken = _getVTokenForUnderlying(token);
                bool member = false;
                try comptroller.checkMembership(caller, vToken) returns (bool m) {
                    member = m;
                } catch {
                    // If checkMembership fails, assume not a member to be safe
                    member = false;
                }
                if (!member) {
                    address[] memory markets = new address[](1);
                    markets[0] = vToken;
                    targets[k] = address(comptroller);
                    data[k] = abi.encodeWithSelector(ComptrollerInterface.enterMarkets.selector, markets);
                    k++;
                }
            } else if (ins.op == ProtocolTypes.LendingOp.Repay) {
                produced[p] = ProtocolTypes.Output({ token: token, amount: 0 });
                p++;
            } else if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
                address vToken = _getVTokenForUnderlying(token);
                uint rate = VTokenInterface(vToken).exchangeRateStored();
                uint requiredV = amount == 0 ? type(uint256).max : (amount * 1e18 + rate - 1) / rate;
                uint256 curV = IERC20(vToken).allowance(caller, address(this));

                if (amount == 0 || curV < requiredV) {
                    targets[k] = vToken;
                    data[k] = abi.encodeWithSelector(IERC20.approve.selector, address(this), requiredV);
                    k++;
                }

                produced[p] = ProtocolTypes.Output({ token: token, amount: amount });
                p++;
            } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
                bool approved = false;
                try comptroller.approvedDelegates(caller, address(this)) returns (bool a) {
                    approved = a;
                } catch {}
                if (!approved) {
                    targets[k] = address(comptroller);
                    data[k] = abi.encodeWithSelector(ComptrollerInterface.updateDelegate.selector, address(this), true);
                    k++;
                }
                produced[p] = ProtocolTypes.Output({ token: token, amount: amount });
                p++;
            } else if (ins.op == ProtocolTypes.LendingOp.GetBorrowBalance) {
                address vToken = _getVTokenForUnderlying(token);
                uint256 bal = VTokenInterface(vToken).borrowBalanceStored(ins.user);
                bal = (bal * 1001) / 1000;
                produced[p] = ProtocolTypes.Output({ token: token, amount: bal });
                p++;
            } else if (ins.op == ProtocolTypes.LendingOp.GetSupplyBalance) {
                address vToken = _getVTokenForUnderlying(token);
                uint256 vBal = VTokenInterface(vToken).balanceOf(ins.user);
                uint256 rate = VTokenInterface(vToken).exchangeRateStored();
                uint256 bal = (vBal * rate) / 1e18;
                bal = (bal * 1001) / 1000;
                produced[p] = ProtocolTypes.Output({ token: token, amount: bal });
                p++;
            }
        }
    }

    function deauthorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address /*caller*/,
        ProtocolTypes.Output[] calldata inputs
    ) external view override returns (address[] memory targets, bytes[] memory data) {
        // Venus can generate multiple revokes per instruction, but standard revokes are 1-to-1
        // We intentionally do NOT exit markets (too disruptive).
        targets = new address[](instrs.length);
        data = new bytes[](instrs.length);

        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingInstruction calldata ins = instrs[i];
            address token = ins.token;
            if (ins.input.index < inputs.length) {
                token = inputs[ins.input.index].token;
            }

            if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
                address vToken = _getVTokenForUnderlying(token);
                targets[i] = vToken;
                data[i] = abi.encodeWithSelector(IERC20.approve.selector, address(this), 0);
            } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
                // Revoke delegate
                targets[i] = address(comptroller);
                data[i] = abi.encodeWithSelector(ComptrollerInterface.updateDelegate.selector, address(this), false);
            } else {
                targets[i] = address(0);
                data[i] = bytes("");
            }
        }
    }

    function _getVTokenForUnderlying(address underlying) internal view returns (address) {
        VTokenInterface[] memory vTokens = comptroller.getAllMarkets();
        for (uint i = 0; i < vTokens.length; i++) {
            try vTokens[i].underlying() returns (address u) {
                if (u == underlying) return address(vTokens[i]);
            } catch {}
        }
        revert("Venus: vToken not found");
    }
}

