contracts/gateways/ProtocolGateway.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";

/// @notice Shared base for v2 GatewayWrite/GatewayView contracts.
///         Holds immutable router address and common access modifiers.
abstract contract ProtocolGateway {
    /// @notice Immutable router address that can call protected functions
    address public immutable ROUTER;

    /// @notice Restrict function access to only the router contract
    modifier onlyRouter() {
        require(msg.sender == ROUTER, "Only router can call");
        _;
    }

    /// @notice Restrict access to router or the user themselves
    /// @param user The user the function is being called for
    modifier onlyRouterOrSelf(address user) {
        require(msg.sender == ROUTER || msg.sender == user, "Only router or self can call");
        _;
    }

    /// @notice Restrict access to the router's owner (for emergency recovery)
    modifier onlyRouterOwner() {
        require(msg.sender == Ownable(ROUTER).owner(), "Only router owner");
        _;
    }

    /// @param router The address of the router contract that can call protected functions
    constructor(address router) {
        require(router != address(0), "router=0");
        ROUTER = router;
    }
}

contracts/interfaces/venus/ComptrollerIn.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

import "./VTokenInterface.sol";

/**
 * @title Venus's Comptroller Interface
 * @notice Interface for interacting with Venus Comptroller
 * @dev Adapted for compatibility with Solidity ^0.8.10 from Venus v4 documentation
 * @dev Source: https://docs-v4.venus.io/technical-reference/reference-core-pool/comptroller/diamond/facets/market-facet
 */
interface ComptrollerInterface {
    /**
     * @notice Indicator that this is a Comptroller contract (for inspection)
     */
    function isComptroller() external pure returns (bool);

    /**
     * @notice Enters a list of markets (vTokens)
     * @param vTokens The list of addresses of the vToken markets to enter
     * @return success A list of error codes (0=success, otherwise a failure)
     */
    function enterMarkets(address[] calldata vTokens) external returns (uint[] memory);

    /**
     * @notice Exits a vToken market
     * @param vTokenAddress Address of the vToken market to exit
     * @return success 0=success, otherwise a failure
     */
    function exitMarket(address vTokenAddress) external returns (uint);

    /**
     * @notice Returns the list of vToken markets an account has entered
     * @param account The address of the account to query
     * @return The list of vToken markets the account has entered
     */
    function getAssetsIn(address account) external view returns (VTokenInterface[] memory);

    /**
     * @notice Checks if an account is entered into a specific market
     * @param account The address of the account to check
     * @param vToken The vToken market to check
     * @return True if the account is in the market, otherwise false
     */
    function checkMembership(address account, address vToken) external view returns (bool);

    /**
     * @notice Returns a list of all vToken markets
     * @return A list of all vToken market addresses
     */
    function getAllMarkets() external view returns (VTokenInterface[] memory);

    /**
     * @notice Determine the amount of vToken that could be seized in a liquidation
     * @param vTokenBorrowed The borrowed vToken
     * @param vTokenCollateral The collateral vToken
     * @param actualRepayAmount The amount of vTokenBorrowed underlying to repay
     * @return errorCode 0=success, otherwise a failure
     * @return seizeTokens The number of vTokenCollateral tokens to seize
     */
    function liquidateCalculateSeizeTokens(
        address vTokenBorrowed,
        address vTokenCollateral,
        uint actualRepayAmount
    ) external view returns (uint, uint);

    /**
     * @notice Calculate amount of tokens to seize in a VAI liquidation
     * @param vTokenCollateral The collateral vToken
     * @param actualRepayAmount The amount of VAI to repay
     * @return errorCode 0=success, otherwise a failure
     * @return seizeTokens The number of vTokenCollateral tokens to seize
     */
    function liquidateVAICalculateSeizeTokens(address vTokenCollateral, uint actualRepayAmount) external view returns (uint, uint);

    /**
     * @notice Update delegate status to allow/disallow borrowing on behalf
     * @param delegate The delegate address
     * @param allowBorrows Whether to allow the delegate to borrow
     */
    function updateDelegate(address delegate, bool allowBorrows) external;

    function approvedDelegates(address user, address delegate) external view returns (bool);

    /**
     * @notice Return information about a specific market
     * @dev On Venus v4 core pool, the third value is the liquidation threshold,
     *      NOT an "isComped" flag as on some older Comptroller variants.
     * @param vToken The vToken address to get market data for
     * @return isListed Whether the market is listed
     * @return collateralFactorMantissa The collateral factor (LTV) for the market (scaled by 1e18)
     * @return liquidationThresholdMantissa The liquidation threshold (LLTV) for the market (scaled by 1e18)
     */
    function markets(address vToken)
        external
        view
        returns (
            bool isListed,
            uint256 collateralFactorMantissa,
            uint256 liquidationThresholdMantissa
        );

    /**
     * @notice Get the account liquidity information
     * @param account The address of the account
     * @return error Error code (0=success, otherwise a failure)
     * @return liquidity The USD value borrowable by the account
     * @return shortfall The USD value of collateral needed to meet obligations
     */
    function getAccountLiquidity(address account) external view returns (uint, uint, uint);

    /**
     * @notice Check if a specific action is allowed
     * @param vToken The vToken market to check
     * @param action The action to check (mint=1, redeem=2, borrow=3, repay=4, etc.)
     * @return True if the action is allowed
     */
    function actionAllowed(address vToken, uint8 action) external view returns (bool);

    /**
     * @notice Checks if a borrow is allowed
     * @param vToken The vToken market to check
     * @param borrower The account borrowing
     * @param borrowAmount The amount of underlying to borrow
     * @return 0=success, otherwise a failure
     */
    function borrowAllowed(address vToken, address borrower, uint borrowAmount) external returns (uint);

    /**
     * @notice Checks if a mint is allowed
     * @param vToken The vToken market to check
     * @param minter The account minting
     * @param mintAmount The amount of underlying to mint
     * @return 0=success, otherwise a failure
     */
    function mintAllowed(address vToken, address minter, uint mintAmount) external returns (uint);

    /**
     * @notice Checks if a redeem is allowed
     * @param vToken The vToken market to check
     * @param redeemer The account redeeming
     * @param redeemTokens The amount of vTokens to redeem
     * @return 0=success, otherwise a failure
     */
    function redeemAllowed(address vToken, address redeemer, uint redeemTokens) external returns (uint);

    /**
     * @notice Checks if a repay is allowed
     * @param vToken The vToken market to check
     * @param payer The account paying
     * @param borrower The account having their loan repaid
     * @param repayAmount The amount of underlying to repay
     * @return 0=success, otherwise a failure
     */
    function repayBorrowAllowed(address vToken, address payer, address borrower, uint repayAmount) external returns (uint);

    /**
     * @notice Returns the price oracle used by the comptroller
     * @return The address of the oracle
     */
    function oracle() external view returns (address);
}

contracts/interfaces/venus/VTokenInterfa.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

/**
 * @title Venus's VToken Interface
 * @notice Interface for interacting with Venus VTokens
 * @dev Adapted for compatibility with Solidity ^0.8.10 from Venus documentation
 */
interface VTokenInterface {
    /*** Market Events ***/
    event AccrueInterest(uint cashPrior, uint interestAccumulated, uint borrowIndex, uint totalBorrows);
    event Mint(address minter, uint mintAmount, uint mintTokens);
    event Redeem(address redeemer, uint redeemAmount, uint redeemTokens);
    event Borrow(address borrower, uint borrowAmount, uint accountBorrows, uint totalBorrows);
    event RepayBorrow(address payer, address borrower, uint repayAmount, uint accountBorrows, uint totalBorrows);
    event LiquidateBorrow(address liquidator, address borrower, uint repayAmount, address vTokenCollateral, uint seizeTokens);

    /*** VToken State ***/
    function underlying() external view returns (address);
    function decimals() external view returns (uint8);
    function totalSupply() external view returns (uint);
    function totalBorrows() external view returns (uint);
    function totalReserves() external view returns (uint);
    function exchangeRateStored() external view returns (uint);
    function accrualBlockNumber() external view returns (uint);
    function comptroller() external view returns (address);
    function symbol() external view returns (string memory);
    function name() external view returns (string memory);
    function reserveFactorMantissa() external view returns (uint);
    function getCash() external view returns (uint);
    function borrowIndex() external view returns (uint);

    /*** User Account Functions ***/
    function balanceOf(address owner) external view returns (uint);
    function allowance(address owner, address spender) external view returns (uint);
    function approve(address spender, uint amount) external returns (bool);
    function transfer(address dst, uint amount) external returns (bool);
    function transferFrom(address src, address dst, uint amount) external returns (bool);

    /*** VToken Market Functions ***/
    function mint(uint mintAmount) external returns (uint);
    function redeem(uint redeemTokens) external returns (uint);
    function redeemUnderlying(uint redeemAmount) external returns (uint);
    function borrow(uint borrowAmount) external returns (uint);
    function borrowBehalf(address borrower, uint borrowAmount) external returns (uint);
    function repayBorrow(uint repayAmount) external returns (uint);
    function repayBorrowBehalf(address borrower, uint repayAmount) external returns (uint);

    /*** Borrow-Related Functions ***/
    function borrowBalanceCurrent(address account) external returns (uint);
    function borrowBalanceStored(address account) external view returns (uint);
    function supplyRatePerBlock() external view returns (uint);
    function borrowRatePerBlock() external view returns (uint);
    function exchangeRateCurrent() external returns (uint);
    function accrueInterest() external returns (uint);

    /*** Liquidation Functions ***/
    function liquidateBorrow(address borrower, uint repayAmount, address vTokenCollateral) external returns (uint);

    /*** Admin Functions ***/
    function _setReserveFactor(uint newReserveFactorMantissa) external returns (uint);
    function _reduceReserves(uint reduceAmount) external returns (uint);
    function _setInterestRateModel(address newInterestRateModel) external returns (uint);
}

contracts/v2/gateways/aave/AaveGatewayWr.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { ProtocolGateway } from "../../../gateways/ProtocolGateway.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import { IGateway } from "../../interfaces/IGateway.sol";
import { ProtocolTypes } from "../../interfaces/ProtocolTypes.sol";

import { IPool } from "@aave/core-v3/contracts/interfaces/IPool.sol";
import { IPoolAddressesProvider } from "@aave/core-v3/contracts/interfaces/IPoolAddressesProvider.sol";
import { IPoolDataProvider } from "@aave/core-v3/contracts/interfaces/IPoolDataProvider.sol";

interface IVariableDebtToken {
    function borrowAllowance(address fromUser, address spender) external view returns (uint256);
}

contract AaveGatewayWrite is IGateway, ProtocolGateway, ReentrancyGuard {
    using SafeERC20 for IERC20;

    IPoolAddressesProvider public immutable poolAddressesProvider;
    uint16 public immutable REFERRAL_CODE;

    constructor(address router, address _poolAddressesProvider, uint16 _referralCode) ProtocolGateway(router) {
        poolAddressesProvider = IPoolAddressesProvider(_poolAddressesProvider);
        REFERRAL_CODE = _referralCode;
    }

    function processLendingInstruction(
        ProtocolTypes.Output[] calldata inputs,
        bytes calldata data
    ) external onlyRouter returns (ProtocolTypes.Output[] memory outputs) {
        ProtocolTypes.LendingInstruction memory instr = abi.decode(data, (ProtocolTypes.LendingInstruction));
        address token = instr.token;
        uint256 amount = instr.amount;
        if (instr.input.index < inputs.length) {
            token = inputs[instr.input.index].token;
            amount = inputs[instr.input.index].amount;
        }
        if (instr.op == ProtocolTypes.LendingOp.Deposit || instr.op == ProtocolTypes.LendingOp.DepositCollateral) {
            deposit(token, instr.user, amount);
            outputs = new ProtocolTypes.Output[](0);
        } else if (instr.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
            (address u, uint256 amt) = withdraw(token, instr.user, amount);
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: u, amount: amt });
        } else if (instr.op == ProtocolTypes.LendingOp.Borrow) {
            borrow(token, instr.user, amount);
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: amount });
        } else if (instr.op == ProtocolTypes.LendingOp.Repay) {
            uint256 refund = repay(token, instr.user, amount);
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: refund });
        } else if (instr.op == ProtocolTypes.LendingOp.GetBorrowBalance) {
            uint256 bal = _getBorrowBalance(token, instr.user);
            // NO BUFFER in execution
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: bal });
        } else if (instr.op == ProtocolTypes.LendingOp.GetSupplyBalance) {
            uint256 bal = _getSupplyBalance(token, instr.user);
            // NO BUFFER in execution
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: bal });
        } else if (instr.op == ProtocolTypes.LendingOp.SetEMode) {
            // Note: SetEMode is handled specially - it produces approval targets
            // for the user to call the Pool directly (see authorize function)
            outputs = new ProtocolTypes.Output[](0);
        } else {
            revert("Unknown op");
        }
    }

    function deposit(address token, address onBehalfOf, uint256 amount) internal nonReentrant {
        address pool = poolAddressesProvider.getPool();
        require(pool != address(0), "Pool not set");
        // Router should approve this gateway to pull from router balance
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(token).forceApprove(pool, amount);
        IPool(pool).supply(token, amount, onBehalfOf, REFERRAL_CODE);
    }

    function withdraw(
        address underlying,
        address user,
        uint256 amount
    ) internal nonReentrant returns (address, uint256) {
        address aToken = _getAToken(underlying);
        require(aToken != address(0), "aToken not found");
        IERC20 a = IERC20(aToken);
        uint256 balance = a.balanceOf(user);
        // Clamp amount to balance if requested amount > balance
        // This handles rounding errors (e.g. 1 wei less) and type(uint256).max
        if (amount > balance) {
            amount = balance;
        }
        uint256 allowance = a.allowance(user, address(this));
        require(allowance >= amount, "aToken: allowance");
        a.safeTransferFrom(user, address(this), amount);
        address pool = poolAddressesProvider.getPool();
        require(pool != address(0), "Pool not set");
        uint256 amountOut = IPool(pool).withdraw(underlying, amount, address(this));
        IERC20(underlying).safeTransfer(msg.sender, amountOut);
        return (underlying, amountOut);
    }

    function borrow(address token, address user, uint256 amount) internal nonReentrant {
        address pool = poolAddressesProvider.getPool();
        require(pool != address(0), "Pool not set");
        (, , address vDebt) = _getReserveTokens(token);
        require(vDebt != address(0), "vDebt not found");
        uint256 allowance = IVariableDebtToken(vDebt).borrowAllowance(user, address(this));
        require(allowance >= amount, "borrow allowance");
        IPool(pool).borrow(token, amount, 2, REFERRAL_CODE, user);
        IERC20(token).safeTransfer(msg.sender, amount);
    }

    function repay(
        address token,
        address user,
        uint256 amount
    ) internal nonReentrant returns (uint256 refund) {
        address pool = poolAddressesProvider.getPool();
        require(pool != address(0), "Pool not set");
        uint256 pre = IERC20(token).balanceOf(address(this));
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(token).forceApprove(pool, amount);
        uint256 repaid = IPool(pool).repay(token, amount, 2, user);
        uint256 post = IERC20(token).balanceOf(address(this));
        // Prefer protocol's returned amount when available
        uint256 spent = repaid > 0 && repaid <= amount ? repaid : (pre + amount > post ? pre + amount - post : amount);
        refund = amount > spent ? amount - spent : 0;
        if (refund > 0) {
            IERC20(token).safeTransfer(msg.sender, refund);
        }
    }

    function authorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address caller,
        ProtocolTypes.Output[] calldata inputs
    ) external view returns (address[] memory targets, bytes[] memory data, ProtocolTypes.Output[] memory produced) {
        targets = new address[](instrs.length);
        data = new bytes[](instrs.length);

        // 1. Calculate exact output count to match execution logic
        uint256 outCount = 0;
        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingOp op = instrs[i].op;
            if (
                op == ProtocolTypes.LendingOp.WithdrawCollateral ||
                op == ProtocolTypes.LendingOp.Borrow ||
                op == ProtocolTypes.LendingOp.GetBorrowBalance ||
                op == ProtocolTypes.LendingOp.GetSupplyBalance ||
                op == ProtocolTypes.LendingOp.Repay
            ) {
                outCount++;
            }
        }
        produced = new ProtocolTypes.Output[](outCount);
        uint256 pIdx = 0;

        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingInstruction calldata ins = instrs[i];

            // Simulation logic to determine actual token and amount
            address token = ins.token;
            uint256 amount = ins.amount;
            if (ins.input.index < inputs.length) {
                token = inputs[ins.input.index].token;
                amount = inputs[ins.input.index].amount;
            }

            if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
                address aToken = _getAToken(token);
                uint256 cur = IERC20(aToken).allowance(caller, address(this));
                uint256 required = amount;

                // Add to produced
                produced[pIdx] = ProtocolTypes.Output({ token: token, amount: amount });
                pIdx++;

                if (amount != 0 && cur >= required) {
                    targets[i] = address(0);
                    data[i] = bytes("");
                } else {
                    targets[i] = aToken;
                    data[i] = abi.encodeWithSelector(IERC20.approve.selector, address(this), required);
                }
            } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
                (, , address vDebt) = _getReserveTokens(token);

                // Add to produced
                produced[pIdx] = ProtocolTypes.Output({ token: token, amount: amount });
                pIdx++;

                if (vDebt == address(0)) {
                    targets[i] = address(0);
                    data[i] = bytes("");
                } else {
                    uint256 cur = IVariableDebtToken(vDebt).borrowAllowance(caller, address(this));
                    uint256 required = amount; // Exact amount

                    if (cur >= required && amount != 0) {
                        targets[i] = address(0);
                        data[i] = bytes("");
                    } else {
                        targets[i] = vDebt;
                        data[i] = abi.encodeWithSignature(
                            "approveDelegation(address,uint256)",
                            address(this),
                            type(uint256).max
                        );
                    }
                }
            } else if (ins.op == ProtocolTypes.LendingOp.GetBorrowBalance) {
                uint256 bal = _getBorrowBalance(token, ins.user);
                bal = (bal * 1001) / 1000; // Buffer
                produced[pIdx] = ProtocolTypes.Output({ token: token, amount: bal });
                pIdx++;
                targets[i] = address(0);
                data[i] = bytes("");
            } else if (ins.op == ProtocolTypes.LendingOp.GetSupplyBalance) {
                uint256 bal = _getSupplyBalance(token, ins.user);
                bal = (bal * 1001) / 1000; // Buffer
                produced[pIdx] = ProtocolTypes.Output({ token: token, amount: bal });
                pIdx++;
                targets[i] = address(0);
                data[i] = bytes("");
            } else if (ins.op == ProtocolTypes.LendingOp.Repay) {
                // Repay produces a refund output (usually 0)
                produced[pIdx] = ProtocolTypes.Output({ token: token, amount: 0 });
                pIdx++;
                targets[i] = address(0);
                data[i] = bytes("");
            } else if (ins.op == ProtocolTypes.LendingOp.SetEMode) {
                // SetEMode: user calls Pool.setUserEMode(categoryId) directly
                // amount encodes the categoryId
                address pool = poolAddressesProvider.getPool();
                targets[i] = pool;
                data[i] = abi.encodeWithSignature("setUserEMode(uint8)", uint8(amount));
            } else {
                // Deposit / DepositCollateral produce NO output
                targets[i] = address(0);
                data[i] = bytes("");
            }
        }
    }

    function deauthorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address /*caller*/,
        ProtocolTypes.Output[] calldata inputs
    ) external view override returns (address[] memory targets, bytes[] memory data) {
        targets = new address[](instrs.length);
        data = new bytes[](instrs.length);

        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingInstruction calldata ins = instrs[i];
            address token = ins.token;
            if (ins.input.index < inputs.length) {
                token = inputs[ins.input.index].token;
            }

            if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
                // Revoke aToken approval
                address aToken = _getAToken(token);
                targets[i] = aToken;
                data[i] = abi.encodeWithSelector(IERC20.approve.selector, address(this), 0);
            } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
                // Revoke Credit Delegation
                (, , address vDebt) = _getReserveTokens(token);
                if (vDebt != address(0)) {
                    targets[i] = vDebt;
                    data[i] = abi.encodeWithSignature("approveDelegation(address,uint256)", address(this), 0);
                }
            } else {
                // SetEMode and other ops don't need deauthorization
                targets[i] = address(0);
                data[i] = bytes("");
            }
        }
    }

    /// @notice Returns the Aave Pool address for direct calls (e.g., setUserEMode)
    function getPool() external view returns (address) {
        return poolAddressesProvider.getPool();
    }

    /// @notice Helper to encode setUserEMode calldata
    /// @param categoryId The E-Mode category ID (0 = disable E-Mode)
    /// @return target The Aave Pool address
    /// @return callData The encoded function call
    function encodeSetEMode(uint8 categoryId) external view returns (address target, bytes memory callData) {
        target = poolAddressesProvider.getPool();
        callData = abi.encodeWithSignature("setUserEMode(uint8)", categoryId);
    }

    function _getAToken(address underlying) internal view returns (address) {
        IPoolDataProvider data = IPoolDataProvider(poolAddressesProvider.getPoolDataProvider());
        (address aToken, , ) = data.getReserveTokensAddresses(underlying);
        return aToken;
    }

    function _getReserveTokens(
        address underlying
    ) internal view returns (address aToken, address sDebt, address vDebt) {
        IPoolDataProvider data = IPoolDataProvider(poolAddressesProvider.getPoolDataProvider());
        (aToken, sDebt, vDebt) = data.getReserveTokensAddresses(underlying);
    }

    function _getBorrowBalance(address token, address user) internal view returns (uint256) {
        (, , address vDebt) = _getReserveTokens(token);
        return vDebt == address(0) ? 0 : IERC20(vDebt).balanceOf(user);
    }

    function _getSupplyBalance(address token, address user) internal view returns (uint256) {
        (address aToken, , ) = _getReserveTokens(token);
        return aToken == address(0) ? 0 : IERC20(aToken).balanceOf(user);
    }

    // ============ Emergency Recovery ============

    /// @notice Recover stuck tokens (only callable by router's owner)
    function recoverTokens(address token, address to, uint256 amount) external onlyRouterOwner {
        uint256 balance = IERC20(token).balanceOf(address(this));
        uint256 toRecover = amount == type(uint256).max ? balance : amount;
        if (toRecover > balance) toRecover = balance;
        if (toRecover > 0) {
            IERC20(token).safeTransfer(to, toRecover);
        }
    }
}

contracts/v2/gateways/compound/CompoundG.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { ProtocolGateway } from "../../../gateways/ProtocolGateway.sol";
import { IGateway } from "../../interfaces/IGateway.sol";
import { ProtocolTypes } from "../../interfaces/ProtocolTypes.sol";
import { ICompoundComet } from "../../interfaces/compound/ICompoundComet.sol";

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";

contract CompoundGatewayWrite is IGateway, ProtocolGateway, Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    // base token => Comet
    mapping(address => ICompoundComet) public tokenToComet;

    // registry for view
    address[] private _comets;
    mapping(address => bool) private _isRegistered;

    event CometRegistered(address indexed baseToken, address indexed comet);
    event CometReplaced(address indexed baseToken, address indexed oldComet, address indexed newComet);

    constructor(address router, address owner_) ProtocolGateway(router) Ownable(owner_) {}

    function addComet(ICompoundComet comet) external onlyOwner {
        address base = comet.baseToken();
        require(base != address(0), "Compound: base=0");
        require(!_isRegistered[address(comet)], "Compound: already registered");

        tokenToComet[base] = comet;
        _isRegistered[address(comet)] = true;
        _comets.push(address(comet));
        emit CometRegistered(base, address(comet));
    }

    function setCometForBase(address baseToken, address comet_) external onlyOwner {
        require(baseToken != address(0) && comet_ != address(0), "Compound: zero");
        require(address(tokenToComet[baseToken]) == address(0), "Compound: comet already set");
        require(!_isRegistered[comet_], "Compound: comet already registered");

        tokenToComet[baseToken] = ICompoundComet(comet_);
        emit CometRegistered(baseToken, comet_);
    }

    function allComets() external view returns (address[] memory) {
        return _comets;
    }

    function processLendingInstruction(
        ProtocolTypes.Output[] calldata inputs,
        bytes calldata data
    ) external onlyRouter returns (ProtocolTypes.Output[] memory outputs) {
        ProtocolTypes.LendingInstruction memory ins = abi.decode(data, (ProtocolTypes.LendingInstruction));
        address market = _decodeMarket(ins.context);
        address token = ins.token;
        uint256 amount = ins.amount;
        if (ins.input.index < inputs.length) {
            token = inputs[ins.input.index].token;
            amount = inputs[ins.input.index].amount;
        }

        if (ins.op == ProtocolTypes.LendingOp.Deposit) {
            if (market != address(0) && market != token) depositCollateral(market, token, amount, ins.user);
            else deposit(token, ins.user, amount);
            outputs = new ProtocolTypes.Output[](0);
        } else if (ins.op == ProtocolTypes.LendingOp.DepositCollateral) {
            address base = market != address(0) ? market : token;
            depositCollateral(base, token, amount, ins.user);
            outputs = new ProtocolTypes.Output[](0);
        } else if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
            address base = market != address(0) ? market : token;
            (address u, uint256 amt) = withdrawCollateral(base, token, ins.user, amount);
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: u, amount: amt });
        } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
            borrow(token, ins.user, amount);
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: amount });
        } else if (ins.op == ProtocolTypes.LendingOp.Repay) {
            repay(token, ins.user, amount);
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: 0 });
        } else if (ins.op == ProtocolTypes.LendingOp.GetBorrowBalance) {
            ICompoundComet comet = tokenToComet[token];
            uint256 bal = address(comet) == address(0) ? 0 : comet.borrowBalanceOf(ins.user);
            // NO BUFFER in execution
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: bal });
        } else if (ins.op == ProtocolTypes.LendingOp.GetSupplyBalance) {
            uint256 bal;
            if (market != address(0) && market != token) {
                ICompoundComet comet = tokenToComet[market];
                bal = address(comet) == address(0) ? 0 : uint256(comet.collateralBalanceOf(ins.user, token));
            } else {
                ICompoundComet comet = tokenToComet[token];
                bal = address(comet) == address(0) ? 0 : comet.balanceOf(ins.user);
            }
            // NO BUFFER in execution (Fixes Withdraw revert)
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: bal });
        } else {
            revert("Compound: unknown op");
        }
    }

    function deposit(address token, address onBehalfOf, uint256 amount) internal nonReentrant {
        ICompoundComet comet = tokenToComet[token];
        require(address(comet) != address(0), "Compound: base comet not found");
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(token).forceApprove(address(comet), amount);
        comet.supplyTo(onBehalfOf, token, amount);
    }

    function depositCollateral(
        address market,
        address collateral,
        uint256 amount,
        address receiver
    ) internal nonReentrant {
        ICompoundComet comet = tokenToComet[market];
        require(address(comet) != address(0), "Compound: market comet not found");
        IERC20(collateral).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(collateral).forceApprove(address(comet), amount);
        comet.supplyTo(receiver, collateral, amount);
    }

    function withdrawCollateral(
        address market,
        address collateral,
        address user,
        uint256 amount
    ) internal nonReentrant returns (address, uint256) {
        ICompoundComet comet = tokenToComet[market];
        require(address(comet) != address(0), "Compound: market comet not found");

        // Fix: Use msg.sender (Router) as receiver to simplify flow and ensure authorization alignment
        comet.withdrawFrom(user, msg.sender, collateral, amount);

        return (collateral, amount);
    }

    function borrow(address token, address user, uint256 amount) internal nonReentrant {
        ICompoundComet comet = tokenToComet[token];
        require(address(comet) != address(0), "Compound: base comet not found");
        comet.withdrawFrom(user, address(this), token, amount);
        IERC20(token).safeTransfer(msg.sender, amount);
    }

    function repay(address token, address user, uint256 amount) internal nonReentrant {
        ICompoundComet comet = tokenToComet[token];
        require(address(comet) != address(0), "Compound: base comet not found");
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(token).forceApprove(address(comet), amount);
        comet.supplyTo(user, token, amount);
    }

    function authorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address caller,
        ProtocolTypes.Output[] calldata inputs
    ) external view returns (address[] memory targets, bytes[] memory data, ProtocolTypes.Output[] memory produced) {
        targets = new address[](instrs.length);
        data = new bytes[](instrs.length);
        produced = new ProtocolTypes.Output[](_countOutputs(instrs));
        uint256 pIdx = 0;

        for (uint256 i = 0; i < instrs.length; i++) {
            (address target, bytes memory callData, ProtocolTypes.Output memory output, bool hasOutput) = 
                _processAuthorizeOp(instrs[i], caller, inputs);
            targets[i] = target;
            data[i] = callData;
            if (hasOutput) {
                produced[pIdx++] = output;
            }
        }
    }

    function _countOutputs(ProtocolTypes.LendingInstruction[] calldata instrs) private pure returns (uint256 count) {
        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingOp op = instrs[i].op;
            if (
                op == ProtocolTypes.LendingOp.WithdrawCollateral ||
                op == ProtocolTypes.LendingOp.Borrow ||
                op == ProtocolTypes.LendingOp.Repay ||
                op == ProtocolTypes.LendingOp.GetBorrowBalance ||
                op == ProtocolTypes.LendingOp.GetSupplyBalance
            ) {
                count++;
            }
        }
    }

    function _processAuthorizeOp(
        ProtocolTypes.LendingInstruction calldata ins,
        address caller,
        ProtocolTypes.Output[] calldata inputs
    ) private view returns (address target, bytes memory callData, ProtocolTypes.Output memory output, bool hasOutput) {
        address market = _decodeMarket(ins.context);
        address token = ins.token;
        uint256 amount = ins.amount;
        if (ins.input.index < inputs.length) {
            token = inputs[ins.input.index].token;
            amount = inputs[ins.input.index].amount;
        }

        if (ins.op == ProtocolTypes.LendingOp.Deposit || ins.op == ProtocolTypes.LendingOp.Repay) {
            if (ins.op == ProtocolTypes.LendingOp.Repay) {
                output = ProtocolTypes.Output({ token: token, amount: 0 });
                hasOutput = true;
            }
        } else if (
            ins.op == ProtocolTypes.LendingOp.DepositCollateral ||
            (ins.op == ProtocolTypes.LendingOp.Deposit && market != address(0) && market != token)
        ) {
            // No output
        } else if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
            (target, callData) = _getWithdrawAuth(market, token, caller);
            output = ProtocolTypes.Output({ token: token, amount: amount });
            hasOutput = true;
        } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
            (target, callData) = _getBorrowAuth(token, caller);
            output = ProtocolTypes.Output({ token: token, amount: amount });
            hasOutput = true;
        } else if (ins.op == ProtocolTypes.LendingOp.GetBorrowBalance) {
            uint256 bal = _getBorrowBalance(token, ins.user);
            output = ProtocolTypes.Output({ token: token, amount: (bal * 1001) / 1000 });
            hasOutput = true;
        } else if (ins.op == ProtocolTypes.LendingOp.GetSupplyBalance) {
            uint256 bal = _getSupplyBalance(token, market, ins.user);
            output = ProtocolTypes.Output({ token: token, amount: (bal * 1001) / 1000 });
            hasOutput = true;
        }
    }

    function _getWithdrawAuth(address market, address token, address caller) private view returns (address target, bytes memory callData) {
        address base = market != address(0) ? market : token;
        ICompoundComet comet = tokenToComet[base];
        if (!comet.isAllowed(caller, address(this))) {
            target = address(comet);
            callData = abi.encodeWithSelector(ICompoundComet.allow.selector, address(this), true);
        }
    }

    function _getBorrowAuth(address token, address caller) private view returns (address target, bytes memory callData) {
        ICompoundComet comet = tokenToComet[token];
        if (!comet.isAllowed(caller, address(this))) {
            target = address(comet);
            callData = abi.encodeWithSelector(ICompoundComet.allow.selector, address(this), true);
        }
    }

    function _getBorrowBalance(address token, address user) private view returns (uint256) {
        ICompoundComet comet = tokenToComet[token];
        return address(comet) == address(0) ? 0 : comet.borrowBalanceOf(user);
    }

    function _getSupplyBalance(address token, address market, address user) private view returns (uint256) {
        if (market != address(0) && market != token) {
            ICompoundComet comet = tokenToComet[market];
            return address(comet) == address(0) ? 0 : uint256(comet.collateralBalanceOf(user, token));
        } else {
            ICompoundComet comet = tokenToComet[token];
            return address(comet) == address(0) ? 0 : comet.balanceOf(user);
        }
    }

    function deauthorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address /*caller*/,
        ProtocolTypes.Output[] calldata inputs
    ) external view override returns (address[] memory targets, bytes[] memory data) {
        targets = new address[](instrs.length);
        data = new bytes[](instrs.length);

        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingInstruction calldata ins = instrs[i];
            address market = _decodeMarket(ins.context);
            address token = ins.token;
            if (ins.input.index < inputs.length) {
                token = inputs[ins.input.index].token;
            }

            if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
                address base = market != address(0) ? market : token;
                ICompoundComet comet = tokenToComet[base];
                if (address(comet) != address(0)) {
                    targets[i] = address(comet);
                    // allow(manager, isAllowed) -> false
                    data[i] = abi.encodeWithSelector(ICompoundComet.allow.selector, address(this), false);
                }
            } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
                ICompoundComet comet = tokenToComet[token];
                if (address(comet) != address(0)) {
                    targets[i] = address(comet);
                    data[i] = abi.encodeWithSelector(ICompoundComet.allow.selector, address(this), false);
                }
            } else {
                targets[i] = address(0);
                data[i] = bytes("");
            }
        }
    }

    function _decodeMarket(bytes memory ctx) internal pure returns (address market) {
        if (ctx.length >= 32) {
            assembly {
                market := mload(add(ctx, 32))
            }
        }
    }

    // ============ Emergency Recovery ============

    /// @notice Recover stuck tokens (owner only)
    function recoverTokens(address token, address to, uint256 amount) external onlyOwner {
        uint256 balance = IERC20(token).balanceOf(address(this));
        uint256 toRecover = amount == type(uint256).max ? balance : amount;
        if (toRecover > balance) toRecover = balance;
        if (toRecover > 0) {
            IERC20(token).safeTransfer(to, toRecover);
        }
    }
}

contracts/v2/gateways/euler/EulerGateway.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { ProtocolGateway } from "../../../gateways/ProtocolGateway.sol";
import { IGateway } from "../../interfaces/IGateway.sol";
import { ProtocolTypes } from "../../interfaces/ProtocolTypes.sol";
import { IEVC } from "../../interfaces/euler/IEVC.sol";
import { IEulerVault } from "../../interfaces/euler/IEulerVault.sol";

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title EulerGatewayWrite
 * @notice Gateway for Euler V2 lending protocol
 * @dev Euler V2 uses vault-based architecture with EVC (Ethereum Vault Connector)
 *
 * Context encoding for LendingInstruction:
 *   - bytes context = abi.encode(address borrowVault, address collateralVault)
 *   - borrowVault: The vault to borrow from (becomes controller)
 *   - collateralVault: The vault where collateral is deposited
 *
 * EVC Authorization Model:
 *   - Users must enable collateral vaults via EVC before they can back debt
 *   - Users must enable controller (borrow vault) before borrowing
 *   - Gateway uses operator authorization to act on behalf of users
 */
contract EulerGatewayWrite is IGateway, ProtocolGateway, Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    /// @notice The Ethereum Vault Connector (EVC) singleton
    IEVC public immutable evc;

    error ZeroAddress();
    error InvalidContext();
    error UnsupportedOperation();

    constructor(
        address router,
        address owner_,
        address evc_
    ) ProtocolGateway(router) Ownable(owner_) {
        if (evc_ == address(0)) revert ZeroAddress();
        evc = IEVC(evc_);
    }

    // ============ IGateway Implementation ============

    function processLendingInstruction(
        ProtocolTypes.Output[] calldata inputs,
        bytes calldata data
    ) external onlyRouter returns (ProtocolTypes.Output[] memory) {
        ProtocolTypes.LendingInstruction memory ins = abi.decode(data, (ProtocolTypes.LendingInstruction));
        (address borrowVault, address collateralVault) = _decodeContext(ins.context);

        // Resolve amount from input if referenced
        uint256 amount = ins.input.index < inputs.length ? inputs[ins.input.index].amount : ins.amount;

        return _dispatch(ins.op, borrowVault, collateralVault, amount, ins.user);
    }

    function _dispatch(
        ProtocolTypes.LendingOp op,
        address borrowVault,
        address collateralVault,
        uint256 amount,
        address user
    ) internal returns (ProtocolTypes.Output[] memory) {
        // Deposit operations - no output
        if (op == ProtocolTypes.LendingOp.DepositCollateral) {
            _depositCollateral(collateralVault, amount, user);
            return _noOutput();
        }

        // Collateral operations - output collateral token
        if (op == ProtocolTypes.LendingOp.WithdrawCollateral) {
            address token = IEulerVault(collateralVault).asset();
            return _output(token, _withdrawCollateral(collateralVault, amount, user));
        }
        if (op == ProtocolTypes.LendingOp.GetSupplyBalance) {
            address token = IEulerVault(collateralVault).asset();
            return _output(token, _getSupplyBalance(collateralVault, user));
        }

        // Borrow operations - output loan token
        if (op == ProtocolTypes.LendingOp.Borrow) {
            address token = IEulerVault(borrowVault).asset();
            return _output(token, _borrow(borrowVault, amount, user));
        }
        if (op == ProtocolTypes.LendingOp.Repay) {
            address token = IEulerVault(borrowVault).asset();
            return _output(token, _repay(borrowVault, amount, user));
        }
        if (op == ProtocolTypes.LendingOp.GetBorrowBalance) {
            address token = IEulerVault(borrowVault).asset();
            return _output(token, _getBorrowBalance(borrowVault, user));
        }

        revert UnsupportedOperation();
    }

    // ============ Internal Write Functions ============

    function _depositCollateral(
        address vault,
        uint256 amount,
        address onBehalfOf
    ) internal nonReentrant {
        address token = IEulerVault(vault).asset();
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(token).forceApprove(vault, amount);

        // Deposit to vault, shares credited to onBehalfOf
        IEulerVault(vault).deposit(amount, onBehalfOf);
    }

    function _withdrawCollateral(
        address vault,
        uint256 amount,
        address user
    ) internal nonReentrant returns (uint256) {
        // Clamp to available balance
        uint256 maxWithdraw = IEulerVault(vault).maxWithdraw(user);
        if (amount > maxWithdraw) {
            amount = maxWithdraw;
        }

        if (amount == 0) return 0;

        address token = IEulerVault(vault).asset();
        uint256 preBal = IERC20(token).balanceOf(address(this));

        // Use EVC batch to withdraw on behalf of user
        // Gateway must be authorized as operator via EVC
        IEVC.BatchItem[] memory items = new IEVC.BatchItem[](1);
        items[0] = IEVC.BatchItem({
            targetContract: vault,
            onBehalfOfAccount: user,
            value: 0,
            data: abi.encodeCall(IEulerVault.withdraw, (amount, address(this), user))
        });

        evc.batch(items);

        uint256 postBal = IERC20(token).balanceOf(address(this));
        uint256 withdrawn = postBal - preBal;

        // Forward to router
        IERC20(token).safeTransfer(msg.sender, withdrawn);

        return withdrawn;
    }

    function _borrow(
        address vault,
        uint256 amount,
        address user
    ) internal nonReentrant returns (uint256) {
        // In Euler V2, debt is attributed to the caller (msg.sender) or onBehalfOfAccount via EVC
        // Since gateway is the caller, we must use EVC batch to borrow on behalf of user
        // The borrowed assets are sent to this contract, then forwarded to router

        address token = IEulerVault(vault).asset();
        uint256 preBal = IERC20(token).balanceOf(address(this));

        // Build EVC batch item to borrow on behalf of user
        IEVC.BatchItem[] memory items = new IEVC.BatchItem[](1);
        items[0] = IEVC.BatchItem({
            targetContract: vault,
            onBehalfOfAccount: user,
            value: 0,
            data: abi.encodeCall(IEulerVault.borrow, (amount, address(this)))
        });

        evc.batch(items);

        uint256 postBal = IERC20(token).balanceOf(address(this));
        uint256 borrowed = postBal - preBal;

        IERC20(token).safeTransfer(msg.sender, borrowed);

        return borrowed;
    }

    function _repay(
        address vault,
        uint256 amount,
        address user
    ) internal nonReentrant returns (uint256 refund) {
        address token = IEulerVault(vault).asset();
        uint256 pre = IERC20(token).balanceOf(address(this));

        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(token).forceApprove(vault, amount);

        // Get actual debt to determine repay amount
        uint256 debt = IEulerVault(vault).debtOf(user);

        if (debt > 0) {
            uint256 toRepay = amount > debt ? debt : amount;
            IEulerVault(vault).repay(toRepay, user);
        }

        uint256 post = IERC20(token).balanceOf(address(this));
        refund = post > pre ? post - pre : 0;

        if (refund > 0) {
            IERC20(token).safeTransfer(msg.sender, refund);
        }
    }

    // ============ View Functions ============

    function _getBorrowBalance(address vault, address user) internal view returns (uint256) {
        return IEulerVault(vault).debtOf(user);
    }

    function _getSupplyBalance(address vault, address user) internal view returns (uint256) {
        // Convert shares to assets for accurate balance
        uint256 shares = IEulerVault(vault).balanceOf(user);
        return IEulerVault(vault).convertToAssets(shares);
    }

    // ============ Authorization ============

    /// @dev Helper struct to reduce stack depth in authorize()
    struct AuthState {
        uint256 targetIdx;
        uint256 pIdx;
        bool isOperator;
        bool operatorEmitted;
    }

    function authorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address caller,
        ProtocolTypes.Output[] calldata inputs
    ) external view returns (address[] memory targets, bytes[] memory data, ProtocolTypes.Output[] memory produced) {
        // Count outputs
        uint256 outCount = _countOutputs(instrs);
        produced = new ProtocolTypes.Output[](outCount);

        // Maximum possible targets: operator + collateral enable + controller enable per instruction
        targets = new address[](1 + instrs.length * 2);
        data = new bytes[](1 + instrs.length * 2);

        AuthState memory state;
        state.isOperator = evc.isAccountOperatorAuthorized(caller, address(this));

        for (uint256 i = 0; i < instrs.length; i++) {
            _processAuthInstruction(instrs[i], caller, inputs, targets, data, produced, state);
        }

        // Compact arrays to actual size
        assembly {
            mstore(targets, mload(state))
            mstore(data, mload(state))
        }
    }

    function _countOutputs(ProtocolTypes.LendingInstruction[] calldata instrs) internal pure returns (uint256 count) {
        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingOp op = instrs[i].op;
            if (
                op == ProtocolTypes.LendingOp.WithdrawCollateral ||
                op == ProtocolTypes.LendingOp.Borrow ||
                op == ProtocolTypes.LendingOp.Repay ||
                op == ProtocolTypes.LendingOp.GetBorrowBalance ||
                op == ProtocolTypes.LendingOp.GetSupplyBalance
            ) {
                count++;
            }
        }
    }

    function _processAuthInstruction(
        ProtocolTypes.LendingInstruction calldata ins,
        address caller,
        ProtocolTypes.Output[] calldata inputs,
        address[] memory targets,
        bytes[] memory data,
        ProtocolTypes.Output[] memory produced,
        AuthState memory state
    ) internal view {
        (address borrowVault, address collateralVault) = _decodeContext(ins.context);
        uint256 amount = ins.input.index < inputs.length ? inputs[ins.input.index].amount : ins.amount;

        if (ins.op == ProtocolTypes.LendingOp.DepositCollateral) {
            _authDepositCollateral(caller, collateralVault, targets, data, state);
        } else if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
            _authWithdrawCollateral(caller, collateralVault, ins.token, amount, targets, data, produced, state);
        } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
            _authBorrow(caller, borrowVault, collateralVault, ins.token, amount, targets, data, produced, state);
        } else if (ins.op == ProtocolTypes.LendingOp.GetBorrowBalance) {
            // For auth, use placeholder - actual balance queried at execution time
            produced[state.pIdx++] = ProtocolTypes.Output({ token: ins.token, amount: ins.amount });
        } else if (ins.op == ProtocolTypes.LendingOp.GetSupplyBalance) {
            // For auth, use placeholder - actual balance queried at execution time
            produced[state.pIdx++] = ProtocolTypes.Output({ token: ins.token, amount: ins.amount });
        } else if (ins.op == ProtocolTypes.LendingOp.Repay) {
            produced[state.pIdx++] = ProtocolTypes.Output({ token: ins.token, amount: 0 });
        }
    }

    function _authDepositCollateral(
        address caller,
        address collateralVault,
        address[] memory targets,
        bytes[] memory data,
        AuthState memory state
    ) internal view {
        if (!evc.isCollateralEnabled(caller, collateralVault)) {
            targets[state.targetIdx] = address(evc);
            data[state.targetIdx] = abi.encodeWithSelector(IEVC.enableCollateral.selector, caller, collateralVault);
            state.targetIdx++;
        }
    }

    function _authWithdrawCollateral(
        address caller,
        address /* collateralVault */,
        address token,
        uint256 amount,
        address[] memory targets,
        bytes[] memory data,
        ProtocolTypes.Output[] memory produced,
        AuthState memory state
    ) internal view {
        if (!state.isOperator && !state.operatorEmitted) {
            targets[state.targetIdx] = address(evc);
            data[state.targetIdx] = abi.encodeWithSelector(IEVC.setAccountOperator.selector, caller, address(this), true);
            state.targetIdx++;
            state.operatorEmitted = true;
        }
        produced[state.pIdx++] = ProtocolTypes.Output({ token: token, amount: amount });
    }

    function _authBorrow(
        address caller,
        address borrowVault,
        address collateralVault,
        address token,
        uint256 amount,
        address[] memory targets,
        bytes[] memory data,
        ProtocolTypes.Output[] memory produced,
        AuthState memory state
    ) internal view {
        // Operator authorization
        if (!state.isOperator && !state.operatorEmitted) {
            targets[state.targetIdx] = address(evc);
            data[state.targetIdx] = abi.encodeWithSelector(IEVC.setAccountOperator.selector, caller, address(this), true);
            state.targetIdx++;
            state.operatorEmitted = true;
        }
        // Enable collateral
        if (!evc.isCollateralEnabled(caller, collateralVault)) {
            targets[state.targetIdx] = address(evc);
            data[state.targetIdx] = abi.encodeWithSelector(IEVC.enableCollateral.selector, caller, collateralVault);
            state.targetIdx++;
        }
        // Enable controller
        if (!evc.isControllerEnabled(caller, borrowVault)) {
            targets[state.targetIdx] = address(evc);
            data[state.targetIdx] = abi.encodeWithSelector(IEVC.enableController.selector, caller, borrowVault);
            state.targetIdx++;
        }
        produced[state.pIdx++] = ProtocolTypes.Output({ token: token, amount: amount });
    }

    function deauthorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address caller,
        ProtocolTypes.Output[] calldata /*inputs*/
    ) external view override returns (address[] memory targets, bytes[] memory data) {
        // Check if any operation requires operator privileges
        bool needsDeauth = false;
        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingOp op = instrs[i].op;
            if (op == ProtocolTypes.LendingOp.WithdrawCollateral || op == ProtocolTypes.LendingOp.Borrow) {
                needsDeauth = true;
                break;
            }
        }

        if (needsDeauth) {
            // Revoke operator authorization
            targets = new address[](1);
            data = new bytes[](1);
            targets[0] = address(evc);
            data[0] = abi.encodeWithSelector(
                IEVC.setAccountOperator.selector,
                caller,
                address(this),
                false
            );
        } else {
            targets = new address[](0);
            data = new bytes[](0);
        }
    }

    // ============ Helpers ============

    function _decodeContext(bytes memory ctx) internal pure returns (address borrowVault, address collateralVault) {
        if (ctx.length < 64) revert InvalidContext();
        (borrowVault, collateralVault) = abi.decode(ctx, (address, address));
    }

    function _noOutput() internal pure returns (ProtocolTypes.Output[] memory) {
        return new ProtocolTypes.Output[](0);
    }

    function _output(address token, uint256 amount) internal pure returns (ProtocolTypes.Output[] memory outputs) {
        outputs = new ProtocolTypes.Output[](1);
        outputs[0] = ProtocolTypes.Output({ token: token, amount: amount });
    }

    // ============ Emergency Recovery ============

    /// @notice Recover stuck tokens (owner only)
    function recoverTokens(address token, address to, uint256 amount) external onlyOwner {
        uint256 balance = IERC20(token).balanceOf(address(this));
        uint256 toRecover = amount == type(uint256).max ? balance : amount;
        if (toRecover > balance) toRecover = balance;
        if (toRecover > 0) {
            IERC20(token).safeTransfer(to, toRecover);
        }
    }
}

contracts/v2/gateways/morpho/MorphoBlueG.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { ProtocolGateway } from "../../../gateways/ProtocolGateway.sol";
import { IGateway } from "../../interfaces/IGateway.sol";
import { ProtocolTypes } from "../../interfaces/ProtocolTypes.sol";
import { IMorphoBlue, MarketParams, Market, Position, MorphoLib } from "../../interfaces/morpho/IMorphoBlue.sol";

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title MorphoBlueGatewayWrite
 * @notice Gateway for Morpho Blue lending protocol
 * @dev Morpho Blue uses market-based lending where each market is identified by:
 *      - loanToken: The token being borrowed
 *      - collateralToken: The token used as collateral
 *      - oracle: Price oracle address
 *      - irm: Interest rate model address
 *      - lltv: Liquidation loan-to-value ratio
 *
 * Context encoding for LendingInstruction:
 *   - bytes context = abi.encode(MarketParams) for full market params
 *   - The token field should be the collateral token for collateral ops,
 *     or the loan token for borrow/repay ops
 */
contract MorphoBlueGatewayWrite is IGateway, ProtocolGateway, Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;
    using MorphoLib for MarketParams;

    /// @notice The Morpho Blue singleton contract
    IMorphoBlue public immutable morpho;

    /// @notice Registered markets by their ID
    mapping(bytes32 => MarketParams) public registeredMarkets;

    /// @notice List of registered market IDs for enumeration
    bytes32[] public marketIds;

    /// @notice Whether a market ID is registered
    mapping(bytes32 => bool) public isMarketRegistered;

    event MarketRegistered(bytes32 indexed marketId, address loanToken, address collateralToken);

    error MarketNotRegistered(bytes32 marketId);
    error InvalidMarketParams();
    error ZeroAddress();

    constructor(
        address router,
        address owner_,
        address morpho_
    ) ProtocolGateway(router) Ownable(owner_) {
        if (morpho_ == address(0)) revert ZeroAddress();
        morpho = IMorphoBlue(morpho_);
    }

    /// @notice Register a market for use with this gateway
    /// @param params The market parameters
    function registerMarket(MarketParams calldata params) external onlyOwner {
        if (params.loanToken == address(0) || params.collateralToken == address(0)) {
            revert InvalidMarketParams();
        }

        bytes32 marketId = params.id();

        if (!isMarketRegistered[marketId]) {
            registeredMarkets[marketId] = params;
            marketIds.push(marketId);
            isMarketRegistered[marketId] = true;
            emit MarketRegistered(marketId, params.loanToken, params.collateralToken);
        }
    }

    /// @notice Get all registered market IDs
    function getAllMarketIds() external view returns (bytes32[] memory) {
        return marketIds;
    }

    /// @notice Get market params by ID
    function getMarketParams(bytes32 marketId) external view returns (MarketParams memory) {
        if (!isMarketRegistered[marketId]) revert MarketNotRegistered(marketId);
        return registeredMarkets[marketId];
    }

    function processLendingInstruction(
        ProtocolTypes.Output[] calldata inputs,
        bytes calldata data
    ) external onlyRouter returns (ProtocolTypes.Output[] memory) {
        ProtocolTypes.LendingInstruction memory ins = abi.decode(data, (ProtocolTypes.LendingInstruction));
        MarketParams memory params = _decodeMarketParams(ins.context);

        // Resolve amount from input if referenced
        uint256 amount = ins.input.index < inputs.length ? inputs[ins.input.index].amount : ins.amount;

        return _dispatch(ins.op, params, amount, ins.user);
    }

    function _dispatch(
        ProtocolTypes.LendingOp op,
        MarketParams memory params,
        uint256 amount,
        address user
    ) internal returns (ProtocolTypes.Output[] memory) {
        // Deposit operations - no output
        if (op == ProtocolTypes.LendingOp.Deposit) {
            _supply(params, amount, user);
            return _noOutput();
        }
        if (op == ProtocolTypes.LendingOp.DepositCollateral) {
            _supplyCollateral(params, amount, user);
            return _noOutput();
        }

        // Collateral operations - output collateral token
        if (op == ProtocolTypes.LendingOp.WithdrawCollateral) {
            return _output(params.collateralToken, _withdrawCollateral(params, amount, user));
        }
        if (op == ProtocolTypes.LendingOp.GetSupplyBalance) {
            return _output(params.collateralToken, _getCollateralBalance(params, user));
        }

        // Loan operations - output loan token
        if (op == ProtocolTypes.LendingOp.Borrow) {
            return _output(params.loanToken, _borrow(params, amount, user));
        }
        if (op == ProtocolTypes.LendingOp.Repay) {
            return _output(params.loanToken, _repay(params, amount, user));
        }
        if (op == ProtocolTypes.LendingOp.GetBorrowBalance) {
            return _output(params.loanToken, _getBorrowBalance(params, user));
        }

        revert("MorphoBlue: unknown op");
    }

    function _noOutput() internal pure returns (ProtocolTypes.Output[] memory) {
        return new ProtocolTypes.Output[](0);
    }

    function _output(address token, uint256 amount) internal pure returns (ProtocolTypes.Output[] memory outputs) {
        outputs = new ProtocolTypes.Output[](1);
        outputs[0] = ProtocolTypes.Output({ token: token, amount: amount });
    }

    // ============ Internal Write Functions ============

    function _supply(MarketParams memory params, uint256 amount, address onBehalfOf) internal {
        IERC20(params.loanToken).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(params.loanToken).forceApprove(address(morpho), amount);

        morpho.supply(params, amount, 0, onBehalfOf, "");
    }

    function _supplyCollateral(MarketParams memory params, uint256 amount, address onBehalfOf) internal {
        IERC20(params.collateralToken).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(params.collateralToken).forceApprove(address(morpho), amount);

        morpho.supplyCollateral(params, amount, onBehalfOf, "");
    }

    function _withdrawCollateral(
        MarketParams memory params,
        uint256 amount,
        address user
    ) internal returns (uint256) {
        bytes32 marketId = params.id();
        Position memory pos = morpho.position(marketId, user);

        // Clamp to available collateral
        if (amount > pos.collateral) {
            amount = pos.collateral;
        }

        // Withdraw to router (msg.sender)
        morpho.withdrawCollateral(params, amount, user, msg.sender);

        return amount;
    }

    function _borrow(MarketParams memory params, uint256 amount, address user) internal returns (uint256) {
        // Borrow to this contract, then forward to router
        (uint256 borrowed, ) = morpho.borrow(params, amount, 0, user, address(this));
        IERC20(params.loanToken).safeTransfer(msg.sender, borrowed);
        return borrowed;
    }

    function _repay(MarketParams memory params, uint256 amount, address user) internal returns (uint256 refund) {
        IERC20 loanToken = IERC20(params.loanToken);
        uint256 pre = loanToken.balanceOf(address(this));

        loanToken.safeTransferFrom(msg.sender, address(this), amount);
        loanToken.forceApprove(address(morpho), amount);

        // Get user's actual debt to decide repay strategy
        bytes32 marketId = params.id();
        Position memory pos = morpho.position(marketId, user);

        uint256 repaid;
        if (pos.borrowShares > 0) {
            // Calculate current debt in assets
            Market memory mkt = morpho.market(marketId);
            uint256 debtAssets = mkt.totalBorrowShares > 0
                ? (uint256(pos.borrowShares) * uint256(mkt.totalBorrowAssets) + uint256(mkt.totalBorrowShares) - 1) / uint256(mkt.totalBorrowShares)
                : 0;

            if (amount >= debtAssets) {
                // Full repay: use shares to avoid overflow/rounding issues
                (repaid, ) = morpho.repay(params, 0, pos.borrowShares, user, "");
            } else {
                // Partial repay: use assets
                (repaid, ) = morpho.repay(params, amount, 0, user, "");
            }
        }

        uint256 post = loanToken.balanceOf(address(this));
        refund = post > pre ? post - pre : 0;

        if (refund > 0) {
            loanToken.safeTransfer(msg.sender, refund);
        }
    }

    // ============ View Functions ============

    function _getBorrowBalance(MarketParams memory params, address user) internal returns (uint256) {
        // IMPORTANT: Accrue interest first to get accurate debt including pending interest
        // Morpho Blue lazily accrues interest - position() returns stale values without this
        morpho.accrueInterest(params);

        return _getBorrowBalanceView(params, user);
    }

    /// @dev View version of _getBorrowBalance for use in authorize() - may be slightly stale
    function _getBorrowBalanceView(MarketParams memory params, address user) internal view returns (uint256) {
        bytes32 marketId = params.id();
        Position memory pos = morpho.position(marketId, user);

        if (pos.borrowShares == 0) return 0;

        // Convert shares to assets using market state
        // borrowAssets = borrowShares * totalBorrowAssets / totalBorrowShares
        // Round UP with extra buffer to ensure full debt repayment
        Market memory mkt = morpho.market(marketId);
        if (mkt.totalBorrowShares == 0) return 0;

        uint256 numerator = uint256(pos.borrowShares) * uint256(mkt.totalBorrowAssets);
        uint256 denominator = uint256(mkt.totalBorrowShares);
        // Round up: (a + b - 1) / b, then add 1 extra wei for double-rounding safety
        return ((numerator + denominator - 1) / denominator) + 1;
    }

    function _getCollateralBalance(MarketParams memory params, address user) internal view returns (uint256) {
        bytes32 marketId = params.id();
        Position memory pos = morpho.position(marketId, user);
        return pos.collateral;
    }

    // ============ Authorization ============

    function authorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address caller,
        ProtocolTypes.Output[] calldata inputs
    ) external view returns (address[] memory targets, bytes[] memory data, ProtocolTypes.Output[] memory produced) {
        targets = new address[](instrs.length);
        data = new bytes[](instrs.length);

        // Count outputs
        uint256 outCount = 0;
        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingOp op = instrs[i].op;
            if (
                op == ProtocolTypes.LendingOp.WithdrawCollateral ||
                op == ProtocolTypes.LendingOp.Borrow ||
                op == ProtocolTypes.LendingOp.Repay ||
                op == ProtocolTypes.LendingOp.GetBorrowBalance ||
                op == ProtocolTypes.LendingOp.GetSupplyBalance
            ) {
                outCount++;
            }
        }
        produced = new ProtocolTypes.Output[](outCount);
        uint256 pIdx = 0;

        // Check if caller has authorized this gateway on Morpho
        bool isAuth = morpho.isAuthorized(caller, address(this));
        bool authEmitted = false; // Only emit setAuthorization once

        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingInstruction calldata ins = instrs[i];
            MarketParams memory params = _decodeMarketParams(ins.context);

            address token = ins.token;
            uint256 amount = ins.amount;
            if (ins.input.index < inputs.length) {
                token = inputs[ins.input.index].token;
                amount = inputs[ins.input.index].amount;
            }

            if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
                produced[pIdx] = ProtocolTypes.Output({ token: params.collateralToken, amount: amount });
                pIdx++;

                if (isAuth || authEmitted) {
                    targets[i] = address(0);
                    data[i] = "";
                } else {
                    targets[i] = address(morpho);
                    data[i] = abi.encodeWithSelector(IMorphoBlue.setAuthorization.selector, address(this), true);
                    authEmitted = true;
                }
            } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
                produced[pIdx] = ProtocolTypes.Output({ token: params.loanToken, amount: amount });
                pIdx++;

                if (isAuth || authEmitted) {
                    targets[i] = address(0);
                    data[i] = "";
                } else {
                    targets[i] = address(morpho);
                    data[i] = abi.encodeWithSelector(IMorphoBlue.setAuthorization.selector, address(this), true);
                    authEmitted = true;
                }
            } else if (ins.op == ProtocolTypes.LendingOp.GetBorrowBalance) {
                uint256 bal = _getBorrowBalanceView(params, ins.user);
                bal = (bal * 1001) / 1000; // Buffer
                produced[pIdx] = ProtocolTypes.Output({ token: params.loanToken, amount: bal });
                pIdx++;
                targets[i] = address(0);
                data[i] = "";
            } else if (ins.op == ProtocolTypes.LendingOp.GetSupplyBalance) {
                uint256 bal = _getCollateralBalance(params, ins.user);
                bal = (bal * 1001) / 1000; // Buffer
                produced[pIdx] = ProtocolTypes.Output({ token: params.collateralToken, amount: bal });
                pIdx++;
                targets[i] = address(0);
                data[i] = "";
            } else if (ins.op == ProtocolTypes.LendingOp.Repay) {
                produced[pIdx] = ProtocolTypes.Output({ token: params.loanToken, amount: 0 });
                pIdx++;
                targets[i] = address(0);
                data[i] = "";
            } else {
                // Deposit / DepositCollateral - no auth needed, no output
                targets[i] = address(0);
                data[i] = "";
            }
        }
    }

    function deauthorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address /*caller*/,
        ProtocolTypes.Output[] calldata /*inputs*/
    ) external view override returns (address[] memory targets, bytes[] memory data) {
        targets = new address[](instrs.length);
        data = new bytes[](instrs.length);

        bool needsDeauth = false;
        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingOp op = instrs[i].op;
            if (op == ProtocolTypes.LendingOp.WithdrawCollateral || op == ProtocolTypes.LendingOp.Borrow) {
                needsDeauth = true;
                break;
            }
        }

        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingOp op = instrs[i].op;
            if (needsDeauth && (op == ProtocolTypes.LendingOp.WithdrawCollateral || op == ProtocolTypes.LendingOp.Borrow)) {
                // Only need to deauth once, put it on first sensitive op
                targets[i] = address(morpho);
                data[i] = abi.encodeWithSelector(IMorphoBlue.setAuthorization.selector, address(this), false);
                needsDeauth = false; // Only emit once
            } else {
                targets[i] = address(0);
                data[i] = "";
            }
        }
    }

    // ============ Helpers ============

    function _decodeMarketParams(bytes memory ctx) internal pure returns (MarketParams memory params) {
        if (ctx.length >= 160) {
            // Full MarketParams: 5 * 32 bytes = 160
            params = abi.decode(ctx, (MarketParams));
        } else {
            revert("MorphoBlue: invalid context - expected MarketParams");
        }
    }

    // ============ Emergency Recovery ============

    /// @notice Recover stuck tokens (owner only)
    function recoverTokens(address token, address to, uint256 amount) external onlyOwner {
        uint256 balance = IERC20(token).balanceOf(address(this));
        uint256 toRecover = amount == type(uint256).max ? balance : amount;
        if (toRecover > balance) toRecover = balance;
        if (toRecover > 0) {
            IERC20(token).safeTransfer(to, toRecover);
        }
    }
}

