pragma solidity ^0.8.30;

import { ProtocolGateway } from "../../../gateways/ProtocolGateway.sol";
import { IGateway } from "../../interfaces/IGateway.sol";
import { ProtocolTypes } from "../../interfaces/ProtocolTypes.sol";
import { PendleAdapter } from "./PendleAdapter.sol";

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";

contract PendleGateway is IGateway, ProtocolGateway, Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    PendleAdapter public adapter;

    constructor(address router, address owner_) ProtocolGateway(router) Ownable(owner_) {}

    function setAdapter(address _adapter) external onlyOwner {
        adapter = PendleAdapter(_adapter);
    }

    function processLendingInstruction(
        ProtocolTypes.Output[] calldata inputs,
        bytes calldata data
    ) external onlyRouter override returns (ProtocolTypes.Output[] memory outputs) {
        ProtocolTypes.LendingInstruction memory ins = abi.decode(data, (ProtocolTypes.LendingInstruction));

        // Both Swap and SwapExactOut use the same logic - Pendle's router handles both
        // The Pendle API provides appropriate calldata for each swap type
        if (ins.op == ProtocolTypes.LendingOp.Swap || ins.op == ProtocolTypes.LendingOp.SwapExactOut) {
            address tokenIn = ins.token;
            uint256 amountIn = ins.amount;

            // Resolve input pointer if present
            if (ins.input.index < inputs.length) {
                tokenIn = inputs[ins.input.index].token;
                amountIn = inputs[ins.input.index].amount;
            }

            require(address(adapter) != address(0), "Pendle: Adapter not set");
            require(tokenIn != address(0), "Pendle: Zero token");
            require(amountIn > 0, "Pendle: Zero amount");

            // Decode swap payload from context
            // context should contain: (address tokenOut, uint256 minAmountOut, bytes memory pendleData)
            (address tokenOut, uint256 minAmountOut, bytes memory pendleData) = abi.decode(
                ins.context,
                (address, uint256, bytes)
            );

            // Transfer tokens to adapter
            IERC20(tokenIn).safeTransferFrom(msg.sender, address(adapter), amountIn);

            // Execute swap
            (uint256 amountOut, uint256 amountRefund) = adapter.executeSwap(
                tokenIn,
                tokenOut,
                amountIn,
                minAmountOut,
                pendleData
            );

            // Return outputs: [TokenOut, TokenInRefund]
            outputs = new ProtocolTypes.Output[](2);
            outputs[0] = ProtocolTypes.Output({ token: tokenOut, amount: amountOut });
            outputs[1] = ProtocolTypes.Output({ token: tokenIn, amount: amountRefund });

            // Transfer tokens to Router (msg.sender) so it can handle PushToken/etc
            if (amountOut > 0) {
                IERC20(tokenOut).safeTransfer(msg.sender, amountOut);
            }
            if (amountRefund > 0) {
                IERC20(tokenIn).safeTransfer(msg.sender, amountRefund);
            }
        } else {
            revert("Pendle: Unsupported Op");
        }
    }

    function authorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address /*caller*/,
        ProtocolTypes.Output[] calldata inputs
    )
        external
        pure
        override
        returns (address[] memory targets, bytes[] memory data, ProtocolTypes.Output[] memory produced)
    {
        targets = new address[](instrs.length);
        data = new bytes[](instrs.length);

        uint256 outCount = 0;
        for (uint256 i = 0; i < instrs.length; i++) {
            if (instrs[i].op == ProtocolTypes.LendingOp.Swap || instrs[i].op == ProtocolTypes.LendingOp.SwapExactOut) {
                outCount += 2;
            }
        }
        produced = new ProtocolTypes.Output[](outCount);
        uint256 pIdx = 0;

        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingInstruction calldata ins = instrs[i];

            if (ins.op == ProtocolTypes.LendingOp.Swap || ins.op == ProtocolTypes.LendingOp.SwapExactOut) {
                targets[i] = address(0);
                data[i] = bytes("");

                (address tokenOut, uint256 minAmountOut, ) = abi.decode(ins.context, (address, uint256, bytes));

                produced[pIdx] = ProtocolTypes.Output({ token: tokenOut, amount: minAmountOut });
                pIdx++;

                address tokenIn = ins.token;
                if (ins.input.index < inputs.length) {
                    tokenIn = inputs[ins.input.index].token;
                }
                produced[pIdx] = ProtocolTypes.Output({ token: tokenIn, amount: 0 });
                pIdx++;
            } else {
                targets[i] = address(0);
                data[i] = bytes("");
            }
        }
    }

    function deauthorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address /*caller*/,
        ProtocolTypes.Output[] calldata /*inputs*/
    ) external pure override returns (address[] memory targets, bytes[] memory data) {
        targets = new address[](instrs.length);
        data = new bytes[](instrs.length);
        for (uint256 i = 0; i < instrs.length; i++) {
            targets[i] = address(0);
            data[i] = bytes("");
        }
    }
}

contracts/v2/gateways/venus/VenusGateway.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { ProtocolGateway } from "../../../gateways/ProtocolGateway.sol";
import { IGateway } from "../../interfaces/IGateway.sol";
import { ProtocolTypes } from "../../interfaces/ProtocolTypes.sol";

import { ComptrollerInterface } from "../../../interfaces/venus/ComptrollerInterface.sol";
import { VTokenInterface } from "../../../interfaces/venus/VTokenInterface.sol";

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

contract VenusGatewayWrite is IGateway, ProtocolGateway, ReentrancyGuard {
    using SafeERC20 for IERC20;

    ComptrollerInterface public immutable comptroller;

    constructor(address router, address _comptroller) ProtocolGateway(router) {
        comptroller = ComptrollerInterface(_comptroller);
    }

    function processLendingInstruction(
        ProtocolTypes.Output[] calldata inputs,
        bytes calldata data
    ) external onlyRouter returns (ProtocolTypes.Output[] memory outputs) {
        ProtocolTypes.LendingInstruction memory ins = abi.decode(data, (ProtocolTypes.LendingInstruction));
        address token = ins.token;
        uint256 amount = ins.amount;
        if (ins.input.index < inputs.length) {
            token = inputs[ins.input.index].token;
            amount = inputs[ins.input.index].amount;
        }

        if (ins.op == ProtocolTypes.LendingOp.Deposit || ins.op == ProtocolTypes.LendingOp.DepositCollateral) {
            deposit(token, ins.user, amount);
            outputs = new ProtocolTypes.Output[](0);
        } else if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
            (address u, uint256 amt) = withdrawCollateral(token, ins.user, amount);
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: u, amount: amt });
        } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
            borrow(token, ins.user, amount);
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: amount });
        } else if (ins.op == ProtocolTypes.LendingOp.Repay) {
            uint256 refund = repay(token, ins.user, amount);
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: refund });
        } else if (ins.op == ProtocolTypes.LendingOp.GetBorrowBalance) {
            address vToken = _getVTokenForUnderlying(token);
            uint256 bal = VTokenInterface(vToken).borrowBalanceCurrent(ins.user);
            // NO BUFFER in execution
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: bal });
        } else if (ins.op == ProtocolTypes.LendingOp.GetSupplyBalance) {
            address vToken = _getVTokenForUnderlying(token);
            uint256 vBal = VTokenInterface(vToken).balanceOf(ins.user);
            uint256 rate = VTokenInterface(vToken).exchangeRateStored();
            uint256 bal = (vBal * rate) / 1e18;
            // NO BUFFER in execution
            outputs = new ProtocolTypes.Output[](1);
            outputs[0] = ProtocolTypes.Output({ token: token, amount: bal });
        } else {
            revert("Venus: unknown op");
        }
    }

    function deposit(address token, address user, uint256 amount) internal nonReentrant {
        address vToken = _getVTokenForUnderlying(token);
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(token).forceApprove(vToken, amount);
        uint err = VTokenInterface(vToken).mint(amount);
        require(err == 0, "Venus: mint failed");
        uint vBal = VTokenInterface(vToken).balanceOf(address(this));
        require(vBal > 0, "Venus: zero vTokens");
        VTokenInterface(vToken).transfer(user, vBal);
    }

    function withdrawCollateral(
        address collateral,
        address user,
        uint256 underlyingAmount
    ) internal nonReentrant returns (address, uint256) {
        address vToken = _getVTokenForUnderlying(collateral);

        // Get user's supply balance in vTokens and convert to underlying
        uint256 vBalance = VTokenInterface(vToken).balanceOf(user);
        uint256 exchangeRate = VTokenInterface(vToken).exchangeRateCurrent();
        uint256 supplyBalance = (vBalance * exchangeRate) / 1e18;

        // Clamp to supply balance if requesting more than available
        if (underlyingAmount >= supplyBalance || underlyingAmount == 0) {
            // Withdraw all - transfer all vTokens and redeem
            VTokenInterface(vToken).transferFrom(user, address(this), vBalance);
            uint256 pre = IERC20(collateral).balanceOf(address(this));
            uint err = VTokenInterface(vToken).redeem(vBalance);
            require(err == 0, "Venus: redeem failed");
            uint256 balanceAfter = IERC20(collateral).balanceOf(address(this));
            uint256 redeemed = balanceAfter - pre;
            IERC20(collateral).safeTransfer(msg.sender, redeemed);
            return (collateral, redeemed);
        }

        // Partial withdraw - calculate required vTokens for desired underlying
        uint requiredV = (underlyingAmount * 1e18 + exchangeRate - 1) / exchangeRate;
        VTokenInterface(vToken).transferFrom(user, address(this), requiredV);

        uint256 balanceBefore = IERC20(collateral).balanceOf(address(this));
        uint256 err2 = VTokenInterface(vToken).redeem(requiredV);
        require(err2 == 0, "Venus: redeem failed");
        uint256 actualRedeemed = IERC20(collateral).balanceOf(address(this)) - balanceBefore;
        IERC20(collateral).safeTransfer(msg.sender, actualRedeemed);
        return (collateral, actualRedeemed);
    }

    function borrow(address token, address user, uint256 amount) internal nonReentrant {
        address vToken = _getVTokenForUnderlying(token);
        uint err = VTokenInterface(vToken).borrowBehalf(user, amount);
        require(err == 0, "Venus: borrow failed");
        IERC20(token).safeTransfer(msg.sender, amount);
    }

    function repay(
        address token,
        address user,
        uint256 amount
    ) internal nonReentrant returns (uint256 refund) {
        address vToken = _getVTokenForUnderlying(token);
        uint256 pre = IERC20(token).balanceOf(address(this));
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        IERC20(token).forceApprove(vToken, amount);
        uint err = VTokenInterface(vToken).repayBorrowBehalf(user, amount);
        require(err == 0, "Venus: repay failed");
        uint256 post = IERC20(token).balanceOf(address(this));
        refund = post > pre ? post - pre : 0;
        if (refund > 0) {
            IERC20(token).safeTransfer(msg.sender, refund);
        }
    }

    /// @dev Helper struct to reduce stack depth in authorize
    struct AuthCounts {
        uint256 outCount;
        uint256 authCount;
    }

    function authorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address caller,
        ProtocolTypes.Output[] calldata inputs
    ) external view returns (address[] memory targets, bytes[] memory data, ProtocolTypes.Output[] memory produced) {
        // PASS 1: Count needed outputs and auth targets
        AuthCounts memory counts = _countAuthRequirements(instrs, caller, inputs);

        // Allocate exact sizes
        targets = new address[](counts.authCount);
        data = new bytes[](counts.authCount);
        produced = new ProtocolTypes.Output[](counts.outCount);

        // PASS 2: Fill Arrays
        _fillAuthArrays(instrs, caller, inputs, targets, data, produced);
    }

    function _countAuthRequirements(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address caller,
        ProtocolTypes.Output[] calldata inputs
    ) private view returns (AuthCounts memory counts) {
        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingInstruction calldata ins = instrs[i];

            // Output counting
            if (_producesOutput(ins.op)) {
                counts.outCount++;
            }

            // Auth counting
            counts.authCount += _countAuthForInstruction(ins, caller, inputs);
        }
    }

    function _producesOutput(ProtocolTypes.LendingOp op) private pure returns (bool) {
        return op == ProtocolTypes.LendingOp.WithdrawCollateral ||
               op == ProtocolTypes.LendingOp.Borrow ||
               op == ProtocolTypes.LendingOp.Repay ||
               op == ProtocolTypes.LendingOp.GetBorrowBalance ||
               op == ProtocolTypes.LendingOp.GetSupplyBalance;
    }

    function _countAuthForInstruction(
        ProtocolTypes.LendingInstruction calldata ins,
        address caller,
        ProtocolTypes.Output[] calldata inputs
    ) private view returns (uint256) {
        (address token, uint256 amount) = _resolveTokenAmount(ins, inputs);

        if (ins.op == ProtocolTypes.LendingOp.Deposit || ins.op == ProtocolTypes.LendingOp.DepositCollateral) {
            return _needsEnterMarkets(token, caller) ? 1 : 0;
        } else if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
            return _needsWithdrawApproval(token, amount, caller) ? 1 : 0;
        } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
            return _needsBorrowDelegation(caller) ? 1 : 0;
        }
        return 0;
    }

    function _resolveTokenAmount(
        ProtocolTypes.LendingInstruction calldata ins,
        ProtocolTypes.Output[] calldata inputs
    ) private pure returns (address token, uint256 amount) {
        token = ins.token;
        amount = ins.amount;
        if (ins.input.index < inputs.length) {
            token = inputs[ins.input.index].token;
            amount = inputs[ins.input.index].amount;
        }
    }

    function _needsEnterMarkets(address token, address caller) private view returns (bool) {
        address vToken = _getVTokenForUnderlying(token);
        try comptroller.checkMembership(caller, vToken) returns (bool m) {
            return !m;
        } catch {
            return true;
        }
    }

    function _needsWithdrawApproval(address token, uint256 amount, address caller) private view returns (bool) {
        address vToken = _getVTokenForUnderlying(token);
        uint256 rate = VTokenInterface(vToken).exchangeRateStored();
        uint256 requiredV = amount == 0 ? type(uint256).max : (amount * 1e18 + rate - 1) / rate;
        uint256 curV = IERC20(vToken).allowance(caller, address(this));
        return amount == 0 || curV < requiredV;
    }

    function _needsBorrowDelegation(address caller) private view returns (bool) {
        try comptroller.approvedDelegates(caller, address(this)) returns (bool a) {
            return !a;
        } catch {
            return true;
        }
    }

    function _fillAuthArrays(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address caller,
        ProtocolTypes.Output[] calldata inputs,
        address[] memory targets,
        bytes[] memory data,
        ProtocolTypes.Output[] memory produced
    ) private view {
        uint256 k = 0; // auth index
        uint256 p = 0; // output index

        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingInstruction calldata ins = instrs[i];
            (address token, uint256 amount) = _resolveTokenAmount(ins, inputs);

            if (ins.op == ProtocolTypes.LendingOp.Deposit || ins.op == ProtocolTypes.LendingOp.DepositCollateral) {
                if (_needsEnterMarkets(token, caller)) {
                    address vToken = _getVTokenForUnderlying(token);
                    address[] memory markets = new address[](1);
                    markets[0] = vToken;
                    targets[k] = address(comptroller);
                    data[k] = abi.encodeWithSelector(ComptrollerInterface.enterMarkets.selector, markets);
                    k++;
                }
            } else if (ins.op == ProtocolTypes.LendingOp.Repay) {
                produced[p++] = ProtocolTypes.Output({ token: token, amount: 0 });
            } else if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
                if (_needsWithdrawApproval(token, amount, caller)) {
                    address vToken = _getVTokenForUnderlying(token);
                    uint256 rate = VTokenInterface(vToken).exchangeRateStored();
                    uint256 requiredV = amount == 0 ? type(uint256).max : (amount * 1e18 + rate - 1) / rate;
                    targets[k] = vToken;
                    data[k] = abi.encodeWithSelector(IERC20.approve.selector, address(this), requiredV);
                    k++;
                }
                produced[p++] = ProtocolTypes.Output({ token: token, amount: amount });
            } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
                if (_needsBorrowDelegation(caller)) {
                    targets[k] = address(comptroller);
                    data[k] = abi.encodeWithSelector(ComptrollerInterface.updateDelegate.selector, address(this), true);
                    k++;
                }
                produced[p++] = ProtocolTypes.Output({ token: token, amount: amount });
            } else if (ins.op == ProtocolTypes.LendingOp.GetBorrowBalance) {
                address vToken = _getVTokenForUnderlying(token);
                uint256 bal = VTokenInterface(vToken).borrowBalanceStored(ins.user);
                produced[p++] = ProtocolTypes.Output({ token: token, amount: (bal * 1001) / 1000 });
            } else if (ins.op == ProtocolTypes.LendingOp.GetSupplyBalance) {
                address vToken = _getVTokenForUnderlying(token);
                uint256 vBal = VTokenInterface(vToken).balanceOf(ins.user);
                uint256 rate = VTokenInterface(vToken).exchangeRateStored();
                uint256 bal = (vBal * rate) / 1e18;
                produced[p++] = ProtocolTypes.Output({ token: token, amount: (bal * 1001) / 1000 });
            }
        }
    }

    function deauthorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address /*caller*/,
        ProtocolTypes.Output[] calldata inputs
    ) external view override returns (address[] memory targets, bytes[] memory data) {
        // Venus can generate multiple revokes per instruction, but standard revokes are 1-to-1
        // We intentionally do NOT exit markets (too disruptive).
        targets = new address[](instrs.length);
        data = new bytes[](instrs.length);

        for (uint256 i = 0; i < instrs.length; i++) {
            ProtocolTypes.LendingInstruction calldata ins = instrs[i];
            address token = ins.token;
            if (ins.input.index < inputs.length) {
                token = inputs[ins.input.index].token;
            }

            if (ins.op == ProtocolTypes.LendingOp.WithdrawCollateral) {
                address vToken = _getVTokenForUnderlying(token);
                targets[i] = vToken;
                data[i] = abi.encodeWithSelector(IERC20.approve.selector, address(this), 0);
            } else if (ins.op == ProtocolTypes.LendingOp.Borrow) {
                // Revoke delegate
                targets[i] = address(comptroller);
                data[i] = abi.encodeWithSelector(ComptrollerInterface.updateDelegate.selector, address(this), false);
            } else {
                targets[i] = address(0);
                data[i] = bytes("");
            }
        }
    }

    function _getVTokenForUnderlying(address underlying) internal view returns (address) {
        VTokenInterface[] memory vTokens = comptroller.getAllMarkets();
        for (uint i = 0; i < vTokens.length; i++) {
            try vTokens[i].underlying() returns (address u) {
                if (u == underlying) return address(vTokens[i]);
            } catch {}
        }
        revert("Venus: vToken not found");
    }

    // ============ Emergency Recovery ============

    /// @notice Recover stuck tokens (only callable by router's owner)
    function recoverTokens(address token, address to, uint256 amount) external onlyRouterOwner {
        uint256 balance = IERC20(token).balanceOf(address(this));
        uint256 toRecover = amount == type(uint256).max ? balance : amount;
        if (toRecover > balance) toRecover = balance;
        if (toRecover > 0) {
            IERC20(token).safeTransfer(to, toRecover);
        }
    }
}

contracts/v2/interfaces/compound/ICompou.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

interface ICompoundComet {
    struct AssetInfo {
        uint8 offset;
        address asset;
        address priceFeed;
        uint64 scale;
        uint64 borrowCollateralFactor;
        uint64 liquidateCollateralFactor;
        uint64 liquidationFactor;
        uint128 supplyCap;
    }

    function baseToken() external view returns (address);
    function allow(address manager, bool isAllowed) external;
    function isAllowed(address owner, address manager) external view returns (bool);

    // Supply/withdraw APIs
    function supplyTo(address dst, address asset, uint amount) external;
    function withdrawFrom(address src, address to, address asset, uint amount) external;

    // Views
    function balanceOf(address owner) external view returns (uint256); // base position
    function borrowBalanceOf(address account) external view returns (uint256);
    function collateralBalanceOf(address account, address asset) external view returns (uint128);

    // View methods for view gateway
    function numAssets() external view returns (uint8);
    function getAssetInfo(uint8 i) external view returns (AssetInfo memory);
    function getAssetInfoByAddress(address asset) external view returns (AssetInfo memory);
    function getPrice(address priceFeed) external view returns (uint128);
    function userCollateral(address account, address asset) external view returns (uint128, uint128);
    function getSupplyRate(uint utilization) external view returns (uint64);
    function getBorrowRate(uint utilization) external view returns (uint64);
    function getUtilization() external view returns (uint);
    function baseTokenPriceFeed() external view returns (address);
    function priceScale() external pure returns (uint64);
}

contracts/v2/interfaces/euler/IEulerVaul.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

/// @title IEulerVault
/// @notice Interface for Euler V2 vaults (EVK - Euler Vault Kit)
/// @dev Extends ERC-4626 with borrowing functionality
interface IEulerVault {
    // ============ ERC-4626 Standard Functions ============

    /// @notice The underlying asset of the vault
    function asset() external view returns (address);

    /// @notice Total assets held by the vault
    function totalAssets() external view returns (uint256);

    /// @notice Convert assets to shares
    function convertToShares(uint256 assets) external view returns (uint256);

    /// @notice Convert shares to assets
    function convertToAssets(uint256 shares) external view returns (uint256);

    /// @notice Deposit assets and receive shares
    /// @param assets Amount of assets to deposit
    /// @param receiver Address to receive shares
    /// @return shares Amount of shares minted
    function deposit(uint256 assets, address receiver) external returns (uint256 shares);

    /// @notice Withdraw assets by burning shares
    /// @param assets Amount of assets to withdraw
    /// @param receiver Address to receive assets
    /// @param owner Address whose shares will be burned
    /// @return shares Amount of shares burned
    function withdraw(uint256 assets, address receiver, address owner) external returns (uint256 shares);

    /// @notice Redeem shares for assets
    /// @param shares Amount of shares to redeem
    /// @param receiver Address to receive assets
    /// @param owner Address whose shares will be burned
    /// @return assets Amount of assets received
    function redeem(uint256 shares, address receiver, address owner) external returns (uint256 assets);

    /// @notice Get share balance of an account
    function balanceOf(address account) external view returns (uint256);

    /// @notice Maximum withdrawable assets for an owner
    function maxWithdraw(address owner) external view returns (uint256);

    /// @notice Maximum redeemable shares for an owner
    function maxRedeem(address owner) external view returns (uint256);

    // ============ EVK Borrowing Extensions ============

    /// @notice Borrow assets from the vault
    /// @param assets Amount of assets to borrow
    /// @param receiver Address to receive borrowed assets
    /// @return Amount actually borrowed
    function borrow(uint256 assets, address receiver) external returns (uint256);

    /// @notice Repay borrowed assets
    /// @param assets Amount of assets to repay
    /// @param receiver Address of the borrower whose debt to repay
    /// @return Amount actually repaid
    function repay(uint256 assets, address receiver) external returns (uint256);

    /// @notice Get debt balance of an account
    /// @param account Address to query
    /// @return Amount of debt in asset units
    function debtOf(address account) external view returns (uint256);

    /// @notice Get debt balance in shares
    /// @param account Address to query
    /// @return Amount of debt shares
    function debtOfExact(address account) external view returns (uint256);

    /// @notice Total borrowed assets
    function totalBorrows() external view returns (uint256);

    // ============ Interest Rate ============

    /// @notice Current interest rate
    function interestRate() external view returns (uint256);

    /// @notice Interest accumulator for debt calculation
    function interestAccumulator() external view returns (uint256);

    // ============ Vault Info ============

    /// @notice Name of the vault token
    function name() external view returns (string memory);

    /// @notice Symbol of the vault token
    function symbol() external view returns (string memory);

    /// @notice Decimals of the vault token
    function decimals() external view returns (uint8);
}

contracts/v2/interfaces/euler/IEVC.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

/// @title IEVC
/// @notice Interface for the Ethereum Vault Connector (EVC)
/// @dev The EVC mediates between vaults in the Euler V2 protocol
interface IEVC {
    /// @notice Batch operation item
    struct BatchItem {
        address targetContract;
        address onBehalfOfAccount;
        uint256 value;
        bytes data;
    }

    /// @notice Execute a batch of operations
    /// @param items Array of batch items to execute
    function batch(BatchItem[] calldata items) external payable;

    /// @notice Enable a vault as collateral for an account
    /// @param account The account to enable collateral for
    /// @param vault The vault to enable as collateral
    function enableCollateral(address account, address vault) external;

    /// @notice Disable a vault as collateral for an account
    /// @param account The account to disable collateral for
    /// @param vault The vault to disable as collateral
    function disableCollateral(address account, address vault) external;

    /// @notice Enable a vault as controller for an account (allows borrowing)
    /// @param account The account to enable controller for
    /// @param vault The vault to enable as controller
    function enableController(address account, address vault) external;

    /// @notice Disable controller for an account
    /// @dev Will revert if account has outstanding debt
    /// @param account The account to disable controller for
    function disableController(address account) external;

    /// @notice Check if a vault is enabled as collateral for an account
    /// @param account The account to check
    /// @param vault The vault to check
    /// @return True if the vault is enabled as collateral
    function isCollateralEnabled(address account, address vault) external view returns (bool);

    /// @notice Check if a vault is enabled as controller for an account
    /// @param account The account to check
    /// @param vault The vault to check
    /// @return True if the vault is enabled as controller
    function isControllerEnabled(address account, address vault) external view returns (bool);

    /// @notice Get all collateral vaults for an account
    /// @param account The account to query
    /// @return Array of vault addresses enabled as collateral
    function getCollaterals(address account) external view returns (address[] memory);

    /// @notice Get all controller vaults for an account
    /// @param account The account to query
    /// @return Array of vault addresses enabled as controller
    function getControllers(address account) external view returns (address[] memory);

    /// @notice Set or revoke an operator for an account
    /// @param account The account to set operator for
    /// @param operator The operator address
    /// @param authorized True to authorize, false to revoke
    function setAccountOperator(address account, address operator, bool authorized) external;

    /// @notice Check if an operator is authorized for an account
    /// @param account The account to check
    /// @param operator The operator to check
    /// @return True if the operator is authorized
    function isAccountOperatorAuthorized(address account, address operator) external view returns (bool);

    /// @notice Get the owner of an account (handles sub-accounts)
    /// @param account The account address
    /// @return The owner address
    function getAccountOwner(address account) external view returns (address);
}

contracts/v2/interfaces/IGateway.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

import { ProtocolTypes } from "./ProtocolTypes.sol";

interface IGateway {
    function processLendingInstruction(
        ProtocolTypes.Output[] calldata inputs,
        bytes calldata data
    ) external returns (ProtocolTypes.Output[] memory outputs);

    /// @notice Return user-side authorization calls for the given lending instructions.
    ///         One element per input (or (address(0), bytes("")) if none needed).
    ///         Also returns the produced outputs from the simulation of these instructions.
    function authorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address caller,
        ProtocolTypes.Output[] calldata inputs
    ) external view returns (address[] memory targets, bytes[] memory data, ProtocolTypes.Output[] memory produced);

    /// @notice Return user-side deauthorization calls for the given lending instructions.
    ///         Used to revoke permissions after execution.
    function deauthorize(
        ProtocolTypes.LendingInstruction[] calldata instrs,
        address caller,
        ProtocolTypes.Output[] calldata inputs
    ) external view returns (address[] memory targets, bytes[] memory data);
}

contracts/v2/interfaces/morpho/IMorphoBl.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

/// @title IMorphoBlue Interface
/// @notice Interface for Morpho Blue lending protocol
/// @dev See https://github.com/morpho-org/morpho-blue

struct MarketParams {
    address loanToken;
    address collateralToken;
    address oracle;
    address irm;
    uint256 lltv;
}

struct Market {
    uint128 totalSupplyAssets;
    uint128 totalSupplyShares;
    uint128 totalBorrowAssets;
    uint128 totalBorrowShares;
    uint128 lastUpdate;
    uint128 fee;
}

struct Position {
    uint256 supplyShares;
    uint128 borrowShares;
    uint128 collateral;
}

interface IMorphoBlue {
    /// @notice The state of a market (by id)
    function market(bytes32 id) external view returns (Market memory);

    /// @notice The position of a user in a market
    function position(bytes32 id, address user) external view returns (Position memory);

    /// @notice Whether an address is authorized to act on behalf of another
    function isAuthorized(address authorizer, address authorized) external view returns (bool);

    /// @notice The nonce of an authorizer for EIP-712 signatures
    function nonce(address authorizer) external view returns (uint256);

    /// @notice Compute market ID from market params
    function idToMarketParams(bytes32 id) external view returns (MarketParams memory);

    // ============ Write Functions ============

    /// @notice Supply assets to a market (lender side - earns yield)
    /// @param marketParams The market to supply to
    /// @param assets The amount of assets to supply (use 0 if using shares)
    /// @param shares The amount of shares to mint (use 0 if using assets)
    /// @param onBehalf The address to supply on behalf of
    /// @param data Callback data (empty for no callback)
    /// @return assetsSupplied The actual amount of assets supplied
    /// @return sharesSupplied The actual amount of shares minted
    function supply(
        MarketParams memory marketParams,
        uint256 assets,
        uint256 shares,
        address onBehalf,
        bytes memory data
    ) external returns (uint256 assetsSupplied, uint256 sharesSupplied);

    /// @notice Withdraw assets from a market (lender side)
    /// @param marketParams The market to withdraw from
    /// @param assets The amount of assets to withdraw (use 0 if using shares)
    /// @param shares The amount of shares to burn (use 0 if using assets)
    /// @param onBehalf The address to withdraw on behalf of
    /// @param receiver The address to receive the withdrawn assets
    /// @return assetsWithdrawn The actual amount of assets withdrawn
    /// @return sharesWithdrawn The actual amount of shares burned
    function withdraw(
        MarketParams memory marketParams,
        uint256 assets,
        uint256 shares,
        address onBehalf,
        address receiver
    ) external returns (uint256 assetsWithdrawn, uint256 sharesWithdrawn);

    /// @notice Borrow assets from a market
    /// @param marketParams The market to borrow from
    /// @param assets The amount of assets to borrow (use 0 if using shares)
    /// @param shares The amount of borrow shares to mint (use 0 if using assets)
    /// @param onBehalf The address to borrow on behalf of
    /// @param receiver The address to receive the borrowed assets
    /// @return assetsBorrowed The actual amount of assets borrowed
    /// @return sharesBorrowed The actual amount of borrow shares minted
    function borrow(
        MarketParams memory marketParams,
        uint256 assets,
        uint256 shares,
        address onBehalf,
        address receiver
    ) external returns (uint256 assetsBorrowed, uint256 sharesBorrowed);

    /// @notice Repay borrowed assets
    /// @param marketParams The market to repay to
    /// @param assets The amount of assets to repay (use 0 if using shares)
    /// @param shares The amount of borrow shares to burn (use 0 if using assets)
    /// @param onBehalf The address to repay on behalf of
    /// @param data Callback data (empty for no callback)
    /// @return assetsRepaid The actual amount of assets repaid
    /// @return sharesRepaid The actual amount of borrow shares burned
    function repay(
        MarketParams memory marketParams,
        uint256 assets,
        uint256 shares,
        address onBehalf,
        bytes memory data
    ) external returns (uint256 assetsRepaid, uint256 sharesRepaid);

    /// @notice Supply collateral to a market
    /// @param marketParams The market to supply collateral to
    /// @param assets The amount of collateral assets to supply
    /// @param onBehalf The address to supply on behalf of
    /// @param data Callback data (empty for no callback)
    function supplyCollateral(
        MarketParams memory marketParams,
        uint256 assets,
        address onBehalf,
        bytes memory data
    ) external;

    /// @notice Withdraw collateral from a market
    /// @param marketParams The market to withdraw collateral from
    /// @param assets The amount of collateral assets to withdraw
    /// @param onBehalf The address to withdraw on behalf of
    /// @param receiver The address to receive the withdrawn collateral
    function withdrawCollateral(
        MarketParams memory marketParams,
        uint256 assets,
        address onBehalf,
        address receiver
    ) external;

    /// @notice Set authorization for an address to act on behalf of the caller
    /// @param authorized The address to authorize/deauthorize
    /// @param newIsAuthorized Whether to authorize (true) or deauthorize (false)
    function setAuthorization(address authorized, bool newIsAuthorized) external;

    /// @notice Set authorization with signature (EIP-712)
    function setAuthorizationWithSig(
        address authorizer,
        address authorized,
        bool newIsAuthorized,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external;

    /// @notice Accrue interest for a market
    function accrueInterest(MarketParams memory marketParams) external;

    /// @notice Executes a flash loan.
    /// @dev Flash loans have access to the whole balance of the contract (all markets combined).
    /// @dev Zero fees - flashFee is always zero.
    /// @param token The token to flash loan.
    /// @param assets The amount of assets to flash loan.
    /// @param data Arbitrary data to pass to the `onMorphoFlashLoan` callback.
    function flashLoan(address token, uint256 assets, bytes calldata data) external;
}

/// @title IMorphoFlashLoanCallback
/// @notice Interface that users willing to use `flashLoan`'s callback must implement.
interface IMorphoFlashLoanCallback {
    /// @notice Callback called when a flash loan occurs.
    /// @param assets The amount of assets that was flash loaned.
    /// @param data Arbitrary data passed to the `flashLoan` function.
    function onMorphoFlashLoan(uint256 assets, bytes calldata data) external;
}

/// @notice Library for computing Morpho Blue market IDs
library MorphoLib {
    /// @notice Compute the market ID from market params
    function id(MarketParams memory params) internal pure returns (bytes32) {
        return keccak256(abi.encode(params));
    }
}

contracts/v2/interfaces/ProtocolTypes.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.30;

library ProtocolTypes {
    struct ProtocolInstruction {
        string protocolName;
        bytes data;
    }

    struct Output {
        address token;
        uint256 amount;
    }

    // Common lending instruction schema for gateways (deposit/withdraw/borrow/repay)
    enum LendingOp {
        Deposit,
        DepositCollateral,
        WithdrawCollateral,
        Borrow,
        Repay,
        GetBorrowBalance,
        GetSupplyBalance,
        Swap,
        SwapExactOut,
        SetEMode  // Aave V3 E-Mode: amount encodes categoryId (0 = disable E-Mode)
    }
    struct InputPtr {
        uint256 index;
    }
    /**
     * Cross-protocol instruction.
     * - For Compound you can pass `context = abi.encode(address marketBaseToken)`
     *   which tells the gateway which Comet (market) to use. Empty means infer from token.
     */
    struct LendingInstruction {
        LendingOp op;
        address token; // underlying being acted on (base or collateral)
        address user; // end user on whose account we operate
        uint256 amount; // amount in underlying units
        bytes context; // (optional) protocol-specific blob (e.g., Compound marketBaseToken)
        InputPtr input; // (optional) pointer to prior output (UTXO); use if index < inputs.length
    }
}
